#define PI 3.14159265359
const int MAX_MARCHING_STEPS = 164;
const float EPSILON = 0.0015;
const float NEAR_CLIP = 0.0;
const float FAR_CLIP = 80.00;

vec3 lightDirection = vec3(0.702, 0.1686, 0.6745);

// https://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }

float sdBox( vec3 p, vec3 b ){
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float onion( in float d, in float h ){
    return abs(d)-h;
}

float sdTorus( vec3 p, vec2 t ){
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float bendTorus( vec3 p, vec2 dim ){
    float wave = sin(iTime * 2.0) * 0.2;
    float c = cos(wave*p.x);
    float s = sin(wave*p.x);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3( p.xy*m, p.z);
    return sdTorus(q, dim);
}

float bendBox( vec3 p, vec3 dim ){
    float wave = sin(iTime * 2.0) * 0.2;
    float c = cos(wave*p.x);
    float s = sin(wave*p.x);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3( p.x, m*p.yz);
    return sdBox(q, dim);
}

float map(vec3 pos){
    float thick = 0.08;
    float d = onion(bendTorus( pos.xzy, vec2(1.0,0.2) ), thick);

    d = max( d, pos.y+cos(iTime+1.2));
    float d1 = onion(bendTorus( pos.xzy, vec2(1.1,0.5) ), thick-0.01);
    d1 = max( d1, pos.x+sin(iTime-0.3));

    float d2 = onion(bendTorus( pos.xzy, vec2(1.2,0.8) ), thick-0.02);
    d2 = max( d2, pos.y+cos(iTime-0.9));

    float d3 = onion(bendTorus( pos.xzy, vec2(1.3,1.1) ), thick-0.03);
    d3 = max( d3, pos.x+sin(iTime+0.5));

    vec3 posBox = pos;
    float boxZ = 0.1;
    float box = bendBox(posBox, vec3(12.5, 3.1, boxZ));

    // cut it all in half so that the interior parts are visible
    float tori = min(d3,min(d2,min(d1, d)));
    return opSubtraction(box,tori);
}    

vec2 squareFrame(vec2 res, vec2 coord){
    vec2 uv = 2.0 * coord.xy / res.xy - 1.0;
    uv.x *= res.x / res.y;
    return uv;
}

float raymarching(vec3 eye, vec3 marchingDirection){
    float depth = NEAR_CLIP;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = map(eye + depth * marchingDirection);
        if (dist < EPSILON){
            return depth;
        }

        depth += dist;

        if (depth >= FAR_CLIP) {
            return FAR_CLIP;
        }
    }
    return FAR_CLIP;
}

float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax ) {
    float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ ) {
        float h = map( ro + rd*t );
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

float ao( in vec3 pos, in vec3 nor ){
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.06*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos );
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

vec3 computeNormal(vec3 pos){
    vec2 eps = vec2(0.01, 0.);
    return normalize(vec3(
        map(pos + eps.xyy) - map(pos - eps.xyy),
        map(pos + eps.yxy) - map(pos - eps.yxy),
        map(pos + eps.yyx) - map(pos - eps.yyx)
    ));
}

float diffuse(vec3 normal){
    float ambient = 0.3;
    return clamp( dot(normal, lightDirection) * ambient + ambient, 0.0, 1.0 );
}

float specular(vec3 normal, vec3 dir){
    vec3 h = normalize(normal - dir);
    float specularityCoef = 40.;
    return clamp( pow(max(dot(h, normal), 0.), specularityCoef), 0.0, 1.0);
}

float fresnel(vec3 normal, vec3 dir){
    return pow( clamp(1.0+dot(normal,dir),0.0,1.0), 2.0 );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr ){
    vec3 cw = normalize(ta-ro);
    vec3 cp = vec3(sin(cr), cos(cr),0.0);
    vec3 cu = normalize( cross(cw,cp) );
    vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = squareFrame(iResolution.xy,fragCoord.xy);

    float camSpeed = 1.0;
    vec3 eye = vec3( 
               0.5+2.5*sin(camSpeed*iTime),
                2.5,
                2.3 - 3.0*cos(camSpeed*iTime)
    );

    vec3 ta = vec3(0.0, 0.0, 0.0);
    mat3 camera = setCamera( eye, ta, 0.0 );
    float fov = 1.5;
    vec3 dir = camera * normalize(vec3(uv, fov));

    float shortestDistanceToScene = raymarching(eye, dir);

    vec3 color;
    vec3 bgColor = vec3(0.086, 0.290, 0.800);

    if (shortestDistanceToScene < FAR_CLIP - EPSILON) {
        vec3 collision = (eye += (shortestDistanceToScene*0.995) * dir );
        float shadow  = softshadow(collision, lightDirection, 0.02, 2.5 );
        vec3 normal = computeNormal(collision);
        float diffLight = diffuse(normal);
        float specLight = specular(normal, dir);
        float fresnelLight = fresnel(normal, dir);
        float ambientOcc = ao(collision, normal);
        vec3 texCol = vec3(1.00, 0.352, 0.207);
        color = (diffLight + specLight + fresnelLight) * texCol;

        shadow = mix(shadow, 1.0, 0.7);
        color = color * ambientOcc * shadow;

    } else {
        color = bgColor;
    }


    fragColor = vec4(clamp(color,0.0,1.0) , 1.0);
}

#define PI 3.1415927

// draw a moving bullet.
const float TIME_PHASE1 = 2.0;
// draw explosion.
const float TIME_PHASE2 = 2.5;

const float BULLET_RADIUS = 4.0;
const float BULLET_RADIUS2 = 9.0;
const float EXPLOSION_RADIUS = 15.0;
const vec3 BULLET_COLOR = vec3(1.0, 0.6, 0.0);
const vec4 EXPLOSION_COLOR = vec4(1.0, 0.6, 0.0, 1.0);
const vec4 EXPLOSION_SQUARE_COLOR = vec4(1.0, 1.0, 0.6, 1.0);
const int SQUARE_COUNT = 3;
const float SQUARE_MAX_HALF_WIDTH = 11.0;

// the speed of bullet.
float MOVING_SPEED;
float START_X;
float START_Y;
float explosionX;
float explosionY;

vec2 uv;

void drawBackground(out vec4 fragColor){
    fragColor = vec4(vec3(texture(iChannel0, uv/iResolution.xy)), 1.0);
}

// xy: position of squares' center.
// z: the half of width and height of the squares.
vec4 square[SQUARE_COUNT];
void drawExplosion(float t, out vec4 fragColor){
    // draw circle.
    float r = t * EXPLOSION_RADIUS / (TIME_PHASE2 - TIME_PHASE1); 
    vec2 o = vec2(explosionX, explosionY);
    float d = distance(uv, o);
    if(d <= EXPLOSION_RADIUS && d > r){
    	fragColor = EXPLOSION_COLOR;
    }

    // draw squares.
    int i;
    for(i = 0; i < SQUARE_COUNT; ++i){
    	if(abs(uv.x - square[i].x) <= square[i].z && 
           abs(uv.y - square[i].y) <= square[i].z){
    		fragColor = EXPLOSION_SQUARE_COLOR;
        }
    }
}

void drawBullet(float t, out vec4 fragColor){
    // the center of the two circles.
    vec2 o = vec2(START_X + MOVING_SPEED * t, START_Y);
    float d = distance(uv, o);

    // draw smaller circle.
    if(d <= BULLET_RADIUS){
		fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    }
	// draw bigger circle.
    else if(d <= BULLET_RADIUS2){
    	float R, G, B;
        float W = smoothstep(BULLET_RADIUS, BULLET_RADIUS2, d);
        R = 1.0 - (1.0 - BULLET_COLOR.x) * W;
        G = 1.0 - (1.0 - BULLET_COLOR.y) * W;
        B = 1.0 - (1.0 - BULLET_COLOR.z) * W;
		fragColor = vec4(R, G, B, 1.0);
    }
}

void init(vec2 fragCoord){
    uv = fragCoord;

    START_Y = iResolution.y / 2.0;
    START_X = iResolution.x / 4.0;
    MOVING_SPEED = iResolution.x * 0.5 / TIME_PHASE1;

    explosionX = START_X + MOVING_SPEED * TIME_PHASE1;
    explosionY = START_Y;
}

#define rand(x, y) fract(cos(x * (12.9898) + y * (4.1414)) * 43758.5453)
#define rand2(x, y) fract(cos(x * (11.2333) + y * (6.6262)) * 43758.5453)

void randExplusionEffect(float t, vec2 xy){
    float ti = ceil(iTime / TIME_PHASE2);
	vec2 seed = vec2(ti, 100.0 / (ti + 1.0));
    int i;
    float r;
    float degree;
    for(i = 0; i < SQUARE_COUNT; ++i){
        r = rand(seed.x, seed.y) * EXPLOSION_RADIUS;
        degree = rand2(seed.x, seed.y) * PI * 2.0;

        square[i].x = explosionX + r * cos(degree);
        square[i].y = explosionY + r * sin(degree);
        square[i].z = 
            rand(square[i].x, square[i].y) * SQUARE_MAX_HALF_WIDTH * (
            1.0 - t / (TIME_PHASE2 - TIME_PHASE1));

        seed.x = iResolution.x * rand(seed.x + ti, seed.y);
        seed.y = iResolution.y * rand2(seed.y + ti, seed.x);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    init(fragCoord);

    // draw background
    drawBackground(fragColor);

    float t = iTime;
    while(t > TIME_PHASE2) {
        t -= TIME_PHASE2;
    }

    if(t < TIME_PHASE1){
		drawBullet(t, fragColor);
    }else{
    	randExplusionEffect(t - TIME_PHASE1, fragCoord);
		drawExplosion(t - TIME_PHASE1, fragColor);
    }
}

