//マッピング（立体配置）
float map(vec3 p) {
	float t = length(mod(p, 2.0) - 1.0) - 0.35;
	return t;
}

//法線を取得する（ことで立体的な絵になる）
vec3 getN(vec3 p) {
	float t = map(p);
	vec2 d = vec2(0.001, 0.0);

	return normalize(vec3(
		t - map(p + d.xyy),
		t - map(p + d.yxy),
		t - map(p + d.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
	vec2 uv = ( 2.0 * fragCoord.xy - iResolution.xy ) / min(iResolution.x , iResolution.y);

    //レイを飛ばす方向
	vec3 dir = normalize(vec3(uv, 1.0));
    float x_po,y_po;
    //調査ポジションの定義

    if( uv.x*uv.x + uv.y*uv.y >0.45){
        x_po = -1.;
        y_po = -0.;
    }else{
        x_po = 1.0;
        y_po = 0.0;
    }
    if( (uv.x*uv.x + uv.y*uv.y)>1.0){
        x_po =0.0;
        y_po = 1.0;
    }

    if(uv.x*uv.x + uv.y*uv.y>2.0){
        x_po = 2.0;
        y_po = -2.0;
    }


    vec3 pos = vec3(y_po*cos(iTime) ,x_po*sin(iTime) , iTime);

    //変数定義
	float t= 0.0;

    int mx =75;//int(floor(20.*(sin(iTime)+1.) ) );

    //レイを飛ばして調査する
	for(int i = 0 ; i < 25+mx; i++ ) {
        //マップしてる図形に対しレイを飛ばし、
        //その分距離を進める
		t += map(dir * t + pos);
	}

    //計算を終了した時点のレイの位置を定義する
    vec3 ip = dir * t + pos;

    //法線定義
	vec3 L = normalize(vec3(1,2,3));
	vec3 N = getN(ip);

    // Output to screen

    //dot(L,N):法線とライトの座標との内積を求める
    //dir*0.1:色を抑えてる（このままだとRGBがキツい）
    fragColor = vec4(dot(L, N)*0.75 + x_po*3.*dir * 0.2, 1.0);
}

const float CELLSIZE = 0.75f;
const float SPEED = 400.55f;
const float DIST = 0.1f;

const float OFFSET = 0.3f;

#define FILL_WALLS

#ifndef FILL_WALLS
const int ITERATIONS = 25;
#endif

vec4 Grid(vec2 uv)
{    
    float invertedAspect = iResolution.y / iResolution.x;

    uv.y *= invertedAspect;
    uv.x += sin(iTime * SPEED * 1.0f / iResolution.x) * DIST;
    uv.y += cos(iTime * SPEED * 1.0f / iResolution.x) * DIST;

    float cellSize = 0.1f * CELLSIZE;
    float thickness = 0.001f;

    vec2 closest = round(uv / cellSize) * cellSize;
    vec2 distances = abs(closest - uv);
    distances.y *= 0.5f;

    float x = step(0.0f,thickness - distances.x);
    float y = step(0.0f,thickness - distances.y);

    return max(x,y) * vec4(1.0f,1.0f,0.5f,1.0f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord/iResolution.xy;

    vec2 centeredUV = uv;
    centeredUV *= 2.0f;
    centeredUV -= 1.0f;
    fragColor = Grid(uv) * 10.0f;

    #ifdef FILL_WALLS
    vec3 texelSize = 1.0f / iResolution;
    int ITERATIONS = int(OFFSET / texelSize.x);
    #endif

    for(int i=0;i<=ITERATIONS;++i)
    {
        float ratio = float(i) / float(ITERATIONS);      
        fragColor += Grid(uv + OFFSET * (0.5f * ratio + 0.5f* smoothstep(0.0f, 0.2f,ratio)) * (-centeredUV)* (1.0f - ratio));
    }

    fragColor /= float(ITERATIONS);
}

float grid(vec2 p, vec2 res){
    p = mod(p,res.y);
    float d = length(p-res.y*.5);
    return smoothstep(d,d+1.5/iResolution.y, res.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    vec2 res = vec2(.1,.2);
    vec2 u = uv;
    u.x +=  mod( u.y, res.x);
    u.x -= u.y+iTime * .1;
    if(uv.y > .5){
   // u.x += sin(iTime)*0.1;
        u +=  mod( u, res.x);
    }
    fragColor = vec4(grid(u,res));
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv *= 2.0; // scale
    float sp = iTime/2.0;
    uv -= sp;

    vec2 grid = fract(uv);
    float divN = 3.0;
    vec2 id = floor(uv);
    uv = mod(uv, divN);

    float randIdx = rand(id);
    float randIdy = rand(id+vec2(100.0));
    vec2 uv2 = grid;
    float divX = 2.0; float divY = 2.0;
    uv2.x *= (1.0+floor(randIdx*divX));
    uv2.y *= (1.0+floor(randIdy*divY));

    grid = fract(uv2);
    id += floor(uv2)/vec2(divX, divY);

    vec3 col = vec3(rand(id), grid);

    //example
    if(rand(id) < 0.2){
        col = vec3(0.0);
    }

    fragColor = vec4(col,1.0);
}

const float PI = 3.14159265359;

mat2 rotate(in float r){
    float c=cos(r), s=sin(r);
    return mat2(c, -s, s, c);
}
float hash(in vec2 n){
    return fract(sin(dot(n, vec2(12.9898, 78.233)))*46123.9878);
}
vec3 hash3(in vec2 n){
    return vec3(
            hash(n+vec2(1.0)),
            hash(n+vec2(10000.0)),
            hash(n+vec2(100.0))
        );
}
float usin(in float x){
  return 0.5+0.5*sin(x);
}
float easeInOut(in float x, in float e){
    float x2 = x*2.0;
    return x<0.5 ? 0.5*pow(x2, e) : 1.0-0.5*pow(2.0-x2, e);
}


float sdSphere(in vec3 p, in float r){
    return length(p) - r;
}
float sdPlane(in vec3 p, in float h){
    return p.y - h;
}
float sdBox(in vec3 p, in vec3 b){
    vec3 d = abs(p) - b;
    return length(max(d, 0.)) + min(max(d.x, max(d.y, d.z)), 0.);
}

float rei(in vec3 p){
    vec3 q = p;
    float res,d;

    // top obj
    q.x = abs(q.x);
    q.y -= 0.6;
    q.xy *= rotate(PI*0.25);
    d = sdBox(q, vec3(1.0, 0.1, 0.1));
    res = d;

    // point
    q = p;
    q -= vec3(0., 0.1, 0.);
    q.xy *= rotate(PI*0.25);
    d = sdBox(q, vec3(0.1));
    res = min(res,d);

    // bottom obj
    q = p;
    d = sdBox(q-vec3(0., -0.2, 0.), vec3(0.4, 0.1, 0.1));
    d = min(d, sdBox(q-vec3(0.3, -0.5, 0.), vec3(0.1, 0.2, 0.1)));
    d = min(d, sdBox(q-vec3(0.2, -0.6, 0.), vec3(0.15, 0.1, 0.1)));
    d = min(d, sdBox(q-vec3(-0.15, -0.55, 0.), vec3(0.1, 0.28, 0.1)));
    res = min(res,d);

    return res;
}
float wa(in vec3 p){
    vec3 tmp_p = p;
    vec3 q;
    float res,d;

    // left obj (nogi_hen)
    p = tmp_p-vec3(-0.4, 0., 0.);
    q = p;
    d = sdBox(q-vec3(-0.02, 0.45, 0.), vec3(0.25, 0.08, 0.1));
    d = min(d, sdBox(q-vec3(0., 0.05, 0.), vec3(0.4, 0.1, 0.1)));
    d = min(d, sdBox(q-vec3(0., -0.2, 0.), vec3(0.08, 0.6, 0.1)));
    q.x = abs(q.x);
    q.xy *= rotate(PI*0.25);
    d = min(d, sdBox(q, vec3(0.5, 0.08, 0.1)));
    res = d;

    // right obj (tsukuri)
    p = tmp_p-vec3(0.4, 0., 0.);
    q = p - vec3(0., -0.2, 0.);
    d = max(-sdBox(q, vec3(0.2, 0.25, 0.2)), sdBox(q, vec3(0.3, 0.35, 0.1)));
    res = min(res, d);

    return res;
}

float reiwa(in vec3 p){
    vec3 q = p;
    float t = iTime*0.8;
    float it = floor(t);
    float ft = fract(t);

    float d_rei = rei(q);
    float d_wa = wa(q);
    float d_reiwa = min(rei(q-vec3(-0.9, 0., 0.)), wa(q-vec3(0.8, 0., 0.)));

    float pattern = floor(mod(t, 3.0));
    float pct = easeInOut(ft, 8.0);
    return  pattern<1.0 ? mix(d_rei, d_wa, pct) :
            pattern<2.0 ? mix(d_wa, d_reiwa, pct) :
                          mix(d_reiwa, d_rei, pct);
}

vec3 opRep(in vec3 p, in vec3 c){
    return mod(p-0.5*c, c) - 0.5*c;
}

vec2 map(in vec3 p){
    vec3 q = p;

    for(int i=0;i<2;i++){
        q = abs(q) - vec3(4.0);
        q.xy *= rotate(iTime*0.2);
        q.yz *= rotate(iTime*0.3);
        q.zx *= rotate(iTime*0.1);
    }
    q = opRep(q, vec3(5.0+floor(q)));
    q = mix(p, q, easeInOut(usin(iTime*0.5), 4.0));
    vec2 res = vec2(reiwa(q), 1.0);
    return res;
}

vec2 castRay(in vec3 ro, in vec3 rd){
    float minD=0.5, maxD=20.0;
    float d = minD, m = -1.0;
    for(int i=0;i<32;i++){
        vec2 tmp = map(ro + rd*d);
        if(tmp.x<0.001 || maxD<tmp.x) break;
        d += tmp.x;
        m = tmp.y;
    }
    if(maxD<d) m = -1.0;
    return vec2(d, m);
}

vec3 calcNormal(in vec3 p){
    vec2 e = vec2(1.0, -1.0)*0.0001;
    return normalize(vec3(
            e.xyy*map(p+e.xyy).x +
            e.yxy*map(p+e.yxy).x +
            e.yyx*map(p+e.yyx).x +
            e.xxx*map(p+e.xxx).x
        ));
}

vec3 render(in vec3 ro, in vec3 rd){
  vec3 color = vec3(0.0);

  vec2 res = castRay(ro, rd);
  float d = res.x;
  float m = res.y;

  vec3 pos = ro + rd*d;
  vec3 nor = calcNormal(pos);

  vec3 lp = vec3(1.0);
  vec3 ld = normalize(lp - pos);

  color =
      m==1.0 ? mix(vec3(1.), vec3(0.1, 0.4, 0.9), exp(-0.2*length(pos-lp)))*clamp(dot(ld, nor), 0., 1.):
               vec3(1.0);

  return color;
}

mat3 lookAt(in vec3 eye, in vec3 tar, in float r){
    vec3 cw = normalize(tar - eye);
    vec3 cp = vec3(sin(r), cos(r), 0.);
    vec3 cu = normalize(cross(cw, cp));
    vec3 cv = normalize(cross(cu, cw));
    return mat3(cu, cv, cw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    float t = iTime*1.4 + 1.0*sin(iTime*0.8);
    float it = floor(t);
    float ft = fract(t);
    ft = sqrt(ft);

    vec3 ro1 = hash3(vec2(it))*2.0-1.0;
    vec3 ro2 = hash3(vec2(it+1.0))*2.0-1.0;
    vec3 roa = mix(ro1, ro2, ft)*mix(3.0, 1.0, easeInOut(clamp(iTime*0.1, 0., 1.), 4.0));
    vec3 rob = vec3(0., 0., 1.0+sin(iTime*0.2));

    vec3 tar = vec3(0.);
    vec3 rda = lookAt(roa, tar, 0.)*normalize(vec3(p, 1.0));
    vec3 rdb = lookAt(rob, tar, 0.)*normalize(vec3(p, 1.0));

    vec3 colora = render(roa, rda);
    vec3 colorb = render(rob, rdb);

    vec3 color = mix(colora, colorb, easeInOut(usin(iTime*0.45), 6.0));

    fragColor = vec4(color, 1.0);
}

const float PI = 3.1415926535897932384626433832795;


float circles(vec2 uv, float r){
    // Position of point assuming center is (.5,.5)
    vec2 pos = vec2(0.5)-fract(uv);

    // Find squared distance from origin
    float lineLength = distance(pos, vec2(0.));
    return 1.-step(r, lineLength);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.yy;

    // Scale the coords up by 5
    uv *= 5.;

    // Note the column and row of each coord
	vec2 colRow = floor(uv);

    // Grab fractional remainders for each to create [0, 1] blocks
    uv = fract(uv);

    // This flips if seconds are even or odd
    bool flipper = mod(floor(iTime), 2.) == 0.;

    // Scaled and offset so seconds are in increments of Pi
    float t = PI/2.+(fract(iTime)*PI);

    float v = (sin(t)+1.)/2.;
    if(flipper){
        // For any pixel in an even column
        if(mod(colRow.x, 2.) == 0.)
            uv += vec2(0, v);
        else
            uv -= vec2(0, v);
    }
    else{
        // For any pixel in an even row
        if(mod(colRow.y, 2.) == 0.)
            uv += vec2(v,0.);
        else
            uv -= vec2(v,0.);
    }



    // Lets look at colors now    
    vec3 col = vec3(0.);

    // Paint the coordinate system with colors
//  col = vec3(uv.x, uv.y,0);

    // Paint in the circles, without destroying the background
    float val = circles(uv, .1);
    if(val != 0.)
	    col = vec3(val);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float fill (float d, float r, float blur) {
  return smoothstep(r, r - .005 - blur, d);
}

float stroke (float d, float r, float lw, float blur) {
  float hlw = lw / 2.;
  return smoothstep(r + hlw + .005, r + (hlw * blur), d) *
    smoothstep(r - hlw - .005, r - (hlw * blur), d);
}

float dfSquare (vec2 p, vec2 ratio) {
  return max(abs(p.x) / ratio.x, abs(p.y) / ratio.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord.xy - .5 * iResolution.xy) / iResolution.y;

  	vec3 color = vec3(0.);

    float t = sin(iTime * 1.5) * .05 + .05;
	float r = length(uv); // for circle

  	// background
    color += cos(fract(uv.x/uv.y*5.+iTime*.4) * 5.) * 2.5;
  	color *= mix(color, vec3(.2, .3, .8) * 1.5, r + .3 - t * 1.5);

    // forbidden sign
  	color = mix(color, vec3(0.), fill(r, .313 + t, 0.));
  	color = mix(color, vec3(.8, .3, .2), fill(r, .32 + t, .02));
  	color = mix(color, vec3(1.), stroke(r, .3 + t, .02, 0.));
  	color = mix(color, vec3(1.), fill(dfSquare(uv, vec2(1. + t * 4., .3 + t * 1.)), .2, .005));
  	color += vec3(uv.y) * fill(r, .32 + t, .02);

    fragColor = vec4(color, 1.);
}

float box(vec2 uv, vec2 topLeft, vec2 bottomRight)
{
    return uv.x > topLeft.x && uv.x < bottomRight.x && uv.y < topLeft.y && uv.y > bottomRight.y ? 1. : 0.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    // Center our coordinate system    
    vec2 uv = (fragCoord - .5*iResolution.xy) / min(iResolution.x, iResolution.y);

    // Scale that $#!+ !
    uv *= 1.+.5*(sin(iTime));

    // Show space coordinates as colors on the background
    vec3 col = vec3(uv.x,uv.y,0.0);

	// Tack on a shape
    col += box(uv, vec2(-.1,.1), vec2(.1,-.1));


    // Output to screen
    fragColor = vec4(col,1.0);
}

mat2 rot(float a) {
    float s = sin(a);
    float c = cos(a);

    return mat2(c, s, -s, c);
}

float box(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return max(max(q.x, q.y), q.z);
}

vec2 shape(vec3 p) {
    float a = box(p, vec3(1));

	p = abs(p) - vec3(1);
    float b = box(p + vec3(1, 0, 0), vec3(1.200,0.200,0.200));
    float c = box(p + vec3(0, 0, 1), vec3(0.2, 0.2, 1));
    float d = box(p + vec3(0, 1, 0), vec3(0.2, 1, 0.2));

    vec2 s = vec2(a, 1.0);
    vec2 t = vec2(min(b, min(c, d)), 2.0);

    return s.x < t.x ? s : t;
}

void opIFS(inout vec4 q) {
    for(int i = 0; i < 9; i++) {
        q.xyz = abs(q.xyz) - vec3(0.900,1.393,0.594);
        q.xz *= rot(0.628 + float(i)*0.932);
        q.xy *= rot(0.644);

        q *= 1.2;
    }
}

vec2 de(vec3 p) {
    vec4 q = vec4(p, 1);

    opIFS(q);

    return shape(q.xyz)/vec2(q.w, 1);
}

vec3 form(vec2 p, sampler2D s) {
    for(int i = 0; i < 3; i++) {
        p = abs(p)/clamp(dot(p, p), 0.5, 1.0) - vec2(0.1, 0.3);
        p *= rot(0.33);
    }

    return texture(s, p).rgb;
}

vec3 mat(vec3 p, vec3 n, sampler2D s) {
    vec3 m = pow(abs(n), vec3(10.0));
    m /= dot(vec3(1), m);

    vec3 x = form(p.yz, s);
    vec3 y = form(p.xz, s);
    vec3 z = form(p.xy, s);

    return (m.x*x*x + m.y*y*y + m.z*z*z);
}

vec3 bump(vec3 p, vec3 n, sampler2D s, float bf) {
    vec2 h = vec2(0.01, 0);
    vec3 g = mat3(
        mat(p - h.xyy, n, s),
        mat(p - h.yxy, n, s),
        mat(p - h.yyx, n, s)
    )*vec3(0.299, 0.548, 0.114);

    g -= dot(mat(p, n, s), vec3(0.299, 0.548, 0.114));
    g -= n*dot(g, n);

    return normalize(n + bf*g);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (gl_FragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
    vec2 mo = 5.0*iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    float a = 6.8;
    float at = iTime*0.1;

    vec3 ro = vec3(a*sin(at + mo.x), 2.0*sin(at*2.0 + mo.y), -a*cos(at + mo.x));
    vec3 ww = normalize(vec3(0)-ro);
    vec3 uu = cross(vec3(0, 1, 0), ww);
    vec3 vv = cross(ww, uu);
    vec3 rd = normalize(mat3(uu, vv, ww)*vec3(uv, 1));

    float t = 0.0, m = -1.0, mx = 10.0;
    for(int i = 0; i < 200; i++) {
        vec2 d = de(ro + rd*t);
        if(d.x < 0.001 || t >= mx) break;
        t += d.x*0.5;
        m = d.y;
    }

    if(t < mx) {
        vec3 p = ro + rd*t;
        vec2 h = vec2(0.001, 0.0);
        vec3 n = normalize(vec3(
            de(p + h.xyy).x - de(p - h.xyy).x,
            de(p + h.yxy).x - de(p - h.yxy).x,
            de(p + h.yyx).x - de(p - h.yyx).x
        ));

        vec3 ld = normalize(p);

        vec3 alb = vec3(1);
        vec4 q = vec4(p, 1);

        opIFS(q);
        if(m == 1.0) {
			alb = vec3(0.075,0.401,1.000);
            n = bump(p, n, iChannel0, 0.2);
        } else if(m == 2.0) {
            alb = vec3(1.000,0.550,0.015);
            n = bump(p, n, iChannel0, 0.15);
        }

        float oat = t/50.0;
        float occ = exp2(-pow(max(0.0, 1.0 - de(p + n*oat).x/oat), 2.0));
        float dif = max(0.0, dot(ld, n));

        float sp = pow(max(0.0, dot(reflect(-ld, n), -rd)), 32.0);

        col = 0.75*occ*(alb*(0.25 + dif) + 2.0*vec3(0.6, 0.7, 1.0)*sp);
    }

    fragColor = vec4(pow(col, vec3(0.4545)), 1);
}

#define PI 3.14195265359

mat2 rotate(float a)
{
	float s = sin(a);
	float c = cos(a);
	return mat2(c, -s,s, c);
}

//https://www.shadertoy.com/view/Ws23zt
float sdTetra(vec3 pos,float i){
    pos.xy *= rotate(iTime+i);
    pos.xz *= rotate(iTime*2.0);
    return (max(abs(pos.x+pos.y)-pos.z,abs(pos.x-pos.y)+pos.z)-0.2)/sqrt(3.);
}

float sdPlane( vec3 pos){
    vec3 n = normalize(vec3(0.0,1.0,0.0)); 
	return dot(pos,n.xyz);
}

float sdBox(vec3 pos, vec3 b){
    vec3 d = abs(pos) - b;
    return length(max(d,0.0)) + min(max(d.x,max(d.y,d.z)),0.0);
}

float sdPanel(vec3 pos){
    float d = sdBox(pos,vec3(0.9,0.9,0.01));
    d = max(d, -sdBox(pos-vec3(0.9,0.0,0.0),vec3(0.1,0.2,0.02)));
    d = max(d, -sdBox(pos-vec3(-0.9,0.0,0.0),vec3(0.1,0.2,0.02)));
    d = max(d, -sdBox(pos-vec3(0.0,0.9,0.0),vec3(0.2,0.1,0.02)));
    d = max(d, -sdBox(pos-vec3(0.0,-0.9,0.0),vec3(0.2,0.1,0.02)));
    return d;
}

vec3 rotPanel(vec3 p,vec3 id, float rep){
    p.xy = mod(p.xy,rep)-rep*0.5; 

    float c = 4.0;//rotation cycle
    float d = 0.2;
    float mt = mod(iTime,c);
    float t = clamp(mt - (c-d), 0.0, 1.0)/d;
    float rad =  mix(floor(iTime/c),floor(iTime/c+1.0),t)*PI/2.0;
    p.xy *= rotate(rad);
    //p.xy *= rotate(rad*(1.0-2.0*mod(id.y,2.0)));//switch the rotation direction by each row
    return p;
}


vec2 map(vec3 pos){
    float rep = 2.0;
    float d = 100000.0;
    float m = -1.0;
    vec3 id = floor(pos/rep);

    float plane = sdBox(pos-vec3(0.0,-0.01,4.0),vec3(5.0,0.1,5.0));
    d = min(d,plane);
    m = (d < plane) ? m : 1.0;

    vec3 p = pos;
    p.x += (1.0-2.0*mod(floor(p.y/rep),2.0))*iTime;
    p = rotPanel(p,id,rep);

    if(id.x > -7.0 && id.x < 6.0 && id.y > -1.0 && id.y < 4.0){
        float panel = sdPanel(p-vec3(0.0,0.0,2.0)); 
   		d = min(d,panel);
        m = (d < panel) ? m : 2.0;
    }

    vec3 t1= vec3(-0.5,1.0,4.0),t2=vec3(0.0,1.0,4.0),t3=vec3(0.5,1.0,4.0);
    t1.x += 2.0*sin(iTime*1.1); t1.y += 0.6*cos(iTime*1.3); t1.z += -1.0*sin(iTime*2.9);
    t2.x += 2.0*cos(iTime*2.1); t2.y += -0.6*cos(iTime*0.8); t2.z += 1.0*sin(iTime*1.5);
    t3.x += -2.0*sin(iTime*0.6); t3.y += 0.6*sin(iTime*2.5); t3.z += 1.0*cos(iTime*1.2);
    float tetra = min(sdTetra(pos-t1,1.0),
                     min(sdTetra(pos-t2,2.0),sdTetra(pos-t3,3.0)));
    d = min(d,tetra);
    m = (d < tetra) ? m : 3.0;

    return vec2(d,m);
}

//https://www.shadertoy.com/view/Xds3zN
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    // bounding volume
    float tp = (10.0-ro.y)/rd.y; if( tp>0.0 ) tmax = min( tmax, tp );

    float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 calcNorm(vec3 pos)
{
    float d = 0.0001;
    return normalize(vec3(
        map(pos + vec3(  d, 0.0, 0.0)).x - map(pos + vec3( -d, 0.0, 0.0)).x,
        map(pos + vec3(0.0,   d, 0.0)).x - map(pos + vec3(0.0,  -d, 0.0)).x,
        map(pos + vec3(0.0, 0.0,   d)).x - map(pos + vec3(0.0, 0.0,  -d)).x
    ));
}

vec3 texPanel(vec2 uv,vec2 id){
    //range of uv : [-1,1]
    float y =  mod(id.x,2.0) == 0.0 ? uv.y : -uv.y ;
    vec3 c1,c2,c3,c4;

    float u = mod(id.x,2.0);
    float v = mod(id.y,2.0);

    c1 = (u == 0.0) ? vec3(0.58, 0.9, 0.45) : vec3(0.07, 0.1, 0.55);
    c2 = (u == 0.0) ? vec3(0.07, 0.1, 0.55) : vec3(0.58, 0.9, 0.45);
    c3 = (u == 0.0) ? vec3(0.82, 0.12, 0.22) : vec3(0.36, 0.0, 0.48);
    c4 = (u == 0.0) ? vec3(0.36, 0.0, 0.48) : vec3(0.82, 0.12, 0.22);

    float d1 = smoothstep(uv.x-0.05,uv.x,uv.y);
    float d2 = smoothstep(uv.x-0.05,uv.x,-uv.y);

    vec3 p1 = mix(c1,c2,d1);
    vec3 p2 =  mix(c3,c4,d2); 
    return u == v ? p1 : p2;
}

vec3 texPlane(vec2 uv){
    vec3 col = vec3(0.0);
    vec2 id = floor(uv/0.4);
    float u = mod(id.x,4.0);

    if(u == 0.0) col = vec3(0.9);
    if(u == 2.0) col = vec3(1.3);

    return col;
}

vec3 light = normalize(vec3(0.0,2.0,3.0));

vec3 render(vec3 ro, vec3 rd){
    vec3 col = vec3(0.0);

    float t = 0.0;
    vec2 m;
    vec3 pos;
    for(int i = 0; i < 128; i++){
        pos = ro + t*rd;
      	m = map(pos);
        t += m.x*0.8;
    }
    pos = ro + t*rd;

    if(abs(m.x) < 0.0001){
    	vec3 norm = calcNorm(pos);
        vec3 v = normalize(ro-pos);
        vec3 l = normalize(light-pos);
        vec3 r = normalize(reflect(-l,norm));

        float diff = clamp(dot(light, norm), 0.1, 1.0);
        float spec = pow(max(dot(r,v), 0.0), 64.0);
        float shadow = calcSoftshadow( pos, light, 0.02, 2.5 );
        diff *=  shadow;

        vec3 tex;
        if(m.y == 1.0){
            tex = texPlane(pos.xz);
        }

        if(m.y == 2.0){
        	float rep = 2.0;
        	vec3 p = pos;
        	p.x += (1.0-2.0*mod(floor(p.y/rep),2.0))*iTime;
        	vec3 id = floor(p/rep);

        	p = rotPanel(p,id,rep);
        	tex = texPanel(p.xy,id.xy);
        }

        if(m.y == 3.0){
          tex = vec3(1.0,0.1,0.1);
        }

    	col = clamp(vec3(diff)* tex,0.0,1.0) + vec3(0.1);
        col += spec*vec3(.5);

    }else{
        col = vec3(0.2);
    }
	return col ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy*2.0 - iResolution.xy)/min(iResolution.x,iResolution.y);

    vec3 up = vec3(0.,1.,0.);
    vec3 lookAt = vec3(0.0,0.5,0.0);
    vec3 ro = vec3(0.0,0.5,6.0);
    ro.xz *= rotate(0.2*sin(iTime*0.5));

    vec3 cDir = normalize(lookAt-ro);
    vec3 cSide = normalize(cross(cDir,up));
    vec3 cUp = normalize(cross(cSide,cDir));

    vec3 rd= normalize(cSide * p.x + cUp * p.y + cDir );

	vec3 col = render(ro,rd);
    vec2 q = fragCoord.xy / iResolution.xy;   
    col *= 0.4 + 0.6 * pow(16.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), 0.1);
    fragColor = vec4(col,1.0);
}

