float noise ( vec2 uv) {
float a = rand(uv);
float b = rand (uv+vec2(1,0));
 float c  = rand (uv+vec2(0,1));
 float d  = rand ( uv+vec2(1,1));
vec2 u =smoothstep(0.,1.,fract(uv));
return mix (a,b,u.x)+(c-a)*u.y*(1.-u.x)+(d-b)*u.x*u.y;
}
float sdBezier(vec2 pos, vec2 A, vec2 B, vec2 C)
{    
    vec2 a = B - A;
    vec2 b = A - 2.0*B + C;
    vec2 c = a * 2.0;
    vec2 d = A - pos;
