const float PI = 3.14159265359;

// sdf_circle returns:
//    negative value when inside the circle
//    positive value when outside the circle
float sdf_circle(vec2 sampleAt, vec2 center, float radius) {
    return length(center - sampleAt) - radius;
}

vec4 color_blue_green(float value) {
    // quick hack way to color things, such that 
    // negative values are blue and positive values are green
    const float offset = 0.5;
    float offsetValue = value + offset;

    vec4 colorMask = vec4(0.8, 1.0, 0.8, 1.0);
    if(offsetValue < offset){
        colorMask = vec4(0.8, 0.8, 1.0, 1.0);
    }
    // to disable coloring set:
    // colorMask = vec4(1.0); 

    return vec4(offsetValue, offsetValue, offsetValue, 1.0) * colorMask;
}

// color negative (inside) black and outside white
vec4 color_black_white(float value) {
    if(value > 0.0){
        return vec4(0.0, 0.0, 0.0, 1.0);
    } else {
        return vec4(1.0, 1.0, 1.0, 1.0);
    }
}

// color using absolute value of the signed distance function
vec4 color_grayscale(float value) {
    return vec4(abs(value), abs(value), abs(value), 1);
}


float smin(float a, float b, float k) {
    // see http://www.iquilezles.org/www/articles/smin/smin.htm
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime * 1.0;
	// this part ensures we don't distort the image
    vec2 sampleAt = 2.0 * (fragCoord.xy / iResolution.xy) - 1.0;
  	sampleAt.x *= iResolution.x / iResolution.y;

    // calculating the shapes
    float right = sdf_circle(sampleAt, vec2(sin(t)*0.8, 0), 0.4);
    float left  = sdf_circle(sampleAt, vec2(0, sin(t*2.41)*0.3), 0.5);

    // try using max, smin
    float value = min(left, right);

    // min acts like addition of shapes:
    //   min(negative, positive) ~= min(inside, outside) = inside
    // max acts like intersection of shapes
    //   max(negative, positive) ~= max(inside, outside) = outside

    // try different coloring functions:
    // color_blue_green:
    //   blue  = negative value
    //   green = positive value
    // color_black_white
    //   all negative values will be white, positive black
    // color_grayscale
    //   absolute value of the distance field
    fragColor = color_blue_green(value);
}

