////////////////////////////////////////////////////////////////////////////////
//
// "Destroyer of Lipschitz" - This is a cleaner looking version of an old shader
// of mine (https://www.shadertoy.com/view/XscfW2) from 2018. Share and enjoy!
//
// Copyright 2019 Mirco Müller
//
// Author(s):
//   Mirco "MacSlow" Müller <macslow@gmail.com>
//
// This program is free software: you can redistribute it and/or modify it
// under the terms of the GNU General Public License version 3, as published
// by the Free Software Foundation.
//
// This program is distributed in the hope that it will be useful, but
// WITHOUT ANY WARRANTY; without even the implied warranties of
// MERCHANTABILITY, SATISFACTORY QUALITY, or FITNESS FOR A PARTICULAR
// PURPOSE.  See the GNU General Public License for more details.
//
// You should have received a copy of the GNU General Public License along
// with this program.  If not, see <http://www.gnu.org/licenses/>.
//
////////////////////////////////////////////////////////////////////////////////

const int MAX_ITER = 80; // 48 or 64 will work too (faster) but not look clean
const float EPSILON = .0001;
const float STEP_SIZE = .5; // .75 or 1.1 will work also (faster) but not look clean
const int AA_SIZE = 2; // 1: no anti-aliasing (faster), 2 or up: better anti-aliasing (slower)

float opBend (inout vec3 p, float deg)
{
    float rad = radians (deg);
    float cy = cos (rad * p.y);
    float sy = sin (rad * p.y);
    mat2  my = mat2 (cy, -sy, sy, cy);
    float cx = cos (rad * p.x);
    float sx = sin (rad * p.x);
    mat2  mx = mat2 (cx, -sx, sx, cx);
    p = mix (vec3 (mx* p.zx, p.y), vec3 (my * p.xy, p.z), .15*cos (p.x));

    return .0;
}

float displace (vec3 p)
{
    float result = 1.;
    float factor = 6. + 4.*cos (2.*iTime);
	result = .375 * sin (factor * p.x) * cos (factor * p.y) * sin (factor * p.z);

    return result;
}

float opCombine (in float d1, in float d2, in float r) {
    float h = clamp (.5 + .5 * (d2 - d1) / r, .0, 1.);
    return mix (d2, d1, h) - r * h * (1. - h);
}

float sdSphere (in vec3 p, in float radius)
{
	return length (p) - radius;
}

float sdPlane (in vec3 p, in float height)
{
	return length (p.y - height);
}

float udBox (in vec3 p, in vec3 size, in float radius)
{
	return length (max (abs (p) - size, .0)) - radius;
}

mat2 r2d (in float degree)
{
    float rad = radians (degree);
    float c = cos (rad);
	float s = sin (rad);

    return mat2 (c, s, -s, c);
}

float scene (in vec3 p)
{
	// well, kind of self explanatory
    float ground = sdPlane (p, -2.);

	// the weird distorted thing in the middle of the scene
    vec3 p2 = p;
    opBend (p2, 45. * cos (.25*iTime));
    p2.zx *= r2d (50.*iTime);
    p2.xy *= r2d (-75.*iTime);
    float dt = sdSphere (p2, .5);
    float dp = displace (p2);
    float ball2 = dt + dp;

	// the structure rotating around the scene
    p.xz *= r2d (20.*iTime);
    p.zy *= r2d (-30.*iTime);
    float x = p.x*.125;
    float y = p.y*.125;
    float z = p.z*.125;
    float fourthOrderChmutovBanchoffSurface = 3. + 8.*(x*x*x*x + y*y*y*y + z*z*z*z) - 8.*(x*x + y*y + z*z);
    fourthOrderChmutovBanchoffSurface *= .75;
    ground = opCombine (ground, ball2, 2.5);
    fourthOrderChmutovBanchoffSurface = opCombine (fourthOrderChmutovBanchoffSurface, ground, .5);

    return fourthOrderChmutovBanchoffSurface;
}

vec3 normal (in vec3 p, in float epsilon)
{
    vec2 e = vec2 (epsilon, .0);
    float d = scene (p);
    return normalize (vec3 (scene (p + e.xyy),
                            scene (p + e.yxy),
                            scene (p + e.yyx)) - d);
}

float raymarch (in vec3 ro, in vec3 rd)
{
    float d = .0;
    float t = .0;
    for (int iter = 0; iter < MAX_ITER; ++iter) {
        t = scene (ro + d * rd);
        if (abs(t) < EPSILON*(1. + .125*t)) break;
        d += t*STEP_SIZE;
    }

    return d;
}

float shadow (in vec3 p, in vec3 lpos)
{
    float distanceToLight = distance (lpos, p);
    vec3 n = normal (p, distanceToLight*EPSILON);
    vec3 ldir = normalize (lpos - p);
    float distanceToObject = raymarch (p + .01 * n, ldir);
    bool isShadowed = distanceToObject < distanceToLight;

	return isShadowed ? .3 : 1.;
}

float ao (in vec3 p, in vec3 n, float stepsize, int iterations, float intensity) {
    float ao = .0; 
    float dist = .0; 
    for (int a = 1; a <= iterations; ++a) {
        dist = float (a)*stepsize;
        ao += max (.0, (dist - scene (p + n*dist))/dist);
    }   
    return 1. - ao*intensity;
}

// rusty old blinn/phong shading model
vec3 shade (in vec3 p, in vec3 rd, in vec3 n)
{
	// attributes of first light
    vec3 lightPosition1 = vec3 (1.);
    lightPosition1.xz *= r2d (60.*iTime);
    vec3 l1 = normalize (lightPosition1 - p);
    float d1 = distance (p, lightPosition1);
    float lightIntensity1 = 5.;
	float att1 = 2./(d1*d1);
    vec3 lightColor1 = vec3 (.9, .8, .7);

	// attributes of second light
 	vec3 lightPosition2 = vec3 (1., 1.*cos (2.*iTime), 1.);
    lightPosition2.xz *= r2d (20.*iTime);
    vec3 l2 = normalize (lightPosition2 - p);
    float d2 = distance (p, lightPosition2);
    float lightIntensity2 = 3.;
	float att2 = 2./(d2*d2);
    vec3 lightColor2 = vec3 (.7, .8, .9);

	// diffuse term parts
    vec3 diffuseColor1 = max (dot (n, l1), .0) * lightColor1 * lightIntensity1;
    vec3 diffuseColor2 = max (dot (n, l2), .0) * lightColor2 * lightIntensity2;

	// specular coefficient
	float shiny = 60.;
	vec3 h1 = normalize (-rd + l1);
	vec3 h2 = normalize (-rd + l2);
	float sp1 = pow (max(.0, dot (h1, l1)), shiny);
	float sp2 = pow (max(.0, dot (h2, l2)), shiny);

	// there is only one 'material'
    vec3 matertialColor = vec3 (.4, .7, .6);

    float ao = ao (p, n, .15, 4, .15);

    return ao*att1*shadow (p, lightPosition1) * (matertialColor*diffuseColor1 + sp1*vec3 (1.))+
           ao*att2*shadow (p, lightPosition2) * (matertialColor*diffuseColor2 + sp2*vec3 (1.));
}

vec3 camera (in vec2 uv, in vec3 ro, in vec3 aim, in float zoom)
{
	vec3 f = normalize (aim - ro);
	vec3 wu = vec3 (.0, 1., .0);
	vec3 r = normalize (cross (wu, f));
	vec3 u = normalize (cross (f, r));
	vec3 c = ro + f*zoom;
	return normalize (c + r*uv.x + u*uv.y - ro);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// orbit camera preparation
    float dist2cam = 3.;
    float azimuthAngle = ((iMouse.x/iResolution.x) * 2. - 1.) * 179.;
    float elevationAngle = ((iMouse.y/iResolution.y) * 2. - 1.) * -40.;
    float x = dist2cam*cos (radians (azimuthAngle));
    float y = dist2cam*sin (radians (elevationAngle));
    float z = dist2cam*sin (radians (azimuthAngle));

	// stuff
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec3 ro = vec3 (x, y, z);
	float fog = .0;
	float d = .0;
	vec3 col = vec3 (.0);
	vec3 aim = vec3 (.0);
	float zoom = 1.7;

	// walk over AA-grid
	for (int x = 0; x < AA_SIZE; ++x) {
		for (int y = 0; y < AA_SIZE; ++y) {

			// anti-alias offset
			vec2 pixelOffset = vec2 (float (x), float (y))/float  (AA_SIZE);

			// normalize and aspect-correct UVs
			vec2 uv = (fragCoord.xy + pixelOffset)/iResolution.xy;
    		uv = uv*2. - 1.;
            uv.x *= iResolution.x/iResolution.y;

    		// create viewray
    		vec3 rd = camera (uv, ro, aim, zoom);

			// primary/view ray
			d = raymarch (ro, rd);
			fog = 1. / (1. + d*d*.02);
			vec3 p = ro + d*rd;
			vec3 n = normal (p, d*EPSILON);
			vec3 ctmp = shade (p, rd, n);
			ctmp *= fog;

			// secondary/reflection ray
			vec3 refr = normalize (reflect (rd, n));
			float refd = raymarch (p + .0005*n, refr);
			vec3 refp = p + refd * refr;
			vec3 refn = normal (refp, EPSILON);
			col += ctmp + .05*shade (refp, refr, refn);
		}
	}
	col /= float (AA_SIZE*AA_SIZE);

    // distance-mist, vignette, tone-map, gamma-correct
	col = mix (col, vec3 (.2, .35, .7), pow (1. - 1./d, 90.));
	col *= 1. - .5*length (fragCoord.xy/iResolution.xy*2. - 1.);
    col = col / (1. + col);
    col = pow (col, vec3 (1./2.2));

    fragColor = vec4 (col, 1.);
}

#define N 800.      // number of random partics
#define k 400.      // plane sine wave (kx) propag from left to right
#define W1 .001     // dielectric material width middle
#define W2 .5       // dielectric material width bottom
#define Lcoher 0*15 // coherence length. 0: coherent source (laser)

#define rnd2(p) fract(sin((p)*mat2(127.1,311.7,269.5,183.3))*43758.5453123)
#define rnd(p)  fract(sin((p)* 78.233) * 43758.5453)
#define srnd(p) ( 2.*rnd(p) - 1. )
#define K(x)    smoothstep(1.,0.,abs(x))  // photon kernel (incoherent source)

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    U /= R.y;

    float v = 0., phi, a=1.,l,
          W = U.y < .35 ? W2 : W1;

    for (float i=0.; i<N; i++) {
        vec2 P = .5+vec2(W,1)*(2.*rnd2(vec2(i))-1.);// random partic
     // P.y = fract(.5*P.y-U.y+.5)+U.y-.5;          // sliding window for continuity
        l = length(U-P);
     // a = max(0.,(U-P).x/l);                      // (scattering function)
        phi = k*( a*l + P.x );                      // phase left -> P -> U
        if(U.y>.66) phi += 6.28*rnd2(P).x;          // top: incoherent phases
#if Lcoher > 0                                      //  incoherent source
      float x = phi/float(Lcoher),
         iphi = floor(x) , f = fract(x);
      phi += 3.14*( K(f) * srnd(iphi) + K(1.-f) * srnd(iphi+1.) ); // random phase per photon
#endif        
        v += sin(phi - 10.*iTime) /l; // (l*l)      // diffracted wave
    }
    O = vec4(.5+.5*v*.3/sqrt(N));

    if (abs(U.x-.5) < W/2.+2./R.x) O.g = .7;        // dielectric width
    if (U.y<.05) O = vec4(.5+.5*sin(k*U.x - 10.*iTime),0,0,1); // ref without material
}

mat2 rot(float a) {
  float c = cos(a);
  float s = sin(a);

  return mat2(c, s, -s, c);
}

float len(vec3 p, float l) {
  vec3 q = pow(abs(p), vec3(l));
  return pow(q.x + q.y + q.z, 1.0/l);
}

float len(vec2 p, float l) {
  vec2 q = pow(abs(p), vec2(l));
  return pow(q.x + q.y, 1.0/l);
}

float box(vec3 p, vec3 b) {
  vec3 q = abs(p) - b;
  return max(max(q.x, q.y), q.z);
}

float torus(vec3 p, vec2 h, float l) {
  vec2 q = vec2(len(p, l) - h.x, p.y);
  return len(q, l) - h.y;
}

vec2 shape(vec3 p) {
  float a = torus(p, vec2(1, 0.2), 16.0);

  p.y = abs(p.y) - 0.2;

  float b = torus(p, vec2(1.1, 0.14), 16.0);

  vec2 s = vec2(a, 1.0);  
  vec2 t = vec2(b, 2.0);

  return s.x < t.x ? s : t;
}

vec2 path(float z) {
  return vec2(0.5*sin(0.3*z + 1.0), cos(0.6*z));
}

float glow = 0.0;
vec2 de(vec3 p) {
  float sc = 1.0;

  p.xy += path(p.z);

  vec3 op = p;
  p.z = mod(p.z + 1.5, 3.0) - 1.5;
  vec4 q = vec4(p*sc, 1);

  q.xyz -= 1.0;

  for(int i = 0; i < 5; i++) {
    q.xyz = abs(q.xyz + 1.0) - 1.0;
    q.xz *= rot(0.55);
    q.xy *= rot(0.1);

    q *= 1.1;
  }

  vec2 s = shape(q.xyz)/vec2(q.w*sc, 1);

  float at = mod(iTime, 100.0);
  vec3 gop = op - vec3(0, 0, at);

  vec2 t = vec2(length(gop) - 0.1, 2.0);

  glow += 0.1/(0.01 + t.x*t.x);

  return s.x < t.x ? s : t;
}

float form(vec2 p) {
  p = mod(p + 2.0, 4.0) - 2.0;

  for(int i = 0; i < 10; i++) {
    p = abs(p)/clamp(dot(p, p), 0.5, 0.8) - vec2(0.0, 0.6);
  }

  return smoothstep(0.5, 0.8, abs(p.y));
}

float mat(vec3 p, vec3 n) {
  vec3 m = pow(abs(n), vec3(10.0));

  float x = form(p.yz);
  float y = form(p.xz);
  float z = form(p.xy);

  return (m.x*x + m.y*y + m.z*z)/(m.x + m.y + m.z);
}

vec3 bump(vec3 p, vec3 n, float bf) {
  vec2 h = vec2(0.01, 0.0);
  vec3 g = vec3(
    mat(p - h.xyy, n),
    mat(p - h.yxy, n),
    mat(p - h.yyx, n));

  g -= mat(p, n);
  g -= n*dot(g, n);

  return normalize(n + bf*g);
}

vec3 mat(vec3 p, vec3 n, sampler2D s) {
  vec3 m = pow(abs(n), vec3(10.0));

  vec3 x = texture(s, p.yz).rgb;
  vec3 y = texture(s, p.xz).rgb;
  vec3 z = texture(s, p.xy).rgb;

  return (m.x*x + m.y*y + m.z*z)/(m.x + m.y + m.z);
}

vec3 bump(vec3 p, vec3 n, sampler2D s, float bf) {
  vec2 h = vec2(0.009, 0.0);

  vec3 g = mat3(
    mat(p - h.xyy, n, s),
    mat(p - h.yxy, n, s),
    mat(p - h.yyx, n, s))*vec3(0.299, 0.589, 0.114);

  g -= dot(mat(p, n, s), vec3(0.299, 0.589, 0.114));
  g -= n*dot(g, n);

  return normalize(n + bf*g);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

  vec3 col = vec3(0);

  float at = mod(iTime, 100.0);

  vec3 ro = vec3(0, 0, -3.0 + at);
  ro.xy -= path(ro.z);

  vec3 la = vec3(0, 0, at);
  la.xy -= path(la.z);

  vec3 ww = normalize(la-ro);
  vec3 uu = cross(vec3(0, 1, 0), ww);
  vec3 vv = cross(ww, uu);
  vec3 rd = normalize(mat3(uu, vv, ww)*vec3(uv, 1));

  float t = 0.0, m = -1.0, mx = 50.0;
  for(int i = 0; i < 200; i++) {
    vec2 d = de(ro + rd*t);
    if(d.x < 0.001 || t >= mx) break;
    t += d.x*0.75;
    m = d.y;
  }

  vec2 h = vec2(0.001, 0.0);
  vec3 salb = vec3(1.00, 0.89, 0.25);
  vec3 lp = ro + vec3(0, 0, 3);

  if(t < mx) {
    vec3 p = ro + rd*t;
    vec3 n = normalize(vec3(
      de(p + h.xyy).x - de(p - h.xyy).x,
      de(p + h.yxy).x - de(p - h.yxy).x,
      de(p + h.yyx).x - de(p - h.yyx).x));

    vec3 alb = salb;

    vec3 ld = normalize(lp-p);

    if(m == 1.0) {
      alb = vec3(0.1, 0.2, 0.3);
      n = bump(p*0.25, n, 2.0);
    } else if(m == 2.0) {
      alb = vec3(0.3, 0.2, 0.1);
      n = bump(p, n, iChannel0, 1.0);
    }

    float ot = t/50.0;
    float occ = exp2(-pow(max(0.0, 1.0 - de(p + n*ot).x/ot), 2.0));
    float dif = max(0.0, dot(ld, n));

    float spe = pow(max(0.0, dot(reflect(-ld, n), -rd)), 16.0);
    float fre = pow(dot(rd, n) + 1.0, 2.0);

    col = 2.0*mix(occ*(alb*(0.1 + dif) + salb*spe), alb, fre);
  }

  col += 0.05*salb*glow;

  //col = mix(col, vec3(0.2, 0.3, 0.4), 1.0 - exp(-0.1*t));
  //col = vec3(1)*form(uv);
  fragColor = vec4(pow(col, vec3(0.4545)), 1);
}

vec2 hash2 (vec2 p)
{
    return fract(vec2(5978.23857, 2915.98275)*sin(vec2(
        p.x*832.2388 + p.y*234.9852,
        p.x*921.7381 + p.y*498.2348
        )))*2.-1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.y*20. + iTime,
    f = fract(p),
    m = f*f*(3.-f-f);
    p -= f;

    float n = mix(
        mix(dot(hash2(p + vec2(0,0)), f - vec2(0,0)), 
            dot(hash2(p + vec2(1,0)), f - vec2(1,0)), m.x),
        mix(dot(hash2(p + vec2(0,1)), f - vec2(0,1)), 
            dot(hash2(p + vec2(1,1)), f - vec2(1,1)), m.x),
        m.y);

    fragColor = vec4(.5*n+.5);
}

