/* These are some simple color-tweaking effects using a variant on YCbCr and YCoCg that I hacked
 * together, called YCwCm.
 * Y is luma (perceptual lightness) and it isn't calculated especially well; it ranges from 0.0 
 * to 1.0, with 0.0 including but not limited to black, and 1.0 including (not just) white.
 * Cw is Chroma Warm, and determines whether a color is close to blue/green or red/yellow; it
 * ranges from -1.0 to 1.0, with -1.0 blue to green, 0.0 purple to grayscale to chartreuse, and 1.0
 * red to yellow. Cm is Chroma Mild, and determines whether a color is close to blue/red or
 * green/yellow; it ranges from -1.0 to 1.0, with -1.0 blue to red, 0.0 cyan to gray to orange, and
 * 1.0 green to yellow. 
 * You can change mul and add to edit the effect. A few versions are provided.
 */

//// yellowing dingy effect
//const vec3 mul = vec3(0.9, 0.7, 0.75);
//const vec3 add = vec3(0.05, 0.14, 0.16);

//// hot/on-fire effect?
//const vec3 mul = vec3(1.1, 0.8, 1.0);
//const vec3 add = vec3(-0.1, 0.65, 0.2);

// ice effect?
const vec3 mul = vec3(1.0, 0.7, 0.7);
const vec3 add = vec3(0.4, -0.5, -0.15);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 mulWave = mix(mul, vec3(1.0), sin(iTime) * 0.5 + 0.5);
    vec3 addWave = mix(add, vec3(0.0), sin(iTime) * 0.5 + 0.5);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 tgt = texture( iChannel0, uv );
    // xyz is used when a color is using luma, warm, mild instead of red, green, blue channels
    tgt.xyz = addWave + mulWave * vec3(dot(tgt.rgb, vec3(0.375, 0.5, 0.125)), tgt.r - tgt.b, tgt.g - tgt.b);
    fragColor.rgb = clamp(vec3(
           dot(tgt.xyz, vec3(1.0, 0.625, -0.5)),
           dot(tgt.xyz, vec3(1.0, -0.375, 0.5)),
           dot(tgt.xyz, vec3(1.0, -0.375, -0.5))),
       0.0, 1.0);
    fragColor.a = 1.0;
}

vec3 COLOR1 = vec3(0.1, 0.1, 0.0);
vec3 COLOR2 = vec3(0.0, 0.1, 0.1);
float BLOCK_WIDTH = 0.01;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	// To create the BG pattern
	vec3 final_color = vec3(0.0);
	vec3 bg_color = vec3(0.0);
	vec3 wave_color = vec3(0.0);

	float c1 = mod(uv.x, 20. * BLOCK_WIDTH );
	c1 = step(BLOCK_WIDTH, c1);

	float c2 = mod(uv.y, 20. * BLOCK_WIDTH );
	c2 = step(BLOCK_WIDTH, c2);

	bg_color = mix(uv.x * COLOR1, uv.y * COLOR2, c1 * c2);


	// To create the waves
	float wave_width = 0.01;
	uv  = -1.3 + 2.4* uv;
	uv.y += 0.1;
	for(float i = 0.0; i < 13.0; i++) {

		uv.y += (0.3 * sin(uv.x + i/1.5 + iTime ));
		wave_width = abs(1.0 / (150.0 * uv.y));
		wave_color += vec3(wave_width * 1.7 * sin(iTime-1.5), wave_width, wave_width * 1.5 * sin(iTime+1.));
	}

	final_color = bg_color + wave_color;


	fragColor = vec4(final_color, 1.0);
}

#define PI 3.1415926 
#define PI2 6.2831852 

#define hue(h)clamp(abs(fract(h + vec4(3, 2, 1, 0) / 3.0) * 6.0 - 3.0) - 1.0 , 0.0, 1.0)

void mainImage(out vec4 fragColor, in vec2 fragCoord)
 { 
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;

    float CIRCLES = 20.0;
    float cS = 0.375;

    float sm = 1.0 / iResolution.y * 2.0; // smooth
    float ps = 1.0 / iResolution.y * sqrt(iResolution.y) * 0.225; // circle thin

    float d = length(uv);

    float a = atan(uv.y, uv.x);
    a = a < 0.0 ? PI + (PI - abs(a)) : a;

    float lPos = a /PI2;

    float m = 0.0;
    float partSize = 1.0 / CIRCLES;
    vec3 col;
    for(float i = CIRCLES; i > 1.0; i -= 1.0) {

        float ilPos = fract(lPos + i*0.1 + iTime * 0.1);
        float cPos = partSize * i + ilPos * partSize;
        float invPos = partSize * (i + 1.0) - ilPos * partSize;
        float nzF = (1.0 - ilPos);
        float mP0 = texture(iChannel0, vec2(partSize * i, 0.0)).x;
        float mP = texture(iChannel0, vec2(cPos, 0.0)).x;
        float mPInv = texture(iChannel0, vec2(invPos, 0.0)).x;

        mP = (mP + mPInv) / 2.0;

        float rDiff = i*(1.0 / CIRCLES * 0.35);
        float r = mP * (1.0 / CIRCLES * 3.0) - rDiff;

        float subm = smoothstep(cS - ps + r, cS - ps + sm + r, d) * smoothstep(cS + r, cS - sm + r, d);

        if (subm > 0.0) {
            col = hue(i / CIRCLES * 0.5 + iTime * 0.05 + mP0 * 0.84).rgb;
        }

        m += subm;
    }

    m = clamp(m, 0.0, 1.0);

    float r = (sin(iTime * 0.5) * 0.5 + 0.5);
    float b = (cos(iTime * 0.5) * 0.5 + 0.5);
    vec3 backCol = vec3(r, 0.0, b) * length(uv * 0.75) * 0.5;

    col = mix(backCol, col, m);

    fragColor = vec4(col, 1.0);
}

# define WARP 1.
# define FREQ_RANGE 20.
# define PI 3.141592653589793
# define SCALE iResolution.x/
# define SOUNDSOURCE iChannel1
// http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float getfrequency(float x) {
	return texture(SOUNDSOURCE, vec2(floor(x * FREQ_RANGE + 1.0) / FREQ_RANGE, .25)).x + 0.06;
}

float getfrequency_smooth(float x) {
	float index = floor(x * FREQ_RANGE) / FREQ_RANGE;
    float next = floor(x * FREQ_RANGE + 1.0) / FREQ_RANGE;
	return mix(getfrequency(index), getfrequency(next), smoothstep(0.0, 1.0, fract(x * FREQ_RANGE)));
}

float getfrequency_blend(float x) {
    return mix(getfrequency(x), getfrequency_smooth(x), 0.5);
}

float getMaxFreq(){
	return texture(SOUNDSOURCE, vec2(1.)).x;
}

vec3 gradients( in vec2 fragCoord ) {
	vec2 uv = fragCoord/iResolution.xy;

    return hsv2rgb(vec3 (
        0.5+0.5*sin(0.4*iTime*WARP)*(
            sqrt(
                (
                    pow(0.5-0.6*cos(0.3*WARP*iTime)*(fragCoord.x-(iResolution.x-iResolution.y)/2.)/iResolution.y,2.)
                )
                +
                (
                    pow(0.7-0.4*sin(0.15*WARP*iTime)*uv.y,2.)
                )
            )
        ),
        0.8+0.2*cos(0.2*WARP*iTime)*(
            sqrt(
                (
                    pow(0.7-0.2*sin(0.5*WARP*iTime)*(fragCoord.x-(iResolution.x-iResolution.y)/2.)/iResolution.y,2.)
                )
                +
                (
                    pow(-0.1*cos(0.3*WARP*iTime)*uv.y,2.)
                )
        	)
        ),
        0.9+0.1*sin(WARP*iTime)*(
            sqrt(
                (
                    pow(0.6-
                        sin(2.*WARP*iTime)*
                        (
                            fragCoord.x-(
                                iResolution.x-iResolution.y
                            )/2.
                        )/iResolution.y,2.
                       )
                )
                +
                (
                    pow(0.2-cos(WARP*iTime)*uv.y,2.)
                )
        	)
        )
    ));
}

vec3 ripples( in vec2 fragCoord, in vec2 cent ){
	vec2 uv = fragCoord/iResolution.xy;
    float scale = iResolution.x/1920.;
    float max_rad = sqrt(
        pow(iResolution.x,2.0)
        +pow(iResolution.y,2.0)
    );

    float r = sqrt(
    	pow(fragCoord.x-cent.x,2.0)
        +pow(fragCoord.y-cent.y,2.0)
    );

    return vec3 (
    	1.-0.9*sin((26.*(r/scale-150.*WARP*iTime)/(0.2*max_rad/scale)+0.1))
    );
}

float fade( in float f_in, in float f_out){
	float warp_time = iTime*WARP;
    float fade_duration = f_out-f_in;
    return max(0.,min(1./(f_out-f_in)*(f_out-warp_time),1.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float weight = .90;
    float pi = 3.141592653589793;
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cent = 0.5*iResolution.xy;
    float scale = iResolution.x/1920.;
    vec2 offset = vec2 (-.1*iResolution.y, -.1*iResolution.y);
    vec2 cent2 = vec2 (-0.2*iResolution.x, -0.2*iResolution.y);
    vec2 cent3 = vec2 (1.2*iResolution.x,1.2*iResolution.y);
    vec2 cent4 = vec2 (-0.2*iResolution.x,1.2*iResolution.y);
    vec2 cent5 = vec2 (1.2*iResolution.x,-0.2*iResolution.y);
    vec2 cent6 = vec2 (iResolution.x/2., iResolution.y*1.05);
    vec2 cent7 = vec2 (iResolution.x/2., -iResolution.y*0.15);

    vec3 col = fade(60.,80.)*gradients(fragCoord)*weight
        	   +fade(80.,60.)*(
                   			   hsv2rgb(vec3 (
                                   sin(0.01*iTime)-0.4*getfrequency_smooth(
                                       0.3*sin(
                                           max(0.01,
                                               min(1.,
                                                sqrt(
                                                    pow((cent.x-fragCoord.x)/(500.*scale),2.)
                                                    +pow((cent.y-fragCoord.y)/(500.*scale),2.)
                                                )
                                               )
                                           )   
                                       )
                                   ),
                                   1.,
                                   0.3
                               )))
        	   -(
                   +fade(25.,40.)*(
                   	+cos(-iTime*WARP*0.02)*ripples(fragCoord,cent3)
                   	+cos(-iTime*WARP*0.02)*ripples(fragCoord,cent3+offset)
                   	+cos(-iTime*WARP*0.02)*ripples(fragCoord,cent2)
                    +cos(-iTime*WARP*0.02)*ripples(fragCoord,cent2+offset)
                    +cos(-iTime*WARP*0.02)*ripples(fragCoord,cent4)
                    +cos(-iTime*WARP*0.02)*ripples(fragCoord,cent4+offset)
                    +cos(-iTime*WARP*0.02)*ripples(fragCoord,cent5)
                    +cos(-iTime*WARP*0.02)*ripples(fragCoord,cent5+offset)
                   )
                   +fade(37.,20.)*fade(50.,60.)*(
                   	+2.*cos(-iTime*WARP*0.1)*ripples(fragCoord,cent6)
                   	+2.*cos(-iTime*WARP*0.1)*ripples(fragCoord,cent6-offset)
                   	+2.*cos(-iTime*WARP*0.1)*ripples(fragCoord,cent7)
                   	+2.*cos(-iTime*WARP*0.1)*ripples(fragCoord,cent7+offset)
                   )
                   /*+fade(70.,30.)*(
                    +cos(-iTime*WARP/10.)*ripples(fragCoord,cent)
                    +cos(-iTime*WARP/10.)*ripples(fragCoord,cent+offset)
                   )*/
                   +fade(80.,40.)*1.*(
                    +(0.3+0.7*getfrequency_smooth(0.1*mod(iTime,10.))
                     *ripples(fragCoord,cent
                      +(0.7+0.3*getfrequency_smooth(0.1*mod(iTime,10.)))
                      +vec2(-offset.x/4.*sin(iTime*WARP),
                            -offset.x/4.*cos(iTime*WARP))
                      +(0.7+0.3*getfrequency_smooth(0.1*mod(iTime,10.)))
                       *vec2(-offset.x*4.*sin(0.4*iTime*WARP),
                            -offset.x*4.*cos(0.4*iTime*WARP))
                      )
                     )
                    +(0.3+0.7*getfrequency_smooth(0.1*mod(iTime,10.))
                     *ripples(fragCoord,cent
                      +(0.7+0.3*getfrequency_smooth(0.1*mod(iTime,10.)))
                       *vec2(offset.x/4.*sin(iTime*WARP),
                             offset.x/4.*cos(iTime*WARP))
                      +(0.7+0.3*getfrequency_smooth(0.1*mod(iTime,10.)))
                       *vec2(-offset.x*4.*sin(0.4*iTime*WARP),
                             -offset.x*4.*cos(0.4*iTime*WARP))
                      )
                     )
                   )
               )*(1.-weight);

    fragColor = vec4(0.2+0.8*sqrt(col),1);
}

