precision highp float;

// number of samples used for motion blur, set to 1.
// for no motion-blur, if the frame-rate is too low
// higher values give smoother motion-blur results
const float MOTION_BLUR_SAMPLES = 12.;

const int MAX_ITER    = 32;
const float STEP_SIZE = 1.;
const float EPSILON   = .001;

mat2 r2d (float d) {
	float a = radians(d);
	float c = cos(a);
	float s = sin (a);
	return mat2 (vec2 (c, s), vec2 (-s, c));
}

float map (vec3 p, inout int id, inout vec3 pout, float t)
{
	// rotate the cube
	p.xz *= r2d (135.*t);
	p.zy *= r2d (90.*t);
	p.xy *= r2d (-73.*t);

	// twist the cube
	p.xy *= r2d (35.*cos(p.z + 2.*t));
	p.yz *= r2d (60.*sin(p.x + 3.*t));
	p.zx *= r2d (40.*sin(p.y + 3.*t));

    // build cube from infinite planes, so it's
    // easier to get the UVs per plane later
	float size = .75;
	float p1 = p.y - size;
	float p2 = -p.y - size;
	float p3 = p.z - size;
	float p4 = -p.z - size;
	float p5 = p.x - size;
	float p6 = -p.x - size;

	float d = max (p1, p2);
	d = max (d, p3);
	d = max (d, p4);
	d = max (d, p5);
	d = max (d, p6);

    // material/UV id
	if (d == p1) id = 1;
	if (d == p2) id = 2;
	if (d == p3) id = 3;
	if (d == p4) id = 4;
	if (d == p5) id = 5;
	if (d == p6) id = 6;

    // scale UVs
	pout = 10.*p;

	return d;
}

float march (vec3 ro, vec3 rd, inout int id, inout vec3 pout, float tm)
{
	float t = .0;
	float d = .0;

    for (int i = 0; i < MAX_ITER; i++)
    {
        t = map (ro+d*rd, id, pout, tm);
        if (abs (t) < EPSILON*(1. + .125*t)) break;
        d += t*STEP_SIZE;
    }

    return d;
}

vec3 background (vec2 uv, float t)
{
	float d = fract (5.*length (uv) - 3.*t);
	float m = smoothstep (.0, 1., d);
    return mix (vec3 (.0125), vec3 (.3, .0125, .0), 1. - m);
}

vec3 shade (int id, vec3 pout, float t)
{
    vec3 color = vec3 (.0);
	float dark = .1;
	float light = .9;

	if (id == 1) {
		pout.xz *= r2d (90.*t);
		float lf = 4.*texture (iChannel0, .25*pout.xz).r;
		float hf = .25*texture (iChannel0, 4.*pout.xz).r;
		float d = cos (2.*pout.z - 6.*t + sin(2.*(pout.x+lf+hf)));
		float m = smoothstep (.3, .7, d);
		color = mix (vec3 (dark), vec3 (light), m);
	}
	if (id == 2) {
        t *= 2.;
		pout.xz *= r2d (-135.*t);
		pout.xz *= 20.;
        float d = cos(pout.x*.12 + t)+
                  cos(pout.x*.13 - t)+
                  cos(pout.z*.1 + t)+
            	  cos(pout.z*.105 - t);
	    float m = smoothstep (.0, 1., d);

		color = mix (vec3 (dark), vec3 (light), m);
	}
	if (id == 3) {
		float lf = 2.*texture (iChannel0, .125*pout.xy).r;
		float hf = .125*texture (iChannel0, 8.*pout.xy).r;
		float d = cos (5.*(pout.x+lf+hf)-5.*(pout.y-lf+hf)+15.*t);
		float m = smoothstep (.5, .55, d);
		color = mix (vec3 (dark), vec3 (light), m);
	}
	if (id == 4) {
		pout.xy *= 1. + 1.*(.5 + .5*cos(4.*t));
		pout.xy *= r2d (45.*t);
		float lf = texture (iChannel0, .25*pout.xy-.6*t).r;
		float hf = .25*texture (iChannel0, 2.*pout.xy+.4*t).r;
		float d = cos (pout.x+lf+hf + sin(lf+hf*pout.y));
		float m = smoothstep (.0, 1., d);
		color = mix (vec3 (dark), vec3 (light), m);
	}
	if (id == 5) {
		float d = fract (length (.5*pout.zy)+2.*t);
		float m = smoothstep (.45, .55, d);
		color = mix (vec3 (dark), vec3 (light), d);
	}
	if (id == 6) {
		vec2 off1 = 5.*vec2 (cos (1.6*t), sin (2.4*t));
		vec2 off2 = 6.*vec2 (cos (2.1*t), sin (1.5*t));
		float d1 = fract (length (pout.yz + off1) - .3);
		float d2 = fract (length (pout.yz + off2) - .3);
		float m1 = smoothstep (.5, .52, d1);
		float m2 = smoothstep (.5, .52, d2);
		float m = m1*(1. - m2) + m2*(1. - m1);
		color = mix (vec3 (dark), vec3 (light), m);
	}

    return color;
}

vec3 camera (in vec2 uv, in vec3 ro, in vec3 aim, in float zoom)
{
    vec3 f = normalize (vec3 (aim - ro));
    vec3 wu = vec3 (.0, 1., .0);
    vec3 r = normalize (cross (wu, f));
    vec3 u = normalize (cross (f, r));
    vec3 c = ro + f*zoom;

    return normalize (c + uv.x*r + uv.y*u - ro);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uvRaw = fragCoord.xy/iResolution.xy;
	vec2 uv = uvRaw*2. - 1.;
    uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3 (1., 1.5, 2.);
    vec3 aim = vec3 (.0, .5*cos(4.*iTime), .0);
    float zoom = 1.5+.2*cos(3.*iTime);
    vec3 rd = camera (uv, ro, aim, zoom);

	int id = 0;
	vec3 pout;

    float d;
    float fog;
    vec3 bg;
    vec3 fg;
	vec3 c = vec3 (.0);

    const float numSamples = MOTION_BLUR_SAMPLES;
    for (float samples = .0; samples < numSamples; ++samples) {
        float t = iTime - samples/numSamples/60.;
		d = march (ro, rd, id, pout, t);
    	fog = abs (1./(2. - exp(d*d*.35)));
    	bg = background (uv, t);
        fg = shade (id, pout, t);
        c += (d < 3.25) ? fg*fog : bg;
    }
    c /= numSamples;

    c = c / (1. + c);
	c *= 1. - .75*length (uvRaw*2.-1.);
	c *= mix (1., .5, cos(700.*uv.y));
	c *= mix (1., .5, cos(700.*uv.x));
    c = pow (c, vec3 (1./2.2));

	fragColor = vec4(c, 1.);
}

#define offsetDist(offset) dist = distance(pos,ballPos+offset)-ballRadius;if (dist < t){t = dist;newPos = ballPos+offset;}
vec4 distToFractal(vec3 pos){
    vec3 ballPos = vec3(0);
    float ballRadius = 1.3;
    float time = 3.14159265*sin(iTime*1.1)/20.;
    vec2 rotator = vec2(cos(time),sin(time));
    vec3 X = vec3(1.5,0,0);
    vec3 Y = vec3(0,1,0);
    vec3 Z = vec3(0,0,1);
    float t = 1000.;
    vec3 newPos;
    float dist;

    for (int i = 0; i < 6; ++i){



        //float midt = length(pos-ballPos)-ballRadius;
        offsetDist(vec3(0));
        ballRadius /= 2.;

        offsetDist(X)
       	offsetDist(Y)
        offsetDist(Z)
        offsetDist(-X)
        offsetDist(-Y)
        offsetDist(-Z)
      	/*if (midt < t){
            return vec4(normalize(pos-ballPos),midt);

        }*/

        X = vec3(rotator.x*X.x - rotator.y*X.y,rotator.x*X.y+rotator.y*X.x,0);
        X /= 2.;
        Y /= 2.;

        Z /= 2.;
        ballPos = newPos;
    }
    return vec4(normalize(pos-newPos),t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.x;
    float aspect = iResolution.x/iResolution.y;
    vec3 viewingNormal = normalize( vec3( uv-vec2(0.5,0.5/aspect),1));
    float time = iTime * 0.1;
    vec3 cameraPos = 10.*vec3(cos(time),cos(iTime)/5.,sin(time));
    vec3 currentPos = cameraPos;
    vec3 Z = -normalize(currentPos);
    vec3 X = normalize(cross(vec3(0,1,0),Z));
    vec3 Y = cross(Z,X);
    viewingNormal = viewingNormal.x * X + viewingNormal.y * Y + viewingNormal.z *Z;
    fragColor = vec4(0,0,0,1);
    for (int i = 0; i < 50; ++i){
        vec4 NT = distToFractal(currentPos);
        if (NT.w < 0.001){
            const vec3 lightPos = vec3(2,-10,4);
            vec3 position = viewingNormal*NT.w;
            vec3 surfaceToLight = normalize(lightPos - position);
            vec3 reflected = reflect(viewingNormal,NT.xyz);
            float sky = asin(dot(NT.xyz,vec3(0,1,0)))/3.141459+0.5;
            float shine = smoothstep(0.8,1.,dot(reflected,surfaceToLight));
            float diffuse = dot(NT.xyz,surfaceToLight);
            fragColor = vec4(vec3(0.1+0.3*shine+0.5*sky+0.3*diffuse),1);
        }
        currentPos += viewingNormal*NT.w;

    }
}

#define PI 3.14159265359
#define rot(a) mat2(cos(a + PI*0.5*vec4(0,1,3,0)))
#define Z min(iFrame, 0)

// how many segments there are
#define SEGMENTS 21
// only display the nth segment
//#define SEGMENT 0

#define COLOR_SHELL vec4(0.015, 0.01, 0.005, 0.3)
#define COLOR_SHELL2 vec4(0.15, 0.01, 0.001, 0.6)
#define COLOR_ORGAN vec4(0.3, 0.1, 0.01, 0.7)
#define COLOR_SUB vec3(0.5, 0.8, 0.9)

#define VR_SCALE 0.5

vec4 dummy = vec4(0);

// iq's distance to a box
float sdBox( vec3 p, vec3 b ) {
    vec3 d = abs(p) - b;
    return length(max(d,0.0)) + min(max(d.x,max(d.y,d.z)),0.0);
}

// iq's distance to capped cylinder
float sdCappedCylinder( vec3 p, vec2 h ) {
    vec2 d = abs(vec2(length(p.xz),p.y)) - h;
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

// iq's smooth minimum
float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

// iq's box filtered grid
float grid( in vec2 p ) {
    vec2 dpdx = dFdx(p);
    vec2 dpdy = dFdy(p);
    const float N = 15.0;
    p += 1.0/N*0.5;
    vec2 w = max(abs(dpdx), abs(dpdy));
    vec2 a = p + 0.5*w;                        
    vec2 b = p - 0.5*w;           
    vec2 i = (floor(a)+min(fract(a)*N,1.0)-
              floor(b)-min(fract(b)*N,1.0))/(N*w);
    return (1.0-i.x)*(1.0-i.y);
}

// dave hoskins hash
vec3 hash33( vec3 p3 ) {
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

// path parametric equation
vec3 path( float x ) {
    x += iTime*0.7;
    return vec3(cos(x), sin(x*2.3312)*0.3 + sin(x*1.456)*0.4, sin(x))*1.0;
}

// path derivative
vec3 pathd( float x ) {
    x += iTime*0.7;
    return vec3(-sin(x), cos(x*2.3312)*0.3*2.3312+cos(x*1.456)*0.4*1.456, cos(x))*1.0;
}

// path scale
float paths( float x ) {
    return 1.0 - cos(x*2.0)*0.3;
}

// one leg
float leg( vec3 p, const bool complex, out vec4 albedo, float parm, float phase, int end ) {

    float scaleP = paths(parm);

    // walk animation
    float xro = iTime*-3.0+phase*2.0*PI + parm*12.0;
    float sro = sin(xro);
    float cro = cos(xro);

    float ends = 1.0;
    if (end == 0) ends = 0.2;
    if (end == SEGMENTS-1) ends = 0.4;

    p = p.yxz;

    p.yz *= rot(sro*0.3*ends);
    p.yx *= rot(-0.2);
    p.y += 0.02;

    float de = length(p)-0.03;
    float s = 0.0;
    float scale = 1.0;
    float col = 0.0;

    float temp = (cro*-0.5+0.5)*0.5*ends;

    for (int i = Z ; i < 3 ; i++) {

        // offset the leg segment
        p.y -= 0.04;
        p.yx *= rot( temp + 0.1*scale );
        p.y -= 0.04;

        // bend toward a point
        s += smoothstep(-0.04, 0.04, p.y)*scale;
        // find out the segment dimension
        vec2 dim = vec2(0.039 - 0.02*(scale *= 1.1) - 0.003*s, 0.04);
        // get distance
        float d = sdCappedCylinder( p, dim ) - 0.002;
        de = smin(de, d, 0.01);

        // colorize a bit
        if ( complex ) {
            float f = smoothstep(0.01, -0.01, d);
            float c = sin(p.y*60.0)*-0.5+0.5;
        	col += f*c;
        }
    }

    if ( complex ) {
        float f = max(0.2, 0.75-col*0.7);
        vec4 colOrg = mix(COLOR_ORGAN, COLOR_SHELL2, clamp((1.0-scaleP)*3.0+0.5, 0.0, 1.0));
        albedo = mix(colOrg, COLOR_SHELL, f);
    }

    return de;

}

// one body part with two legs, return organic/shell as vec2
vec2 body( vec3 p, const bool complex, out vec4 albedo, float parm, int index ) {

    float scaleP = paths(parm);
    p /= scaleP;

    // add a base body
    vec3 dim = vec3(0.04, 0.01, 0.05);
    dim += cos(p.zxx*vec3(48,38,30))*vec3(0.01, 0.005, 0.02);
   	float org = sdBox(p, dim) - 0.04;

    // shell above it
    vec3 inShell = p - vec3(0, 0.055, -0.01);
    inShell.yz *= rot(-0.15);
    inShell.yz -= cos(inShell.xx*vec2(20, 15))*0.02;
    inShell.x = abs(inShell.x) - cos(inShell.z*20.0+1.0)*0.01;
    float de = sdBox(inShell, vec3(0.08, 0, 0.09)) - 0.01;

    // legs
    vec3 inLe = p;
    float phase = step(p.x, 0.0)*0.5;
    inLe.x = abs(inLe.x);
    if (index == 0) {
        // recycle legs into antennas
        phase *= 0.4;
        inLe.xz *= rot(-1.2);
        inLe -= vec3(0.12, 0.02, 0.02);
        inLe.xy *= rot(-0.3);
        // add mandibles while we're here
        vec3 inMandi = p;
       	inMandi.x = abs(inMandi.x) - 0.04;
        inMandi.yz += vec2(0.03, -0.1);
        inMandi.xy *= rot(0.3);
        inMandi.yz *= rot(-0.3);
        float mandi = sdBox(inMandi, vec3(0.01, 0.03, 0.04)) - 0.025;
        de = min(de, mandi);
    } else if (index == SEGMENTS-1) {
        phase *= 0.2;
        inLe.xz *= rot(1.2);
        inLe -= vec3(0.1, 0.02, -0.01);
        inLe.xy *= rot(-0.3);
    } else {
        inLe.xy += vec2(-0.07, 0.02);
    }

    // add more stuff between the body and leg
    vec4 leAlbedo = vec4(0);
    float le = leg(inLe, complex, leAlbedo, parm, phase, index);
    float bum = length(inLe)-0.03;
    le = smin(le, bum, 0.03);

    // add bump mapping and coloring
    if ( complex ) {
        float tex1 = textureLod(iChannel0, inShell.xz*1.0 + float(index)*0.1424, 0.0).r;
        float tex2 = textureLod(iChannel0, inShell.xz*1.4 + float(index)*0.1424, 0.0).r;
        float she = smoothstep(0.0, 0.025, de+tex1*0.005);
        de += tex1*0.03;
        org -= tex2*0.03;
        vec4 colOrg = mix(COLOR_ORGAN, COLOR_SHELL2, clamp((1.0-(scaleP*scaleP))*3.0-0.5, 0.0, 1.0));
        float leAl = smoothstep(0.01, 0.0, le);
        colOrg = mix(colOrg, leAlbedo, leAl);
        vec4 baseColor = mix(COLOR_SHELL, colOrg, she);
        albedo = baseColor;
    }

    // then add the legs to the shell
    de = smin(de, le, 0.005);

    // return organic + shell to composite later
    return vec2(org, de) * scaleP;
}

// distance estimator
float de( vec3 p, const bool complex, out vec4 albedo ) {

    #ifdef SEGMENT
    vec2 seg = body(p, complex, albedo, 0.0, SEGMENT);
    return smin(seg.x, seg.y, 0.01);
    #endif

    // find the two closest points
    vec3[2] closestParmDistIndex = vec3[2](vec3(0, 9e9, 0), vec3(0, 9e9, 0));
    float currentParm = 0.0;
    for (int i = Z ; i < SEGMENTS ; i++) {
        vec3 current = path(currentParm);
        vec3 currentD = p - current;
        float currentDist = dot(currentD, currentD);
        if (currentDist < closestParmDistIndex[0].y) {
            closestParmDistIndex[1] = closestParmDistIndex[0];
            closestParmDistIndex[0] = vec3(currentParm,currentDist,float(i));
        } else if (currentDist < closestParmDistIndex[1].y) {
            closestParmDistIndex[1] = vec3(currentParm,currentDist,float(i));
        }
        currentParm -= 0.17 / length(pathd(currentParm)) * paths(currentParm);
    }

    float dOrg = 8.0;
    float dShe = 8.0;

    albedo = COLOR_SHELL;

    for (int i = Z; i < 2 ; i++) {
        float centerParm = closestParmDistIndex[i].x;
        int centerIndex = int(closestParmDistIndex[i].z);
        vec3 center = path(centerParm);

        // create basis
        vec3 upZ = path(centerParm + 0.3) - center;
        vec3 forward = normalize(path(centerParm + 0.1) - center);
        vec3 right = normalize(cross(forward, upZ));
        vec3 up = cross(forward, right);
        // offset and transform
        vec3 tp = p - center;
        tp = tp * mat3(right, up, forward);

        // get distance to the object
        vec4 albObj = vec4(0);
        vec2 de = body(tp, complex, albObj, centerParm, centerIndex);
        float minDe = min(de.x, de.y);

        // composite organic/shell differently so shell/legs don't merge together
        dOrg = smin(dOrg, de.x, 0.07);
        dShe = min(dShe, de.y);

        if (complex) {
            albedo = mix(albedo, albObj, smoothstep(0.01, -0.01, minDe));
        }
    }

    float d = smin(dShe, dOrg, 0.02);

    return d;
}

// normal function, call de() in a for loop for faster compile times.
vec3 getNormal( vec3 p, float here ) {
    vec3 n = vec3(0);
    for (int i = Z ; i < 3 ; i++) {
        vec3 s = p;
        s[i] += 0.01;
        n[i] = de(s, true, dummy);
    }
    return normalize(n.xyz-here);
}

// PBR WORKFLOW BELOW

float DistributionGGX(vec3 N, vec3 H, float roughness) {
    float a = roughness*roughness;
    float a2 = a*a;
    float NdotH = max(dot(N, H), 0.0);
    float NdotH2 = NdotH*NdotH;
    float num = a2;
    float denom = (NdotH2 * (a2 - 1.0) + 1.0);
    denom = PI * denom * denom;
    return num / denom;
}

float GeometrySchlickGGX(float NdotV, float roughness) {
    float r = (roughness + 1.0);
    float k = (r*r) / 8.0;
    float num = NdotV;
    float denom = NdotV * (1.0 - k) + k;
    return num / denom;
}

float GeometrySmith(vec3 N, vec3 V, vec3 L, float roughness) {
    float NdotV = max(dot(N, V), 0.0);
    float NdotL = max(dot(N, L), 0.0);
    float ggx2 = GeometrySchlickGGX(NdotV, roughness);
    float ggx1 = GeometrySchlickGGX(NdotL, roughness);
    return ggx1 * ggx2;
}

vec3 fresnelSchlick(float cosTheta, vec3 F0) {
    return F0 + (1.0 - F0) * pow(1.0 - cosTheta, 5.0);
}

vec3 computeLighting(in vec3 normal, in vec3 viewDir,
                     in vec3 albedo, in float metallic, in float roughness,
                     in vec3 lightDir, in vec3 radiance) {

    vec3 result = vec3(0);

    // find half way vector
    vec3 halfwayDir = normalize(viewDir + lightDir);

    // figure out surface reflection
    vec3 F0 = vec3(0.04);
    F0 = mix(F0, albedo, metallic);

    // find the PBR terms
    float NDF = DistributionGGX(normal, halfwayDir, roughness);
    float G = GeometrySmith(normal, viewDir, lightDir, roughness);
    vec3 F = fresnelSchlick(max(dot(halfwayDir, viewDir), 0.0), F0);

    vec3 kS = F;
    vec3 kD = 1.0 - kS;
    kD *= 1.0 - metallic;

    // Cook Torrance BRDF
    vec3 numerator = NDF * G * F;
    float denominator = 4.0 * max(dot(normal, viewDir), 0.0) * max(dot(normal, lightDir), 0.0);
    vec3 specular = numerator / max(denominator, 0.001);  

    // add light contribution
    float NdotL = max(dot(normal, lightDir), 0.0);
    result += (kD * albedo / PI + specular) * radiance * NdotL;

    return result;
}

// PBR WORKFLOW ABOVE

// fake subsurface scattering
vec3 computeSSS(in vec3 normal, in vec3 viewDir, 
                in vec3 albedo, in float trans, in float index,
                in vec3 lightDir, in vec3 radiance) {
    float add = 1.0 - index;
    add *= add;
    add *= add;
    add *= add;
    add *= add;
    float fr = dot(viewDir, normal)*0.5+0.5;
    float lu = dot(viewDir, lightDir)*-0.5+0.5;
    add *= fr*fr;
    add *= lu;
    return radiance*add*1.0*trans*albedo;
}

// soft shadow function
float traceShadow( in vec3 from, in vec3 dir, in vec3 normal, const float sinTheta ) {
    if (dot(dir, normal) < 0.0) return 0.0;
    float minAlpha = 1.0;
    float totdist = 0.0;
    #define SHADOW_STEPS 20
    for (int i = Z ; i < SHADOW_STEPS ; i++) {
        vec3 p = from+dir*totdist;
        if (dot(p, p) > 6.0) return minAlpha;
        float dist = de(p, false, dummy);
        float rad = dist / (totdist*sinTheta);
        float alpha = rad * 0.5 + 0.5;
        if (alpha <= 0.0) {
            return 0.0;
        } else if (alpha < minAlpha) {
            minAlpha = alpha;
        }
        totdist += max(0.01, dist*0.8);
    }
    return minAlpha;
}

// get lighting here
vec3 getLighting( in vec3 p, in vec3 dir, float index ) {
    // get surface albedo and roughness
    vec4 albedo = vec4(0);
    float d = de(p, true, albedo);
    // get surface normal
    vec3 n = getNormal(p, d);

    vec3 result = vec3(0);
    const vec3 sunDir = normalize(vec3(1, 4, 2));
    const vec3 subDir = normalize(vec3(2, -7, 3));

    // add two lights, main one with shadows
    float shadow = traceShadow(p+n*0.01, sunDir, n, 0.05);
    result += computeLighting(n, dir, albedo.rgb, (1.0-albedo.a)*0.3, albedo.a, sunDir,
                              vec3(0.9, 0.85, 0.5)*10.0)*shadow;
    result += computeLighting(n, dir, albedo.rgb, (1.0-albedo.a)*0.3, albedo.a, subDir,
                              COLOR_SUB*0.5);
    // and add subsurface scattering
    result += computeSSS(n, dir, albedo.rgb, albedo.a, index, sunDir,
                         vec3(0.9, 0.85, 0.5)*10.0);
    result += computeSSS(n, dir, albedo.rgb, albedo.a, index, subDir,
                         COLOR_SUB*0.5);

    return result;
}

// get background
vec3 getBackground( in vec3 dir ) {
    // raytrace two plane
    dir.y = abs(dir.y);
    float d = 1.0 / dir.y;
    vec2 p = (dir*d).xz;
    // add a simple grid
    vec3 base = mix(COLOR_SUB*0.007, vec3(0.001), grid(p));
    // and a black fog over it
    base = mix(base, vec3(0.0002), 1.0-exp(-d*0.4));
    return base;
}

// return ray color for this position and direction
vec4 getColor( in vec2 fragCoord, in vec3 from, in vec3 dir ) {

    // get random stuff
    vec3 rnd = hash33(vec3(fragCoord, iFrame*2+0));

    // find the angular extent of this pixel
    vec3 ddir = normalize(dir+fwidth(dir)*0.5);
    float sinPix = length(cross(dir, ddir));
    // add some noise to the base distance as dithering
    float totdist = 0.2*rnd.x;

    // keep track of the closest position
    vec3 fullPos = vec3(0);
    float fullAlpha = 0.0;
    int fullIndex = 0;

    #define STEPS 100
    for (int i = Z ; i < STEPS ; i++) {
        vec3 p = from + dir*totdist;
        // found sky, break early
        if (dot(p, p) > 6.0) break;
        // find out the average alpha here
        float dist = de(p, false, dummy);
        float rad = dist / (totdist*sinPix);
        float alpha = rad * -0.5 + 0.5;

        // we have a covering sample, consider it
        if (alpha > 0.0 && alpha > fullAlpha) {
            fullPos = p;
            fullAlpha = alpha;
            fullIndex = i;
            // sample is fully opaque, break early
            if (alpha >= 1.0) break;
        }

        // move to next sample point
        totdist += max(0.0004, dist*0.8);
    }

    // if we found the surface add lighting
    if (fullAlpha > 0.0) {
        float index = float(fullIndex)/float(STEPS-1);
        fullAlpha = clamp(fullAlpha, 0.0, 1.0);
        return vec4(getLighting(fullPos, -dir, index), fullAlpha);
    }

    return vec4(0);
}

// vr entry point
void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir ) {

    // get color and mix with background
    vec4 col = getColor(fragCoord, fragRayOri/VR_SCALE, fragRayDir);
    fragColor.rgb = mix(getBackground(fragRayDir), col.rgb, col.a);
    // tonemapping
    fragColor.rgb = fragColor.rgb / (fragColor.rgb + vec3(1));
    // gamma correction
    fragColor.rgb = pow(fragColor.rgb, vec3(1.0/2.2));
    // add noise
    vec3 rnd = hash33(vec3(fragCoord, iFrame*2+1));
    fragColor.rgb += (rnd-0.5)*0.08;

    fragColor.a = 1.0;
}

// main entry point, simulate vr input
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord - iResolution.xy * 0.5;
    uv /= iResolution.y;

    vec3 from = vec3(0, 0, -2.0);
    #ifdef SEGMENT
    from = vec3(0, 0, -0.5);
    #endif
    vec3 dir = normalize(vec3(uv, 1.0));

    vec2 r = vec2(iTime*0.1, 0.3);

    if (iMouse.z > 0.25) {
        r = iMouse.xy - iResolution.xy*0.5;
        r *= -0.01;
    }

    dir.yz *= rot(r.y+0.1);
    from.yz *= rot(r.y);
    dir.xz *= rot(r.x);
    from.xz *= rot(r.x);

    mainVR(fragColor, fragCoord, from*VR_SCALE, dir);

    // add some vignetting in non vr
    vec2 uvv = fragCoord / iResolution.xy * 2.0 - 1.0;
    float vigD = dot(uvv, uvv);
    fragColor.rgb = mix(fragColor.rgb, vec3(0), vigD*0.3);

}

// mostly copied from this shadertoy code https://www.shadertoy.com/view/Mdl3Rr


#define BRUTE_FORCE_AA 1

#if BRUTE_FORCE_AA
#define AA_SAMPLES 4
#define INSCATTER_STEPS 30
#define NOISE_AMPLITUDE 0.1
#else
#define INSCATTER_STEPS 50
#define NOISE_AMPLITUDE 0.05
#endif

#define INF 1.0e38
#define HIT(x) hit = min(hit, x)

// Shadow rays can make things faster if there are big occluders
// but kinda ugly with no #include statement
//#define HIT(x) if (x < INF) return 0.0

mat3 m = mat3( 0.00,  0.80,  0.60,
			  -0.80,  0.36, -0.48,
			  -0.60, -0.48,  0.64 );

float hash (float n)
{
	return fract(sin(n)*43758.5453);
}

float noise (in vec3 x)
{
	vec3 p = floor(x);
	vec3 f = fract(x);

	f = f*f*(3.0-2.0*f);

	float n = p.x + p.y*57.0 + 113.0*p.z;

	float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
						mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
					mix(mix( hash(n+113.0), hash(n+114.0),f.x),
						mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
	return res;
}

float fbm (vec3 p)
{
	float f;
	f  = 0.5000*noise( p ); p = m*p*2.02;
	f += 0.2500*noise( p ); p = m*p*2.03;
	f += 0.1250*noise( p ); //p = m*p*2.01;
	//f += 0.0625*noise( p );
	return f;
}

float box(vec3 org, vec3 dir, vec3 size, out float far)
{
	// compute intersection of ray with all six bbox planes
	vec3 invR = 1.0 / dir;
	vec3 tbot = invR * (-0.5*size - org);
	vec3 ttop = invR * (0.5*size - org);

	// re-order intersections to find smallest and largest on each axis
	vec3 tmin = min (ttop, tbot);
	vec3 tmax = max (ttop, tbot);

	// find the largest tmin and the smallest tmax
	vec2 t0 = max (tmin.xx, tmin.yz);
	float near;
	near = max (t0.x, t0.y);
	t0 = min (tmax.xx, tmax.yz);
	far = min (t0.x, t0.y);

	// check for hit
	return near < far && far > 0.0 ? near : INF;
}

float box(vec3 org, vec3 dir, vec3 size)
{
	float far;
	return box(org, dir, size, far);
}

float impulse (float k, float x)
{
	float h = k * x;
	return h * exp (1.0 - h);
}

float impulse2 (float k0, float k1, float x)
{
	float k = k0;
	if (x > 1.0/k0)
	{
		x += 1.0/k1 - 1.0/k0;
		k = k1;
	}
	float h = k * x;
	return h * exp (1.0 - h);
}

float cubicPulse (float w, float x)
{
	x = abs (x);
	if (x > w)
		return 0.0;
	x /= w;
	return 1.0 - x * x * (3.0 - 2.0 * x);
}

mat2 rot(float angle)
{
	float c = cos(angle);
	float s = sin(angle);
	return mat2(c,-s,s,c);
}

// rd doesn't have to be normalized
float sphere(vec3 ro, vec3 rd, float r)
{
	float b = dot(ro, rd);
	float c = dot(ro, ro) - r * r;
	float a = dot(rd, rd);
	// Exit if râ€™s origin outside s (c > 0) and r pointing away from s (b > 0)
	if (c > 0.0 && b > 0.0)
		return INF;
	float discr = b*b - a*c;
	// A negative discriminant corresponds to ray missing sphere
	if (discr < 0.0)
		return INF;
	// Ray now found to intersect sphere, compute smallest t value of intersection
	float t = - b - sqrt(discr);
	t /= a;
	// If t is negative, ray started inside sphere so clamp t to zero
	t = max(0.0, t);
	return t;
}

float sdsuperegg( in vec3 p, in float r )
{
    float k = 2.5;

     return pow(abs(p.z)/5.,k) + pow(abs(p.x)/5.,k) + pow(abs(p.y)/6.,k)-pow(r,k);
}

float superegg(vec3 ro, vec3 rd, float r)
{
    float far;

    float t = 0.0;
	float hit = -1.0;
	for(int i=0; i < 24; i++)
	{
		float h = sdsuperegg(ro + rd*t,0.05);
		if (h < 1e-5)
			hit = t;
		t += h;
	}
    return hit > -1.0 ? hit : INF;
}

float sdCone( vec3 p, vec2 c )
{
	// c must be normalized
	float q = length(p.xy);
	return dot(c,vec2(q,p.z));
}

float sdPlane (vec3 p, vec4 n)
{
	// n must be normalized
	return dot(p,n.xyz) + n.w;
}

float sdSphere (vec3 p, float s)
{
	return length(p)-s;
}

float roof(vec3 ro, vec3 rd)
{
	float hit = -ro.y/rd.y;
	// An offset, so that shadow rays starting from the roof don't
	// think they're unoccluded
	if (hit < -0.1)
		return INF;

	// We've hit the plane. If we've hit the window, but
	// not the beams, return no hit.
	vec2 pos = ro.xz + hit*rd.xz;
	vec2 window = abs(pos) - 0.71;
	// single beams
	//vec2 beams = 0.02 - abs(pos);
	// double beams
	vec2 beams = 0.015 - abs(mod(pos, 0.54) - 0.27);
	if (max(max(window.x, window.y), max(beams.x, beams.y)) < 0.0)
		return INF;

	return hit;
}

#define ROOFPOS vec3(0.4,-0.5,0.01)

float intersect (vec3 ro, vec3 rd)
{
	float hit = INF;

	// stuff
	HIT(superegg (ro + vec3(-0.1,0.5+sin(iTime)*0.03,0.35), rd, 0.25));

	mat2 m = rot(3.5);
	vec3 rorot = ro + vec3(0.4,-0.6,0.3);
	vec3 rdrot = rd;
	rorot.xz = m*rorot.xz;
	rdrot.xz = m*rdrot.xz;
	//HIT(box (rorot, rdrot, vec3(0.35,0.2,0.35)));

	// roof
	rorot = ro + ROOFPOS;
	rdrot = rd;
    m = rot(1.5);
	// reuse the previous rotation matrix
	rorot.xy = m*rorot.xy;
	rdrot.xy = m*rdrot.xy;
    m = rot(0.2);
    rorot.yz = m*rorot.yz;
	rdrot.yz = m*rdrot.yz;
	HIT(roof(rorot, rdrot));

	// floor
	float floorHit = -(ro.y + 0.95)/rd.y;
	if (floorHit < 0.0)
		floorHit = INF;
	HIT(floorHit);

	return hit;
}

float particles (vec3 p)
{
	vec3 pos = p;
	pos.y -= iTime*0.02;
	float n = fbm(20.0*pos);
	n = pow(n, 5.0);
	float brightness = noise(10.3*p);
	float threshold = 0.26;
	return smoothstep(threshold, threshold + 0.15, n)*brightness*90.0;
}

float transmittance (vec3 p)
{
	return exp (0.4*p.y);
}

vec3 inscatter (vec3 ro, vec3 rd, vec3 roLight, vec3 rdLight, vec3 lightDir, float hit, vec2 screenPos)
{
	float far;
	float near = box(roLight + vec3(0.0, 1.0, 0.0), rdLight, vec3(1.5, 3.0, 1.5), far);
	if(near == INF || hit < near)
		return vec3(0);

	float distAlongView = min(hit, far) - near;
	float oneOverSteps = 1.0/float(INSCATTER_STEPS);
	vec3 step = rd*distAlongView*oneOverSteps;
    vec3 pos = ro + rd*near;
	float light = 0.0;

    // add noise to the start position to hide banding
    // TODO: blue noise
	pos += rd*noise(vec3(2.0*screenPos, 0.0))*NOISE_AMPLITUDE;

	for(int i = 0; i < INSCATTER_STEPS; i++)
	{
		float l = intersect(pos, lightDir) == INF ? 1.0 : 0.0;
		l *= transmittance(pos);
		light += l;
		light += particles(pos)*l;
		pos += step;
	}

	light *= oneOverSteps * distAlongView;
	return light*vec3(0.6);
}

vec3 rot (vec3 v, vec3 axis, vec2 sincosangle)
{
	return v*sincosangle.y + cross(axis, v)*sincosangle.x + axis*(dot(axis, v))*(1.0 - sincosangle.y);
}

vec3 surface(vec2 fragCoord, vec3 ro, vec3 u, vec3 v, vec3 w, vec3 lightRotAxis, vec2 lightAngleSinCos, vec3 lightDir)
{
    vec2 q = fragCoord.xy/iResolution.xy;
	vec2 p = -1.0 + 2.0*q;
	p.x *= iResolution.x/iResolution.y;

	vec3 rd = normalize( p.x*u + p.y*v + 1.5*w );

	// raycast the scene
	float hit = intersect(ro,rd);
	vec3 hitPos = ro + hit * rd;

	// white window
	if (hit == INF)
		return vec3(1.0);

	// direct light (screw shading!)
	vec3 c = vec3(0.0);
    float shadowBias = 1.0e-4;
	if (intersect(hitPos + lightDir*shadowBias, lightDir) == INF)
		c = vec3(0.9);

    lightAngleSinCos.x *= -1.0; // rev angle
	vec3 roLight = rot(ro + ROOFPOS, lightRotAxis, lightAngleSinCos);
	vec3 rdLight = rot(rd, lightRotAxis, lightAngleSinCos);
	c += inscatter(ro, rd, roLight, rdLight, lightDir, hit, fragCoord);

    // color correction - Sherlock color palette ;)
	c.r = smoothstep(0.0, 1.0, c.r + 0.02);
	c.g = smoothstep(0.0, 1.0, c.g - 0.1);
	c.b = smoothstep(0.0, 1.0, c.g - 0.1);

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // camera
	vec3 ro = normalize(vec3(1.0,-0.1,0.1));
	float cameraAngle = iMouse.x/iResolution.x - 0.5;
	if(iMouse.z < 0.5)
		cameraAngle = 0.5*sin(0.1*iTime);
	float cca = cos(cameraAngle);
	float sca = sin(cameraAngle);
	mat2  m = mat2(cca,-sca,sca,cca);
	ro = vec3(m*ro.xz,ro.y).xzy;
	vec3 w = -ro;
	ro *= 2.5;
	vec3 u = normalize(cross( vec3(0.0,1.0,0.0), w ));
	vec3 v = normalize(cross(w,u));

    // light
    vec3 lightRotAxis = vec3(0.707,0,0.707); //1,0,1 normalized
	vec2 lightAngleSinCos = vec2(sin(0.58), cos(0.58));
	vec3 lightDir = rot(vec3(0,1,0), lightRotAxis, lightAngleSinCos);

#if BRUTE_FORCE_AA
    float invAA = 1.0 / float(AA_SAMPLES);
    vec3 c = vec3(0);
    vec2 offset = vec2(-0.5, -0.5);
    for (int i=0; i < AA_SAMPLES; i++)
    {
        for (int j=0; j < AA_SAMPLES; j++)
        {
            c += surface(fragCoord + offset, ro, u, v, w, lightRotAxis, lightAngleSinCos, lightDir);
            offset.y += invAA;
        }
        offset.x += invAA;
        offset.y = -0.5;
    }
    c *= invAA * invAA;
#else
    vec3 c = surface(fragCoord, ro, u, v, w, lightRotAxis, lightAngleSinCos, lightDir);
#endif

	fragColor = vec4(c, 0.0);
}

const float SIZE = 1.0;
const float MINIMUM_HIT_DISTANCE = 0.001;
const float MAXIMUM_TRACE_DISTANCE = 1000.0;
const int NUMBER_OF_STEPS = 100;
const float PI = 3.14159265359;

mat3 rotateY(float rad) {
    float c = cos(rad);
    float s = sin(rad);
    return mat3(
        c, 0.0, -s,
        0.0, 1.0, 0.0,
        s, 0.0, c
    );
}

mat3 rotateX(float rad) {
    float c = cos(rad);
    float s = sin(rad);
    return mat3(
        1.0, 0.0, 0.0,
        0.0, c, s,
        0.0, -s, c
    );
}

mat3 rotateZ(float rad) {
    float c = cos(rad);
    float s = sin(rad);
    return mat3(
        c, s, 0.0,
        -s, c, 0.0,
        0.0, 0.0, 1.0
    );
}

mat2 rotationMatrix2d(float time) { // matrix representation of a complex number can be used to rotate 2d vectors
  return mat2(cos(time), sin(time), -sin(time), cos(time));
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf
}

float sdRoundBox( vec3 p, vec3 b, float r ) {
  vec3 d = abs(p) - b;
  return length(max(d,0.0)) - r
         + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf
}

float sdTriPrism( vec3 p, vec2 h, float narrow ) {
    vec3 q = abs(p);
    return max(q.z-h.y,max(q.x*0.866025+p.y*narrow,-p.y)-h.x*0.5);
}

float sdPlane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float sdCappedCylinder( vec3 p, vec2 h ) {
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float smoothMin(float dstA, float dstB, float k) {
    float h = max(k - abs(dstA - dstB), 0.0) / k;
    return min(dstA, dstB) - h * h * h * k * 1.0 / 6.0;
}

float opOnion( in float sdf, in float thickness ) {
    return abs(sdf)-thickness;
}

float mapTheWorld(in vec3 p) {
    float displacement = sin(5.0 * p.x) * cos(5.0 * p.y) * sin(5.0 * p.z) * 0.25;

    float barrel = sdRoundBox(p + vec3(0.0, -0.5, 0.0), vec3(0.1, 0.1, 1.0), 0.06);

    vec3 barrelTipPos = rotateX(PI * 0.5) * p + vec3(0.0, -1.15, -0.55);
    float barrelTipInner = sdCappedCylinder(barrelTipPos, vec2(0.0675, 2.0));
    float tri = sdTriPrism(rotateZ(PI * 0.25) * rotateY(PI * 0.5) * p + vec3(-0.28, -1.1, 0.0), vec2(0.28, 0.28), 0.5);

    float x = sdRoundBox(p + vec3(0.0,-0.2,-0.24), vec3(0.01,0.1,0.1), 0.01);

    barrel = max(barrel, -tri);
    barrel = max(barrel, -barrelTipInner);
    barrel = min(barrel, x);

    vec3 handlePos = rotateX(PI * 0.04) * p + vec3(0.0, 0.0, -0.6);
    float handle = sdRoundBox(handlePos, vec3(0.1, 0.5, 0.25), 0.05);

    float trigger = sdRoundBox(p + vec3(0.0, -0.3, -0.1), vec3(0.05, 0.2, 0.2), 0.025);
    float triggerHole = sdRoundBox(p + vec3(0.0, -0.3, -0.1), vec3(0.1, 0.14, 0.14), 0.025);

    trigger = max(trigger, -triggerHole);

    float sight = sdTriPrism(p + vec3(0.0, -0.65, 0.65), vec2(0.01, 0.4), 0.1);

    return min(sight, smoothMin(barrel, min(handle, trigger), 0.2));
}

vec3 calculateNormal(in vec3 currentPosition) {
    const vec3 small_step = vec3(0.001, 0.0, 0.0);

    float gradient_x = mapTheWorld(currentPosition + small_step.xyy) - mapTheWorld(currentPosition - small_step.xyy);
    float gradient_y = mapTheWorld(currentPosition + small_step.yxy) - mapTheWorld(currentPosition - small_step.yxy);
    float gradient_z = mapTheWorld(currentPosition + small_step.yyx) - mapTheWorld(currentPosition - small_step.yyx);

    vec3 normal = vec3(gradient_x, gradient_y, gradient_z);

    return normalize(normal);
}

vec3 remap(vec3 col) { // map range -1 to 1, to, range 0 to 1
  return col * 0.5 + 0.5;
}

mat2 rotation() {
    return rotationMatrix2d(iTime * 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec3 coord = vec3((fragCoord - 0.5 * iResolution.xy) / iResolution.y, 0.0); // adjusting for the viewport size

  vec3 ro = vec3(0, 1, -2), // where the camera is sitting
        look_at = vec3(0); // where the camera is pointing

  ro.xz *= rotation();

  float zoom = 0.8; // camera zoom
  vec3 f = normalize(look_at - ro), // forward vector
    r = normalize(cross(vec3(0, 1, 0), f)); // right vector
  vec3 u = cross(f, r), // up vector
    c = ro + f * zoom, // center point of the virtual screen (virtual screen is a screen that the objects are painted onto and sits between the camera and the objects)
    i = c + coord.x * r + coord.y * u, // ray intersection point with our virtual screen
    rd = normalize(i - ro); // ray direction
  float dS, dO = 0.0; // distance to surface and distance to origin;
  vec3 currentPosition; // point along the ray (from which we emit our spheres);
  vec3 col = vec3(0); // empty canvas

  for(int i = 0; i < 100; i++) { // ray marcher
    currentPosition = ro + rd * dO; // the point coord is the ray origin plus the distance from the origin times ray direction
    dS = mapTheWorld(currentPosition);
    if (dS < MINIMUM_HIT_DISTANCE) {
      vec3 normal = calculateNormal(currentPosition);
      // For now, hard-code the light's position in our scene
      vec3 lightPosition = vec3(2.0, -5.0, 3.0);
      lightPosition.xz *= rotation();
      // Calculate the unit direction vector that points from
      // the point of intersection to the light source
      vec3 direction_to_light = normalize(currentPosition - lightPosition);

      float diffuse_intensity = max(0.0, dot(normal, direction_to_light));

      col = vec3(0.5, 0.5, 0.5) * diffuse_intensity;
      break;
    }; // register a hit - one of our spheres can register a hit on our object because distance of surface is tiny
    if (dO > MAXIMUM_TRACE_DISTANCE) break; // maximum distance that our camera will pick up
    dO += dS; // This is stepping to the next point coord to emit another sphere
  }

  fragColor = vec4(col, 1.0); // our final color
}

