#define AA  1   // optional antialiasing

float PrTorusDf (vec3 p, float ri, float rc);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 ltDir, qHit;
float dstFar;
int idObj;
const float pi = 3.14159;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q, ds;
  float dMin, d;
  dMin = dstFar;
  ds = vec3 (0.92, 1.07, 1.);
  q = p * ds;
  d = PrTorusDf (q, 1., 15.);
  DMINQ (1);
  q = p.zxy * ds;
  d = PrTorusDf (q, 1., 15.);
  DMINQ (2);
  q = p.yzx * ds;
  d = PrTorusDf (q, 1., 15.);
  DMINQ (3);
  return 0.8 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 80; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0002, -0.0002);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += clamp (h, 0.2, 1.);
    if (sh < 0.05) break;
  }
  return 0.3 + 0.7 * sh;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn;
  float dstObj, sh, nDotL;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    vn = VaryNf (4. * ro, vn, 8.);
    if (idObj == 1) col4 = vec4 (1., 0.2, 0.2, 0.2);
    else if (idObj == 2) col4 = vec4 (0.2, 1., 0.2, 0.2);
    else if (idObj == 3) col4 = vec4 (0.2, 0.2, 1., 0.2);
    col4.rgb *= 0.3 + 0.7 * SmoothBump (0.07, 0.93, 0.02,
       mod (64. * atan (qHit.y, - qHit.x) / (2. * pi), 1.));
    sh = ObjSShadow (ro, ltDir);
    nDotL = max (dot (vn, ltDir), 0.);
    nDotL *= nDotL;
    col = col4.rgb * (0.2 + 0.8 * sh * nDotL * nDotL) +
       col4.a * step (0.95, sh) * sh * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
  } else col = vec3 (0.1);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, ori, ca, sa;
  float tCur, el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az += 0.47 * tCur;
    el += 0.43 * tCur;
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -60.);
  zmFac = 3.;
  dstFar = 100.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (pow (col, vec3 (0.8)), 1.);
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

// Title: Simple Raymarching Example Scene
// Author: Sam Loeschen https://twitter.com/samloeschen

// Description:
// When I started learning raymarching I basically just wanted pre-built "scene" that I could dive into and start tinkering with,
// so here is a simple example that does exactly that! No step-by-step tutorial, just simple explanations of what everything does.
// If you want to start messing around, the first thing I would do is modify the scene() function with more shapes. You can find 
// more primitive at http://iquilezles.org/www/articles/distfunctions/distfunctions.htm. 

// Things to try:
// 1. Check out ourBox() and ourSphere() to see how I animated the primitives and gave them material IDs.
// 2. See if you can add a shape and material of your own.
// 3. Play with the camera and directional light, and see if you can animate them.

#define RAYMARCH_STEPS 50
#define EPSILON 0.005
#define BACKGROUND vec4(0, 0.3, 0.3, 1) // windows 95?
#define MIN_DIST 0.01
#define MAX_DIST 100.0

#define BLUE_MATERIAL_ID 0.5
#define RED_MATERIAL_ID 1.0

// iq's axis to direction rotation. Aligns an axis (z) to point along a direction (d)
mat3 rotationAlign(vec3 d, vec3 z) {
    vec3  v = cross( z, d );
    float c = dot( z, d );
    float k = 1.0/(1.0+c);

    return mat3(v.x*v.x*k + c,     v.y*v.x*k - v.z,    v.z*v.x*k + v.y,
                v.x*v.y*k + v.z,   v.y*v.y*k + c,      v.z*v.y*k - v.x,
                v.x*v.z*k - v.y,   v.y*v.z*k + v.x,    v.z*v.z*k + c    );
}


// composes a view matrix for a direction and global up vector
// based on gluLookAt man page
mat4 viewMatrix (vec3 dir, vec3 up) { 
    vec3 f = normalize(dir);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}

// simple box sdf. for a library of other primitives, see http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float box (vec3 pos, vec3 size) {
  vec3 d = abs(pos) - size;
  return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

// simple sphere sdf. for a library of other primitives, see http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sphere(vec3 pos, float radius) {
  return length(pos) - radius;
}

// returns the distance to our rotating box and the its associated material ID packed into a vec2
vec2 ourBox (vec3 pos) {

    // get a rotation matrix for our box's rotation
    vec3 dir = normalize(vec3(cos(iTime * 0.5), 0.0, sin(iTime * 0.5)));
    vec3 axis = normalize(vec3(0.0, 1.0, 0.0)); // up
    mat3 rotation = rotationAlign(dir, axis);

    // rotate our world point with the box's rotation matrix
    pos *= rotation;
    return vec2(box(pos, vec3(1.0)), BLUE_MATERIAL_ID);
}

// returns the distance to our moving sphere and the its associated material ID packed into a vec2
vec2 ourSphere (vec3 pos) {

    // just translate the sphere up and down
    vec3 offset = vec3(sin(iTime * 0.5), sin(iTime) * 3.0, cos(iTime) * 3.0);
    pos += offset;

    return vec2(sphere(pos, 1.0), RED_MATERIAL_ID);
}

// this is the combined scene sdf function! if you want to add shapes, put them in here.
// it returns a vec2 containing a depth value for the x coordinate, and a material ID for the y coordinate.
vec2 scene (vec3 pos) {
    // test our shapes
    vec2 boxTest        = ourBox(pos);
    vec2 sphereTest     = ourSphere(pos);
    vec2 result         = boxTest;

    // we want to return the nearest surface, so we sort by distance
    result = mix(result, sphereTest, step(sphereTest.x, result.x));

    // if you want to add more shapes, modify the result like so:
    // result = mix(result, yourNewShapeTest, step(yourNewShapeTest.x, result.x));
    return result;
}

// creates a view space ray going out from the camera
vec3 calcRay (float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size * 0.5;
    float z = size.y / tan(radians(fieldOfView) * 0.5);
    return normalize(vec3(xy, -z));
}

// gets an estimated normal of the scene sdf at a given point
// we use the x coord because that is the depth value that is returned from the scene
vec3 calcNormal (vec3 pos) {
    const vec2 eps = vec2(EPSILON, 0);
    return normalize(vec3(scene(pos + eps.xyy).x - scene(pos - eps.xyy).x,
                          scene(pos + eps.yxy).x - scene(pos - eps.yxy).x,
                          scene(pos + eps.yyx).x - scene(pos - eps.yyx).x));
}

// returns a color based on the material id from the scene sample
// right now it uses gross branching, but this could be eliminated with clever usage of step()
vec3 calcMaterial (float id) {
    if (id == RED_MATERIAL_ID)      return vec3(1, 0, 0);
    if (id == BLUE_MATERIAL_ID)     return vec3(0, 0, 1);
    return vec3(1);
}

// raymarches our scene with a provided camera position, camera direction, and simple directional light
vec4 marchScene (vec2 fragCoord, vec3 camPos, vec3 camDir, vec3 lightDir) {

    // calculate the world direction of the ray at this pixel
    vec3 viewDir = calcRay(45.0, iResolution.xy, fragCoord.xy);
    mat4 viewMat = viewMatrix(
        camDir,
        vec3(0.0, 1.0, 0.0)
    );
    vec3 worldDir = (viewMat * vec4(viewDir, 1)).xyz;

    // do our raymarching!
    float depth = MIN_DIST;
    for (int i = 0; i < RAYMARCH_STEPS; i++) {
        vec3 rayPos = camPos + depth * worldDir;

        // sample the scene at this point in the ray.
        // the sample's x coord is our distance value, and the y coord is the material id
        vec2 sceneSample = scene(rayPos);
        float shortestDist = sceneSample.x;
        float materialId = sceneSample.y;

        if (shortestDist < EPSILON) {

            // we hit something, so get estimate our normal to do lighting and return a material color
            vec3 normal = calcNormal(rayPos);
            float light = max(dot(lightDir, normal) * 0.5 + 0.5, 0.15); // half lambert with a floor
            vec3 surfaceColor = calcMaterial(materialId);
            vec4 outputColor = vec4(surfaceColor * light, 1.0);
            return outputColor;
        }

        // increase the depth of our ray by the shortest distance to an object in the scene.
        // since we know how far away everything is, we don't need to increment our ray in tiny steps!
        depth += shortestDist;

        // we reached our max distance and didn't hit anything, so just return the background
        if (depth > MAX_DIST - EPSILON) {
            return BACKGROUND;
        }
    }
    return BACKGROUND;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // let's place the camera 20 units away from the origin on the negative z axis,
    // and orient it to look at the center of the scene
    vec3 camPos = vec3(0, 0, 20); 
    vec3 camDir = normalize(-camPos);

    // we'll have our light coming from above and behind us
    vec3 lightDir = normalize(vec3(0, 1, 1));

    fragColor = marchScene(fragCoord, camPos, camDir, lightDir);
}

vec3 black = vec3(0.0,0.0,0.0);
vec3 red = vec3(0.7,0.4,0.0);
vec3 blue = vec3(0.0,.8,0.8);
vec3 gold = vec3(0.9,0.9,0.0);
vec3 white = vec3(1.0,1.0,1.0);

float rand(vec2 uv)
{
    //return fract(sin(dot(uv, vec2(12.9898,78.233)))*10000.*iTime);
	//return fract(sin(dot(uv, iMouse.xy))*10000.);
	//return (fract(sin(dot(uv, vec2(12., 70.)))*100000.));
    return (fract(sin(dot(uv, vec2(12., 70.)))*43758.5453123));
}

// Value noise
float noise(in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = rand(i);
    float b = rand(i + vec2(1.0, 0.0));
    float c = rand(i + vec2(0.0, 1.0));
    float d = rand(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners porcentages
    return mix(a, b, u.x) + 
            (c - a)* u.y * (1.0 - u.x) + 
            (d - b) * u.x * u.y;
}

vec3 circle(vec2 uv, vec2 pos)
{       
    uv.x *= iResolution.x/iResolution.y;

    float d = length(uv - pos) *0.5;    

    float offset = noise(uv*4.+vec2(.0, -iTime*4.))*.1;

    float r = 0.09;
    float rate = clamp(sin(20.0 * r * iTime)*r , 0.25, 0.30);
    float circle = smoothstep(rate, r, d+offset);
    vec3 mixed = mix(black, red, circle);

    return mixed;
}

vec3 circle2(vec2 uv, vec2 pos)
{       
    uv.x *= iResolution.x/iResolution.y;

    float d = length(uv - pos);    

    float offset = noise(uv*4.+vec2(.0, -iTime*4.))*.1;

    float r = 0.01;
    float rate = clamp(sin(20.0 * r * iTime)*r , 0.25, 0.30);
    float circle2 = smoothstep(rate, r, d+offset);
    vec3 mixed = mix(black, gold, circle2);

    return mixed;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float height = step(0.0,uv.y) - step(0.35,uv.y);
    float width = step(0.4,uv.x) -step(0.6,uv.x);

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 image = mix(black, blue, height * width);

    height = step(0.35,uv.y) - step(0.45,uv.y);
    width = step(0.5,uv.x) -step(0.51,uv.x);

    image = mix(image, white, height * width);

    vec3 mCircle = circle(uv, vec2(0.89,0.52));

    image = mix(mCircle, image, image);

    vec3 mCircle2 = circle2(uv, vec2(0.89,0.52));

    image = mix(mCircle2, image, image);

    // Output to screen
    fragColor = vec4(image,1.0);
}

const float EPS = 0.0001;
const float PI = acos(-1.);

void sphere(in vec3 pos, out float d, in float r)
{
    d = length(pos) - r;
}

mat2 rot(in float phi)
{
    return mat2(cos(phi), -sin(phi), sin(phi), cos(phi));
}

void random2f(in ivec2 pos, out vec2 o) {
    o = vec2(
            fract(sin(dot(vec2(pos) ,vec2(12.9898,78.233))) * 43758.5453),
            fract(sin(dot(vec2(pos) ,vec2(58.1213,32.422))) * 31671.8792)
        );
}

void voronoi(in vec3 pos, out float res) {
    vec2 x = 4.*pos.xy;
    ivec2 p = ivec2(floor(x));
    vec2 f = fract(x);

    ivec2 mb;
    vec2 mr;
    res = 8.0;
    for (int j=-1; j<=1; j++) {
        for (int i=-1; i<=1; i++) {
            ivec2 b = ivec2(i, j);
            vec2 r;
            random2f(p + b, r);
            r += vec2(b) - f;
            float d = dot(r, r);
            if (d < res) {
                res = d;
                mb = b;
                mr = r;
            }
        }
    }
    res = 8.0;
    for (int j=-1; j<=1; j++) {
        for (int i=-1; i<=1; i++) {
            ivec2 b = mb + ivec2(i, j);
            vec2 r;
            random2f(p + b, r);
            r += vec2(b) - f;
            float d = dot(0.5*(mr+r), normalize(r-mr));
            res = min(res, d);
        }
    }
}


void beam(in vec3 pos, out float d)
{
    if (pos.z > 0.)
    {
        d = 10000.;
    } else
    {
        d = length(pos.xy) - (0.2 - 0.1 * pos.z);
    }
}

void huergb(in float hue, out vec3 color)
{
    float h = mod(hue, 1.);
    float f = mod(6. * h, 1.);
    if (6. * h < 1.)
    {
        color = vec3(1., f, 0.);
    } else if (6. * h < 2.)
    {
        color = vec3(1. - f, 1., 0.);
    } else if (6. * h < 3.)
    {
        color = vec3(0., 1., f);
    } else if (6. * h < 4.)
    {
        color = vec3(0., 1. - f, 1.);
    } else if (6. * h < 5.)
    {
        color = vec3(f, 0., 1.);
    } else
    {
        color = vec3(1., 0., 1. - f);
    }
}

void ufocolor(in vec3 pos, out vec3 color, in bool b, in vec3 bcolor, out float d)
{
    float r = smoothstep(0., 1., length(pos.xy));
    float phi = atan(pos.x, pos.y);
    if (pos.z < 0.)
    {   
           d = 0.02 * sin(3. * PI * r) * (0.2 / r + 1.);
        vec3 c = vec3(0.2);
        if (b)
        {
            huergb(phi / (2.*PI) + 0.5 * r - 0.5 * iTime, c);
            c = mix(
                bcolor,
                c,
                smoothstep(0.5, 0.7, r)
            );
        }
        color = mix(
            c,
            vec3(0.),
            smoothstep(0.25 * PI - 0.05, 0.25 * PI + 0.05, r)
        );
    } else
    {
        float f = mod(3.5 * phi / PI + 0.5, 1.) - 0.5;
        f = smoothstep(0.1, 0.2, abs(f));
        vec3 c = mix(
            vec3(0., 0., 0.5),
            vec3(0.4),
            f
        );
        color = mix(
            c,
            vec3(0.1),
            smoothstep(0.925, 0.95, r)
        );
        d = 0.025 * f * smoothstep(1., 0.9, r);
        d += 0.1 * smoothstep(0.5, 0., r);
    }
    float v;
    voronoi(pos, v);
    v = smoothstep(0.1, 0., v);
    d -= 0.02*v;
    color *= 1. - 0.2*v;
}

void ufo(in vec3 r, in bool b, in vec3 bcolor, out vec3 color, out vec4 m, out float d)
{
    float d3, d4, d5, dis;
    float rad = length(r);
    float angle = atan(r.y, r.x);
    vec3 c;
    ufocolor(r, color, b, bcolor, dis);
    m = vec4(20., 0.3, 0.5, 0.2);
    if (r.z < 0.)
    {
    	sphere(r - vec3(0., 0., 1.75), d, 2. + dis);
    } else
    {
    	sphere(r + vec3(0., 0., 1.75), d, 2. + dis);
    }
    if (b)
    {
        beam(r, d4);
        if (d > d4)
        {
            d = d4;
            color = bcolor * 0.5;
            m = vec4(1., 1., 0., 0.);
        }
    }
    float phi = mod(angle + PI/7., 2.*PI/7.);
    vec3 r2 = vec3(vec2(rad*sin(phi), rad*cos(phi)), r.z);
    sphere(r2 - vec3(0.7*sin(PI/14.), 0.7*cos(PI/14.), 0.1), d4, 0.1);
    if (d > d4)
    {
        d = d4;
        color = clamp(sin(10.* iTime + angle) - 0.2, 0.05, 1.)*vec3(1., 1., 0.2);
        m = vec4(1., 0.9, 0.1, 0.);
    }
}

void scene(in vec3 pos, out vec3 color, out vec4 m, out float d)
{
    float d1;
    vec3 color1;
    vec4 m1;

    d = 10000.;

    vec3 r = vec3(0.);
    r.x = 3.*cos(iTime);
    r.y = 2.*sin(iTime);
    r = pos - r;
    float rand1 = fract(sin(floor(iTime)) * 47828.12);
    float rand2 = fract(sin(floor(iTime) + 1.) * 47828.12);
    float rand3 = fract(sin(floor(5.*iTime) + 1.) * 12311.41);
    float phi = 0.2*smoothstep(rand1, rand2, fract(iTime)) + sin(2.*iTime);
    vec3 bcolor;
    huergb(0.1 * (2. + sin(0.2 * iTime)) * r.z + 0.8 * iTime, bcolor);
    bcolor *= 0.95;
    r.xy = rot(iTime - PI/.4)*r.xy;
    r.yz = rot(0.3*sin(phi))*r.yz;

    ufo(r, rand3 > 0.2, bcolor, color1, m1, d1);

    if (d > d1)
    {
        d = d1;
        color = color1;
        m = m1;
    }
}

void norm(in vec3 p, out vec3 n) {
    float x1, x2, y1, y2, z1, z2;
    vec3 c;
    vec4 m;
    scene(vec3(p.x + EPS, p.y, p.z), c, m, x1);
    scene(vec3(p.x - EPS, p.y, p.z), c, m, x2);
    scene(vec3(p.x, p.y + EPS, p.z), c, m, y1);
    scene(vec3(p.x, p.y - EPS, p.z), c, m, y2);
    scene(vec3(p.x, p.y, p.z + EPS), c, m, z1);
    scene(vec3(p.x, p.y, p.z - EPS), c, m, z2);
    n = normalize(vec3(x1 - x2, y1 - y2, z1 - z2));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float scale = 1.5;

    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = texture(iChannel0, uv).rgb;
    uv = 2.*uv - 1.0;
    uv.x *= iResolution.x/iResolution.y;
    uv *= scale;

    vec3 o = vec3(10., 0., 0.7 + 4.*sin(1.1*iTime));
    vec3 t = vec3(0., 0., -0.7);
    vec3 dir = normalize(t - o);

    vec3 up = vec3(0.0, 0.0, 1.0);
    vec3 right = normalize(cross(dir, up));
    t += scale*uv.x * right + scale*uv.y * up;
    dir = normalize(t - o);

    float depth = 0.0;
    float dis;
    vec3 n, c;
    vec4 m;
    for (int i=0; i<100; i++) {
        vec3 pos = o + depth * dir;
        scene(pos, c, m, dis);
        if (dis < EPS) {
            norm(pos, n);
            vec3 l;
            color = vec3(0.);
            for (int j=0; j<2; j++)
            {
                if (j == 0)
                {
                    l = normalize(pos - vec3(-3., 0., 1.));
                } else
                {
                    l = normalize(pos - vec3(0., 3.*sin(1.2*iTime), 5.*cos(1.2*iTime)));
                }
                float diff = clamp(dot(l, n), 0.0, 1.0);
                vec3 r = normalize(2.0*n*dot(n, -l) + l);
                float spec = clamp(dot(r, -dir), 0.0, 1.0);
                spec = pow(spec, m.x);
                color += clamp((m.y + diff * m.z)* c.rgb + m.w * spec * vec3(1.), 0.0, 1.0);    
            }
            break;
        }
        depth += dis;
        if (depth >= 50.)
        {
            break;
        }
    }

    fragColor = vec4(color, 1.);
}

#define NUM_STEPS 128
#define FUDGE_AMOUNT 0.75
#define EPS 0.001
#define FAR 50.0

#define PI 3.1415926535898


vec3 tri(in vec3 x){return abs(x-floor(x)-.5);} // Triangle function.

float surfFunc(in vec3 p){
	return dot(tri(p*0.5 + tri(p*0.25).yzx), vec3(0.777));
}

vec2 path(in float z){ float s = sin(z/34.)*cos(z/12.)*cos(sin(z/20.)); return vec2(s*12.+sin(s)*6.1, s*15.); }

mat3 lookAtMatrix(vec3 origin, vec3 target, float roll) {
    vec3 rr = vec3(sin(roll), cos(roll), 0.0);
    vec3 ww = normalize(target - origin);
    vec3 uu = normalize(cross(ww, rr));
    vec3 vv = normalize(cross(uu, ww));

    return mat3(uu, vv, ww);
}

mat2 r2(float a){
    float s = sin(a);
    float c = cos(a);
    return mat2(s, -c, c, s);
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf 
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0)) - r
         + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf 
}

float trackPlanks(vec3 tp){
    tp.xy = (tp.xy - path(tp.z))*vec2(0.5, 0.47071);
    tp.z = mod(tp.z, 1.) -0.5;
    float bmp2 = texture(iChannel1, tp.xz/4.).r*.015;
    return sdBox(vec3(tp.x,tp.y+1.4,tp.z), vec3(1., .05152, .2))+bmp2;
}
float trackRails(vec3 tp){
    tp.xy = (tp.xy - path(tp.z))*vec2(0.5, 0.47071);
    tp.z = mod(tp.z, 1.) -0.5;
    tp.x = abs(tp.x)-1.;
    return sdRoundBox(vec3(tp.x, tp.y+1.3, tp.z), vec3(.015, .015, .5), .12);
}
float map(vec3 p){
    //float ground = p.y+2.5+surfFunc(p)-tri(p).y;
    float ground = p.y + (sin(sin(p.z*0.1253) - p.x*0.311)*1.31 + cos(p.z*0.53 + sin(p.x*0.127))*0.12)*1.7 + 0.2;
    ground += tri(p).y;
    //float bmp = texture(iChannel0, p.xz/10.).r*.1;
    //float tx = textureLod(iChannel0, p.xz/16. + p.xy/80., 0.0).x;

    //ground+=bmp;
    // Round tunnel.
    // For a round tunnel, use the Euclidean distance: length(tun.y)
    vec2 tun = (p.xy - path(p.z))*vec2(0.5, 0.47071);
    float n = 1.- length(tun) + (0.5);
    n += surfFunc(p/2.);
    //n+=bmp;
    vec3 tp = p;

    tp.xy = tun;


    float planks = trackPlanks(p);
    float rails = trackRails(p);

    return min(min(max(ground, n), planks), rails);
}

float trace(vec3 o, vec3 r){
    float t = 0.0;
    for(int i = 0; i < NUM_STEPS; i++){
        float d = map(o+r*t);
        t += d * FUDGE_AMOUNT;
        if(abs(d) < EPS || t > FAR) break;
    }
    return t;
}
// Surface normal.
vec3 getNormal(in vec3 p) {

	const float eps = 0.001;
	return normalize(vec3(
		map(vec3(p.x + eps, p.y, p.z)) - map(vec3(p.x - eps, p.y, p.z)),
		map(vec3(p.x, p.y + eps, p.z)) - map(vec3(p.x, p.y - eps, p.z)),
		map(vec3(p.x, p.y, p.z + eps)) - map(vec3(p.x, p.y, p.z - eps))
	));

}
// Tri-Planar blending function. Based on an old Nvidia tutorial.
vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n ){

    n = max((abs(n) - 0.2)*7., 0.001); // max(abs(n), 0.001), etc.
    n /= (n.x + n.y + n.z );  

	return (texture(tex, p.yz)*n.x + texture(tex, p.zx)*n.y + texture(tex, p.xy)*n.z).xyz;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5; uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv, 1.0 - dot(uv, uv) * .33));
    vec3 o = vec3(0.0, 0.0, iTime*22.0);

    vec3 lookAt = o + vec3(0.0, -0.05,.245);
    o.xy += path(o.z);
    lookAt.xy +=path(lookAt.z);    

    mat3 camMat = lookAtMatrix(o, lookAt, -o.x/15.);  //clamp(o.x/3.,-.3,.3));
    r = normalize(camMat * r);



    float hit = trace(o, r);
    vec3 surfPos = o + r * hit;
    vec3 n = getNormal(surfPos);
    vec4 bg = vec4(0.0);
    vec3 l = normalize(vec3(-0.1, 0.38, -0.2));
    //l.xz *= r2(iTime*3.);
    float diff = max(dot(n,l), 0.5);
	bg = pow(vec4(.1, .7, .8, 1), vec4(4.*max(r.y,-0.141)+1.5)); //+(dot(r,l)*.315+.215);
    float fog = smoothstep(0.75, 01.75, hit*0.03);
    fragColor=vec4(0.0);

    float d = map(surfPos);
    float d2 = trackRails(surfPos);
    float d3 = trackPlanks(surfPos);

    if(abs(d) < EPS+.4){
        vec3 tx = tex3D(iChannel2, surfPos/8., n);
        fragColor = mix(vec4(1.0*diff), bg, fog);        //+ hit* -.04;
        if(abs(d2)<EPS+.01){
            fragColor = vec4(1.0, 0.0, 0.0, 1.0)*diff;
        }
        else if(abs(d3) < EPS+0.1){
            vec4 tx = texture(iChannel1, surfPos.xz);
            fragColor = vec4(0.0, 1.0, 0.0, 1.0)*diff;
        }
    }
    else
        fragColor = vec4(bg);
}

