// reduce this to improve frame rate in windowed mode (0 disables anti-aliasing)
#define AA_QUALITY 3

// allow a little bleed between pixels - I think this looks more photographic, but blurrier
#define AA_ROUND false
#define AA_ROUND_RADIUS 0.7071


const float phi = 1.6180339887498948;
const float tau = 6.2831853071795865;

// from here: https://www.shadertoy.com/view/4dVBzz
#define M1 1597334677U     //1719413*929
#define M2 3812015801U     //140473*2467*11
#define M3 3299493293U     //467549*7057

#define F0 (1.0/float(0xffffffffU))

#define hash(n) n*(n^(n>>15))

#define coord1(p) ((p)*M1)
#define coord2(p) ((p).x*M1^(p).y*M2)
#define coord3(p) ((p).x*M1^(p).y*M2^(p).z*M3)

float hash1(uint n){return float(hash(n))*F0;}
vec2 hash2(uint n){return vec2(hash(n)*uvec2(0x1U,0x3fffU))*F0;}
vec3 hash3(uint n){return vec3(hash(n)*uvec3(0x1U,0x1ffU,0x3ffffU))*F0;}
vec4 hash4(uint n){return vec4(hash(n)*uvec4(0x1U,0x7fU,0x3fffU,0x1fffffU))*F0;}


uint seed = 1u;

float Polygon( vec2 uv, float h )
{
    vec4 rand1 = hash4(coord3(uvec3(h*2.,0,seed)));
    vec4 rand2 = hash4(coord3(uvec3(h*2.,1,seed)));

    float mid = (rand1.x-.5)*exp2(-h*2.);
    float f = abs(uv.y-rand1.y+.5)-rand1.z-2.; //2.*mix(1.,rand(),.5+.5*h); // so 1.+rand->2.*rand
    f = max(f,abs(uv.x-mid)-rand1.w-.5+h*.4); //1.5*mix(1.,rand(),.7+.3*h);
    f = max(f,abs(dot(uv,vec2(1,1)/sqrt(2.))-rand2.x+.5)-rand2.y-1.);
    f = max(f,abs(dot(uv,vec2(1,-1)/sqrt(2.))-rand2.z+.5)-rand2.w-1.);

    // support-stalk
    //f = min(f,max(uv.y,abs(uv.x-mid)-.1));

    return f;
}


float BevelMax( float a, float b, float bevelSize )
{
    return max(max(a,b), dot(vec2(a,b),normalize(vec2(.5,1))) + bevelSize );
}

float SDF( vec3 p )
{
	// octahedral, but with random distances so some planes won't be seen
    float a = Polygon( p.zy-vec2(0,0), 0. );
    float b = Polygon( p.zy-vec2(0,2), .5 );
    float c = Polygon( p.zy-vec2(0,4), 1. );

/*    vec3 f = vec3(a,min(a,b),min(min(a,b),c));
    f = max( f, vec3(abs(p.x)-.2) );
    return min( f.z, p.y+.5 ); // ground*/

    float w = abs(p.x);

    vec3 rand = hash3(coord1(seed^0x1999u));

    float ground = p.y+.5;

    return min(min(min(BevelMax(a,w-rand.x,.1),BevelMax(b,w-rand.y,.03)),BevelMax(c,w-rand.z,.1)),ground);
}



// higher precision trace function canibalised from one of my other shaders - not sure if this is the best version but it's good enough
const float epsilon = .0004;
float Trace( vec3 rayStart, vec3 rayDirection )
{
    float t = 0.;

    float h = 0.;
    float lasth = 0.;
    float bestt = 0.;
    float bestDist = 1e30;
    float sdf = 0.;
    for ( int i=0; i < 200; i++ )
    {
        lasth = h;
        sdf = SDF( rayDirection*t+rayStart );
        h = sdf + epsilon*.5;
		t += h;
        if ( sdf < bestDist ) { bestt = t; bestDist = sdf; } // not sure if all these conditionals will compile well, could flip to use step and mix
        if ( h < epsilon ) break;
    }

    if ( sdf == bestDist )
    {
        // improve precision
		float lastt = t-epsilon;
		float lastsdf = SDF( rayDirection*lastt+rayStart );
		float sdf = SDF( rayDirection*t+rayStart );
       	t = mix( lastt, t, (0.-lastsdf)/(sdf-lastsdf) );
    }
    else if ( t < bestt*2. )
    {
        // use the closest sample we had, to avoid sampling back-sides and things
        t = bestt;
		// this can add a fringe, but maybe better than the alternative
    }

    return t;
}


vec3 GetNormal( vec3 p )
{
    vec2 d = vec2(-1,1)*epsilon;
    return normalize(
        	SDF(p+d.xxx)*d.xxx +
        	SDF(p+d.yyx)*d.yyx +
        	SDF(p+d.yxy)*d.yxy +
        	SDF(p+d.xyy)*d.xyy
        );
}


void mainImage2( out vec4 fragColour, in vec2 fragCoord )
{
    seed = uint(iTime/2.);

    vec3 camPos = vec3(-5,1,-3);
    float a = iTime*.1;
    if ( iMouse.z > 0. )
    {
        camPos.y = 12.*iMouse.y/iResolution.y+.000001;
        a += .5*tau*(iMouse.x/iResolution.x-.5);
    }
    camPos.xz = camPos.xz*cos(a)+vec2(1,-1)*sin(a)*camPos.zx;
    vec3 camK = normalize(vec3(0,3,0) - camPos);
    vec3 camI = normalize(cross(vec3(0,1,0),camK));
    vec3 camJ = cross(camK,camI);
#if 1
    // architectural projection (non-orthographic)
    camJ = vec3(0,1./sqrt(camJ.y),0);  // (0,1,0) works too, but causes more vertical stretching at steep angles
#endif

    vec3 ray = vec3( (fragCoord-iResolution.xy*.5)/iResolution.y, .8 );
    ray = ray.x*camI + ray.y*camJ + ray.z * camK;
    ray = normalize( ray );

    float t = Trace( camPos, ray );
    vec3 pos = camPos+t*ray;

    if ( t < 1e3 && SDF(pos) < 10. )
    {
//    	fragColour.rgb = fract(pos);

        vec3 normal = GetNormal(pos);

        vec3 toLight = normalize(vec3(-1,2,3));

        if ( Trace( pos+toLight*.001-normal*SDF(pos), toLight ) > 1e2 )
        {
        	fragColour.rgb = vec3(.9,.8,.7)*max( dot(normal,toLight), 0. );
        }

        float aoStep = 1.;
        float ao = smoothstep(-1.,1.,SDF(pos+normal*aoStep)/aoStep);
        aoStep = .4;
        ao = mix( ao, smoothstep(-1.,1.,SDF(pos+normal*aoStep)/aoStep), .5 );
        aoStep = .15;
        ao = mix( ao, smoothstep(-1.,1.,SDF(pos+normal*aoStep)/aoStep), .4 );

        fragColour.rgb += ao*mix( vec3(.3), vec3(.2,.3,.4), (.5+.5*normal.y) );
    }
    else
    {
        fragColour.rgb = vec3(.5,.7,1);
    }

    fragColour.rgb = pow(fragColour.rgb,vec3(1./2.2));
    fragColour.a = 1.;
}


void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
#if ( AA_QUALITY == 0 )
    mainImage2( fragColour, fragCoord );
#else
    int numSamples = max( 1, int((1920.*1080.*float(AA_QUALITY)*.2) / (iResolution.x*iResolution.y)) );

    vec4 colSum = vec4(0);
    for ( int i=0; i < numSamples; i++ )
    {
        uvec2 quasi2 = uvec2(0xC13FA9A9u,0x91E10DA5u);
        uint seed = uint(i);
        //seed += uint(iFrame*numSamples); // randomize per frame - causes shimmering
        //seed += uint(fragCoord.x)*quasi2.x+uint(fragCoord.y)*quasi2.y; // randomize per pixel - this looks bad at low sample counts (and at high counts it's less important)
        vec2 jitter = vec2( quasi2 * seed ) / exp2(32.);

        if ( AA_ROUND )
        {
            // circle of confusion slightly bigger than a pixel - should look more photographic
            jitter.x *= 6.283185;
            jitter = AA_ROUND_RADIUS*(1.-jitter.y*jitter.y)*vec2(cos(jitter.x),sin(jitter.x));
        }
        else
        {
            jitter -= .5;
        }

        vec4 col;
        mainImage2( col, fragCoord + jitter );
        colSum += col;
    }

    fragColour = colSum/float(numSamples);
#endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord/iResolution.yx.xy.yx;


    // Time varying pixel color
    vec3 col = 2.5 + 3.4*cos(iTime*.05+p.xyx+vec3(3,0,6));

 col *= p.x / p.y + sin(iTime*.05+vec3(0,4,0));

    // Output to screen
    fragColor = vec4(col,.5);
}

