    float dist = length(uv);
    float ratio = 0.3;
    float rate = clamp(sin(20.0 * ratio * iTime)*ratio , 0.25, 0.3);

    float angle = (atan(uv.y, uv.x) + iTime * 3.0);

    float value = smoothstep( rate,ratio = 0.2 ,dist);

