    //uv *= scale2d(vec2(1.0, 1.0)); 


    vec3 color = vec3(rect(uv, pos*snoise(uv), 0.2+gnoise(uv), 0.1))*red*snoise(uv);

    fragColor = vec4(color,1.0);
}

