mat2 r2d (float deg) {
    float r = radians (deg);
    float c = cos (r);
    float s = sin (r);
    return mat2 (c, s, -s, c);
}

float smin (float d1, float d2, float k) {
    float h = clamp (.5 + .5*(d2 - d1)/k, .0, 1.);
    return mix (d2, d1, h) - h*k*(1. - h);
}

// pathVariation1() & pathVariation2() are two random functions meant to distort the
// radius of an endless cylinder in order to create the rough shape of a tunnel/cave
float pathVariation1 (vec3 p) {
    float v = sin (p.x + p.y + p.z);
    return sin (v) + cos (v*v);
}

float pathVariation2 (vec3 p) {
    float v = sin (p.x * p.y - p.z);
    return sin (v*v) + cos (v+v);
}

// hash(), noise(), fbm() are from an example by iq and/or Shane
float hash( float n ) {
    return fract(sin(n)*43758.5453);
}

float noise( in vec3 x ) {
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f*f*(3. - 2.*f);

    float n = p.x + p.y*57. + 113.*p.z;

    float res = mix(mix(mix( hash(n+  0.), hash(n+  1.),f.x),
                        mix( hash(n+ 57.), hash(n+ 58.),f.x),f.y),
                    mix(mix( hash(n+113.), hash(n+114.),f.x),
                        mix( hash(n+170.), hash(n+171.),f.x),f.y),f.z);
    return res;
}

float fbm( vec3 p ) {
    float f;
    mat2 m = r2d (1.1);

    f  = .5*noise( p ); p.xz *= m*2.02;
    f += .25*noise( p ); p.xy *= m*2.23;
    f += .125*noise( p ); p.yz *= m*2.71;
    f += .0625*noise( p );

    return f;
}

float map (vec3 p) {
    p.xy *= r2d (-6.*cos(iTime));
    p.z += 4.*iTime;

    float cyl1 = length (p.xy + .3*pathVariation1 (2.*p) + vec2 (-.5, .0)) - (.9 + .05*fbm(7.*p));
    float cyl2 = length (p.xy + .2*pathVariation2 (3.*p) +vec2 (.5, .0)) - (1.1 + .075*fbm(8.*p));

    float cyl = smin (cyl1, cyl2, .75);

    float d = cyl;
    return d;
}

float march (vec3 ro, vec3 rd) {
    float t = .0;
    float d = .0;
    for (int i = 0; i < 48; ++i) {
        t = map (ro+d*rd);
        if (abs (t) < .0001*(1. + .126*t)) break;
        d += t*.75;
    }
    return  d;
}

vec3 norm (vec3 p) {
    float d = map (p);
    vec2 e = vec2 (.001, .0);
    return normalize (vec3 (map (p + e.xyy), map (p + e.yxy), map (p + e.yyx))  - d);
}

// hard shadows
float sha (vec3 p, vec3 n, vec3 ldir, float ldist) {
    float d2w = march (p  + .01*n, ldir);
    return ldist < d2w ? 1. : .3;
}

vec3 shade (vec3 ro, vec3 rd, float d, vec3 p, vec3 n, vec3 lp, vec3 lc) {
    vec3 mat = vec3 (.3);
    vec3 am = vec3 (.05);
    vec3 ldir = normalize (lp - p);
    vec3 h = normalize (-rd + ldir);
    float sp = pow (max (.0, dot (n, h)), 80.);
    float ldist = distance (lp, p);
    float s = sha (p, n, ldir, ldist);
    float att = 3. / (ldist*ldist);
    float li = 2.;
    float diff = max (.0, dot (n, ldir));
    return att*s*(am + diff*mat*li*lc + sp*vec3 (1.));
}

vec3 cam (vec2 uv, vec3 ro, vec3 aim, float z) {
    vec3 f = normalize (aim - ro);
    vec3 wu = vec3 (.0, 1., .0);
    wu.xy *= r2d (65.*iTime);
    vec3 r = normalize (cross (wu, f));
    vec3 u = normalize (cross (f, r));
    vec3 c = ro + f*z;
    return normalize (c + r*uv.x + u*uv.y - ro);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvRaw = uv;
    uv = uv*2. - 1.;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 1. + .5*length (uv);

    vec3 ro = vec3 (-.3*cos (3.*iTime), .1*sin(3.*iTime), 2.);
    vec3 aim = vec3 (.0);
    float z = 1.5;
    vec3 rd = cam (uv, ro, aim, z);
    float d = march (ro, rd);
    float fog = 1. / (1. + d*d*.1);
    vec3 p = ro+d*rd;
    vec3 n = -norm (p);
    vec3 lp1 = vec3 (.0, .0, 2.);
    vec3 lp2 = vec3 (.0, .0, 5.);
    vec3 lp3 = vec3 (.0, .0, 7.);
    vec3 lc1 = vec3 (.9, .8, .7);
    vec3 lc2 = vec3 (.2, .3, .9);
    vec3 lc3 = vec3 (.9, .3, .2);
    vec3 col = shade (ro, rd, d, p, n, lp1, lc1);
    col += shade (ro, rd, d, p, n, lp2, lc2);
    col += shade (ro, rd, d, p, n, lp3, lc3);

    ro = p +.01*n;
    rd = normalize (reflect (rd, n));
    d = march (ro, rd);
    p = ro+d*rd;
    n = -norm (p);

    vec3 rcol = shade (ro, rd, d, p, n, lp1, lc1);
    rcol += shade (ro, rd, d, p, n, lp2, lc2);
    rcol += shade (ro, rd, d, p, n, lp3, lc3);
    col += .5*rcol;

    col *= fog;
    col *= 1. - .25*length (uv);
    col = col / (.76 + col*1.25);
    col *= mix (1., .5, cos (500.*uvRaw.y));
    col = sqrt (col);

    fragColor = vec4 (col, 1.);
}

/*

Another flower, possibly inspired by John Edmark's sculptures.

The code is probably inefficient and lacks documentation, but if you want to
check out code and understand the log-spherical mapping that's used here, look
at "Recursive Lotus" instead: https://www.shadertoy.com/view/3d2Szm

Change the AA to 1 if laggy, or 3 if your graphics card can handle it!

*/

#define AA 2
#define ITER 60
#define M_PI 3.1415926535897932384626433832795

float fov;
float cam_ty;
float cam_incl;
float ctrl;
float cam_azi;
vec3 cam_pos;
float timein = 0.;
vec2 AAradius;
float azrep = M_PI/6.;
float rrep = 2.;
float cam_dist = 2.25;

// Axis rotation taken from tdhooper. R(p.xz, a) rotates "x towards z".
void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

// Modified from http://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdCylinder( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}
float opUsmin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

// From https://www.osar.fr/notes/logspherical/
vec3 logspherical(in vec3 p)
{
	return vec3(
		log(length(p)),
		acos(p.z / length(p)),
		atan(p.y, p.x)
	);
}

float kk(float x) {
	return x>1.?x:exp(x-1.)+0.;
}

float sphFlower(in vec3 p, in float eradius)
{
	p.y -= p.x*M_PI/5.; // azimuth transform
	p.y = fract(p.y/azrep+0.5*azrep)*azrep-0.5*azrep; // azimuth rep
	float unsquish = (1.+1./(M_PI-p.z));
	p.y /= sqrt(unsquish);

	p.x = fract((p.x)*rrep+0.25)/rrep-0.25; // radius rep
	p.z -= 1.6+0.25*eradius; // inclination transform

	// stem
	float ret = sdCylinder(p.xzy-vec3(-0.03,1.,0.), vec2(0.02, 1.));

	// rotate for petal and cap stem
	float sub = -p.z;
	pR(p.yz, 0.6);
	pR(p.xz, 0.4);
	ret = max(ret, -p.x+0.07+sub*0.3);

	// petal
	ret = opUsmin(ret, sdBox(p, vec3(-0.3, 0.04, 0.04))-0.32, 0.1);
	return ret;

}

float sdf(in vec3 p)
{
    float fix = length(p+vec3(0.,0.35,0.))-0.3;
    if (fix<0.05) fix = 10.;
	p *= 1. - 0.02*kk(length(p));

	p.xzy = logspherical(p.xzy);
	float eradius = exp(p.x-0.25);
	p.x -= timein;

	float sd = sphFlower(p, eradius);
	p.x += 0.25;
	sd = min(sd, sphFlower(p, eradius));

	sd *= eradius;
	return min(sd, fix);
}

float iqhash(float n)
{
	return fract(sin(n)*43758.5453);
}

vec3 castRay(in vec3 ro, in vec3 rd)
{
	float rmul = iqhash(rd.x*0.1+rd.y*10.+rd.z*100.+fract(timein))*0.05;
	ro += rd * rmul*2.;
	float tmin = 0.2;
	float tmax = 30.0;

	float t = tmin;
	float m = -1.0;
	float i2;

	for(int i=0; i<ITER; i++)
	{
		i2 = float(i);
	    float precis = 0.0004*t;
		vec3 pos = ro+rd*t;
	    vec2 res = vec2(sdf(pos), 53.);
		if(res.x<precis || t>tmax) break;
		t += res.x*(0.8+rmul);
	    m = res.y;
	}

	if( t>tmax ) {
		i2 = float(ITER);
		m=-1.0;
	}
	vec3 pt = ro+rd*t;
	return vec3(t, m, i2);
}

// Adapted from http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal(in vec3 pos)
{
	vec2 e = vec2(1.0,-1.0)*0.5773;
	const float eps = 0.0005;
	return normalize(
		e.xyy*sdf(pos + e.xyy*eps) + 
		e.yyx*sdf(pos + e.yyx*eps) + 
		e.yxy*sdf(pos + e.yxy*eps) + 
		e.xxx*sdf(pos + e.xxx*eps)
	);
}

void getray(in vec2 pos, out vec3 ro, out vec3 rd)
{
	//pos = pos*-1.333+0.677;
	vec3 ta = vec3(0., cam_ty, 0.);
	ro = cam_pos;
	vec3 ww = normalize(ta - ro);
	vec3 uu = normalize(cross(ww,vec3(0.0,1.0,0.0)));
	vec3 vv = normalize(cross(uu,ww));
	rd = normalize(pos.x*uu + pos.y*vv + fov*ww);
}

vec3 render(in vec3 ro, in vec3 rd, in vec2 scrpos)
{
	vec3 bg = vec3(0.7, 0.9, 1.0) +rd.y*0.8;
	vec3 col = bg;
	vec3 res = castRay(ro,rd);
	float t = res.x;
	float m = res.y;
	vec3 pos = ro + t*rd;

	vec3 ipt = vec3(0.);

	if( m>-0.5 )
	{
		vec3 nor = calcNormal( pos );

		// material        
		col = 0.45 + 0.35*sin( vec3(0.05,0.08,0.10)*(m-1.0) );

		// lighting        
		vec3  lig = normalize( vec3(-0.4, 0.7, -0.6) );
		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
		float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
		float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
		float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );

		vec3 lin = vec3(0.0);
		lin += 1.30*dif*vec3(1.00,0.80,0.55);
		lin += 0.40*amb*vec3(0.40,0.60,1.00);
		lin += 0.50*bac*vec3(0.25,0.25,0.25);
		lin += 0.25*fre*vec3(1.00,1.00,1.00);
		col = col*lin;
	}

	float fogf = pos.y<0. ? -(pos.y * pos.y * 3.): 0.;
	col = mix( col, bg, 1.0-exp( -0.006*t*t + fogf) );

	vec3 inice = vec3(res.z/float(ITER));
	inice *= inice;
	inice = mix( inice, vec3(1.), 1.0-exp( -0.0002*t*t*t ) );
	col = col*(inice)*1.2 + bg*(inice)*0.4;

	//return inice;
	return vec3( clamp(col,0.0,1.0) );
}

// From http://iquilezles.org/www/articles/functions/functions.htm
float gain(float x, float k) 
{
	float a = 0.5*pow(2.0*((x<0.5)?x:1.0-x), k);
	return (x<0.5)?a:1.0-a;
}

vec3 gain(vec3 v, float k)
{
	return vec3(
		gain(v.x, k),
		gain(v.y, k),
		gain(v.z, k)
	);
}

vec3 pillow(in vec3 col, in vec2 uv)
{
	uv -= 0.5;
	float mpow = 5.;
	float d = pow(pow(abs(uv.x),mpow)+pow(abs(uv.y),mpow),1./mpow);
	d = smoothstep(0., 1., d*2.-0.64);
	return mix(col, vec3(0.), d);
}

// Based on http://iquilezles.org/www/articles/raymarchingdf/raymarchingdf.htm
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

	timein = iTime*0.08*1.8+4.;
	ctrl = iTime*0.1+0.1;

	float t2 = ctrl * M_PI + 0.3;
	vec3 shot1 = vec3(0.75, -0.7, cos(t2)*0.3+1.1);
	vec3 shot2 = vec3(0.75, -0.7, cos(t2)*0.25+2.4);
	vec3 shot3 = vec3(cos(t2)*0.392+0.952, -3.0, 0.1);
	vec3 shot = shot3;
	if (mod(ctrl, 3.) <= 2.) shot = shot2;
	if (mod(ctrl, 3.) <= 1.) shot = shot1;
	fov = shot.x;
	cam_ty = shot.y;
	cam_incl = shot.z;


	cam_azi = M_PI * timein / (1.5 * 5.) + 0.12;
	cam_pos = vec3(
		cam_dist*sin(cam_incl)*cos(cam_azi), 
		cam_dist*cos(cam_incl),
		cam_dist*sin(cam_incl)*sin(cam_azi) );

	AAradius  = vec2(1.) / iResolution.xy;
	vec3 tot = vec3(0.);

	// centered ratio-corrected UV
	vec2 cUV = uv-vec2(0.5);
	cUV.x *= iResolution.x/iResolution.y;

	#if AA > 1
	for( int m=0; m<AA; m++ )
	for( int n=0; n<AA; n++ )
	{    	
		// pixel coordinates
		vec2 ofs = AAradius*(vec2(float(m),float(n)) / float(AA) - 0.5);
		vec2 pos = cUV+ofs;
	#else    
		vec2 pos = cUV;
	#endif
		vec3 ro, rd;
		getray(pos, ro, rd);

		vec3 col = render(ro, rd, pos);
		tot += col;
	#if AA>1
	}
	tot /= float(AA*AA);
	#endif
	tot = pow(tot, vec3(0.4545));
	tot = gain(tot, 1.5);
	tot = pillow(tot, uv);
	tot *= 1.-pow(cos(ctrl*M_PI*2.)*0.5+0.5, 200.);

    // Output to screen
    fragColor = vec4(tot, 1.0);
}

float sdHexPrism( vec3 p, vec2 h )
{
    const vec3 k = vec3(-0.8660254, 0.5, 0.57735);
    p = abs(p);
    p.xy -= 2.0*min(dot(k.xy, p.xy), 0.0)*k.xy;
    vec2 d = vec2(
       length(p.xy-vec2(clamp(p.x,-k.z*h.x,k.z*h.x), h.x))*sign(p.y-h.x),
       p.z-h.y );
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

vec2 tile(vec2 _st, float _zoom){
    _st *= _zoom;
    return fract(_st);
}

float sdPentagon( in vec2 p, in float r )
{
    const vec3 k = vec3(0.809016994,0.587785252,0.726542528); // pi/5: cos, sin, tan

    // reflections
    p.y = -p.y;
    p.x = abs(p.x);
    p -= 2.0*min(dot(vec2(-k.x,k.y),p),0.0)*vec2(-k.x,k.y);
    p -= 2.0*min(dot(vec2( k.x,k.y),p),0.0)*vec2( k.x,k.y);

	// side of polygon
    return length(p-vec2(clamp(p.x,-r*k.z,r*k.z),r))*sign(p.y-r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    // p = tile(p,1.);
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float pent = sdPentagon(p,0.85)/sdPentagon(p,0.82) + sdPentagon(p,0.75)/sdPentagon(p,0.72);
    float pent2= sdPentagon(p,0.1+ sin(iTime/2.));


    // Output to screen
    fragColor = vec4(cos(iTime)-clamp(pent,0.,0.1),0.5-clamp(pent-pent2,0.,0.1),1.-clamp(pent2,0.,0.1),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv  = ( fragCoord -.5* iResolution.xy ) / iResolution.y;
    float z =0.2;


    if(sqrt(uv.x*uv.x +uv.y*uv.y) > 0.1){
    uv.y += sign(sin(uv.x*100.))/30.* sin(uv.x*uv.x +uv.y*uv.y) ;//*sin(iTime*2.);
    uv.x += sign(cos(uv.y*100.))/30. * sin(uv.x*uv.x +uv.y*uv.y);//*cos(iTime*2.);
    /* Some Nice Effect */
    }
    // uv.y += sign(sin(uv.x*20.))/200.*clamp(sin(iTime*2.+3.),0.,1.);
    //uv.x += sign(sin(uv.y*20.))/200.*clamp(-sin(iTime*2.),-1.,0.);
    //uv.x += sign(sin(uv.x*20.))/200.*clamp(-cos(iTime*2.),-1.,0.);
     //uv.y += sign(sin(uv.y*20.))/200.*clamp(sin(iTime*2.+3.),0.,1.);

    //uv.x = log(uv.x)*z ;

   // uv.y = log(uv.y)*z ; 



    float p= 0.2  ;
    float q= 0.229 - (1.+cos(iTime*2. - exp(length(uv)*2.) ))/45.;
    float k= 6.;

    float colc = mod(length(uv )  -  iTime/k ,p) ;

	float col1 = mod(length(uv + vec2(0.1,0.))  -  iTime/k ,p) ;
    float col2 = mod(length(uv + vec2(-0.1,0.)) -  iTime/k ,p);

    float col3 = mod(length(uv + vec2(0,-0.1))  -  iTime/k ,p);
    float col4 = mod(length(uv + vec2(0,0.1))   -  iTime/k ,p);



    float colo = mix(col1,col2,0.5);
    float coli = mix(col3,col4,0.5);
    float col  = mix(colo,coli,0.5);

    float aa = (colo + coli) /2.;


    col = clamp(.8,.9, smoothstep(q,q-0.15,col));

    float b  =  1.-col;

    float ring =  smoothstep(1.,0.8,log( (q*10.59) ))+q;

    ring = smoothstep(0.2,0.1,1.-ring);

    fragColor = 1.5*vec4( vec3(0.9,0.2,0.6) * vec3( b+aa ,0.1,aa+col*b*ring )   ,1.0);
    //fragColor  = vec4(col1);
}

/*
A raymarching shader. I thought it would be interesting to tile iq's ellipsoid
approximation, since it can be smoothly turned into an infinite cylinder,
allowing the tiles to connect and disconnect with each other along different
axes over time. I then applied the log-spherical map, so this reveals how the 3
axes are transformed by this map:

- Lines along the rho coordinate converge towards the origin
- Lines along the theta coordinate become like circles of longitude
- Lines along the phi coordinate become like circles of latitude

The ellipsoids also appear nicely twised because rotation is applied in a
gradient (but this can't be pushed very far because it also twists the distance
field).

This is part of a series of explorations on the log-spherical mapping:
https://www.osar.fr/notes/logspherical/
*/

// definitely try changing this value:
#define DENSITY 12.0

#define AA 2
#define M_PI 3.1415926535897932384626433832795

float worldtime;
float centerhue;

// ssN: smooth staircase of iteration N, step height = 2 pi
float ss1(float x) { return x - sin(x); }
float ss2(float x) { return ss1(ss1(x)); }

// alternating smooth staircases
float as(float x) { return smoothstep(0.1,0.4,fract(x))+floor(x); }
float asa(float x) { return as(x*(0.25/M_PI)); }
float asb(float x) { return as(x*(0.25/M_PI)+M_PI*0.5); }
// shelves, returns 1 when the staircases are in the middle of steps
float shelves(float x) { return 1.-pow(abs(sin(x*0.5)), 4.)*0.4; }

// map (0-1) to (0-big) with inverse curve
float zeroInf(float x) {
	return -0.99/(x-1.)-0.98;
}

// Axis rotation taken from tdhooper. R(p.xz, a) rotates "x towards z".
void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

// iq's ellipsod https://www.shadertoy.com/view/tdS3DG
float sdEllipsoid(in vec3 p, in vec3 r)
{
	float k0 = length(p/r);
	float k1 = length(p/(r*r));
	return k0*(k0-1.0)/k1;
}

float sdf(in vec3 p)
{
	const float lpscale = DENSITY/M_PI;

	// Apply the forward log-spherical map
	float r = length(p);
	p = vec3(log(r), acos(p.z / length(p)), atan(p.y, p.x));

	// Get a scaling factor to compensate for pinching at the poles
	// (there's probably a better way of doing this)
	float xshrink = 1.0/(abs(p.y-M_PI)) + 1.0/(abs(p.y)) - 1.0/M_PI;

	// Scale to fit in the ]-pi,pi] interval
	p *= lpscale;

	// prepare values for rotation and stretching of tile contents
	float rotclock = worldtime*5.-p.x;
	float stretch = shelves(worldtime*5.-p.x*0.5)*0.5+0.5;
	float thik = 0.005/(r+0.03);

	// Apply rho-translation, which yields zooming
	p.x -= worldtime;

	// Turn tiled coordinates into single-tile coordinates
	p = fract(p*0.5) * 2.0 - 1.0;
	p.x *= xshrink;

	// rotate and stretch the primitive
	pR(p.xz, asa(rotclock)*M_PI*0.5);
	pR(p.yz, asb(rotclock)*M_PI*0.5);
	float ret = sdEllipsoid(p, vec3(thik, thik, thik*zeroInf(stretch)));

	// Compensate for all the scaling that's been applied so far
	// (and shorten the steps a bit)
	float mul = 0.9*r/lpscale/xshrink;
	return ret * mul;
}

// Minkowski pillow
vec3 pillow(in vec3 col, in vec2 uv)
{
	uv -= 0.5;
	float mpow = 5.;
	float d = pow(pow(abs(uv.x),mpow)+pow(abs(uv.y),mpow),1./mpow);
	d = smoothstep(0., 1., d*2.-0.64);
	return mix(col, vec3(0.), d);
}

// From http://www.iquilezles.org/www/articles/functions/functions.htm
float gain(float x, float k) 
{
	float a = 0.5*pow(2.0*((x<0.5)?x:1.0-x), k);
	return (x<0.5)?a:1.0-a;
}

vec3 gain(vec3 v, float k)
{
	return vec3(
		gain(v.x, k),
		gain(v.y, k),
		gain(v.z, k)
	);
}

// Smooth HSV by iq and Fabrice Neyret: https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb_smooth(in vec3 c)
{
	return c.z * (1.-c.y*smoothstep(2.,1., abs(mod(c.x*6.+vec3(0,4,2), 6.) -3.)));
}

// Shading is flat. The color is an interpolation in HSV space between a
// time-varying hue at the center and a backgroundish color at the outer limits
vec3 shade(in vec3 pos)
{
	float cf = clamp(length(pos*1.6), 0., 1.);
	vec3 hsv1 = vec3(centerhue, 0., 1.);
	vec3 hsv2 = vec3(0.6, 1., 0.2);
    vec3 ret = mix(hsv1, hsv2, cf);
    // and a bit of extra brightness at the center
    ret.y = ret.y*0.2/(ret.y*ret.y+0.025);
	return hsv2rgb_smooth(ret);
}

// Based on http://iquilezles.org/www/articles/raymarchingdf/raymarchingdf.htm
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

	// alternate between moving the camera+color and moving the world
	worldtime = ss1(iTime+6.)*0.5;
	float camtime = ss2(iTime+M_PI+6.);
	centerhue = abs(fract(camtime*0.03)-0.5)*2.2+0.1;

	 // camera movement	
	float an = 0.3*camtime;
	float cy = 0.6+sin(an*2.+1.6)*0.5;
	vec3 ro = vec3((1.3-cy*0.5)*sin(an), cy, (1.3-cy*0.5)*cos(an));
	vec3 ta = vec3( 0.0, 0.0, 0.0 );
	// camera matrix
	vec3 ww = normalize(ta - ro);
	vec3 uu = normalize(cross(ww,vec3(0.0,1.0,0.0)));
	vec3 vv = normalize(cross(uu,ww));

	vec3 bg = vec3(0.15, 0.15, 0.18);
	vec3 tot = bg;

	#if AA>1
	for(int m=0; m<AA; m++)
	for(int n=0; n<AA; n++)
	{
		// pixel coordinates
		vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
		vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
		#else    
		vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
		#endif

		// create view ray
		vec3 rd = normalize(p.x*uu + p.y*vv + 3.5*ww); // fov

		// raymarch
		const float tmax = 2.7;
		float t = 0.5;
		vec3 pos;
		int i2;
		for( int i=0; i<80; i++ )
		{
			pos = ro + t*rd;
			float h = sdf(pos);
			if( h<0.0001 || t>tmax ) break;
			t += h;
			i2 = i;
		}

		// shading/lighting	
		vec3 col = vec3(0.0);
		if( t<tmax )
			col = shade(pos);
		// fog
		col = mix(col, bg, smoothstep(0.8, 2.5, t));
		// glow
		float g = float(i2) * (0.1/80.);
		col += vec3(g);

		tot += col;
	#if AA>1
	}
	tot /= float(AA*AA);
	#endif

	tot = gain(clamp(tot, 0., 1.), 1.5);
	tot = pillow(tot, uv);
	fragColor = vec4(tot, 1.0);
}

