mat2 r2d (float deg) {
	float rad = radians (deg);
	float c = cos (rad);
	float s = sin (rad);
	return mat2 (c,s,-s,c);
}

float texNoise (vec2 p) {
	return texture (iChannel0, p).r;
}

float smin (float d1, float d2, float k) {
	float h = clamp (.5 + .5*(d2 - d1)/k, .0, 1.);
	return mix (d2, d1, h) - h*k*(1. - h);
}

float map (vec3 p, inout int id, inout vec3 pout) {
	float size = 3.;
	float offset = cos (.2*iTime);
	float offset2 = cos (iTime);
	float ground = p.y + size + offset2 + .75*texNoise (offset+.05*p.xz+.1*iTime);
	ground = min (ground, -p.y + size + offset2 + .75*texNoise (offset-.1*iTime+.075*p.xz + 1.));
	float wall = p.z + 3.*size;
	wall = min (wall, -p.z + 3.*size);
	wall = min (wall, p.x + 2.*size);
	wall = min (wall, -p.x + 2.*size);
	vec3 pbar = p;
	pbar.xz *= r2d (25.*cos (pbar.y + sin(3.*iTime)));
	pbar.xy *= r2d (9.*cos (pbar.y + sin(4.*iTime)));
	pbar.yz *= r2d (9.*sin (pbar.y + sin(2.*iTime)));

	pbar.x += .2*cos (pbar.y + 2.*iTime);
	pbar.z += -(.3*(cos(2.*iTime)))*sin (2.*pbar.y + 2.*iTime);
	float thickness = .5-.2*offset2*cos (2.*pbar.y+3.*iTime);
	vec3 s = vec3 (thickness, 3.5, thickness);
	float bar = length (max (vec3 (.0), abs (pbar) - s)) - .1;
	float d = min (wall, smin (ground, bar, 1.5));
	id = 1;
    pout = pbar;
	if (d == wall) {id = 2; pout = p;}
    return d;
}

float march (vec3 ro, vec3 rd, inout int id, inout vec3 pout)
{
	float t = .0;
	float d = .0;
	for (int i = 0; i< 64; ++i) {
		vec3 p = ro+d*rd;
		t = map (p, id, pout);
		if (abs (t) < .000000001*(1. + .125*t)) break;
		d += t*.5;
	}
	return d;
}

vec3 norm (vec3 p){
	int foo;
	vec3 bar;
	float d = map (p, foo, bar);
	vec2 e = vec2 (.01, .0);
	return normalize (vec3 (map (p+e.xyy, foo, bar),
                            map (p+e.yxy, foo, bar),
                            map (p+e.yyx, foo, bar))-d);
}

float sha (vec3 p, vec3 lp, vec3 n, vec3 ldir) {
	float d2l = distance (lp, p);
	int foo;
	vec3 bar;
	float d2w = march (p+.01*n, ldir, foo, bar);
	return d2l < d2w ? 1. : .1;
}

vec3 shade (vec3 ro, vec3 rd, float d, vec3 n, vec3 lp, vec3 lc, float li, int id, vec3 pout) {
    vec3 p = ro + d*rd;
	float ld = distance (p, lp); 
	vec3 ldir = normalize (lp - p);
	float att = 12. / (ld*ld);
	vec3 mat = vec3 (1., .0, .0);
	if (id == 1) mat = vec3 (.0);
	if (id == 2) mat = mix (vec3 (.0), vec3 (1.), smoothstep (.0, .5, sin (3.*p.y+5.*iTime)));
	float s = sha (p, lp, n, ldir);
	float diff = max (.0, dot (n, ldir));
	vec3 h = normalize (-rd + ldir);
	float shiny = 40.;
	float sp = pow (max (.0, dot (n, h)), shiny);
	vec3 am = vec3 (.05);
	return att*s*(am + diff*lc*li*mat + sp*vec3 (1.));
}

vec3 cam (vec2 uv, vec3 ro, vec3 aim, float z) {
	vec3 f = normalize (aim - ro);
	vec3 wu = vec3 (.0, 1., .0);
	vec3 r = normalize (cross (wu, f));
	vec3 u = normalize (cross (f, r));
	vec3 c = ro + f*z;
	return normalize(c + r*uv.x + u*uv.y - ro);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uvRaw = fragCoord/iResolution.xy;
	vec2 uv = uvRaw*2. - 1.;
	uv.x *= iResolution.x/iResolution.y;
	uv *= 1. + .25*length (uv);

	float dist = 3.;
	vec3 ro = vec3 (dist*cos (iTime), .0, dist*sin(iTime));
	vec3 rd = cam (uv, ro, vec3 (.0), 1.25);
	int id = 0;
	vec3 pout = vec3 (.0);
	float d = march (ro, rd, id, pout);
	vec3 p = ro + d*rd;
	vec3 n = norm (p);
	vec3 c = shade (ro, rd, d, n, vec3 (.0, .0, 2.), vec3 (.9, .85, .3), 2.,id, pout);
	c += shade (ro, rd, d, n, vec3 (2., 2., -2.), vec3 (.5, .5, .9), 2.,id, pout);

	if (id == 1) {
		ro = p + .01*n;
		rd = normalize (reflect (rd, n));
		d = march (ro, rd, id, pout);
		p = ro + d*rd;
		n = norm(p);
		vec3 rc = shade (ro, rd, d, n, vec3 (.0, .0, 2.), vec3 (.9, .85, .3), 2.,id, pout);
		rc += shade (ro, rd, d, n, vec3 (2., 2., -2.), vec3 (.3, .3, .9), 2.,id, pout);
		c += .3*rc;
	}
	c=c/(1.25+c*.5);
	c*=1.-.65*length(uvRaw*2.-1.);
	c*=mix(1.,.75,cos(500.*uvRaw.y));
	c=pow(c,vec3(1./2.2));

    fragColor = vec4(c,1.);
}

//-----------------------------------------------------------
// fbm ref.
// https://thebookofshaders.com/13/?lan=jp
// http://www.iquilezles.org/www/articles/warp/warp.htm
//-----------------------------------------------------------

#define EPS 0.0001
#define PI 3.14159265359
#define FLT_MAX 3.402823466e+38
#define FLT_MIN 1.175494351e-38
#define DBL_MAX 1.7976931348623158e+308
#define DBL_MIN 2.2250738585072014e-308

precision highp float;

const int num = 16;
vec2 textureScale = vec2(1.);
vec2 textureOffset = vec2(0.);
vec2 parallaxScale = vec2(0.); // if use parallax with mouse, set value rather than 0.
float speed = 1.2;
float scaleStep = .66;

float bs = 0.; // border size

vec3 oppositeMirror(vec2 coord, vec2 mouse) {
	vec3 st = vec3(0.); // dist uv

    vec3 np = vec3(0.);
	vec3 nn = vec3(0.);
	vec3 nf = normalize(vec3(mouse, 0.));
	vec3 center = vec3(0.);
	vec3 v = nn - (-nf);
    // v = normalize(v);
    // vec3 v = vec3(0.);

	float ts = iTime * speed;
	float t = mod(ts, 1.f); // 0 - 1

	// highp int n = int(num);
    int n = num;

	for(int i=0; i<n; i++) {
		float si = mix(float(i) - 2., float(i) - 3., t); // margin for parallax

		vec3 po = v;
		po.z *= -1.;

		vec2 m = po.xy * parallaxScale * (1.f - si / float(n));
  		//vec2 m = vec2(-0.); // debug: off parallax
  		vec2 rd = 1. / textureScale;

		vec2 uv = coord - textureOffset;

		uv *= 1. / pow(scaleStep, si);
		// centering offset 
		float sc = pow(1. / scaleStep, si) - 1.;
		uv += rd * -.5 * sc + rd * m * - .5;
  		if(
    		0. <= uv.x
    		&& rd.x >= uv.x
    		&& 0. <= uv.y
   		 	&& rd.y >= uv.y
  		) {
    		st.xy = uv;
            st.z = si;
            // flip
    		float ft = mod(floor(ts), 2.);
    		int fi = int(step(.5, ft));
            if(mod(float(i), 2.) == float(fi)) {
                st.x = rd.x - st.x;
            }
        } else {
            break;
        }
	}

	return st; 
}

float random(in vec2 st)
{
    return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

#define OCTAVES 4
float fbm(in vec2 st) {
  float value = 0.;
  float amp = .44;

  for(int i = 0; i < OCTAVES; i++) {
    value += amp * noise(st);
    st *= 2.3;
    amp *= .44;
  }
  return value;
}

float pattern(in vec2 p) {
  float f = 0.;
  vec2 q = vec2(
    fbm(p + iTime * .15 + vec2(0.)),
    fbm(p + iTime * .05 + vec2(2.4, 4.8))
  );
  vec2 r = vec2(
    fbm(q + iTime * .1 + 4. * q + vec2(3., 9.)),
    fbm(q + iTime * .2 + 8. * q + vec2(2.4, 8.4))
  );
  f = fbm(p + r * 2. + iTime * .1);
  return clamp(f, 0., 1.);
}

vec3 gradient(float v, float i) {
	//v += i * .1;
    //vec3 r = vec3(random(vec2(i, i)), random(vec2(i, i)), random(vec2(i, i)));
    float steps = 4.;
	float s = 1. / steps;
    vec3 col = vec3(0.);

    // base colors
    vec3 c1 = vec3(255. / 255.,  255. / 255.,  255. / 255.);
	vec3 c2 = vec3(53. / 255.,  185. / 255.,  208. / 255.);
    vec3 c3 = vec3(0. / 255.,  128. / 255.,  209. / 255.);
	vec3 c4 = vec3(233. / 255., 233. / 255., 215. / 255.);
    vec3 c5 = vec3(230. / 255.,  244. / 255.,  241. / 255.);

	if(v >= 0. && v < s) {
  		col = mix(c1, c2, v * steps);
	} else if (v >= s && v < s * 2.) {
  		col = mix(c2, c3, (v - s) * steps);
	} else if (v >= s * 2. && v < s * 3.) {
    	col = mix(c3, c4, (v - s * 2.) * steps);
    } else {
        col = mix(c4, c5, (v - s * 3.) * steps);
	}
    col += sin((iTime * .95) * vec3(
        sin(iTime * .03 + .2) * .5 + .5,
        sin(iTime * .03 + .8) * .5 + .5,
        sin(iTime * .03 + 1.4) * .5 + .5
    )) * .2;

    float n = float(num);
  	return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    float aspect = iResolution.x / iResolution.y;

    vec2 mouse = (iMouse.xy / iResolution.xy) * 2. - 1.;

	vec3 om = oppositeMirror(uv, mouse);

    uv.xy = om.xy;

	vec3 color = gradient(pattern(uv), om.z);

    float border = 0.;
    border += 1. - step(bs, uv.x) * (1. - step(1. - bs, uv.x));
    border += 1. - step(bs * aspect, uv.y) * (1. - step(1. - bs * aspect, uv.y));
    border =  clamp(border, 0., 1.);

    color = mix(color, vec3(1.), border);

    //color = vec3(border);

	fragColor = vec4(color, 1.);
}

//@author : Arthur Cavalier

// Simple implementation of the noise model introduced by Gilet et al. 
// Local Random-Phase Noise for Procedural Texturing (SIGGRAPH ASIA 2014)
// http://www.unilim.fr/pages_perso/guillaume.gilet/publications/pdf/ProcTextures.pdf

// User Controls
#define MOUSE_CONTROL
#define TIMER_OFFSET
//#define BERNOUILLI
const int   NOF_COSINES = 5;
const float GRID_RES    = 5.0;

// Constants
const float m_pi_2        = 1.57079632;         
const float m_pi          = 3.14159265;          
const float m_2_pi        = 6.28318530;  


// Pseudo Random Number Generation
// From Texton Noise Source Code provided by Galerne et al. 
// https://www.idpoisson.fr/galerne/texton_noise/index.html
// Sourced ::
/* 
 * From http://www.reedbeta.com/blog/2013/01/12/quick-and-easy-gpu-random-numbers-in-d3d11/
 * Same strategy as in Gabor noise by example
 * Apply hashtable to create cellseed
 * Use a linear congruential generator as fast PRNG
 */

uint  wang_hash(uint seed)                                          {seed=(seed^61u)^(seed>>16u);seed*=9u;seed=seed^(seed>>4u);seed*=668265261u;seed=seed^(seed>>15u);return(seed);}
uint  cell_seed(const in ivec2 c, const in uint offset)             {const uint period=1024u;uint s=((uint(c.y)%period)*period+(uint(c.x)%period))*period+offset; if(s==0u){s = 1u;}return(s);}
uint  myrand(inout uint p)                                          {p^=(p<<13u);p^=(p>>17u);p^=(p<<5u);return p;}
float myrand_uniform_0_1(inout uint p)                              {return float(myrand(p))/float(4294967295u);}
float myrand_uniform_m_M(inout uint p, in float mi, in float ma)    {return mi + (myrand_uniform_0_1(p) * (ma - mi));}


// Kaiser Bessel Function
float bessel_3(float x)
{
    float y=0.5*x;
    float y2=(y*y);
    return(y2*y*(1.0/6.0+y2*(1.0/24.0+y2*(1.0/240.0+y2*(1.0/4320.0+y2/120960.0)))));
}

float kaiser_bessel_window(float x)
{
    float i=clamp(x/1.5,-1.0,1.0);
    float t=sqrt(1.0-pow(i,2.0));
    return float(bessel_3(3.0*m_pi*t)/bessel_3(m_pi*3.0));
}

// Procedural Noise Function
vec3 local_random_phase_noise(
        in vec2  texcoords,
        in float resolution,
        in int   cosines,
        in vec2  range_frequency,
        in vec2  range_orientation
    )
{
    vec2  scaled_coords  = texcoords * resolution;
    vec2  cell_coords  = fract(scaled_coords);
    vec2  cell_index   = floor(scaled_coords);

    vec2  scaled_frequency = range_frequency * resolution;

    ivec2 cell_ID;
    uint  prng, seed;

    float lrpn = 0.;
    for (int m=-1; m<=+1; m++)
    for (int n=-1; n<=+1; n++)
    {
        cell_ID.x = int(cell_index.x) + m;
        cell_ID.y = int(cell_index.y) + n;

        seed = cell_seed(cell_ID,0u);
        prng = wang_hash(seed);

        vec2 xy = cell_coords - vec2(m,n) - vec2(0.5);
        float w = 1. / float(cosines);
        float d = dot(xy,xy);

        float sc = 0.; // Sum of cosines
        for(int k=0; k<cosines; k++)
        {
            float fr = myrand_uniform_m_M(prng,scaled_frequency.x,scaled_frequency.y); // pick a frequency 
            float or = myrand_uniform_m_M(prng,range_orientation.x,range_orientation.y); // pick an orientation
            float ph = m_pi*(myrand_uniform_0_1(prng)*2.-1.); // pick a phase (shift)
            float Am = myrand_uniform_0_1(prng)*2.-1.; // pick a amplitude between [-1;1]

            #ifdef BERNOUILLI
                // In Gabor Noise Revisited (JFIG 2018)
                // Tavernier et al. (https://hal.archives-ouvertes.fr/hal-01926451/document)
                // Studied Gabor Noise and its follow ups 
                // They compared the uniform weighting scheme for Gabor Kernels with simple Bernouilli distribution
                // "Splats with a low weight count for little in the sum, which seems like a waste of computation"

                // Because the formulation is quite close
                // Testing with a LRPN seems interesting
                Am = myrand_uniform_0_1(prng) > 0.5 ? -1.0 : 1.0; // choose between -1 or 1
            #endif

            vec2 ff = fr * vec2(cos(or),sin(or)); // oriented frequency
            sc += Am * cos( m_2_pi * dot(xy,ff) + ph);
        }

        lrpn += kaiser_bessel_window(d) * w * sc;
    }
    return vec3(0.5+0.5*lrpn); 
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 texcoords = (2.*fragCoord.xy-iResolution.xy) / iResolution.y;
    vec2 mouse = clamp(iMouse.xy / iResolution.xy, 0., 1.);

    vec2 F,O; // frequency and orientation range ( vec2(min,max) )
    float R = GRID_RES;  // grid resolution
    int   K = NOF_COSINES;   // number of cosines

#ifdef MOUSE_CONTROL
    F = vec2(0.3, max(0.1,mouse.x) ); 
    O = vec2(0.,mouse.y*m_pi);      
#else 
    F = vec2(0.4,0.5);
    O = vec2(0.,m_pi / 6.);
#endif

#ifdef TIMER_OFFSET
    texcoords += vec2(0.1*iTime);
#endif

    vec3 lrpn = local_random_phase_noise(texcoords,R,K,F,O);
    fragColor = vec4(lrpn,1.);
}

vec2 hash(vec2 x)
{
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
