float box(vec3 p, vec3 c)
{
  	vec3 q = abs(p)-c;
  	return min(0., max(q.x, max(q.y, q.z))) + length(max(q,0.));
}

float sphere(vec3 p, float r)
{
  	return length(p)-r;
}

float sdf_smin(float a, float b)
{
	float res = exp(-12.*a) + exp(-12.*b);
 	return -log(max(0.001,res)) / 12.;
}

vec2 add_object(float f, float id, vec2 current)
{
    if (f < current.x) return vec2(f,id);
    return current;
}

vec2 map(vec3 p)
{
    vec2 result = vec2(999.,-1.);

    float backwall = box(p, vec3(.9, .9, .05));
    float leftwall = box(p+vec3(.9,0.,0.), vec3(.05,.9,.9));
    float rightwall = box(p-vec3(.9,0.,0.), vec3(.05,.9,.9));
    float floorwall = box(p+vec3(0.,.9,0.), vec3(.95,.05,.9));

    float room = min(floorwall, min(rightwall, min(backwall, leftwall)));
    result = add_object(room, 0., result);

    vec3 rotbase = p+vec3(0.,0.,.5);
    vec3 rotp = rotbase;
    rotp.x = rotbase.x * cos(iTime*2.2) - rotbase.z * sin(iTime*2.2);
  	rotp.z = rotbase.z * cos(iTime*2.2) + rotbase.x * sin(iTime*2.2);

    float bar = box(rotp + vec3(0.,.8,0.), vec3(.38,.05,.05));
    result = add_object(bar, 2., result);

    float s1 = sphere(p+vec3(.4*sin(iTime*2.3),.4*cos(iTime*1.4), 0.2), .3);
    float s2 = sphere(p+vec3(.5*sin(iTime*1.7),.5*cos(iTime*1.2), .3+.3*sin(iTime*.7)), .2);
    float s3 = sphere(p+vec3(.3*cos(iTime*1.3),.3*sin(iTime*1.7), .4+.35*cos(iTime*.6)), .4);

    float object =sdf_smin(s3, sdf_smin(s1, s2));

    result = add_object(object, 1., result);

    float light_ball = sphere(p+vec3(.2*cos(iTime*2.1),.3*sin(iTime*2.1), .5+.2*cos(iTime*1.6)), .15);
    result = add_object(light_ball, 3., result);

    float overball = sphere(p+vec3(.5*sin(iTime*2.1),.4*sin(iTime*1.5), .6+.4*cos(iTime*2.1)), .1);
    result = add_object(overball, 4., result);

    return result;
}

vec4 getMaterial(int id)
{
    switch(id)
    {
        //shiny, light, texture, darken (should be reworked to emissive light)
        // box
        case 0: return vec4(0.4,1.,.25,0.);

        // blobs
    	case 1: return vec4(1.,1.,0.,0.);

        // bar
    	case 2: return vec4(0.,1.,1.,0);

        // light ball
        case 3: return vec4(0.,0.,0.,0.);

        // dark ball
        case 4: return vec4(0.,0.,0.,1.);
    }
}

vec3 n(vec3 p)
{
  vec2 eps = vec2(0.00001, 0.); // eps defines hot smooth the normals are
  return normalize(vec3(map(p+eps.xyy).x - map(p-eps.xyy).x, map(p+eps.yxy).x - map(p-eps.yxy).x, map(p+eps.yyx).x - map(p-eps.yyx).x));
}

float light(vec3 p)
{
  	vec3 l = vec3(7.*cos(iTime*.5),8.*sin(iTime*.4),-4.);
  	return dot(n(p), normalize(l))*.5+.5;
}

vec3 background(vec2 uv)
{
    vec2 bguv = uv+.01*sin(209.*uv);
    return vec3(.2,.2,.3) * (mod(bguv.y+bguv.x*.3*cos(iTime+bguv.y*6.),(-bguv.x)*.7)*.9+.95);
}

vec4 march(vec2 uv)
{
  vec3 ro = vec3(0,0,-2.9); // ray origin
  vec3 rd = normalize(vec3(uv, 1.)); // ray direction / cam direction
  ro.x += .73*sin(iTime*.62); // simple camera movement and alignment
  ro.y -= .03;

  vec3 p = ro; // current point



  bool hit = false;
  vec3 color = vec3(1.);
  vec3 bounce_pos = vec3(0);
  vec4 bounce_material = vec4(0);

  for(float i=0.;i<100.; i++) // trace steps
  { 
    // catch wandering rays
    if ((abs(p.z) > 3.) || (abs(p.y) > 3.) || (abs(p.x) > 3.))
    {
    	break;
    }      

  	vec2 tracehit = map(p); // get SDF (x = distance, y = object id)

    if (abs(tracehit.x)<0.001)
    {
        vec4 material = getMaterial(int(tracehit.y));

        if (hit == false)
        {
            hit = true;
            bounce_pos = p;

            color = mix(vec3(1.), vec3(light(p)), material.y);
            color *= mix(vec3(1.), texture(iChannel0, cos(p.xy)+p.yz+sin(p.zx)).xyz, material.z);
            color *= (1.-material.w);
            if (material.x > 0.)
            {
            	rd = reflect(rd,n(p));
            	p += rd * 0.0011;
                bounce_material = material;
            	continue;
            }
            break;
        } else {
            // bounce hit
            vec3 bounce_color = mix(vec3(1.), vec3(light(p)), material.y);
            bounce_color *= mix(vec3(1.), texture(iChannel0, cos(p.xy)+p.yz+sin(p.zx)).xyz, material.z);
            bounce_color *= (1.-material.w);
            float bounce_distance = 1. - clamp(length(bounce_pos-p),0.,.75);
            color = mix(color, bounce_color, bounce_material.x * bounce_distance); 
            break;
        }
    }
  	p += tracehit.x * rd;
  }

  if (!hit)
  {
      color = background(uv);
  }

  return vec4(color,1);
}

float random (in vec2 st) {
    return fract(sin(dot(st.xy,vec2(12.98 *iTime,78.233)))*43758.5453123);
