    fragColor = clamp(fragColor, 0.,1.);
}

const vec3 green = vec3(0.137, 0.658, 0.247);

// See shorter contributed versions in the comments :)

// v0.2
float xr(vec2 pos, float aa) {
    float w = 0.077;
    float h = 0.639;
    float tilt = 1.152;
    // xy symmetry
    pos = abs(pos);
    return 1.0 - min(
        // circle
        smoothstep(w-aa, w, abs(length(pos)-1.0+w)) - 
        // black sand clock
        smoothstep(pos.y-aa, pos.y, h) * 
        smoothstep(pos.x-aa, pos.x, pos.y / tilt + .11) *
        // green holes in sand clock
        (1.0 - 
        smoothstep(pos.y-aa, pos.y, h-w*2.) * 
        smoothstep(pos.x+w*3.-aa, pos.x+w*3., pos.y / tilt + .12)
         )
    , 1.);    
}

void mainImage( out vec4 O, in vec2 p ) {
    vec2 uv = 1.1 * ( 2. * p - iResolution.xy ) / iResolution.y;

    // wavy
    if(iMouse.z > 0.0) {
    	uv += 0.03 * sin(uv.y * 3. + iTime * 1.) * 
                     sin(uv.x * 2. - iTime * 2.);
    }

    // noisy
    vec3 n = vec3(fract(sin(uv.y+iTime)*1984.0) > 0.9 ? 0.2 : 0.0);

    O = vec4(mix(green, n, xr(uv, 3./iResolution.y)), 1.0);
}

