    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
	vec2 mouseUV=(2.*iMouse.xy-iResolution.xy)/iResolution.y;
    // Time varying pixel color
    vec2 s[3];
    s[0]=mouseUV;
	s[1]=vec2(-1,0)*sin(iTime*0.5);
    s[2]=vec2(1,0)*sin(iTime*0.5);
    float t=smoothstep(0.2,0.7,circle(uv-s[1],0.1));
    t+=smoothstep(0.2,0.7,circle(uv-s[0],0.1));
    t+=smoothstep(0.2,0.7,circle(uv-s[2],0.1));
    // Output to screen
    fragColor = vec4(t*sin(iTime*2.),t,t*cos(iTime),1.0);
}

