// Returns 1 if the components of v are within [0, 1], 0 otherwise. 
float zeroToOne(vec2 v) { return v.x>=0. && v.x<=1. && v.y>=0. && v.y<=1. ? 1. : 0.; }
float inRange(vec2 v, vec2 bl, vec2 tr) { return v.x>=bl.x && v.x<=tr.x && v.y>=bl.y && v.y<=tr.y ? 1. : 0.; }

// Returns a rotation matrix which will rotate a vector clockwise by angle randians. 
mat2 rotate2d(float angle)
{
    return mat2(cos(angle), -sin(angle),
                sin(angle), cos(angle));
}

float ellipse(vec2 uv, vec2 center, float semimajor, float semiminor)
{
    float theta = acos((uv.x-center.x)/semimajor);
	if(uv.y < center.y)
        theta = 2.*pi - theta;

    float x = center.x + semimajor*cos(theta);
    float y = center.y + semiminor*sin(theta);

    float val = pow(uv.x-center.x, 2.)/pow(semimajor, 2.) + pow(uv.y-center.y, 2.) / pow(semiminor, 2.);
    return step(0., 1.-val);
}

float circle(vec2 uv, vec2 center, float radius) { return distance(uv, center) <= radius ? 1. : 0.;} 


