// Demonstrate the approximation of a function with a finite Fourier series

// plotter forked from https://www.shadertoy.com/view/4tB3WV

// some constants
#define PI 3.14159265359

#define BLACK vec3(0.0)
#define WHITE vec3(1.0)

// dark and light colors from Solarized (https://ethanschoonover.com/solarized/)
#define DARK vec3(0., 0.169, 0.212)
#define LIGHT vec3(0.992, 0.965, 0.89)
#define GRAY vec3(0.396, 0.482, 0.514)

// Solarized "accent colors"
#define ORANGE vec3(0.796, 0.294, 0.086)
#define GREEN vec3(0.522, 0.6, 0.)

// comment out to switch to light mode
#define DARK_MODE

// XY range of the display.
#define DISP_SCALE 9.0 

// Line thickness (in pixels).
#define LINE_SIZE 2.0

// Tick thickness (in pixels).
#define TICK_SIZE 1.0

// Tick length.
#define TICK_LENGTH 0.025 * DISP_SCALE

// Grid line & axis thickness (in pixels).
#define GRID_LINE_SIZE 1.0
#define GRID_AXIS_SIZE 2.0

// Number of grid lines per unit.
#define GRID_LINES 1.0

const vec2 GRADH = vec2(0.01, 0);

// central difference
#define GRAD(f, p) (0.5 * vec2(f(p - GRADH.xy) - f(p + GRADH.xy), f(p - GRADH.yx) - f(p + GRADH.yx)) / GRADH.xx)

// PLOT(Function, Color, Destination, Screen Position)
#define PLOT(f, c, d, p) d = mix(c, d, smoothstep(0.0, (LINE_SIZE / iResolution.y * DISP_SCALE), abs(f(p) / length(GRAD(f, p)))))

// maximum supported series terms; increase for difficult examples, but beware of the Gibbs phenomenon
#define MAX_FOURIER_ORDER 12

// number of terms of Fourier series; 8 terms gives a good approximation for this example
#define TERMS 4

// squaring
#define sqr(a) ((a) * (a))

// Example function from Neuwirth; https://doi.org/10.2307/2690622

float Wave(vec2 p)
{
	float q = 0.5;

    float y = sin(p.x)/(1.0 + sqr(q) - 2.0 * q * cos(p.x));

	return p.y - y;
}

// Fourier coefficients of example function

float ck(int k)
{
    float q = 0.5;
    int ord = 1 + int(mod(0.05 * iTime + 0.5, 8.0));

    return ((1 <= k && k <= ord) ? (pow(q, float(k) - 1.0)) : (0.0));
}

// Goertzel-Reinsch algorithm for summing trigonometric (Fourier) series;
// pp. 90-91 of Bulirsch and Stoer, Introduction to Numerical Analysis 3rd ed.

float fsum(bool cc, float x)
{
    float cx = cos(x);
    float h = 0.0, u = 0.0;

    float sg = (cx > 0.0) ? 1.0 : -1.0;
    float lm = 4.0 * ((sg > 0.0) ? (-sqr(sin(0.5 * x))) : (sqr(cos(0.5 * x))));

    for (int k = MAX_FOURIER_ORDER; k >= 0; k--)
    {
        u = h + sg * u;
        h = ck(k) + sg * h + lm * u;
    }

    return ((cc) ? (h - 0.5 * lm * u) : (u * sin(x)));
}

float Fourier(vec2 p)
{	
	return p.y - fsum(false, p.x);
}

#define MAKETICK(c) (clamp(1.0 + 0.5 * TICK_LENGTH - abs(c), 0.0, 1.0))

float grid(vec2 p, bool showAxes, bool showTicks, bool showGrid)
{
	vec2 uv = mod(p, 1.0 / GRID_LINES);
	float halfScale = 0.5 / GRID_LINES;

    float grid = 1.0, tick = 1.0, axis = 1.0;

    if (showTicks) {
	float tickRad = (TICK_SIZE / iResolution.y) * DISP_SCALE;
	tick = halfScale - max( MAKETICK(p.y) * abs(uv.x - halfScale), MAKETICK(p.x) * abs(uv.y - halfScale));
	tick = smoothstep(0.0, tickRad, tick);
    }

    if (showGrid) {
	float gridRad = (GRID_LINE_SIZE / iResolution.y) * DISP_SCALE;
	grid = halfScale - max(abs(uv.x - halfScale), abs(uv.y - halfScale));
	grid = smoothstep(0.0, gridRad, grid);
    }

    if (showAxes) {
	float axisRad = (GRID_AXIS_SIZE / iResolution.y) * DISP_SCALE;
	axis = min(abs(p.x), abs(p.y));
	axis = smoothstep(axisRad - 0.05, axisRad, axis);
    }

	return min(tick, min(grid, axis));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 aspect = iResolution.xy / iResolution.y;
	vec2 uv = ( fragCoord.xy / iResolution.y ) - 0.5 * aspect;
	uv *= DISP_SCALE;

    vec3 col = WHITE;

    // set up axes and ticks
    #ifdef DARK_MODE
    col = mix(GRAY, DARK, grid(uv, true, true, false));
    #else
    col = mix(GRAY, LIGHT, grid(uv, true, true, false));
    #endif

    // offset the curves every few seconds
    float offset = 0.25 * DISP_SCALE * clamp(2.0 * cos(0.1 * PI * iTime), 0.0, 1.0);

    PLOT(Wave, GREEN, col, uv + vec2(iTime, -offset));

    PLOT(Fourier, ORANGE, col, uv + vec2(iTime, offset));

	fragColor = vec4( col, 1.0 );
}

