//-----------------CONSTANTS MACROS-----------------

#define PI 3.14159265359
#define E 2.7182818284
#define GR 1.61803398875

//-----------------UTILITY MACROS-----------------

#define time ((sin(float(__LINE__))*GR/2.0/PI+GR/PI)*iTime+100.0)
#define saw(x) (acos(cos(x))/PI)
#define flux(x) (vec3(cos(x),cos(4.0*PI/3.0+x),cos(2.0*PI/3.0+x))*.5+.5)
#define rotatePoint(p,n,theta) (p*cos(theta)+cross(n,p)*sin(theta)+n*dot(p,n) *(1.0-cos(theta)))
#define carpet_floor(x) (floor(x)+pow((fract(x)), 10.))

void ray_ray(in vec3 eye, in vec3 ray1, in vec3 p1, in vec3 ray2, out float t1, out float t2){


    float a = dot(ray1,ray1);
    float b = dot(ray1,ray2);
    float c = dot(ray2,ray2);
    float d = dot(ray1,eye-p1);
    float e = dot(eye-p1,ray2);

    t1 = (b*e-c*d)/(a*c-b*b);
    t2 = (a*e-b*d)/(a*c-b*b);
}


#define iTime (iTime/GR)

#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy*2.0-1.0;
    uv.x *= iResolution.x/iResolution.y;

	//vec3 eye = vec3(0.0, 0.0, 3.0);
	vec3 eye = vec3(cos(iTime), sin(iTime*.5), sin(iTime))*2.0;
    vec3 look = vec3(0.0, 0.0, 0.0);
    vec3 up = vec3(0.0, 1.0, 0.0);
    vec3 foward = normalize(look-eye);
    vec3 right = normalize(cross(foward, up));
    up = normalize(cross(right, foward));
    vec3 ray = normalize(foward+uv.x*right+uv.y*up);

    fragColor = vec4(0.0);

 	const float outerCount = 4.0;
 	const float innerCount = 4.0;

    float map = 0.0;
    float sum = 0.0;

    float min_scale = 1./GR;
    float max_scale = 1.;
    float scale_steps = 4.;
   	for(float s = 0.; s < scale_steps; s+=1.)
    {
        float scale_iteration = (s/scale_steps);
        float scalar = min_scale+scale_iteration*(max_scale-sin(time*scale_iteration)*min_scale);
    for(float i = 0.0; i < outerCount; i+=1.0)
    {

        float theta1 = i/outerCount*PI;

        for(float j = 0.0; j < innerCount; j+=1.0)
        {

            float theta2 = j/innerCount*PI*2.;


            float omega1 = theta1+PI/2.*(carpet_floor(scale_iteration*time));
            float omega2 = theta2+PI*carpet_floor(scale_iteration*time)
                						+carpet_floor(time)*PI;//*sign(cos(i*PI));

       	 	vec3 p1 = vec3(cos(omega1)*sin(omega2),
                           sin(omega1)*sin(omega2),
                           cos(omega2))*scalar;

            omega2 += PI/outerCount*2.;
       	 	vec3 p2 = vec3(cos(omega1)*sin(omega2),
                           sin(omega1)*sin(omega2),
                           cos(omega2))*scalar;

            omega2 -= PI/outerCount*2.;
            omega1 += PI/outerCount*2.;
       	 	vec3 p3 = vec3(cos(omega1)*sin(omega2),
                           sin(omega1)*sin(omega2),
                           cos(omega2))*scalar;

            float t1;
            float t2;
            vec3 ray2 = normalize(p2-p1);

            ray_ray(eye, ray, p1, ray2, t1, t2);

            float dist = length((eye+ray*t1)-(p1+ray2*t2));

            float lineWidth = 5.0/min(iResolution.x, iResolution.y);

            float lineLength = length(p2-p1);


                float sides = (1.0-smoothstep(0.0, lineWidth, dist));
                float ends = (1.0-smoothstep(1.-lineWidth, 1., (t2)/lineLength))*(1.0-smoothstep(1.-lineWidth, 1., 1.-(t2)/lineLength));
                float line = sides*ends;

                map += line*(1.0+i/innerCount+j/outerCount)/2.0;
                sum += 1.0*line;

            ray2 = normalize(p3-p1);

            ray_ray(eye, ray, p1, ray2, t1, t2);

            dist = length((eye+ray*t1)-(p1+ray2*t2));

            lineLength = length(p3-p1);


                sides = (1.0-smoothstep(0.0, lineWidth, dist));
                ends = (1.0-smoothstep(1.-lineWidth, 1., (t2)/lineLength))*(1.0-smoothstep(1.-lineWidth, 1., 1.-(t2)/lineLength));
                line = sides*ends;

                map += line*(1.0+i/innerCount+j/outerCount)/2.0;
                sum += 1.0*line;
        }
    }
    }
	fragColor = vec4(flux(PI*map/sum+time), 1.0)+clamp(sum-1., 0.0, 1.0);
}

