// Basic domain coloring plot (https://en.wikipedia.org/wiki/Domain_coloring)
// of the elliptic nome w = q(z) (https://en.wikipedia.org/wiki/Nome_(mathematics))

#define PI 3.14159265359
#define SCALE 6.0 // plot scale
#define SPACING 0.0625 // grid line spacing

// from Fabrice Neyret, 
#define cmul(A,B) ( mat2( A, -(A).y, (A).x ) * (B) )
#define cinv(Z) ( vec2( (Z).x, -(Z).y ) / dot(Z, Z) ) 
#define cdiv(A,B) cmul( A, cinv(B) )

// Schlick bias function, from http://dept-info.labri.u-bordeaux.fr/~schlick/DOC/gem2.ps.gz
float bias( float a, float x )
{
    return x/((1.0/a - 2.0) * (1.0 - x) + 1.0);
}

// biased sawtooth
float my_saw( float x, float p )
{
    float xs = mod(x, 1.0);
    float xh = clamp(xs, 0.0, p);
    return 0.5 + 0.5 * bias(0.95, xh) * (1.0 - smoothstep(p, 1.0, xs));
}

// modified version of Inigo Quilez's method at https://www.shadertoy.com/view/MsS3Wc
// using "rational smoothstep" from https://tpfto.wordpress.com/2019/03/28/on-a-rational-variant-of-smoothstep/
vec3 smooth_hue( float h )
{
    vec3 rgb = clamp( abs(mod(6.0 * h + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
    return rgb * rgb * rgb/(1.0 - 3.0 * rgb * (1.0 - rgb));
}

// modified DLMF coloring, adapted from https://www.shadertoy.com/view/WtXGWN
vec3 smooth_dlmf( float h )
{
    vec3 rgb = clamp( vec3(1., -1., -1.) * abs((vec3(8., 4., 8.) * mod(h, 1.0) - vec3(4.5, 1.5, 5.5))) + vec3(-1.5, 1.5, 2.5), 0.0, 1.0 );
	return rgb * rgb * rgb/(1.0 - 3.0 * rgb * (1.0 - rgb));
}

// complex square root, from Numerical Recipes

vec2 sqrtz( in vec2 z )
{
	float ar = abs(z.x), ai = abs(z.y);

    float w = (dot(z, z) == 0.0) ? 0.0 : ( (ar >= ai) ? (sqrt(ar) * sqrt(0.5 * (1.0 + sqrt(1.0 + (ai * ai)/(ar * ar))))) : (sqrt(ai) * sqrt(0.5 * (ar/ai + sqrt(1.0 + (ar * ar)/(ai * ai))))));

    return ((w == 0.0) ? vec2(0.0) : ((z.x >= 0.0 ? vec2(w, 0.5 * z.y/w) : vec2(0.5*ai/w, ((z.y >= 0.0) ? w : -w)))));
}

// complex exponential

vec2 expz( in vec2 z )
{
	return exp(z.x) * vec2(cos(z.y), sin(z.y));
}

// complete elliptic integral K(m) via arithmetic-geometric mean (AGM) iteration 

vec2 ellK( in vec2 z )
{
    vec2 agA = vec2(1.0, 0.0), agB = sqrtz(agA - z);
    vec2 tmp = vec2(0.0), h = tmp;

    for( int i = 0; i <= 9; i++ )
    {
        tmp = sqrtz(cmul(agA, agB));
        h = 0.5 * (agB - agA);
        agA += h;
        agB = tmp;
        if (length(h) < 1.0e-6) break;
    }

    return (PI * cinv(agA + agB));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 aspect = iResolution.xy / iResolution.y;
    vec2 z = ( fragCoord.xy / iResolution.y ) - 0.5 * aspect;
    z *= SCALE;

    vec2 w = expz(-PI * cdiv(ellK(vec2(1.0, 0.0) - z), ellK(z))); // elliptic nome q(m)
    // vec2 w = ellK(z); // uncomment if you just want K(m)
    float ph = atan(w.y, w.x);
    float lm = log(0.0001 + length(w));

    vec3 c = vec3(1.0);
    c = smooth_dlmf(0.5 * (ph / PI));
    // uncomment for HSV version 
    // c = smooth_hue(0.5 * (ph / PI));

    c *= mix(1.0, my_saw((0.5 * (lm/PI))/SPACING, 0.95) * my_saw((0.5 * (ph / PI))/SPACING, 0.95), 0.5 + 0.5 * cos(iTime));
    fragColor = vec4(c, 1.0);
}



void mainImage(out vec4 FragColor, in vec2 FragCoord){
    vec2 uv = FragCoord.xy/iResolution.xy;
    uv = 2.*uv - 1.;
    uv.x *= iResolution.x / iResolution.y;


    float da = iTime/(10.);
    vec2 pts = vec2(sin(da*2.), cos(da*2.));

    float d = length(cos(  abs((abs(uv)+atan(da))*pts))*sin(atan(abs(uv))));

    d = atan(d) - cos(da+d*d)*sin(da - d*d);
    d -= atan(cos(da)*abs(  fract(d*d)-da) + 0.5);

    float fill = step(tan(fract(abs(d)-0.5)*da),0.2);


    vec3 col = vec3(fill);

    col += vec3(0., 0., 1.);
    col *= vec3(1., 0., 0.2);
    FragColor = vec4(col, 1.);
	da += uv.x;
}

#define ITERATIONS 20.0
#define POWER 3

#define JULIA

#define SMOOTHFRACT

vec2 cSquare(vec2 c)
{
    return vec2(c.x * c.x - c.y * c.y, 2.0 * c.x * c.y);
}

vec2 cMul(vec2 a, vec2 b)
{
 	return vec2(a.x * b.x - a.y * b.y, a.y * b.x + a.x * b.y);   
}

vec2 cPow(vec2 c, int p)
{
	vec2 res = c;
 	for (int i = 1; i < p; i++)
    {
    	res = cMul(res, c);
    }
    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy * 2.0) - 1.0;
    uv.y /= iResolution.x / iResolution.y;

    #ifdef JULIA
    vec2 z = uv * 2.0;
    vec2 c = vec2(sin(iTime * 0.5) * 0.8, cos((iTime * 0.1 + sin(iTime * 0.21)) * 0.5));
    #else
    vec2 z = vec2(0.0);
    vec2 c = (uv * 2.5);
    #endif

    float i;
    for (i = 0.0; i < ITERATIONS; i++)
    {
        z = cPow(z, POWER) + c;
        if(dot(z, z) > 4.0)
            break;
    }

    #ifdef SMOOTHFRACT
    float log2=0.69314718056;
    float L = log(dot(z, z))/2.0;
    float nu = log(L/log2)/log2;
    i+=1.0-nu;
    #endif

    float dist = float(i) / 10.0;

    fragColor = vec4(sin(dist * 2.0));
}

#define SIZE 20.
#define BLACK_COL vec3(32,43,51)/255.
#define WHITE_COL vec3(235,241,245)/255.

// Get random value
float rand(vec2 co) { 
    return fract(sin(dot(co.xy , vec2(12.9898, 78.233))) * 43758.5453);
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{       
    vec2 uv = fragCoord.xy/iResolution.y;
    float smf = 1./(iResolution.y) * SIZE * 2.; // smooth factor

    vec2 ruv = uv*SIZE;    
    vec2 id = ceil(ruv);       

    ruv.y -= iTime*2. * (rand(vec2(id.x))*0.5+.5); // move up
    ruv.y += ceil(mod(id.x, 2.))*0.3 * iTime; // every 2nd column always move faster 
    vec2 guv = fract(ruv) - 0.5; // ceneterize guv   

    ruv = ceil(ruv);    
    float g = length(guv);

    float v = rand(ruv)*0.5; // random bubble size     
    v *= step(0.1, v); // remove too small bubbles
    float m = smoothstep(v,v-smf, g);
    v*=.8; // bubble inner empty space
    m -= smoothstep(v,v-smf, g);

    vec3 col = mix(BLACK_COL, WHITE_COL, m); // final color

    fragColor = vec4(col,1.0);
}

float hash( float n )
			{
			    return fract(sin(n)*43758.5453);
			}

			float noise( vec3 x )
			{
			    // The noise function returns a value in the range -1.0f -> 1.0f

			    vec3 p = floor(x);
			    vec3 f = fract(x);

			    f       = f*f*(3.0-2.0*f);
			    float n = p.x + p.y*57.0 + 113.0*p.z;

			    return mix(mix(mix( hash(n+0.0), hash(n+1.0),f.x),
			                   mix( hash(n+57.0), hash(n+58.0),f.x),f.y),
			               mix(mix( hash(n+113.0), hash(n+114.0),f.x),
			                   mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z)-.5;
			}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
