					col.b+=noise(uv.yyx*(12.0+i2)+col.rgb+t*sign(sin(i2/3.0)));
				}


				for(int i = 0; i < 16; i++){
                    float i2 = float(i)*1.0;
					col.r+=noise(uv.xyy*(32.0)+col.rgb+t*sign(sin(i2/3.0)));
					col.g+=noise(uv.xyx*(32.0)+col.rgb+t*sign(sin(i2/3.0)));
					col.b+=noise(uv.yyx*(32.0)+col.rgb+t*sign(sin(i2/3.0)));
				}
				col.rgb/=32.0;
				col.rgb=mix(col.rgb,normalize(col.rgb)*2.0,1.0);
				col.rgb+=.3;



    // Output to screen
    fragColor = vec4(col,1.0);
}

float wave(vec2 xy, float wavelength, float speed){
    float x = xy.x;
    float y = xy.y;

    return sin(sqrt(x*x + y*y)/wavelength - iTime*speed);
}
vec4 view(float x, float y, float z){
    return vec4((vec3(x,y,z)+1.)/2., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = (4.*fragCoord.xy - iResolution.xy)/50.;
    vec2 x = vec2(0,0);
    vec2 y = vec2(1,1);

    float a = wave(xy - x, 0.2, 1.);
    float b = wave(xy + x, 0.5, 1.);

	fragColor = view(0.,0.,a+b);
}

float N2(vec2 p)
{	// Dave Hoskins - https://www.shadertoy.com/view/4djSRW
	vec3 p3  = fract(vec3(p.xyx) * vec3(443.897, 441.423, 437.195));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

//applied n2 to kinda sorta get a random vec2
vec2 randVec2(vec2 p)
{
	return vec2(N2(p), N2(p.yx)) - .5;
}


vec2 getCellStar(vec2 cellID)
{
    vec2 temp = randVec2(cellID);
 	return vec2(sin(iTime * temp.x), cos(iTime * temp.y)) * .4;	//moving
    return vec2(temp.x, temp.y) * .8;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 uv = fragCoord.xy / iResolution.xy;
    //uv *= 2.f;
    //uv -= 1.f;
    //uv.x *= iResolution.x/iResolution.y;

    //thanks Fabrice
    vec2 uv = (2.* fragCoord -  iResolution.xy ) /iResolution.y;
    uv *= 5.;

    vec2 gridCell = floor(uv);
    vec2 gridOffset = fract(uv) - .5;

    vec2 cellPoint = getCellStar(gridCell);

    vec2 rekt = gridOffset - cellPoint;	
    float dToPoint = max(rekt.x, rekt.y);

	//dToPoint = sin(iTime) > 0. ? dToPoint :smoothstep(.1, .05, dToPoint);
    //dToPoint = smoothstep(sin(iTime) * .3, .05, dToPoint);
	dToPoint = mix(dToPoint, smoothstep(.1, .05, dToPoint), sin(iTime));

    fragColor = vec4(dToPoint);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

