        if(uv.x*2.0+0.32 > uv.y && uv.x*2.0+0.32 < uv.y+0.03 && uv.y>0.5)
            fragColor = vec4(col,1.0);


        if(-1.0*uv.x*2.0+0.71 > uv.y && -1.0*uv.x*2.0+0.71 < uv.y+0.03 && uv.y<0.5)
            fragColor = vec4(col,1.0);


        if(uv.x<=0.3 && uv.x>=0.29)
