        if(uv.x<=0.5 && uv.x>=0.49)
        	fragColor = vec4(col,1.0);

        if(uv.x<=0.6 && uv.x>=0.59)
        	fragColor = vec4(col,1.0);

        if(pow(uv.x-0.6,2.0)+pow(uv.y-0.659,2.0)<0.02 && pow(uv.x-0.6,2.0)+pow(uv.y-0.659,2.0)>0.017 && uv.x>0.6)
            fragColor = vec4(col,1.0);

        if(5.0*uv.x - 3.45 > uv.y && 5.0*uv.x - 3.45 < uv.y+0.05)
        	fragColor = vec4(col,1.0);

        if(-1.0*5.0*uv.x +5.1 > uv.y && -1.0*5.0*uv.x +5.1 < uv.y+0.05)
        	fragColor = vec4(col,1.0);

        if(uv.y<0.51 && uv.y>0.49 && uv.x>0.791 && uv.x<0.919)
        	fragColor = vec4(col,1.0);

    }
    else
    {
        fragColor = vec4(background_color,1.0);
    }




}

// "Waterworld City" by dr2 - 2019
