#define AA    0  // optional antialiasing

float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrCylDf (vec3 p, float r, float h);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
float PrCapsDf (vec3 p, float r, float h);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
float Minv3 (vec3 p);
float Hashfv2 (vec2 p);
vec2 Hashv2v2 (vec2 p);
float Fbm1 (float p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

#define N_BOAT 2

mat3 boatMat[N_BOAT], bMat;
vec3 boatPos[N_BOAT], bPos, sunDir, qHit;
vec2 gId, obOff;
float boatAng[N_BOAT], bAng, dstFar, tCur, obSzFac, hgSize, nWin;
int idObj, idObjGrp;
const float pi = 3.14159, sqrt3 = 1.7320508;

vec3 TrackPath (float t)
{
  return vec3 (7. * cos (0.06 * t) + 0.1 * t, 0., t);
}

vec3 TrackVel (float t)
{
  return vec3 (-0.06 * 7. * sin (0.06 * t) + 0.1, 0., 1.);
}

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjCDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  if (obSzFac > 0.) {
    dMin /= obSzFac;
    p.xz -= HexToPix (gId * hgSize) + obOff;
    p /= obSzFac;
    p.y -= 2.2;
    q = p;
    d = PrCylDf (q.xzy, 0.25 * (0.7 + 0.1 * q.y * q.y), 2.2);
    DMINQ (1);
    p.y -= 2.4 - 0.01;
    q = p;
    d = PrRoundCylDf (q.xzy, 0.8, 0.1, 0.4);
    DMINQ (2);
    dMin *= obSzFac;
  }
  return dMin;
}

float GrndHt (vec2 p)
{
  float h, s;
  h = Fbm2 (0.5 * p) - 0.5;
  s = 1. - abs (p.x - TrackPath (p.y).x) / 4.;
  if (s > 0.) h = max (h - s * s, -0.5);
  return h;
}

void SetTwrConf ()
{
  vec2 g, w;
  g = HexToPix (gId * hgSize);
  if (GrndHt (g) < 0.1 || Hashfv2 (37. * gId + 99.) < 0.1) obSzFac = 0.;
  else {
    obSzFac = hgSize * 0.5 * sqrt3 * (1. - 0.7 * Hashfv2 (17. * gId + 99.));
    w = Hashv2v2 (73. * gId + 77.);
    obOff = hgSize * max (0., 0.4 * sqrt3 - obSzFac) * w.x * sin (2. * pi * w.y + vec2 (0.5 * pi, 0.));
    nWin = 4. + floor (8. * w.y);
  }
}

float ObjCRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP;
  float dHit, d, s, eps;
  eps = 0.0005;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSize / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2])) / hgSize;
  pM = HexToPix (PixToHex (ro.xz / hgSize));
  gIdP = vec2 (-99.);
  dHit = 0.;
  for (int j = 0; j < 200; j ++) {
    hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
    s = Minv3 (hv);
    p = ro + dHit * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId.x != gIdP.x || gId.y != gIdP.y) {
      gIdP = gId;
      SetTwrConf ();
    }
    d = ObjCDf (p);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
    }
    if (d < eps || dHit > dstFar || p.y < -0.1 || rd.y > 0. && p.y > 10.) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjCNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (- ObjCDf (p + e.xxx), ObjCDf (p + e.xyy), ObjCDf (p + e.yxy), ObjCDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjCSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 gIdP;
  float sh, d, h;
  sh = 1.;
  d = 0.05;
  gIdP = vec2 (-99.);
  for (int j = 0; j < 30; j ++) {
    p = ro + d * rd;
    gId = PixToHex (p.xz / hgSize);
    if (gId.x != gIdP.x || gId.y != gIdP.y) {
      gIdP = gId;
      SetTwrConf ();
    }
    h = ObjCDf (p);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += clamp (h, 0.05, 0.3);
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

float GrndRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = -0.1 - ro.y / rd.y;
  for (int j = 0; j < 100; j ++) {
    p = ro + dHit * rd;
    d = p.y + 0.01 * step (GrndHt (p.xz), -0.01);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

float BoatDf (vec3 p, float dMin)
{
  vec3 q;
  float d;
  p.y -= 0.7;
  q = p;
  d = max (max (PrRoundCylDf (q, 1.2, 2., 3.5),
     - max (PrRoundCylDf (q - vec3 (0., 0.1, 0.), 1.15, 2., 3.5),
     abs (q.y) - 0.1)), abs (q.y + 0.1) - 0.1);
  q.y -= -0.2;
  d = max (SmoothMin (d, max (PrRoundCylDf (q, 1., 2., 3.3), q.y), 0.1), q.z - 2.);
  DMINQ (idObjGrp + 1);
  q = p;
  q.yz -= vec2 (-0.5, -0.2);
  d = max (PrRoundCylDf (q, 1., 1.1, 2.3), max (0.4 - q.y, q.z - 1.2));
  DMINQ (idObjGrp + 2);
  q = p;
  q.yz -= vec2 (0.8, 0.5);
  d = PrCylDf (q.xzy, 0.04, 0.3);
  DMINQ (idObjGrp + 3);
  return dMin;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, dLim;
  const float bSzFac = 0.3;
  dLim = 0.5 / bSzFac;
  dMin = dstFar;
  dMin /= bSzFac;
  for (int k = 0; k < N_BOAT; k ++) {
    q = p - boatPos[k];
    idObjGrp = (k + 1) * 256;
    d = PrCylDf (q.xzy, 2., 2.);
    dMin = (d < dLim) ? BoatDf (boatMat[k] * q / bSzFac, dMin) : min (dMin, d);
  }
  return bSzFac * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 100; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.05;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += clamp (h, 0.01, 0.3);
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol;
  vec2 q;
  float f, fd, ff;
  q = 0.005 * (ro.xz + tCur * vec2 (0.5, 2.) + ((200. - ro.y) / rd.y) * rd.xz);
  ff = Fbm2 (q);
  f = smoothstep (0.1, 0.8, ff);
  fd = smoothstep (0.1, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
  clCol = (0.8 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
     smoothstep (0., 0.05, abs (fd)));
  fd = smoothstep (0.01, 0.1, rd.y);
  col = mix (mix (vec3 (0.8, 0.8, 0.75), vec3 (0.4, 0.5, 0.8), 0.3 + 0.7 * fd), clCol,
     0.1 + 0.9 * f * fd);
  return col;
}

float WakeFac (vec3 row)
{
  vec2 tw[N_BOAT], twa;
  float twLen[N_BOAT], wkFac, ba;
  for (int k = 0; k < N_BOAT; k ++) {
    tw[k] = row.xz - (boatPos[k].xz - Rot2D (vec2 (0., 2.5), boatAng[k]));
    twLen[k] = length (tw[k]);
  }
  if (twLen[0] < twLen[1]) {
    twa = tw[0];
    ba = boatAng[0];
  } else {
    twa = tw[1];
    ba = boatAng[1];
  }
  twa = Rot2D (twa, - ba);
  wkFac = 0.;
  if (length (twa * vec2 (1., 0.5)) < 1.) wkFac =
     clamp (1. - 2. * abs (twa.x), 0., 1.) * clamp (0.5 + twa.y, 0., 1.);
  return wkFac;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, row, rdd;
  float dstObj, dstObjC, dstObjM, dstGrnd, wkFac, sh, a, nDotS;
  int idObjC, ig, id;
  bool wtRefl, glRefl;
  dstObjC = ObjCRay (ro, rd);
  idObjC = idObj;
  dstObjM = ObjRay (ro, rd);
  dstGrnd = (rd.y < 0.) ? GrndRay (ro, rd) : dstFar;
  wtRefl = false;
  glRefl = false;
  wkFac = 0.;
  dstObj = min (dstObjM, dstObjC);
  if (dstGrnd < min (dstObj, dstFar)) {
    row = ro + dstGrnd * rd;
    if (row.y < 0.) {
      ro = row;
      wkFac = WakeFac (row);
      row.xz += 0.2 * tCur;
      if (wkFac > 0.) vn = VaryNf (16. * row, vec3 (0., 1., 0.), 2. * wkFac);
      else vn = VaryNf (2. * row, vec3 (0., 1., 0.), 0.05 *
         (1. - smoothstep (0.1, 0.4, dstGrnd / dstFar)));
      row = ro;
      rd = reflect (rd, vn);
      ro += 0.01 * rd;
      dstObjC = ObjCRay (ro, rd);
      idObjC = idObj;
      dstObjM = ObjRay (ro, rd);
      dstGrnd = dstFar;
      dstObj = min (dstObjM, dstObjC);
      wtRefl = true;
    }
  }
  if (min (dstObj, dstGrnd) < dstFar) {
    if (dstObj < dstGrnd) {
      if (dstObjM < dstObjC) {
        ro += dstObjM * rd;
        vn = ObjNf (ro);
        ig = idObj / 256;
        id = idObj - 256 * ig;
        if (id == 1) {
          if (abs (qHit.y - 0.105) < 0.005) col4 = vec4 (0.8, 0.6, 0.4, 0.1) *
             (1. - 0.4 * SmoothBump (0.42, 0.58, 0.05, mod (7. * qHit.x, 1.)));
          else col4 = (qHit.y > -0.3) ? ((ig == 1) ? vec4 (0.9, 0.9, 0.3, 0.3) :
             vec4 (0.9, 0.3, 0.3, 0.3)) : vec4 (0.7, 0.7, 0.8, 0.1);
        } else if (id == 2) {
          col4 = (abs (abs (qHit.x) - 0.4) < 0.36 && qHit.y > 0.45 && 
             length (vec2 (abs (qHit.x) - 0.1, qHit.y - 0.2)) < 0.7 ||
             abs (abs (qHit.z + 0.2) - 0.6) < 0.5 && abs (qHit.y - 0.65) < 0.2) ?
             vec4 (0., 0., 0.1, -1.) : vec4 (0.9, 0.9, 1., 0.5);
        } else if (id == 3) col4 = vec4 (1., 1., 1., 0.2);
        if (col4.a == -1.) {
          col4.a = 0.1;
          glRefl = true;
        }
        sh = ObjSShadow (ro, sunDir);
     } else {
        ro += dstObjC * rd;
        gId = PixToHex (ro.xz / hgSize);
        vn = ObjCNf (ro);
        if (idObjC == 1) {
          col4 = vec4 (0.8, 0.8, 0.2, 0.2);
        } else if (idObjC == 2) {
          col4 = vec4 (0.7, 0.4, 0.1, 0.1);
          a = mod (nWin * atan (qHit.z, - qHit.x) / pi, 1.);
          if (step (abs (qHit.y), 0.3) * step (a, 0.75) > 0.) {
             if (step (abs (qHit.y), 0.27) * step (a, 0.72) > 0.) {
              col4.rgb = vec3 (0.3, 0.5, 0.3);
              glRefl = true;
            } else col4.rgb *= 1.4;
          }
        }
        sh = ObjCSShadow (ro, sunDir);
      }
    } else {
      ro += dstGrnd * rd;
      vn = vec3 (0., 1., 0.);
      if (dstGrnd < 0.5 * dstFar)
         vn = VaryNf (4. * ro, vn, 1.5 * (1. - smoothstep (0.2, 0.5, dstGrnd / dstFar)));
      col4 = vec4 (vec3 (0., 0.4, 0.) * min (1. - 0.2 * Fbm2 (32. * ro.xz), 1.), 0.1);
      sh = ObjCSShadow (ro, sunDir);
    }
    nDotS = max (dot (vn, sunDir), 0.);
    if (dstObj < dstGrnd) nDotS *= nDotS;
    col = col4.rgb * (0.2 + 0.1 * max (dot (normalize (vec3 (vn.x, 0., vn.z)), - sunDir), 0.) +
       0.8 * sh * nDotS) + col4.a * smoothstep (0.8, 1., sh) *
       pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
    col = mix (col, SkyCol (ro, rd), (wtRefl ? 0.2 + 0.8 * smoothstep (0.4, 0.6, dstObj / dstFar) :
       smoothstep (0.8, 0.9, min (dstGrnd, dstObj) / dstFar)));
    if (glRefl) {
      rdd = reflect (rd, vn);
      rdd.y = abs (rdd.y);
      col = mix (col, 0.8 * SkyCol (ro, rdd), 0.5);
    }
  } else col = SkyCol (ro, rd);
  if (wkFac > 0.) col = mix (col, vec3 (0.8, 0.83, 0.8), wkFac * clamp (0.1 +
     Fbm2 (16. * row.xz), 0., 1.));
  col.rgb *= vec3 (1.1, 0.9, 0.9);
  return clamp (col, 0., 1.);
}

void BoatPM (float t)
{
  vec3 v;
  float c, s, bAz;
  bPos = TrackPath (t);
  bPos.y = 0.;
  bMat[2] = vec3 (Rot2D (vec2 (1., 0.), 0.4 * (Fbm1 (0.2 * t) - 0.5)), 0.);
  bMat[0] = normalize (vec3 (0., 0.1, 1.));
  bMat[1] = cross (bMat[0], bMat[2]);
  v = TrackVel (t);
  bAz = atan (v.z, - v.x);
  bAng = 0.5 * pi - bAz;
  c = cos (bAz);
  s = sin (bAz);
  bMat *= mat3 (c, 0., s, 0., 1., 0., - s, 0., c);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr, dateCur;
  vec3 ro, rd, vd, col;
  vec2 canvas, uv, uvs, ori, ca, sa, mMid, ut, mSize;
  float el, az, asp, winHt, vuId, zmFac, sr, spd;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uvs = uv;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  dateCur = iDate;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  winHt = 0.85;
  mSize = (1./6.) * vec2 (asp, 1.) * winHt;
  mMid = vec2 (asp * (1. - mSize.y) - 0.1 * mSize.y, - winHt + 1.1 * mSize.y);
  ut = abs (uv - mMid) - mSize;
  vuId = 0.;
  if (max (ut.x, ut.y) < 0.) {
    uv = (uv - mMid) / mSize.y;
    vuId = 1.;
  }
  tCur = mod (tCur, 2400.) + 30. * floor (dateCur.w / 7200.);
  hgSize = 1.;
  spd = 2.;
  ro = TrackPath (spd * tCur);
  ro.x += 1. * sin (0.05 * 2. * pi * tCur);
  ro.y = 1. + 2. * Fbm1 (0.1 * tCur);
  vd = TrackVel (spd * tCur);
  el = -0.03 * pi;
  az = atan (vd.x, vd.z);
  if (mPtr.z > 0.) {   
    el += 0.4 * pi * mPtr.y;
    az += 2.2 * pi * mPtr.x;
  }
  for (int k = 0; k < N_BOAT; k ++) {
    BoatPM (spd * tCur + ((vuId == 0. && abs (az) > 0.5 * pi ||
       vuId == 1. && abs (az) < 0.5 * pi) ? -1. : 1.) * (7. * float (k) + 4.5));
    boatPos[k] = bPos;
    boatMat[k] = bMat;
    boatAng[k] = bAng;
    boatPos[k].y = -0.02 + 0.07 * Fbm1 (tCur + 5. * float (k));
  }
  if (vuId == 1.) az += pi;
  ori = vec2 (clamp (el, -0.5 * pi, 0.45 * pi), az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  dstFar = 100.;
  sunDir = normalize (vec3 (0., 1.2, -1.));
  sunDir.xz = Rot2D (sunDir.xz, 0.3 * pi * cos (0.1 * tCur));
  zmFac = 1.5;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  if (abs (uvs.y) < winHt) {
    sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
    for (float a = 0.; a < naa; a ++) {
      rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
         sr * (0.667 * a + 0.5) * pi), zmFac));
      col += (1. / naa) * ShowScene (ro, rd);
    }
  }
  if (max (ut.x, ut.y) < 0. && min (abs (ut.x), abs (ut.y)) * canvas.y < 2.) col = vec3 (0.4, 0.4, 0.);
  if (vuId == 0.) col *= 1. - smoothstep (0.99 * winHt, winHt, abs (uvs.y));
  fragColor = vec4 (col, 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  float dxy, dz;
  dxy = length (p.xy) - r;
  dz = abs (p.z) - h;
  return min (min (max (dxy + rt, dz), max (dxy, dz + rt)), length (vec2 (dxy, dz) + rt) - rt);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

