// How to parametrize a square circle (a circle to a root really) 
// such that it's more or less uniform. The maths can be done but
// for now I quickly eyeballed some smoothstep based reparametrizations.
//
// I provide three different  versions:


// 0 : quartic root, non trigonometric
// 1 : cubic   root, non trigonometric
// 2 : cubic   root,     trigonometric
#define METHOD 2

// Make 0 to see uncorrected result
#define REPARAMETRIZE 1

// Make 16 to see more dramatic effets
#define NUMPOINTS 50

// See also this shader: https://www.shadertoy.com/view/3slGWM




#if METHOD==0
vec2 circle( in float t )
{
    float s = fract(t*4.0);

    #if REPARAMETRIZE==1
    s = s*s*(3.0-2.0*s);  // pick something better
    s = s*s*(3.0-2.0*s);
    s = s*s*(3.0-2.0*s);
    #endif

    vec2 p = pow( vec2(1.0-s,s), vec2(1.0/8.0) );
  //vec2 p = sqrt(sqrt(sqrt(vec2(1.0-s,s))));

    return (t<0.25) ? vec2(-p.y, p.x) :
           (t<0.50) ? vec2(-p.x,-p.y) :
           (t<0.75) ? vec2( p.y,-p.x) : p;
}
#endif

#if METHOD==1
vec2 circle( in float t )
{
    float s = fract(t*4.0);

    #if REPARAMETRIZE==1
    s = s*s*s*(6.0*s*s-15.0*s+10.0); // pick something better
    s = s*s*(3.0-2.0*s);
    #endif

    vec2 p = pow( vec2(1.0-s,s), vec2(1.0/6.0) );

    return (t<0.25) ? vec2(-p.y, p.x) :
           (t<0.50) ? vec2(-p.x,-p.y) :
           (t<0.75) ? vec2( p.y,-p.x) : p;
}
#endif

#if METHOD==2
vec2 circle( in float t )
{
    #if REPARAMETRIZE==1
    float i = floor(4.0*t);
    float s = fract(4.0*t);

    // pick some other smoothstep-like polynomial
    s = s*s*s*(5.0+s*(6.0+s*(-15.0+s*(-21.0+s*(+54.0+s*(-36.0+s*8.0))))));

    t = (i+s)/4.0;
    #endif

    const float pi = 3.1415926535;
    vec2 p = vec2( cos(2.0*pi*t), sin(2.0*pi*t));
    return sign(p)*pow(abs(p),vec2(1.0/3.0)); // cubic root
}
#endif

