float stroke (float d, float r, float lw, float blur) {
  float hlw = lw / 2.;
  return smoothstep(r + hlw, r + hlw - .005 - blur, d) *
    smoothstep(r - hlw, r - hlw + .005 + blur, d);
}

float fill (float d, float r, float blur) {
  return smoothstep(r, r - .005 - blur, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = (fragCoord.xy - .5 * iResolution.xy) / iResolution.y;

  // background
  vec2 p = uv;
  float t = iTime * .08;
  p.x += (mod(floor(p.y * 10.), 2.) == 0.) ? -t : t;

  vec2 st = fract(p * 10.) - .5;

  if ((mod(floor(p.y * 10.), 2.) == 0.) && (mod(floor(p.x * 10.), 2.) == 0.)) {
    float a = iTime;
    st = mat2(sin(a)+2., 0., 0., sin(a)+2.) * mat2(cos(a), -sin(a), sin(a), cos(a)) * st;
  }

  vec3 color = vec3(0.);

  color += smoothstep(.45, .05 + cos(t*5.)*.18+.18, length(st));
  color -= 1. - smoothstep(.4, .45, max(abs(st.x) + (cos(t * 5.) * .05 + .35), abs(st.y)));
  color += 1. - smoothstep(.4, .45, max(abs(st.x), abs(st.y)));

  color *= (mod(floor(p.y * 10.), 2.) == 0.) ? vec3(1., .2, .5) : vec3(.5, .2, 1.);

  color *= 1. - length(vec2(uv)*.8) ;

  // foreground
  float r = cos(atan(
    uv.y * cos(iTime) * .5 + .3,
    uv.x * sin(iTime) * .5 + .2) * 10.) * .04 + clamp(random(uv), .198, .2);

  p = vec2(uv.x + cos(t) * .2 + .2, uv.y);

  color = mix(color, vec3(1.) - vec3(0., 0., vec2(length(p * p * p) * 15., 0.)), stroke(length(p), r, .15, 0.));
  color = mix(color, vec3(.9, .9, 0.) - vec3(0., length(p) * 4., 0.), fill(length(p), r - .068, 0.));

  fragColor = vec4(color, 1.);
}

