  p2.xz = ab2(p2.xz);
  float w = wall(p);
  p2.xz -= 23.0;
  float t=tower(p2);
  vec3 p3 = p;
  p3.z = p3.z-4.5*sign(p.x);
  p3.x = ab2(p3.x)-25.0;
  float f=field(p3);

  float res = t;
  res = min(res, w);
  res = min(res, f);

 /*
