vec2 Rotate(vec2 v, float a) {
    return(vec2(v.x * cos(a) - v.y * sin(a), v.x * sin(a) + v.y * cos(a)));
}

vec2 Hatch(vec2 uv) {
    float c = cos(6.28 * cos(6.28 * uv.x + 233.25 + cos(iTime)));
    float s = sin(6.28 * sin(6.28 * uv.y + 345.43 + sin(iTime)));
    return(vec2(c, s));
}

vec2 Multihatch(vec2 init) {
    vec2 uv1 = Rotate(init, 0.5 + iTime * 0.1);   
    vec2 uv2 = Rotate(init, 1.0 + iTime * 0.1);
    vec2 uv3 = Rotate(init, 1.5 + iTime * 0.1);
    vec2 uv4 = Rotate(init, 2.0 + iTime * 0.1);
    return(Hatch(uv1) + Hatch(uv2) + Hatch(uv3) + Hatch(uv4));
}

const float nfreq = 3.14 * 1.0;
float cNoise(float v1, float v2, float v3, float v4) {
    return(cos(
        cos(v1 * -cos(iTime * 0.5 + 1.0) * nfreq) * sin(v2 * sin(iTime * 0.2 + 3.0) * nfreq) + 
        cos(v3 * -sin(iTime * 0.3 + 2.0) * nfreq) * cos(v4 * cos(iTime * 0.3 + 4.0) * nfreq)
    ) * 0.5 + 0.5);
}

vec3 colorQuery(float a) {
    const int count = 12;
    const vec3 color_lut[count] = vec3[count] (
        // dark blues
        vec3(0.3, 0.1, 0.6),
        vec3(0.2, 0.1, 0.7),
        vec3(0.1, 0.1, 0.3),

        // light blues
        vec3(0.7, 0.5, 0.9),
        vec3(0.2, 0.9, 0.9),
        vec3(0.1, 0.0, 0.2),

        // dark red
        vec3(0.4, 0.2, 0.2),
        vec3(0.5, 0.1, 0.6),
        vec3(0.9, 0.3, 0.2),

        // light red
        vec3(0.7, 0.5, 0.3),
        vec3(0.8, 0.3, 0.2),
        vec3(0.0, 0.1, 0.0)
        );
    float rescale = float(count) * a;
    int ind1 = int(rescale) % count;
    int ind2 = int(rescale + 1.0) % count;

    return(mix(color_lut[ind1], color_lut[ind2], fract(rescale)));
}

const float hashing_freq = 0.04;
const float spiral_freq = 0.9;
const float arm_count = 1.0;
const float p_spiral = 0.1;
const float p_time = 0.1;
const float spiral_out_speed = -20.0;
const float spiral_out_length = 0.5;
const float spiral_shades = 3.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy - 0.5) * 3.14;
    // remap uniform surface into a bubbly surface
    uv = mix(uv, Multihatch(uv), hashing_freq);

    // angle of uv coordinates
    float theta = atan(uv.y, uv.x) + 3.0;
    // distance from 0
    float d = cos(uv.x) * cos(uv.y) / spiral_freq;
    // composition of theta and d produces spiral
    float spiral = cos(d * spiral_out_speed + theta * arm_count);
    // dark highlights
	float vignette = cNoise(uv.x, spiral, d, uv.y);

    // color is darkened by vig, queries a lookup table based on uv coordinates and time
    vec3 col = vignette * colorQuery(sin(d * spiral_out_length) * spiral_shades + 
                        p_spiral * spiral + 
                        p_time * iTime);

    // Output to screen
    fragColor = vec4(col, 1.0);
}

