    //val = val * noise(uv + (iTime) * (iTime * 0.005) - abs(uv.x*clamp(sin(iTime), 0.0, 1.0)) * 100.0);
    val = val + snoise(uv + (iTime * 1.0) + vec2(0.0, -iTime*2.0));


    //waves
    val = step(-0.1*val, uv.y) - step(2.5*val, uv.y);


    vec3 lampLight = abs(vec3(
        				  clamp(abs(sin(iTime)),0.3 , 0.8 ),
                          clamp(abs(cos(iTime)),0.4 , 0.9 ), 
                          clamp(abs(sin(iTime)),0.4 , 1.0 ))
                        	 );


    vec3 Val = mix(lampLight, black, val);
    Val = mix(lampLight, black, Val);

    float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio * 0.25;
    uv.y += 0.5;

    vec2 shapePos = vec2((0.5 * ratio) /*plus minus something to adjust Pos*/, 0.5);
    float dist = distance(shapePos , uv) * 2.50;


    uv -= shapePos;

    float angle = atan(uv.y, uv.x);

    float radius = sin(4.0 * angle);
    radius = smoothstep(-0.75 , 1.0,  cos(angle * 8.0 + iTime * 15.0)  ) * 0.2 + 0.4;

    float radius2 =  1.0 /  abs(cos(angle * 10000.0 + iTime) * sin(angle * 7.0 + iTime)) * 0.5 + 0.1;

    value =  step(radius + radius2 * clamp(sin(iTime * 0.0)+1.2,0.1, 3.0), dist);


    // Output to screen
    fragColor = vec4(value * Val, 1.0);

}

