#define ZERO (min(iFrame,0))

//Combined Geometries

float cell(in vec3 pos, float size, float radius)
{
 	float res = 1e10;
    float k = (0.2 + 0.2 * sin(iTime*0.12)) * size;
    res = opSmoothUnion(res, sdCapsule(pos, vec3(size*0.5, size*0.5, 0.0), vec3(size*0.5, size*0.5, size), radius), k);
    res = opSmoothUnion(res, sdCapsule(pos, vec3(0.0, size*0.5, size*0.5), vec3(size, size*0.5, size*0.5), radius), k);
    res = opSmoothUnion(res, sdCapsule(pos, vec3(size*0.5, 0.0, size*0.5), vec3(size*0.5, size, size*0.5), radius), k);
    return res;
}


float lattices(in vec3 pos, float size, float radius)
{ 
   return cell(opRep(pos, vec3(size)), size, radius);      
}


float flange( in vec3 pos, in vec3 size)
{
   float res = sdBox(translate(pos, size * vec3(0.5,0.0,0.0)), size*vec3(0.5, 1.0, 1.0));
   res = opU(res, sdCylinder(translate(pos, size * vec3(1.0,0.0,0.0)) , vec2(size.x * 0.6, size.y)));
   res = opS(res, sdCylinder(translate(pos, size * vec3(1.0,0.0,0.0)) , vec2(size.x * 0.5 * 0.6, size.y*1.1)));
   return res;
}


float base( in vec3 pos, in vec3 size)
{

   //base plate
   float radius = 0.15;   

   float sdf = sdRoundBox(pos, size, radius);
   float flangePos = 0.70 + 0.3 * sin(iTime*0.2);
   float k = 0.3 + 0.1 * sin(iTime*0.5);


   sdf = opSmoothUnion( sdf, flange(translate(pos, size * vec3(1.0, 0.0, flangePos)), vec3(1.0, size.y * 0.5, 0.5)),k);
   sdf = opSmoothUnion( sdf, flange(translate(mirrorX(pos), size * vec3(1.0, 0.0, flangePos)), vec3(1.0, size.y * 0.5, 0.5)),k); 
   sdf = opSmoothUnion( sdf, flange(translate(pos, size * vec3(1.0, 0.0, -flangePos)), vec3(1.0, size.y * 0.5, 0.5)),k);
   sdf = opSmoothUnion( sdf, flange(translate(mirrorX(pos), size * vec3(1.0, 0.0, -flangePos)), vec3(1.0, size.y * 0.5, 0.5)),k); 

   return sdf;
}

float model( in vec3 pos)
{
    vec3 size = vec3(1.0, 0.2, 2.0);

    float k = 0.5+ 0.3 * sin(iTime*0.5);
    float h = 1.0 + 0.5 * (1.0+ sin(iTime*0.2));
    float wRatio = 0.3;
    float holeRatio = 0.8;

    float sdf = base(pos, size);
    sdf = opSmoothUnion(sdf, sdBox(translate(pos, vec3(0.0, h * 0.5,0.0)), vec3(size.x * wRatio, h *0.5, size.z)),k);

    float inFill = sdBox(translate(pos, vec3(0.0, h * 0.5,0.0)), holeRatio * vec3(size.x*2.0, h *0.5, size.z));
    float interSectionInill = opI(sdf, inFill);
    sdf = opS(sdf, inFill);

    float cellSize = 0.05 + 0.25* (1.0+ sin(iTime*0.1));
    float latticeRadius = 0.01 + 0.005 * cos(iTime*0.2);
    sdf = opSmoothUnion(
        	sdf, 
        	opI(lattices(pos, cellSize, latticeRadius), interSectionInill),k*0.1); 


    sdf = opSmoothUnion( sdf, flange(rotateZ(translate(pos, vec3(0.0, h, 0)), 3.142*0.5), vec3(1.0, size.y * 0.5, 0.5)),k); 
    return sdf;
}

//Scene

vec2 map( in vec3 pos )
{
    float color = 2.7;     
    return vec2(model(pos), color);
}

// http://iquilezles.org/www/articles/boxfunctions/boxfunctions.htm
vec2 iBox( in vec3 ro, in vec3 rd, in vec3 rad ) 
{
    vec3 m = 1.0/rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*rad;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
	return vec2( max( max( t1.x, t1.y ), t1.z ),
	             min( min( t2.x, t2.y ), t2.z ) );
}

const float maxHei = 10.0;

vec2 castRay( in vec3 ro, in vec3 rd )
{
    vec2 res = vec2(-1.0,-1.0);

    float tmin = 1.0;
    float tmax = 20.0;

    // raytrace floor plane
    float tp1 = (0.0-ro.y)/rd.y;
    if( tp1>0.0 )
    {
        tmax = min( tmax, tp1 );
        res = vec2( tp1, 0.0 );
    }

    // raymarch primitives   
    vec2 tb = iBox( ro-vec3(0.0,0.0,0.0), rd, vec3(1000.0,1000.0,1000.0) );
    if( tb.x<tb.y && tb.y>0.0 && tb.x<tmax)
    {
        tmin = max(tb.x,tmin);
        tmax = min(tb.y,tmax);

        float t = tmin;
        for( int i=0; i<70 && t<tmax; i++ )
        {
            vec2 h = map( ro+rd*t );
            if( abs(h.x)<(0.00001*t) )
            { 
                res = vec2(t,h.y); 
                 break;
            }
            t += h.x;
        }
    }

    return res;
}


// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    // bounding volume
    float tp = (maxHei-ro.y)/rd.y; if( tp>0.0 ) tmax = min( tmax, tp );

    float res = 1.0;
    float t = mint;
    for( int i=ZERO; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{
#if 1
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
#else
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(pos+0.0005*e).x;
    }
    return normalize(n);
#endif    
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) * (0.5+0.5*nor.y);
}

// http://iquilezles.org/www/articles/checkerfiltering/checkerfiltering.htm
float checkersGradBox( in vec2 p )
{
    // filter kernel
    vec2 w = fwidth(p) + 0.001;
    // analytical integral (box filter)
    vec2 i = 2.0*(abs(fract((p-0.5*w)*0.5)-0.5)-abs(fract((p+0.5*w)*0.5)-0.5))/w;
    // xor pattern
    return 0.5 - 0.5*i.x*i.y;                  
}

vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.0, 0.0, 0.0) +rd.y*0.8;
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y;
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = (m<1.5) ? vec3(0.0,1.0,0.0) : calcNormal( pos );
        vec3 ref = reflect( rd, nor );

        // material        
		col = 0.35 + 0.45*sin( vec3(0.05,0.08,0.10)*(m-1.0) );
        if( m<1.5 )
        {

            float f = checkersGradBox( 1.0*pos.xz );
            col = f*vec3(0.005);
        }

        // lighting
        float occ = calcAO( pos, nor );
		vec3  lig = normalize( vec3(-0.4, 0.7, -0.6) );
        vec3  hal = normalize( lig-rd );
		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
        float dom = smoothstep( -0.2, 0.2, ref.y );
        float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );

        dif *= calcSoftshadow( pos, lig, 0.02, 2.5 );
        dom *= calcSoftshadow( pos, ref, 0.02, 2.5 );

		float spe = pow( clamp( dot( nor, hal ), 0.0, 1.0 ),16.0)*
                    dif *
                    (0.04 + 0.96*pow( clamp(1.0+dot(hal,rd),0.0,1.0), 5.0 ));

		vec3 lin = vec3(0.0);
        lin += 1.30*dif*vec3(1.00,0.80,0.55);
        lin += 0.30*amb*vec3(0.40,0.60,1.00)*occ;
        lin += 0.40*dom*vec3(0.40,0.60,1.00)*occ;
        lin += 0.50*bac*vec3(0.25,0.25,0.25)*occ;
        lin += 0.25*fre*vec3(1.00,1.00,1.00)*occ;
		col = col*lin;
		col += 9.00*spe*vec3(1.00,0.90,0.70);

    	col = mix( col, vec3(0.0,0.0,0.0), 1.0-exp( -0.0002*t*t*t ) );
    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}


vec4 renderScene(in vec2 fragCoord, float splitratio)
{
    vec3 Resolution = iResolution * splitratio;

    vec2 mo = iMouse.xy/iResolution.xy;
	float time = 15.0 + iTime * 0.05;

    if (iTimeDelta > 0.5)
    {
        int skipStep = 5;
        int offset = iFrame % skipStep;
        if (( (int(fragCoord.x) % skipStep) == offset) || ((int(fragCoord.y) % skipStep) == offset))
        {
             discard;
        }
    }

    // camera	
    vec3 ro = vec3( 4.6*cos(0.1*time + 6.0*mo.x), 1.0 + 2.0*mo.y, 0.5 + 4.6*sin(0.1*time + 6.0*mo.x) );
    vec3 ta = vec3( -0.5, -0.4, 0.5 );
    // camera-to-world transformation
    mat3 ca = setCamera( ro, ta, 0.0 );

    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-Resolution.xy + 2.0*(fragCoord+o))/Resolution.y;
#else    
        vec2 p = (-Resolution.xy + 2.0*fragCoord)/Resolution.y;
#endif

        // ray direction
        vec3 rd = ca * normalize( vec3(p.xy,2.0) );

        // render	
        vec3 col = render( ro, rd );

		// gamma
        col = pow( col, vec3(0.4545) );

        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif


    return vec4( tot, 1.0 );
}

vec4 renderSDF(in vec2 fragCoord, float splitratio)
{
    float zoom = 5.0;
    float zHeight = 0.5;

    vec2 coordInFrame = fragCoord - splitratio * vec2(iResolution.x * splitratio, 0.0);

    vec2 uv = coordInFrame / (iResolution.xy) - vec2(0.5, 0.5);
    vec3 pos = vec3(uv.x * zoom, zHeight, uv.y * zoom);
    float d = map(pos).x;


    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-4.0*abs(d));
	col *= 0.8 + 0.2*cos(500.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.005,abs(d)) );


    return vec4(col,1.0); 
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float splitratio = 1.0;
    if (texelFetch(iChannel0, ivec2(83, 0), 0).x > 0.)
    {
        splitratio = 0.5;
    }


    if (fragCoord.x < iResolution.x * splitratio)
    {
    	fragColor = renderScene(fragCoord, splitratio);
    }
    else
    {
        fragColor = renderSDF(fragCoord, splitratio);
    }
}

/**
 * Part 2 Challenges
 * - Change the diffuse color of the sphere to be blue
 * - Change the specular color of the sphere to be green
 * - Make one of the lights pulse by having its intensity vary over time
 * - Add a third light to the scene
 */

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.0001;

/**
 * Signed distance function for a sphere centered at the origin with radius 1.0;
 */
float sphereSDF(vec3 samplePoint) {
    return length(samplePoint) - 1.0;
}

/**
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */
float sceneSDF(vec3 samplePoint) {
    return sphereSDF(samplePoint);
}

/**
 * Return the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the ey to march before giving up
 */
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}


/**
 * Return the normalized direction to march in from the eye point for a single pixel.
 * 
 * fieldOfView: vertical field of view in degrees
 * size: resolution of the output image
 * fragCoord: the x,y coordinate of the pixel in the output image
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

/**
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    //light 1
    vec3 light1Pos = vec3(4.0 * sin(iTime),
                          2.0,
                          4.0 * cos(iTime));
    vec3 light1Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);

    //light 2
    vec3 light2Pos = vec3(1.0 * sin(0.37 * iTime),
                          1.0 * cos(0.37 * iTime),
                          1.0);//DR: change the light pulse from SIN(0.37 to 2.37)
    vec3 light2Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light2Pos,
                                  light2Intensity);

    //light 3
    //DR: adding the third lights
    vec3 light3Pos = vec3(-4.0 * sin(iTime),
                          -2.0,
                          -4.0 * cos(iTime));
    vec3 light3Intensity = vec3(0.2, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light3Pos,
                                  light3Intensity);


    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 dir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(0.0, 0.0, 5.0);
    float dist = shortestDistanceToSurface(eye, dir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * dir;

    vec3 K_a = vec3(0.2, 0.1, 0.5);
    vec3 K_d = vec3(0.0, 1.0, 1.0);//DR:  change the diffuse color to blue
    vec3 K_s = vec3(0.0, 1.0, 0.0);//DR: change the specular color to green
    float shininess = 10.0;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);

    fragColor = vec4(color, 1.0);
}

float rect(vec2 uv, vec2 pos, float width, float height)
{
    float square = (step(pos.x - width, uv.x) - step(pos.x + width, uv.x)) *
                   (step(pos.y - height, uv.y) - step(pos.y + height, uv.y));


    return square;
}

mat2 rotate2d(float angle)
{
