// @simesgreen

const int _Steps = 312;
const vec3 lightDir = vec3(0.577, 0.177, 0.577);

// transforms
vec3 rotateX(vec3 p, float a)
{
    float sa = sin(a);
    float ca = cos(a);
    vec3 r;
    r.x = p.x;
    r.y = ca*p.y - sa*p.z;
    r.z = sa*p.y + ca*p.z;
    return r;
}

vec3 rotateY(vec3 p, float a)
{
    float sa = sin(a);
    float ca = cos(a);
    vec3 r;
    r.x = ca*p.x + sa*p.z;
    r.y = p.y;
    r.z = -sa*p.x + ca*p.z;
    return r;
}

bool
intersectBox(vec3 ro, vec3 rd, vec3 boxmin, vec3 boxmax, out float tnear, out float tfar)
{
	// compute intersection of ray with all six bbox planes
	vec3 invR = 1.0 / rd;
	vec3 tbot = invR * (boxmin - ro);
	vec3 ttop = invR * (boxmax - ro);
	// re-order intersections to find smallest and largest on each axis
	vec3 tmin = min (ttop, tbot);
	vec3 tmax = max (ttop, tbot);
	// find the largest tmin and the smallest tmax
	vec2 t0 = max (tmin.xx, tmin.yz);
	tnear = min (t0.x, t0.y);
	t0 = min (tmax.xx, tmax.yz);
	tfar = min (t0.x, t0.y);
	// check for hit
	bool hit;
	if ((tnear > tfar)) 
		hit = false;
	else
		hit = true;
	return hit;
}

float luminance(sampler2D tex, vec2 uv)
{
	vec3 c = textureLod(tex, uv, 0.0).xyz;
	return dot(c, vec3(0.33, 0.333, 0.33));
}

vec2 gradient(sampler2D tex, vec2 uv, vec2 texelSize)
{
	float h = luminance(tex, uv);
	float hx = luminance(tex, uv + texelSize*vec2(1.0, 0.0));	
	float hy = luminance(tex, uv + texelSize*vec2(0.0, 1.0));
	return vec2(hx + h, hy * h);
}

vec2 worldToTex(vec3 p)
{
	vec2 uv = p.xz*0.5+0.5;
	uv.y = 1.0 - uv.y;
	return uv;
}

float heightField(vec3 p)
{
	//return sin(p.x*4.0)*sin(p.z*4.0);
	//return luminance(iChannel0, p.xz*0.5+0.5)*2.0-1.0;
	return luminance(iChannel0, worldToTex(p))*0.5;
}

bool traceHeightField(vec3 ro, vec3 rayStep, out vec3 hitPos)
{
	vec3 p = ro;
	bool hit = false;
	float pH = 0.0;
	vec3 pP = p;
	for(int i=0; i<_Steps; i++) {
		float h = heightField(p);
		if ((p.y < h) && !hit) {
			hit = true;
			//hitPos = p;
			// interpolate based on height
            hitPos = mix(pP, p, (pH - pP.y) / ((p.y - pP.y) - (h - pH)));
		}
		pH = h;
		pP = p;
		p += rayStep;
	}
	return hit;
}

vec3 background(vec3 rd)
{
     return mix(vec3(1.0, 1.0, 1.0), vec3(0.0, 0.5, 1.0), abs(rd.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixel = (fragCoord.xy / iResolution.xy)*2.0-1.0;

    // compute ray origin and direction
    float asp = iResolution.x / iResolution.y;
    vec3 rd = normalize(vec3(asp*pixel.x, pixel.y, -2.0));
    vec3 ro = vec3(0.0, 0.0, 2.0);

	vec2 mouse = iMouse.xy / iResolution.xy;

	// rotate view
    float ax = -0.7;
	if (iMouse.x > 0.0) {
    	ax = -(1.0 - mouse.y)*2.0 - 1.0;
	}
    rd = rotateX(rd, ax);
    ro = rotateX(ro, ax);

	float ay = sin(iTime*0.2);
    rd = rotateY(rd, ay);
    ro = rotateY(ro, ay);

	// intersect with bounding box
    bool hit;	
	const vec3 boxMin = vec3(-1.0, -0.01, -1.0);
	const vec3 boxMax = vec3(1.0, 0.5, 1.0);
	float tnear, tfar;
	hit = intersectBox(ro, rd, boxMin, boxMax, tnear, tfar);

	tnear -= 0.0001;
	vec3 pnear = ro + rd*tnear;
    vec3 pfar = ro + rd*tfar;

    float stepSize = length(pfar - pnear) / float(_Steps);

    vec3 rgb = background(rd);
    if(hit)
    {
    	// intersect with heightfield
		ro = pnear;
		vec3 hitPos;
		hit = traceHeightField(ro, rd*stepSize, hitPos);
		if (hit) {
			rgb = hitPos*0.5+1.5;
			vec2 uv = worldToTex(hitPos);
			rgb = texture(iChannel0, uv).xyz;
			//vec2 g = gradient(iChannel0, uv, vec2(1.0) / iResolution.xy);
			//vec3 n = normalize(vec3(g.x, 0.01, g.y));
			//rgb = n*0.5+0.5;
#if 0
			// shadows
			hitPos += vec3(0.0, 0.01, 0.0);
			bool shadow = traceHeightField(hitPos, lightDir*0.01, hitPos);
			if (shadow) {
				rgb *= 0.75;
			}
#endif			
		}
     }

    fragColor=vec4(rgb, 1.0);
	//fragColor = vec4(vec3(tfar - tnear)*0.2, 1.0);
}

// "Gyroidal Torus" by dr2 - 2019
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define AA  1   // optional antialiasing

float PrTorusDf (vec3 p, float ri, float rc);
float Minv3 (vec3 p);
float SmoothMax (float a, float b, float r);
vec2 Rot2D (vec2 q, float a);

vec3 ltDir;
float dstFar;
int idObj;
const float pi = 3.14159;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, rt, rg, tt, ws;
  dMin = dstFar;
  rt = 20.;
  rg = 6.;
  tt = 0.5 * pi;
  ws = 0.2;
  q = p;
  d = PrTorusDf (q.xzy, 1., rt);
  DMIN (1);
  q.xz = vec2 (rt * atan (q.z, - q.x), length (q.xz) - rt);
  q.yz = vec2 (rg * atan (q.z, - q.y), length (q.yz) - rg);
  d = 0.6 * SmoothMax (abs (dot (sin (q), cos (q).yzx)) - ws, abs (q.z) - tt, 0.2);
  DMIN (2);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0002, -0.0002);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += clamp (h, 0.1, 0.5);
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, w;
  float dstObj, sh, nDotL;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 1) col4 = vec4 (1., 0.5, 1., 0.5);
    else if (idObj == 2) {
      w = mod (8. * ro / pi, 1.);
      w = min (w, 1. - w);
      col4 = vec4 (1., 0.7, 0.1, 0.3) * (0.8 + 0.2 * smoothstep (0.05, 0.07, Minv3 (w)));
    }
    sh = ObjSShadow (ro, ltDir);
    nDotL = max (dot (vn, ltDir), 0.);
    col = col4.rgb * (0.2 + 0.8 * sh * nDotL * nDotL) +
       col4.a * step (0.95, sh) * sh * pow (max (dot (normalize (ltDir - rd), vn), 0.), 64.);
  } else col = vec3 (0.6, 0.6, 1.) * (0.2 + 0.2 * (rd.y + 1.) * (rd.y + 1.));
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, ori, ca, sa;
  float tCur, el, az, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az += 0.3 * pi * sin (0.1 * pi * tCur);
    el += 0.1 * pi * sin (0.03 * pi * tCur);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -60.);
  dstFar = 150.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), 2.));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

