//mask is 1.0/(ellipsoid size)
vec2 ellipsoid(vec3 ro, vec3 rd, vec3 mask) {
    mask = max(mask, 0.01);

    ro *= mask;
    rd *= mask;

    float e = 1.0/dot(rd,rd);
    float b = -dot(rd,ro)*e;
    vec3 p = ro+rd*b;
    float a = dot(p,p);
    if (a > 1.0) return vec2(1000,999);
    float c = sqrt((1.0-a)*e);

    return b+vec2(-c,c);
}

//box intersection from iq
//https://www.shadertoy.com/view/ld23DV
vec2 iBox( in vec3 ro, in vec3 rd, in vec3 rad) 
{
	// ray-box intersection in box space
    vec3 m = 1.0/rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*rad;

    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

	float tN = max(max( t1.x, t1.y ), t1.z);
	float tF = min(min( t2.x, t2.y ), t2.z);

	return vec2(max(tN,0.0), tF);
}

float solve(vec2 l) {
    if (l.x > l.y) l.x = FAR;
    return l.x;
}

vec4 roundedcube(vec3 ro, vec3 rd, vec3 size, vec3 radius) {

    vec2 l = iBox(ro,rd,size+radius);

    if (l.x > l.y) return vec4(FAR,0,0,0);

    // inverted radius for the ellipsoid intersection
    radius = 1.0/radius;

    vec3 p = ro+rd*l.x;

    vec3 s = sign(p);
    vec3 m = step(size,p*s);
    m = m.yzx*m.zxy;

    float d = 0.0;

    vec3 cyl = ro-s*size;

    if (m.x > 0.5) {
        l.x = max(l.x,solve(ellipsoid(cyl,rd,vec3(0,1,1)*radius)));
    }
    if (m.y > 0.5) {
        l.x = max(l.x,solve(ellipsoid(cyl,rd,vec3(1,0,1)*radius)));
    }
    if (m.z > 0.5) {
        l.x = max(l.x,solve(ellipsoid(cyl,rd,vec3(1,1,0)*radius)));
    }

    p = ro+rd*l.x;

    s = sign(p);
    if (all(lessThan(size,p*s)))
        l.x = max(l.x,solve(ellipsoid(ro-s*size,rd,radius)));

    p = ro+rd*l.x;

    vec3 n = max(abs(p)-size,0.0)*radius*sign(p);

    return vec4(l.x,n);
}

vec3 background(vec3 n) {
    float a = max(0.0,dot(n,normalize(vec3(0.4,-0.5,0.7))));
    float b = max(0.0,dot(n,normalize(vec3(-0.8,0.2,0.4))));

    vec3 col = vec3(a*vec3(0.8,1,0.4)+b*vec3(0.8,0.2,0.65));
    col = max(col,vec3(0.1,0.4,0.2));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragUV = fragCoord / iResolution.xy;
    seed = iTime;

    vec3 acc = vec3(0);
    for (int aa = 0; aa < antialiasing+min(iFrame,0); aa++) {
#if antialiasing==1
        vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;
#else
        vec2 uv = ((fragCoord+vec2(rand(),rand()))*2.0-iResolution.xy)/iResolution.y;
#endif

        vec3 ro = vec3(0,0,-4);
        vec3 rd = vec3(uv,1);

        if (length(iMouse.xy) > 40.0) {
            rd.yz *= rot(iMouse.y/iResolution.y*3.14-3.14*0.5);
            rd.xz *= rot(iMouse.x/iResolution.x*3.14*2.0-3.14);
            ro.yz *= rot(iMouse.y/iResolution.y*3.14-3.14*0.5);
            ro.xz *= rot(iMouse.x/iResolution.x*3.14*2.0-3.14);
        }

        vec4 d = roundedcube(ro,rd,vec3(0.6+sin(iTime)*0.4,0.8+cos(iTime)*0.5,0.5),vec3(0.3,0.3,0.3));

        vec3 col;

        if (d.x < FAR) {
            vec3 p = ro+rd*d.x;

            vec3 n = d.yzw;

            col = vec3(max(dot(n,normalize(vec3(1,1,-1))),0.1));

            vec3 r = reflect(rd,n);

            col += background(r)*0.12;
        } else {

            col = background(rd);
        }
        acc += col;
    }
    acc /= float(antialiasing);
    fragColor = vec4(sqrt(acc),1);
}

