	vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;               
    vec2 st = vec2(atan(uv.x, uv.x), length(uv));   
    float speed = 2.0;
    float rings = 20.0;
    uv = vec2(st.x / 10000.0 + iTime * speed - st.y * (rings * abs(clamp(sin(iTime), 0.1, 1.0))), st.y);  

    //ring thickness
    float a = fwidth(uv.x);         

    vec2 h = hash(uv);
    float b = fract(uv.x);  


    float mask = smoothstep(0.0, a, abs(b-0.9) );    
    vec3 lampLight = abs(vec3(
        				  clamp(abs(sin(iTime)),0.0 , 0.8 ),
                          clamp(abs(cos(iTime)),0.0 , 0.8 ), 
                          clamp(abs(sin(iTime)),0.4 , 1.0 ))
                        	 );

    vec3 col = mix(vec3(1.0), lampLight, mask);   
    //=================================================
    float value = gnoise(uv + (sin(iTime) * 0.005));
    float angle = atan(uv.y, uv.x);

    float radius = sin(4.0 * angle);
    radius = smoothstep(-0.75 , 1.0,  cos(angle * 8.0 + iTime * 15.0)  ) * 0.2 + 0.4;

    float radius2 =  1.0 /  abs(cos(angle * 10000.0 + iTime) * sin(angle * 7.0 + iTime)) * 0.5 + 0.1;

    //=================================================
    fragColor = vec4(col * radius2 * lampLight, 1.0);        
}

vec3 hsv(vec3 c)
{
	vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0,0.0,1.0);
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix( vec3(1.0), rgb, c.y);
}

float box(vec2 p,vec2 b)
{
	return max(abs(p.x)-b.x,abs(p.y)-b.y);
}

float yama(vec2 p,vec2 s)
{
	float d=box(p+s,vec2(0.25,1.5)); 
    d = min(d, box(p - vec2(0.0, -1.25)+s, vec2(1.5, 0.25)));
    d = min(d, box(p - vec2(1.5, -0.75)+s, vec2(0.25, 0.75)));
    d = min(d, box(p - vec2(-1.5, -0.75)+s, vec2(0.25, 0.75)));
    return d;
}

float da(vec2 p,vec2 s)
{
	float d=box(p+s,vec2(0.25,1.)); 
    d = min(d, box(p - vec2(0.0, -1.25)+s, vec2(1.5, 0.25)));
    d = min(d, box(p - vec2(1.5, -0.25)+s, vec2(0.25, 1.25)));
    d = min(d, box(p - vec2(-1.5, -0.25)+s, vec2(0.25, 1.25)));
    d = min(d, box(p - vec2(0.0, 1.25)+s, vec2(1.75, 0.25)));
    d = min(d, box(p - vec2(0.0, 0.)+s, vec2(1.75, 0.25)));
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p=(fragCoord.xy*2.0-iResolution.xy)/min(iResolution.x,iResolution.y);

   	p*=3.0;

    float y=yama(p,vec2(2.,0));
    float d=da(p,vec2(-2.0,0));

    float e= box(p, vec2(1.3, 0.25));

    float col=min(y,d);
    float z=sign(col);
   	vec3 yamada=vec3(z);

    float pi=3.14;
    vec3 color=vec3(0.,0.,0.);
    float angle = atan(p.y,p.x);
    float radius = length(p)*2.0;

    float h=(angle/pi*5.0)+iTime;
    color = hsv(vec3(h,radius,1.));

    fragColor=vec4(color+yamada,1.);

}

// "Twisted Gyroid Ring" by dr2 - 2019
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define AA  1   // optional antialiasing

float Minv3 (vec3 p);
float SmoothMax (float a, float b, float r);
vec2 Rot2D (vec2 q, float a);

vec3 ltDir, qHit;
float dstFar;
int idObj;
const float pi = 3.14159;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 tt;
  float dMin, d, a, rt, ws;
  dMin = dstFar;
  rt = 20.;
  tt = vec2 (pi, 1.5 * pi);
  ws = 0.2;
  q = p;
  a = atan (q.z, - q.x);
  q.xz = vec2 (rt * a, length (q.xz) - rt);
  d = length (q.yz) - 2. * ws;
  DMINQ (1);
  q.yz = Rot2D (q.yz, a);
  d = length (abs (q.yz) - tt) - ws;
  DMINQ (2);
  d = SmoothMax (max (abs (dot (sin (q), cos (q).yzx)) - ws, 3. * ws - length (q.yz)),
     max (abs (q.y) - tt.x, abs (q.z) - tt.y), 0.2);
  DMINQ (3);
  return 0.6 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0002, -0.0002);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += clamp (h, 0.1, 0.5);
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, w;
  float dstObj, sh, nDotL;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 1) col4 = vec4 (1., 0., 0., 0.5);
    else if (idObj == 2) col4 = vec4 (1., 0.9, 0.9, 0.5);
    else if (idObj == 3) {
      w = mod (4. * qHit / pi, 1.);
      w = min (w, 1. - w);
      col4 = mix (vec4 (0.2, 0.2, 0.8, 0.3), vec4 (0.9, 0.9, 0.2, 0.3), 
         smoothstep (0.05, 0.07, Minv3 (w)));
    }
    sh = ObjSShadow (ro, ltDir);
    nDotL = max (dot (vn, ltDir), 0.);
    col = col4.rgb * (0.2 + 0.8 * sh * nDotL * nDotL) +
       col4.a * step (0.95, sh) * sh * pow (max (dot (normalize (ltDir - rd), vn), 0.), 64.);
  } else col = vec3 (1., 0.8, 0.8) * (0.15 + 0.05 * rd.y);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, ori, ca, sa;
  float tCur, el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az += pi * sin (0.03 * pi * tCur);
    el -= 0.15 * pi * cos (0.03 * pi * tCur);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -60.);
  zmFac = 3. - 0.8 * abs (el);
  dstFar = 120.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;  

    vec2 gv = uv * 50.0 ;
    gv = fract(gv) - 0.5;

    float t = iTime * 5.0;

    float s = (sin(t - length(uv * 2.0) * 5.0) * 0.4 + 0.5) * 0.6;
    float m = smoothstep(s, s - 0.05, length(gv)) + s*2.0;

    vec3 col = vec3(s, 0.0, 0.5) * m;

    fragColor = vec4(col, 1.0);
}

