// input in range [-1,1] to span iResolution.y pixels
float RenderLine( vec2 a, vec2 b, vec2 fragCoord )
{
    a = (iResolution.y*a + iResolution.xy)*.5;
    b = (iResolution.y*b + iResolution.xy)*.5;

    const float halfThickness = lineThickness*.5; 

    const float halfAASoftening = .7; // in pixels (don't change this much)

    float t = dot(fragCoord-a,b-a);
    t /= dot(b-a,b-a);
    t = clamp( t, 0., 1. );
    return smoothstep( halfThickness-halfAASoftening, halfThickness+halfAASoftening, length(fragCoord - mix(a,b,t)) );
}


float RenderLine3D( vec3 a, vec3 b, vec2 fragCoord )
{
    vec3 camPos = vec3(0,0,-5);

    a -= camPos;
    b -= camPos;

    // todo: transform by camera matrix

    a.z /= zoom;
    b.z /= zoom;

    // perspective projection
    return RenderLine( a.xy/a.z, b.xy/b.z, fragCoord );
}


// combine 2 anti-aliased values
float BlendAA( float a, float b )
{
    // a and b values represent what proportion of the pixel is covered by each line,
    // but they don't contain enough information to accurately combine them!
    // if both lines are covering the same part of the pixel the result should be min(a,b)
    // if they cover non-overlapping parts of the pixel the result is a-(1-b)
	// a*b assumes the proportion of overlap is the same in the solid and clear regions
    // this is the safest assumption given the lack of any other info

    // but, tune it until it looks good
    return mix( min(a,b), a*b, .5 );
}


void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    fragColour.rgb = vec3(.8);

    vec3 a = vec3(twistStep*cos(iTime*3./vec3(11,13,17)+1.5));
    mat3 stepTransform =
        scaleStep *
        mat3( cos(a.z), sin(a.z), 0,
             -sin(a.z), cos(a.z), 0,
              0, 0, 1 ) *
        mat3( cos(a.y), 0, sin(a.y),
             0, 1, 0,
             -sin(a.y), 0, cos(a.y) ) *
        mat3( 1, 0, 0,
              0, cos(a.x), sin(a.x),
              0,-sin(a.x), cos(a.x) );

    vec3 b = vec3(.7+iTime/6.,.7+iTime/6.,.6);
    mat3 transform = //mat3(1,0,0,0,1,0,0,0,1); // identity
        mat3( cos(b.z), sin(b.z), 0,
             -sin(b.z), cos(b.z), 0,
              0, 0, 1 ) *
        mat3( cos(b.y), 0, sin(b.y),
             0, 1, 0,
             -sin(b.y), 0, cos(b.y) ) *
        mat3( 1, 0, 0,
              0, cos(b.x), sin(b.x),
              0,-sin(b.x), cos(b.x) );

    float line = 1.;
    #define DrawLine(a,b) line = BlendAA( line, RenderLine3D(a,b,fragCoord) );

    for ( int cube=0; cube < numCubes; cube++ )
    {
        vec3 vertices[8];
        for ( int i=0; i < 8; i++ )
        {
            vertices[i] = transform*(vec3(i>>2,(i>>1)&1,i&1)*2.-1.);
        }

        DrawLine( vertices[0], vertices[1] );
        DrawLine( vertices[2], vertices[3] );
        DrawLine( vertices[4], vertices[5] );
        DrawLine( vertices[6], vertices[7] );
        DrawLine( vertices[0], vertices[2] );
        DrawLine( vertices[1], vertices[3] );
        DrawLine( vertices[4], vertices[6] );
        DrawLine( vertices[5], vertices[7] );
        DrawLine( vertices[0], vertices[4] );
        DrawLine( vertices[1], vertices[5] );
        DrawLine( vertices[2], vertices[6] );
        DrawLine( vertices[3], vertices[7] );

        transform *= stepTransform;
    }

    fragColour.rgb = mix( vec3(.02), vec3(.8), line );

    fragColour.rgb = pow(fragColour.rgb,vec3(1./2.2));
    fragColour.a = 1.;
}

// ray marching
const int max_iterations = 128;
const float grad_step = 0.0001;
const float min_dist = 0.005;
const float clip_far = 1000.0;

// math
const float PI = 3.14159265359;
const float DEG_TO_RAD = PI / 180.0;

vec3 diffuse_colour = vec3(0.0);
vec3 final_diff_colour = vec3(0.0);


const vec3 sky_colour = vec3(0.2, 0.6, 1.0);
const vec3 clouds_colour = vec3(1.0);
const vec3 sun_colour = 32.0 * vec3(1.0, 1.0, 0.0);
const vec3 sun_pos = normalize(vec3( 20.0, 30.0, 30.0 ));

//get a scalar random value from a 3d value
float rand3dTo1d(vec3 value, vec3 dotDir) {
    //make value smaller to avoid artefacts
    vec3 smallValue = sin(value);
    //get scalar value from 3d vector
    float random = dot(smallValue, dotDir);
    //make value more random by making it bigger and then taking the factional part
    random = fract(sin(random) * 143758.5453);
    return random;
}

vec3 rand3dTo3d(vec3 value){
    return vec3(
        rand3dTo1d(value, vec3(12.989, 78.233, 37.719)),
        rand3dTo1d(value, vec3(39.346, 11.135, 83.155)),
        rand3dTo1d(value, vec3(73.156, 52.235, 09.151))
    );
}

/*vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}*/

// this isn't very random but it's a good deal quicker than the above
vec2 random2( vec2 p ) {
    return fract(p * vec2(9.61, 3.28) - p.yx * vec2(5.85, 7.11));
}

// http://www.iquilezles.org/www/articles/smin/smin.htm
// polynomial smooth min (k = 0.1);
float smin( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*h*k*(1.0/6.0);
}

float smax( float a, float b, float k ) {
    return -smin(-a, -b, k);
}

vec2 cell_position(vec2 cell) {
    vec2 point = random2(cell);
    point = 0.5 - 0.4*sin(iTime + 6.2831*point);
    return cell + point;
}

float dist_field(vec3 p) {
    float md = 1000.0;
    vec2 mcell = vec2(0.0);
    vec2 mpos = vec2(0.0);
    vec2 fuv = floor(p.xz);
    for (int i = -1; i <= 1; ++i) {
	    for (int j = -1; j <= 1; ++j) {
            vec2 ij = vec2(float(i), float(j));
            vec2 cell = ij + fuv;
            vec2 pos = cell_position(cell);
    		vec2 mid = p.xz - pos;
            float d = dot(mid, mid);
            if (d < md) {
                md = d;
                mcell = cell;
                mpos = pos;
            }
        }
    }

    float plane = p.y;

    for (int i = -1; i <= 1; ++i) {
	    for (int j = -1; j <= 1; ++j) {
            vec2 ij = vec2(float(i), float(j));
   	        vec2 cell = ij + fuv;
            if (cell != mcell) {
	            vec2 pos = cell_position(cell);
			    vec2 n = normalize(mpos - pos);
			    vec2 pt = (mpos + pos) * 0.5;
                { // smooth pebbles :)
				    plane = smax(plane, -dot(n, p.xz - pt)+0.1, 0.2);
                }
                /*{ // angular blocks
				    plane = max(plane, -dot(n, p.xz - pt)+0.1);
				    vec3 n = vec3(n, -1.0).xzy / sqrt(2.0);
				    vec3 pt = vec3((mpos + pos) * 0.5, -0.15).xzy;
				    plane = max(plane, -dot(n, p - pt));
                }*/
            }
        }
    }

    float floor = p.y + 0.2;
    if (floor < plane) {
        diffuse_colour = vec3(smoothstep(0.08, 0.09, plane)*0.4 + 0.3);
        plane = min(plane, floor);
    } else {
	    diffuse_colour = rand3dTo3d(vec3(mcell, 0.0));
    }
    return plane;
}

vec3 skybox(vec3 dir) {
    float horizon = smoothstep(0.0, 0.25, dir.y);
    float cloud = mix(0.8, 0.0, horizon);
    return mix(sky_colour, clouds_colour, cloud);
}

// get gradient in the world
vec3 gradient( vec3 p ) {
    const vec2 k = vec2(1,-1);
    const vec2 kg = vec2(grad_step,-grad_step);
    return vec3( k.xyy*dist_field( p + kg.xyy) +
                 k.yyx*dist_field( p + kg.yyx) +
                 k.yxy*dist_field( p + kg.yxy) +
                 k.xxx*dist_field( p + kg.xxx) );
}

// parallel light source
vec3 shading( vec3 v, vec3 n, vec3 dir, vec3 eye ) {
	float diffuse  = max( 0.0, dot( sun_pos, n ) );
	float shininess = 80.0;

	vec3 ref = reflect( dir, n );
	float specular = max( 0.0, dot( sun_pos, ref ) );

	specular = pow( specular, shininess );

	return final_diff_colour * diffuse + vec3(specular);
}

// ray marching
// Enhanced Sphere Tracing:
// http://erleuchtet.org/~cupe/permanent/enhanced_sphere_tracing.pdf
vec3 ray_marching(vec3 o, vec3 dir) {
    vec3 colour = vec3(0.0);
    vec3 acc_refl = vec3(1.0);

    // give the ray a bit of a head start by jumping
    // straight to the upper bounding plane!
    float t = 0.0;
    if (dir.y < 0.0) {
        t = -o.y/dir.y;
    } else {
        return skybox(dir);
    }
	t = max(t, min_dist*2.0);

    vec3 v;
    for (int i = 0; i < max_iterations; i++) {
       	float d = dist_field(v = o + dir * t);

        if (d <= min_dist) {
            break;
        }
		if ((t+=d) >= clip_far) {
            return skybox(dir);
	    }
    }
	final_diff_colour = diffuse_colour;

    vec3 n = normalize( gradient(v) );

    colour += shading( v, n, dir, o );

    return colour;
}

// get ray direction
vec3 ray_dir( float fov, vec2 size, vec2 pos ) {
	vec2 xy = pos - size * 0.5;

	float cot_half_fov = tan( ( 90.0 - fov * 0.5 ) * DEG_TO_RAD );
	float z = size.y * 0.5 * cot_half_fov;

	return normalize( vec3( xy, -z ) );
}

// camera rotation : pitch, yaw
mat3 rotationXY( vec2 angle ) {
	vec2 c = cos( angle );
	vec2 s = sin( angle );

	return mat3(
		c.y      ,  0.0, -s.y,
		s.y * s.x,  c.x,  c.y * s.x,
		s.y * c.x, -s.x,  c.y * c.x
	);
}

const vec2 spin = vec2(0.005, 0.0041);
const float radius = 120.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// rotate camera
	mat3 rot = rotationXY( vec2( -0.8, iTime * 0.2 ) );
	//mat3 rot = rotationXY( ( iMouse.xy - iResolution.xy * 0.5 ).yx * vec2( 0.01, -0.01 ) );

	// default ray origin
	vec3 eye = vec3( 0.0, 0.0, 0.0 );
	eye = rot * eye;

    // move in a sinusoidal path so we don't end up in floating point hell!
    vec2 camera = radius * cos(iTime * spin);

    eye += vec3(camera, 2.0).xzy;

    // default ray dir
	vec3 dir = rot * ray_dir( 60.0, iResolution.xy, fragCoord.xy);
	vec3 color = ray_marching(eye, dir);

	fragColor = vec4(color, 1.0);
    return;
}

// variant of https://shadertoy.com/view/3tl3D4

#define hash(p)  fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453)
#define hash2(p) fract(sin( p *mat2( 127.1,311.7, 269.5,183.3 ) )*43758.5453123)

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( u+u - R ) / R.y, 
         V = 2.*fract(4.*U) - 1.,
         T = floor(4.*U);

    float s = cos(iTime+6.3*hash(T)), // 14.*T.x-78.4*T.y),
          l = length(V),                             
          a = atan(V.y,V.x); a = mod(a,6.283);

    a += 10. * exp(-4.*l)
        * s;  s=1.;                  // both optional

    V = l * max(0.,cos(l*1.57))
          * vec2(cos(a),sin(a));

    U  = V * s;                      // chose variant
 // U += V * s;

 // O = vec4(.5+.5*U,.5,1);
    O = vec4(sin(3.14*U.y));
}

#define PI 3.1415926
float rand(float t)
{
    return fract(sin(dot(vec2(t,t) ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time=iTime;
	vec2 uv=(fragCoord.xy*2.0-iResolution.xy)/iResolution.y;
 	vec2 uv1=uv-vec2(0.8,.4);
    float r = length(uv1)*111.;

    float t = ceil(r);
    float a = fract(atan(uv1.y, uv1.x)/PI+time*rand(t)*.1 +t*0.1);

    float ang = rand(t);
    float c = smoothstep(ang,ang-1.5,a*5.) ;

    vec3 col = vec3(.3,0.3,.5)*3.;
    float rr = length(uv-vec2(0.6,1.4))-0.8;
	vec3 coll=vec3(0.,rr*0.1,rr*0.24);

    coll=mix(coll,col*rand(t),c*step(0.1,r/111.));

    fragColor=vec4(coll,1.0);
}

vec4 iSphere(vec3 sp, float sr,vec3 ro, vec3 rd) //intersection entre une sphere et un rayon
{
vec3 p = sp-ro;
float d = dot(-rd,p);
float i = d*d- dot(p,p)+sr*sr;
d = i>0.?-sqrt(i)-d:-1000.;
    if(d<0.)
        i=-abs(i);
return vec4(normalize(rd*d-p),i>0.?d:1e6); //renvoie le vecteur normal au point d'intersection et la distance
}									//1e6 est un nombre tres grand

float iPlane(vec3 ro, vec3 rd, vec3 po, vec3 pn){//intersection entre un plan et un rayon
    float d =dot(rd,pn);
    if(abs(d)<1e-4)
        return -1.;
  	return dot(po-ro,pn)/d;//renvoie juste la distance
}

float iRec(vec3 ro, vec3 rd, vec3 po, vec3 pn,vec2 dim, vec3 vert){//intersection entre un rectangle et un rayon
    vec3 horiz = cross(pn,vert);
	float d =iPlane( ro,  rd,  po,  pn); //on calcule l'intersection avec le plan contenant le rectangle
    if(d>0.){
    	vec3 p = ro+d*rd-po;
    	if(abs(dot(p,horiz))>dim.x || abs(dot(p,vert))>dim.y) //on verifie si on est effectivement a l'interieur du rectangle
    		return 1e6;
     }else
     	return 1e6;
    return d;
}

vec2 rli(vec3 o1, vec3 d1, vec3 o2, vec3 d2){ //intersection entre deux rayons(demi-droites)
	vec3 n = normalize(cross(d1,d2));
    float md = dot(o1-o2,n);
    o1 = o1-md*n;					//o1 projeté dans le plan passant par o2
    vec3 dt = normalize(cross(d1,n)); //"transposée" de d1 dans le plan induit par d1,d2 
    float d = (dot(dt,o1))/dot(dt,d2);
    return vec2(abs(md)*d,abs(md)<d && dot(cross(d2,n),o1)>0.?d:1e6); //renvoie la distance minimale entre les deux rayons ainsi que la distance parcourue par le deuxieme rayon avant ce point
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.*fragCoord-iResolution.xy;

    float vFov = 1.5;			//pas réellement le Fov, mais des les variables "vFov" et "Fov" permettent d'influer sur l'angle
   	float Fov = 1.5;			//de la caméra virtuelle et de la caméra principale respectivement

    vec3 ro = vec3(0,0,0);							//origine des rayons = position de la "camera"
    vec3 rd = normalize(vec3(uv,iResolution.y*Fov));//direction du rayon



    vec3 ld = normalize(vec3(1,1,-1));//direction de la lumiere
    vec3 c = vec3(1);  //couleur temporaire (avcant gestion de la lumuiere


    vec3 spp = vec3(-3,1.1,8);//position de la sphere
    float spr = .8;//rayon de la sphere

    vec4 isph = iSphere(spp,spr,ro,rd);//sphere
    vec3 n = isph.rgb;
    float d = isph.a;//distance a l'objet le plus proche le long du rayon en cours


    float ipl = iPlane(ro,rd,vec3(0,-.5,0),normalize(vec3(0,1,0))); //sol
    if(ipl<d && ipl>0.){
        n = normalize(vec3(0,1,0));
    	d=ipl;
    }


    vec3 so = vec3(1.3,.6+.2*cos(iTime*.9),4.);	//position du centre de l'"ecran"
    so.xz+=.8*vec2(cos(iTime*.4),sin(iTime*.4));//animation
    vec3 sn = normalize(so-vec3(.35,.44,6.6));	//orientation de l'"ecran"
    vec3 ro2 = so+sn*vFov;						//position de la "camera" viruelle
    vec3 vert = normalize(vec3(0,1,0));			//orientation de la verticale de l'écran 
    vert =normalize(vert-dot(sn,vert)*sn);		//correction de la verticale pour qu'elle soit orthogonale a la direction de l'ecran
    vec2 format=vec2(1.6,.9);					//dimension de l'ecran

    float ir = iRec(ro,rd,so,sn,format,vert); 	//"ecran"
    if(ir<d && ir>0.){
        n = sn;
    	d=ir;
        vec3 ro = d*rd;
        vec3 rd2 = normalize(ro-ro2);
        vec4 isph = iSphere(spp,spr,ro,rd2); //encore la sphere
    	vec3 n = isph.rgb;
    	float d = isph.a;


    	float ipl = iPlane(ro,rd2,vec3(0,-.5,0),normalize(vec3(0,1,0)));//encore le sol
    	if(ipl<d && ipl>0.){
        	n = normalize(vec3(0,1,0));
    		d=ipl;
    	}
        float l =(iSphere(spp,spr,ro+d*rd2,ld).a>=1e6?max(0.,dot(ld,n)):0.);//lumiere directe
    	c = mix(vec3(.4+l),vec3(.5,.7,1.),float(d>1e5));//affichage de la scene sur l'"ecran" secondaire
    }



    vec4 io =iSphere(so+sn*vFov,.03,ro,rd); //camera (bille noire)
    if(io.a<d){
        n = io.rgb;
    	d=io.a;
        c = vec3(.1);
    }


    vec3 p =ro+(d-1e-5)*rd;
    float sh = min(iSphere(spp,spr,p,ld).a,min( iRec(p,ld,so,sn,format,vert),iSphere(so+sn*vFov,.03,p,ld).a));
    float l =(sh>=1e6?max(0.,dot(ld,n)):0.);//limiere directe

    vec3 col =  mix(c*vec3(.4+l),vec3(.5,.7,1.),float(d>1e5));//application de l'eclairage 


   	float epsilon = 2./(iResolution.y*Fov);//antialiasing

    vec2 itsc=(rli(ro2,normalize(ro2-spp-vec3(3,1,0)),ro,rd)); // le rayon qui n'intersecte pas la scène
    if(itsc.y<d && itsc.y>0.){
        col=mix(col,vec3(1,0,0),smoothstep(.05+epsilon*itsc.y*itsc.y,.05,itsc.x));
        d=mix(d,itsc.y,step(itsc.x,.06));
    }

    itsc=(rli(ro2,normalize(ro2-spp+.3),ro,rd));//le rayon qui intersecte la sphère
    if(itsc.y<d && itsc.y>0.){
        col=mix(col,vec3(0,1,0),smoothstep(.05+epsilon*itsc.y*itsc.y,.05,itsc.x));
        d=mix(d,itsc.y,step(itsc.x,.06));
    }

    vec3 horiz = cross(sn,vert);	//et les quatre coins
    itsc=(rli(ro2,normalize(ro2-so+format.x*horiz+format.y*vert),ro,rd));
    if(itsc.y<d && itsc.y>0.){
        col=mix(col,vec3(.4,.45,1),smoothstep(.05+epsilon*itsc.y*itsc.y,.05,itsc.x));
        d=mix(d,itsc.y,step(itsc.x,.06));
    }
    itsc=(rli(ro2,normalize(ro2-so-format.x*horiz+format.y*vert),ro,rd));
    if(itsc.y<d && itsc.y>0.){
        col=mix(col,vec3(.4,.45,1),smoothstep(.05+epsilon*itsc.y*itsc.y,.05,itsc.x));
        d=mix(d,itsc.y,step(itsc.x,.06));
    }
    itsc=(rli(ro2,normalize(ro2-so+format.x*horiz-format.y*vert),ro,rd));
    if(itsc.y<d && itsc.y>0.){
        col=mix(col,vec3(.4,.45,1),smoothstep(.05+epsilon*itsc.y*itsc.y,.05,itsc.x));
        d=mix(d,itsc.y,step(itsc.x,.06));
    }
    itsc=(rli(ro2,normalize(ro2-so-format.x*horiz-format.y*vert),ro,rd));
    if(itsc.y<d && itsc.y>0.){
        col=mix(col,vec3(.4,.45,1),smoothstep(.05+epsilon*itsc.y*itsc.y,.05,itsc.x));
        d=mix(d,itsc.y,step(itsc.x,.06));
    }


    col = pow(col*.6,vec3(1./2.24));//correction gamma

    fragColor = vec4(col,1.0);
}

