// color gradient
vec3 gradient(float s)
{
	return vec3(22.0, max(1.0-s*2.0, 0.0), max(s>0.5?1.0-(s-0.15)*5.0:21.0, 1.0));
}

// intersection for a sphere with a ray
#define RADIUS 0.5
bool intersectSphere(vec3 origin, vec3 direction, out float tmin, out float tmax)
{
    bool hit = false;
	float a = dot(direction, direction);
    float b = 2.0*dot(origin, direction);
    float c = dot(origin, origin) - 0.5*0.5;
    float disc = b*b - 4.0*a*c;           // discriminant
    tmin = tmax = 0.0;

    if (disc > 0.0) {
        // Real root of disc, so intersection
        float sdisc = sqrt(disc);
        float t0 = (-b - sdisc)/(2.0*a);          // closest intersection distance
        float t1 = (-b + sdisc)/(2.0*a);          // furthest intersection distance

		tmax = t1;
        if (t0 >= 0.0) 
            tmin = t0;
        hit = true;
    }

    return hit;
}

// rotate around axis
vec2 rt(vec2 x,float y)
{
	return vec2(cos(y)*x.x-sin(y)*x.y,sin(y)*x.x+cos(y)*x.y);
}

// shader main function
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// normalized and aspect ratio corrected pixel coordinate
    vec2 p = (fragCoord.xy / iResolution.xy)*2.0-1.0;
    p.x *= iResolution.x/ iResolution.y*2.0;
    p.y *= iResolution.x/ iResolution.y*2.0;

	// camera and user input
	vec3 oo = vec3(0, 0, 0.4-iMouse.y/iResolution.y);
	vec3 od = normalize(vec3(p.x, p.y, -2.0));
	vec3 o,d;	
	o.xz = rt(oo.xz, 6.3*iMouse.x/iResolution.x);
	o.y = oo.y;
	d.xz = rt(od.xz, 6.3*iMouse.x/iResolution.x);
	d.y = od.y;

	// render
	vec4 col = vec4(0, 0, 0, 0);
	float tmin, tmax;
	if (intersectSphere(o, d, tmin, tmax))
	{	
		// step thoug the sphere with max SLICES steps
		for (float i = 0.0; i < SLICES; i+=1.0)
		{
			// stay within the sphere bounds
			float t = tmin+i/SLICES;
			if (t > tmax) 
				break;
			vec3 curpos = o + d*t;

			// get sphere falloff in s
			float s = (0.5-length(curpos))*2.0;
			s*=s;

			// get turbulence in d
			float a = START_AMPLITUDE;
			float b = START_FREQUENCY;
			float d = START_DENSITY;
			for (int j = 0; j < 3; j++)									
			{
				d += 0.5/abs((fbm(5.0*curpos*b+ANIMATION_SPEED*iTime/b)*2.0-1.0)/a);
				b *= 2.0;
				a /= 2.0;
			}

			// get gradient color depending on s
			col.rgb += gradient(s)*max(d*s,0.0);
		}		
	}

	fragColor = col;
}

#define COUNT 5.
#define COL1 vec3(31, 29, 28)/255.
#define COL2 vec3(6, 243, 5)/255.

#define rand1(p) fract(sin(p* 78.233)* 43758.5453) 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;        

    float sum = 0.;  
    float sumG = 0.; //for GLOW

    for(float i=0.; i<COUNT; i++){
        float dir = mod(i, 2.)*2.-1.;        
        float a = iTime + i + rand1(i) * 3.1415;
        float l = length(vec2(uv.x+sin(a)*.25*dir, uv.y+cos(a*.5)*.25)*dir);

        sum+=.03/l;
        sumG+=.0275/l;
    }        
    sumG-=0.5;

    float fd = smoothstep(.7425, .75,  sum);

    vec3 col = mix(COL1, COL2, clamp(fd + sumG*3.0, 0., 1.));

    fragColor = vec4(col,1.0);
}

vec3 rand3(vec3 p){
    vec3 q=vec3(
        p.x*41.839+p.y*91.793+p.z*23.418,
        p.x*54.941+p.y*73.694+p.z*48.932,
        p.x*14.023+p.y*10.089+p.z*83.573
    );
	return fract(sin(q+0.25)*2434.2434)*2.0-1.0;
}

vec3 smth3(vec3 f){
	return f*f*f*(10.+f*(-15.+f*6.));
}
vec3 dsmth3(vec3 f){
	return 30.*(f*f*(1.+f*(-2.+f)));
}
