// Basic domain coloring plot (https://en.wikipedia.org/wiki/Domain_coloring)
// of the digamma/psi function w = ψ(z) (https://en.wikipedia.org/wiki/Digamma_function)

#define PI 3.14159265359
#define SCALE 5.0 // plot scale
#define SPACING 0.0625 // grid line spacing

// Constants for gamma function approximation

#define LG 4.15
#define P0 2.506628464366
#define P1 41.41740453024
#define P2 -27.0638924937
#define P3 2.239317963303

// from Fabrice Neyret, 
#define cmul(A,B) ( mat2( A, -(A).y, (A).x ) * (B) )
#define cinv(Z) ( vec2( (Z).x, -(Z).y ) / dot(Z, Z) ) 
#define cdiv(A,B) cmul( A, cinv(B) )
#define csqr(Z) cmul(Z, Z)

// Schlick bias function, from http://dept-info.labri.u-bordeaux.fr/~schlick/DOC/gem2.ps.gz
float bias( float a, float x )
{
    return x/((1.0/a - 2.0) * (1.0 - x) + 1.0);
}

// biased sawtooth
float my_saw( float x, float p )
{
    float xs = mod(x, 1.0);
    float xh = clamp(xs, 0.0, p);
    return 0.5 + 0.5 * bias(0.95, xh) * (1.0 - smoothstep(p, 1.0, xs));
}

// modified version of Inigo Quilez's method at https://www.shadertoy.com/view/MsS3Wc
// using "rational smoothstep" from https://tpfto.wordpress.com/2019/03/28/on-a-rational-variant-of-smoothstep/
vec3 smooth_hue( float h )
{
    vec3 rgb = clamp( abs(mod(6.0 * h + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
    return rgb * rgb * rgb/(1.0 - 3.0 * rgb * (1.0 - rgb));
}

// modified DLMF coloring, adapted from https://www.shadertoy.com/view/WtXGWN
vec3 smooth_dlmf( float h )
{
    vec3 rgb = clamp( vec3(1., -1., -1.) * abs((vec3(8., 4., 8.) * mod(h, 1.0) - vec3(4.5, 1.5, 5.5))) + vec3(-1.5, 1.5, 2.5), 0.0, 1.0 );
	return rgb * rgb * rgb/(1.0 - 3.0 * rgb * (1.0 - rgb));
}

// complex cotangent

vec2 cotz( in vec2 z )
{
	return vec2(-sin(2.0 * z.x), sinh(2.0 * z.y))/(cos(2.0 * z.x) - cosh(2.0 * z.y));
}

// complex logarithm

vec2 logz( in vec2 z )
{
	return vec2(log(length(z)), atan(z.y, z.x));
}

// digamma function, Lanczos approximation (https://doi.org/10.1137/0701008) with Toth's coefficients (https://www.rskey.org/gamma.htm)

vec2 Psi( in vec2 z )
{
    vec2 zz = (z.x > 1.0) ? (z) : (vec2(1.0, 0.0) - z);
    vec2 sum1 = P1 * csqr(cinv(zz + vec2(1.0, 0.0))) + P2 * csqr(cinv(zz + vec2(2.0, 0.0))) + P3 * csqr(cinv(zz + vec2(3.0,0.0)));
    vec2 sum2 = vec2(P0, 0.0) + P1 * cinv(zz + vec2(1.0, 0.0)) + P2 * cinv(zz + vec2(2.0, 0.0)) + P3 * cinv(zz + vec2(3.0,0.0));
    vec2 zh = zz + vec2(LG, 0.0);
    vec2 w = logz(zh) - cdiv(zh + (LG - 0.5) * zz, cmul(zz, zh)) - cdiv(sum1, sum2);

    return ((z.x > 1.0) ? w : w - PI * cotz(PI * z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 aspect = iResolution.xy / iResolution.y;
    vec2 z = ( fragCoord.xy / iResolution.y ) - 0.5 * aspect;
    z *= SCALE;

    vec2 w = Psi(z);
    float ph = atan(w.y, w.x);
    float lm = log(0.0001 + length(w));

    vec3 c = vec3(1.0);
    c = smooth_dlmf(0.5 * (ph / PI));
    // uncomment for HSV version 
    // c = smooth_hue(0.5 * (ph / PI));

    c *= mix(1.0, my_saw((0.5 * (lm/PI))/SPACING, 0.95) * my_saw((0.5 * (ph / PI))/SPACING, 0.95), 0.5 + 0.5 * cos(iTime));
    fragColor = vec4(c, 1.0);
}

precision highp float;
float t;

float sym(vec2 p, float fs) {
    vec2 s = vec2(mod(fs,16.), 16.-floor(fs/16.));
    vec2 tsz = vec2(iChannelResolution[0]), rsz = 1. / tsz;
    p = (p + vec2(16.,0.) + s*64.)*rsz;
    return smoothstep(.5, .49, texture(iChannel0, p).a);
}

bool text_balls = false;
float text(vec2 p) {
	if (text_balls) {
		vec2 cid = floor(p), cc=(p-cid)*2.-1.;
		return step(length(cc), .7);
    } else {
        p *= .1;
        p += vec2(.1, .1);
        p *= vec2(iChannelResolution[0]);
        vec2 gsz = vec2(16.,64.);
        vec2 ci = floor(p/gsz), cp = mod(p, gsz);
#define SYM(a) if (ci.x == 0.) { return sym(cp*vec2(2.,1.), a); } ci.x -= 1.;
        if (ci.y == 1.) {
        	SYM(122.); SYM(117.); SYM(132.); SYM(124.); SYM(113.); SYM(119.);
        } else if (ci.y == 0.) {
            SYM(128.); SYM(113.); SYM(131.); SYM(132.); ci.x -= 1.;
            SYM(130.); SYM(113.); SYM(115.); SYM(117.); SYM(130.);
        }
        return 0.;
    }
}

const float PI=3.1415923;
const vec3 E=vec3(0.,.01,1.);

float hash1(float f){return fract(sin(f)*46347.423874);}
float hash2(vec2 v){return hash1(dot(v,vec2(79.53248,31.4328)));}

float noise2(vec2 v) {
	vec2 V=floor(v);v-=V;
	v*=v*(3.-2.*v);
	return mix(
		mix(hash2(V+E.xx), hash2(V+E.zx), v.x),
		mix(hash2(V+E.xz), hash2(V+E.zz), v.x), v.y);
}

mat3 RX(float a){float c=cos(a),s=sin(a);return mat3(1.,0.,0.,0.,-s,c,0.,c,s);}
mat3 RY(float a){float c=cos(a),s=sin(a);return mat3(c,0.,s,0.,1.,0.,-s,0.,c);}
mat3 RZ(float a){float c=cos(a),s=sin(a);return mat3(c,s,0.,-s,c,0.,0.,0.,1.);}

vec3 O, D, P, N;
vec2 UV;
int M;
float l;

void aapl(float offset, vec3 axis, int mi) {
	float x = dot(O,axis), rx = dot(D,axis), s = sign(rx);
	float lp = (s * offset - x) / rx;
	if (lp < l) {
		l = lp;
		P = O + D * l;
		N = - s * axis;
		M = mi;
		UV = axis.x*P.yz + axis.y*P.xz + axis.z*P.xy;
	}
}

float ss(float f){return max(0.,min(1.,f));}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    t = mod(iTime, 120.) * 12.0;
    vec2 R = iResolution.xy;
	vec2 uv=(fragCoord.xy/R)*2.-1.;uv.x*=R.x/R.y;

    //fragColor=vec4(hash2(uv));return;

	vec3 c=vec3(0.);
	float seed = t;

	float tpat = t/32.;

	////////////// TWEAK THESE
	float ls = .2, // lens size
				lf = 4. + 3.,// * sin(t*.1), // focus distance
				lS = 1.; // fov-ish
	vec3 sz = vec3(1.5,2.,6.);
	float or = 4.;
	mat3 mv = mat3(1,0,0,0,1,0,0,0,1);
	//mv = RY(2.*sin(t*.01))*RY(3.*sin(t*.039));
	//mv = RY(2.+sin(t*.05));
	float text_offset = -2. + 6. * hash1(floor(t/8.));
	float pscl = 4.;
	bool text_plane = false;
	bool pix1 = false, pix2 = false;
	bool pixmv = false;
	bool proj2 = false;
	bool proj1 = false;
	vec3 bgcolor = .5 * vec3(.3,.5,.8);
	float zmove = 0.;
	vec2 pixmvs = vec2(0.);
	float pixmvl = 1.;
	bool rpix = true;
	vec3 rpixc = vec3(0.);
	vec3 pixmvc = vec3(1.);
	vec3 pix2c = vec3(1.);
	vec3 pc = vec3(0.,0.,-6.);

	sz=vec3(8.);
	bgcolor = vec3(0.);
	ls = 1.;
	lf = 4.;
	rpix = true;
	rpixc = vec3(tpat/6.);
	mv = RY(tpat)*RX(tpat*.3);

	if (tpat > 6.) {
		pixmv = true;
		pixmvs=vec2(1., 0.);
	}

	if (tpat > 9.) {
		float tt=ss(tpat-9.);
		sz = mix(sz, vec3(1.5,2.,6.), tt);
		or = mix(or,1.,tt);
		ls = mix(ls,.1,tt);
	}

	if (tpat > 13.6) {
		float tt = smoothstep(13.6, 14., tpat);
		lS = mix(10., 1., tt);
		ls = mix(4., .1, tt);
		lf = mix(.1, 7., tt);
		mv = RZ(1.-tt);
		zmove = 4.;
	}

	if (tpat > 14.) {
		mv = RZ(.1*sin(tpat-14.));
	}

	if (tpat > 17.) {
		text_plane = true;
		text_offset = -6. + 2.*floor(tpat-17.);
	}

	if (tpat > 19.) {
		rpix = false;
	}

	if (tpat > 20.) {
		pixmv = false;
	}

	if (tpat > 21.8) {
		float tt=smoothstep(21.8, 22., tpat);
		sz = mix(sz, vec3(4.,1.,-1.), tt);
		pixmv = true;
	}

	if (tpat > 22.) {
		mv = RZ(tpat-22.);
		text_plane = false;
		bgcolor = .01 * vec3(.3,.5,.8);
		zmove = 47.;
	}

	if (tpat > 23.) {
		float tt = ss(tpat-23.);
		pixmvl = mix(pixmvl, 5., tt);
		pixmvs = mix(pixmvs, vec2(1., 10.), tt);
		pixmvc = .5 * vec3(.2, .5, .9);
		lf = 10.;
		ls = .1;
	}

	if (tpat > 24.) {
		text_plane = true;
		text_balls = true;
		text_offset = -8. + 2.*mod(floor(tpat-23.), 4.);
	}

	if (tpat > 26.) {
		rpix = true;
		rpixc = vec3(1.);
		text_plane = false;
		float tt = ss(tpat-26.);
		sz = mix(sz, vec3(2.,2.,-1.), tt);
		or = mix(or, 2., tt);
		mv *= RY(tt*(tpat-26.));
	}

	if (tpat > 28.) {
		proj1 = true;
		lf = 4.;
		pc = vec3(vec2(cos(tpat*.3), sin(tpat*.7)) * 1.5, 10.*sin(tpat*.9));
	}

	if (tpat > 30.) {
		ls = .12, // lens size
				lf = 4. + 3.,// * sin(t*.1), // focus distance
				lS = 7.; // fov-ish
		pixmv = false;
	}

	if (tpat > 34.) {
		proj1 = false;
		proj2 = true;
		lS = .1;
		bgcolor = .5 * vec3(.3,.5,.8);
	}

	if (tpat > 38.) {
		//bgcolor = vec3(1.,.0,.0);
		mv = RX(-3.);
	}

	// PATHTRACER STARTS
	lS *= lf;
#define NS 32.
	for (float s=0.;s<NS;++s) {
		float a = hash1(seed+=uv.x)*2.*PI, r = s/NS;
		O = vec3(vec2(cos(a),sin(a))*r*ls, or);
		vec3 at = vec3(uv*lS, O.z-lf);
		D = normalize(at - O) * mv; O *= mv;
		vec3 kc = vec3(1.);
		for (int i = 0; i < 4; ++i) {
			l = 1e6;
			aapl(sz.x, vec3(1.,0.,0.), 1);
			aapl(sz.y, vec3(0.,1.,0.), 2);
			if (sz.z>0.) aapl(sz.z, vec3(0.,0.,1.), 3);

			if (text_plane) {
				float dist = text_offset - O.z;
				float lt = dist / D.z;
				vec3 tp = O+D*lt;
				if (lt > 0. && lt < l) {
					if (text(tp.xy) > 0.) {
						l = lt;
						P = tp;
						N = vec3(0., 0., sign(dist));
						M = 4;
						UV = P.xy;
					} /* alpha glass fx */ //else { kc *= vec3(.5, .5, .7); }
				}
			}

			vec3 me = vec3(0.), ma = vec3(.7);
			float mr = .5;

			if (M == 4) {
				ma = vec3(.9);
				me = 4. * vec3(step(1.3,mod(t*.5+dot(3.*normalize(vec2(1.,1.)), UV), 2.)));
				mr = .9;
			} else {
                // Windows/ANGLE fix
				//UV.y -= tpat*zmove;
				//UV *= pscl;
                vec2 fix_uv_wtf = (UV - vec2(0., tpat*zmove)) * pscl;

                vec2 cid = floor(fix_uv_wtf);
				vec2 cpc = fract(fix_uv_wtf)*2. - 1.;

                if (rpix) {
					me += rpixc * step(.9,hash2(cid));
				}

				if (pix1) {
					float e2step = 2.;
					float e2a = t * .2;
					vec2 e2dir=vec2(cos(e2a),sin(e2a));
					float d = dot(UV, e2dir);
					me += vec3(2,0,0.5) * step(abs(d),e2step);
				}

				if (pix2) {
					me += max(me, pix2c*mod(floor(-.2*t+(dot(cid/12.,vec2(3.)))), 2.));
				}

				if (pixmv) {
					float pos = floor(t * pixmvs.x + pixmvs.y * hash1(cid.x));
					float len = 16.;
					float p = mod(cid.y + pos, len);
					me += pixmvc * step(len-pixmvl,p);
				}

				{
					vec3 ruv = normalize(floor(P*pscl) - pc);
					ruv *= RY(t*.1);

					vec3 dir = normalize(vec3(1.));

					me += bgcolor;

					if (proj1) {
						 me += 2.*vec3(.9,.7,.2) * step(1.5, mod(t *.1+ 6.*dot(ruv, dir), 2.));
					}

					if (proj2) {
						me += 7. * vec3(.9,.7,.2) * pow(max(0.,dot(dir,ruv)), 100.);
					}
				}

				me *= vec3(
					step(length(cpc),.8),
					step(length(cpc+vec2(.1,0.)),.8),
					step(length(cpc+vec2(.0,.1)),.8)
				);
				mr = .001 + hash2(cid)*.3;
				ma *= step(abs(cpc.x),.9)*step(abs(cpc.y),.9);
			}

			//kc *= 1. - l/40.;
			c += kc * me;
			kc *= ma;

			if (all(lessThan(kc,vec3(.001)))) break;

			O = P;
			D = normalize(mix(
				reflect(D, N),
				vec3(hash1(seed+=P.z),hash1(seed+=D.x),hash1(seed+=P.y))*2.-1., mr));
			D *= sign(dot(D, N));
		}
	}

	c /= NS;

	fragColor=vec4(sqrt(c), 0);
}

const float PI = 3.1415926;
#define AA 2

vec3 hsv(float h, float s, float v){
    vec4 t = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(h) + t.xyz) * 6.0 - vec3(t.w));
    return v * mix(vec3(t.x), clamp(p - vec3(t.x), 0.0, 1.0), s);
}
mat2 rotate(in float r){
    return mat2(cos(r), -sin(r), sin(r), cos(r));
}
float usin(in float x){
    return 0.5+0.5*sin(x);
}
float gray(in vec3 color){
    return dot(color, vec3(0.299, 0.587, 0.114));
}

float sdSphere(in vec3 p, in float r){
    return length(p) - r;
}
float sdBox(in vec3 p, in vec3 b){
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)), 0.) + length(max(d, 0.));
}

vec3 opRep(in vec3 p, in vec3 c){
    return mod(p, c) - 0.5*c;
}

float sdObj(in vec3 p){
    vec3 q = p;
    q.xy *= rotate(q.z*PI*0.05+iTime*0.1);
    q = opRep(q, vec3(5.0));
    // q.z
    q.xy *= rotate(q.z*PI+iTime);
    return sdBox(q, vec3(2.5, 0.1, 2.5));
}

vec2 map(in vec3 p){
    vec2 res = vec2(0.0);
    vec3 q = p;

    res = vec2(sdObj(q), 1.);
    return res;
}

vec3 calcNormal(in vec3 p){
    vec2 e = vec2(1.0, -1.0) * 0.00001;
    return normalize(vec3(
            e.xyy*map(p+e.xyy).x +
            e.yxy*map(p+e.yxy).x +
            e.yyx*map(p+e.yyx).x +
            e.xxx*map(p+e.xxx).x
        ));
}

mat3 lookAt(in vec3 eye, in vec3 tar, in float r){
    vec3 cw = normalize(tar - eye);
    vec3 cp = vec3(sin(r), cos(r), 0.);
    vec3 cu = normalize(cross(cp, cw));
    vec3 cv = normalize(cross(cw, cu));
    return mat3(cu,cv,cw);
}

vec2 castRay(in vec3 ro, in vec3 rd){
    float minD=0.0, maxD = 30.0;
    float d=minD, m=0.0;

    for(int i=0;i<32;i++){
        vec3 pos = ro + rd*d;
        vec2 tmp = map(pos);
        if(tmp.x<0.0000001 || maxD<tmp.x) break;
        d += tmp.x*0.6;
        m = tmp.y;
    }
    if(maxD<d) m = -1.0;

    return vec2(d, m);
}

vec3 render(in vec3 ro, in vec3 rd){
    vec2 res = castRay(ro, rd);
    float d = res.x;
    float m = res.y;

    vec3 pos = ro + rd * d;
    vec3 nor = calcNormal(pos);

    vec3 lightDir = normalize(vec3(cos(iTime), 1., sin(iTime)));

    vec3 color = vec3(0.);
    if(m==1.){
        color += clamp(dot(lightDir, nor), 0., 1.);
        float g = gray(color);
        color = hsv(0.5+0.1*sin(iTime*0.5)+g*(0.4+usin(iTime*0.001)), 1.0, 1.0);
        color += usin(d+iTime*20.0);
        color *= exp(-0.03*d);
    }else {
        color = vec3(0.0);
    }


    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 eye = vec3(0., 0., iTime);
    vec3 tar = eye-vec3(0.1*sin(iTime*1.2+sin(iTime*0.5)), 0.1*sin(iTime*.9+sin(iTime*1.3)), 1.0);

    vec3 color = vec3(0.);
    for(int i=0;i<AA;i++){
        for(int j=0;j<AA;j++){
            vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
            p += vec2(float(i), float(j))/iResolution.xy;
            vec3 dir = normalize(lookAt(eye, tar, iTime*0.4+0.2*sin(iTime*0.8)) * vec3(p, 1.0));
            dir = normalize(vec3(dir.xy, sqrt(max(dir.z*dir.z - dot(dir.xy, dir.xy)*0.2, 0.))));

            color += render(eye, dir);
        }
    }
    color /= float(AA*AA);
    fragColor = vec4(color,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.1 + 0.1*cos(iTime+uv.xyx+vec3(0,0,2));

    // Output to screen
    fragColor = vec4(col,2.1);
}

#define PI acos(-1.)

mat2 rotate(float angle)
{
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c, -s, s, c);
}

vec4 laser(vec2 uv, vec4 color)
{
    uv -= 0.5;
    float w = pow( .1 / length(uv), 3.);
    return color * w * .7;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

	vec3 dir = normalize(vec3(uv, 1));
    dir.xy *= rotate(sin(iTime * .3));

    vec2 luv = vec2(.3 / length(dir.xy), atan(dir.x , dir.y) / PI * .5);

    // Let's give it a green color, we are in the sewer after all
    fragColor = laser(fract(luv), vec4(.1, 1., .1, 1.));
}

// Original idea by Mikkel Gjoel (https://computergraphics.stackexchange.com/a/5952/10515),
// simplified to one readable function.

// Dithers and quantizes color value c in [0, 1] to the given color depth.
// It's expected that rng contains a uniform random variable on [0, 1].
uint dither_quantize(float c, uint depth, float rng) {
    float cmax = float(depth) - 1.0;
    float ci = c * cmax;

    float d;
    if (ci < 0.5 || ci >= cmax - 0.5) {
        // Uniform distribution on [-0.5, 0.5] for edges.
        d = rng - 0.5;
    } else {
        // Symmetric triangular distribution on [-1, 1].
        d = (rng < 0.5) ? sqrt(2.0 * rng) - 1.0 : 1.0 - sqrt(2.0 - 2.0*rng);
    }

    return uint(clamp(ci + d + 0.5, 0.0, cmax));
}



#define LINE_THICKNESS 0.002
#define DEPTH 4.
#define REPS 500.

const uint k = 1103515245U;  // GLIB C
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

float line(float d) {
    return smoothstep(0., d, LINE_THICKNESS);
}


float i2f(uint i) {
    return float(i) / (DEPTH - 1.0);
}

vec3 graphic(vec2 uv) {
    vec2 g = vec2(uv.x * 1.5 - 0.25, (uv.y - 0.05) / 0.4);

    if (-LINE_THICKNESS < g.x && g.x < 0. ||
        1.0 < g.x && g.x < 1.0 + LINE_THICKNESS) {
        return vec3(0.5, 0.5, 0.5);
    }

    uvec3 seed = uvec3(int(uv.x * 10000.), int(uv.y*10000.), iFrame);
    float signal = clamp(g.x, 0.0, 1.0);
    float dithered = i2f(dither_quantize(signal, uint(DEPTH), hash(seed).x));
    float avg = 0.;
    float variance = 0.;
    for (float i = 0.; i < REPS; i += 1.) {
        uvec3 r = seed + uint(i);
        float d = i2f(dither_quantize(signal, uint(DEPTH), hash(r).x));
        avg += d / REPS;
        variance += (d - signal) * (d - signal) / REPS;
    }

    if (uv.y > 5./6.) {
        return vec3(signal);
    }

    if (uv.y > 4./6.) {
        return vec3(avg);
    }

    if (uv.y > 3./6.) {
        return vec3(dithered);
    }

    vec3 c = vec3(0.);

    // Signal.
    c += vec3(0.5) * line(abs(g.y - signal));

    // Average dithered signal.
    c += vec3(1.0, 1.0, 0.0) * line(abs(g.y - avg));

    // Error of average.
    float error = (avg - signal) * 5.;
    c += vec3(1.0, 0.0, 0.0) * line(abs(g.y - 0.5 - error));

    // Variance.
    c += vec3(0.0, 0.0, 1.0) * line(abs(g.y - variance * 15.));

    return c;
}


