// contour/density plotter, with hill shading

// some constants
#define PI 3.14159265359

#define BLACK vec3(0.0)
#define WHITE vec3(1.0)

// dark and light colors from Solarized (https://ethanschoonover.com/solarized/)
#define DARK vec3(0., 0.169, 0.212)
#define LIGHT vec3(0.992, 0.965, 0.89)
#define GRAY vec3(0.396, 0.482, 0.514)

// XY range of the display.
#define DISP_SCALE 6.5 

// comment out to disable contours
#define SHOW_CONTOURS

#ifdef SHOW_CONTOURS
// Contour thickness (in pixels).
#define CONTOUR_SIZE 1.0

// number of contours
#define CONTOURS 12

// contour color
#define CONTOUR_COLOR GRAY
#endif

// comment out to disable hill shading
#define SHADE

// Clip areas outside DISP_SCALE
#define CLIP_EDGES

// rescaling functions

float logistic(float x)
{
	  float ex = exp(-abs(x));
      return ((x >= 0.0) ? 1.0 : ex)/(1.0 + ex);
}

float rescale(float x, vec2 range)
{
	  float a = range.x, b = range.y;
      return (x - a)/(b - a);
}

float rescale(float x, vec2 r1, vec2 r2)
{
	  float a = r1.x, b = r1.y;
      float c = r2.x, d = r2.y;
      return c + (d - c) * ((x - a)/(b - a));
}

// using rational version of smoothstep from https://tpfto.wordpress.com/2019/03/28/on-a-rational-variant-of-smoothstep/
float myStep(float e0, float e1, float x)
{
    float t = clamp((x - e0)/(e1 - e0), 0.0, 1.0);
	return t * t * t/(1.0 - 3.0 * t * (1.0 - t));
}

// custom colormap

vec3 land(float t)
{
    const float eps = 0.05;
    const vec3 bl = vec3(0.0);

    const vec3 c1 = vec3(0.277, 0.355, 0.484);
    const vec3 c2 = vec3(0.337, 0.468, 0.376);
    const vec3 c3 = vec3(0.495, 0.563, 0.427);
    const vec3 c4 = vec3(0.735, 0.728, 0.66);
    const vec3 c5 = vec3(1., 0.997, 0.914);

    vec3 cc = + mix(c1, c2, 4.0 * t)
              + mix(bl, c1 - 2.0 * c2 + c3, 4.0 * t - 1.0) * myStep(0.25 - eps, 0.25 + eps, t)
              + mix(bl, c2 - 2.0 * c3 + c4, 4.0 * t - 2.0) * myStep(0.5 - eps, 0.5 + eps, t)
              + mix(bl, c3 - 2.0 * c4 + c5, 4.0 * t - 3.0) * myStep(0.75 - eps, 0.75 + eps, t);

    return pow(cc, vec3(1.6));

}

const vec2 GRADH = vec2(0.01, 0);

// gradient using central differences
#define GRAD(f, p) (0.5 * vec2(f(p - GRADH.xy) - f(p + GRADH.xy), f(p - GRADH.yx) - f(p + GRADH.yx)) / GRADH.xx)

// PLOT_CONTOUR(Function, Height, Destination, Screen Position); adapted from https://www.shadertoy.com/view/4tB3WV
#define PLOT_CONTOUR(f, z, d, p) d = mix(CONTOUR_COLOR, d, smoothstep(0.0, (CONTOUR_SIZE / iResolution.y * DISP_SCALE), abs((z - f(p))/ length(GRAD(f, p)))))

// utility functions
#define cis(a) vec2( cos(a), sin(a) )
#define sqr(a) ((a) * (a))

// function to plot
float f(vec2 p)
{
      float x = p.x, y = p.y;

      // "peaks" function from MATLAB (https://www.mathworks.com/help/matlab/ref/peaks.html)
      float z = 3.0 * sqr(1.0 - x) * exp(-x * x - sqr(y + 1.0)) - 10.0 * ((0.2 - x * x) * x - y * sqr(y * y)) * exp(-x * x - y * y) - exp(-sqr(x + 1.0) - y * y)/3.0;

      return z;
}

// range of height values; the need to manually determine them is the weakness of this approach
#define ZRANGE vec2(-6, 7)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
        vec2 aspect = iResolution.xy / iResolution.y;
        vec2 uv = ( fragCoord.xy / iResolution.y ) - 0.5 * aspect;
        uv *= DISP_SCALE;

        // evaluate function
        float z = f(uv), g = 1.0;
        vec3 col = land(rescale(z, ZRANGE));

        // plot the contour lines
        #ifdef SHOW_CONTOURS
        float fac = (ZRANGE.y - ZRANGE.x)/float(CONTOURS - 1);
        for (int j = 0; j < CONTOURS; j++) PLOT_CONTOUR(f, ZRANGE.x + fac * float(j), col, uv);
        #endif

        #ifdef SHADE
        // gradient using forward differences
        float h = 0.5 * DISP_SCALE/iResolution.y;
        vec2 grad = z - vec2(f(uv + vec2(h, 0.0)), f(uv + vec2(0.0, h)));
        // uncomment to use GPU derivatives instead
        // vec2 grad = - vec2(dFdx(z), dFdy(z));

        // lighting angles for hill shading
        float th = PI * (0.25 * iTime + 0.25);
        float ph = 0.125 * PI * (1.0 + cos(0.5 * PI * iTime));

        // shading factor
        g = 1.0 - 0.25 * (1.0 + cos(ph) * dot(cis(th), grad)/(pow(h, 1.2) + length(grad)));
        #endif

        col *= g;

        #ifdef CLIP_EDGES
        col = mix(DARK, col, 1.0 - step(0.5 * DISP_SCALE, abs(uv.x)));
        #endif

        fragColor = vec4( vec3(col), 1.0 );
}

// Chain Reaction by eiffie
