//texture asap
float noise4( in vec2 x )
{
    vec2 p = floor(x);
    vec2 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0;

    float res = mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                    mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y);

    return res;
}
//fungsi untuk membuat asap 
float SmokeParticle(vec2 loc, vec2 pos, float size, float rnd)
{
	loc = loc-pos;
	float d = dot(loc, loc)/size;

	if (d > 1.0) return 0.0;

	// Memutarkan particle nya
	float r= time*rnd*1.85;//untuk menggerakkan asap
	float si = sin(r);
	float co = cos(r);
	// Menggunakan 'rnd' untuk menggant noise.
	d = noise4(hash(rnd*828.0)*83.1+mat2(co, si, -si, co)*loc.xy*2./(pos.y*.16)) * pow((1.-d), 3.)*.7;
	return d;
}

//fungsi untuk membuat batu
float RockParticle(vec2 loc, vec2 pos, float size, float rnd)
{
	loc = loc-pos;
	float d = dot(loc, loc)/size;
	if (d > 1.0) return 0.0;
	float r= time*1.5 * (rnd);//untuk menggerakkan batu
	float si = sin(r);
	float co = cos(r);
	d = noise4((rnd*38.0)*83.1+mat2(co, si, -si, co)*loc*143.0) * pow(1.0-d, 15.25);//rendering utama untuk batu
	return pow(d, 2.)*5.;

}


//Gunung
float MountDf (vec3 p, float dMin)
{
  vec3 q;
  float d, a, r, hd, s;
  q = p;
  a = atan (q.z, q.x) / (2. * pi) + 0.5;
  r = length (q.xz);
  s = 2. * Fbm2 (vec2 (33. * a, 7. * r)) - 0.5;
  d = PrCylDf (q.xzy, 2.0, 3.0);
  q.y -= 0.75;
  d = max (d, - (PrSphDf (q, 0.35) - 0.03 * s));
  hd = 0.015 * (1. + sin (64. * pi * a) + 2. * sin (25. * pi * a)) *
     SmoothBump (0.5, 1.8, 0.3, r) + 0.15 * s * SmoothBump (0.1, 2., 0.2, r);
  q.y -= 1.2 + hd;
  d = max (max (d, - PrTorusDf (q.xzy, 2.8, 2.7)), 0.4 - length (q.xz));//Mengatur ukukuran dan bentuk pada gunung
  q = p;
  q.y -= -0.75;
  d = max (SmoothMin (d, PrCylDf (q.xzy, 2.5, 0.05 *
     (1. - smoothstep (2.2, 2.5, length (q.xz)))), 0.2), - q.y);
  if (d < dMin) { dMin = d;  idObj = idMnt; }
  q = p;
  q.y -= lavHt;
  d = PrCylDf (q.xzy, 0., 0.02);
  if (d < dMin) { dMin = d;  idObj = idLav; }
  return 0.8 * dMin;
}

float ObjDf (vec3 p)
{
  float dMin, d;
  dMin = dstFar;
  dMin = MountDf (p, dMin);
  for (int j = 0; j < NROCK; j ++) {
    d = PrSphDf (p - rkPos[j].xyz, rkPos[j].w);
    if (d < dMin) { dMin = d;  idObj = idRock;  qRad = rkPos[j].w; }
  }
  return dMin;
}
//pencahayaan pada gunung
float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 100; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy),
     ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}




//background colour
vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float f;
  if (rd.y >= 0.) {
    col = mix (col, vec3 (0.8), clamp (3. * (f - 0.5) * rd.y + 0.1, 0., 1.));
  } else {
    ro -= ((ro.y + 0.75) / rd.y) * rd;
    col = vec3 (0.17, 0.14, 0.05) * (0.7 + 0.3 * Fbm2 (10. * ro.xz));
    f = 0.3 - smoothstep (0.1, 1., length (ro.xz) / dstFar);
    vn = VaryNf (10. * ro, vec3 (0., 1., 0.), 3. * f);
    col = col * (0.1 + 0.1 * max (vn.y, 0.) + 0.8 * max (dot (vn, sunDir), 0.));
  }
  return col;
}


//triger untuk ouput gunung dan tanah 
vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, roo;
  float dstHit, dstFlm, dstFlmR, intFlm, bgFlm, s, dstSmk;

  roo = ro;


  dstHit = ObjRay (ro, rd);
  bgFlm = (5.0 + 1.0 * Noiseff (10. * tCur));//ambiance pada gunung

  if (dstHit >= dstFar) col = BgCol (ro, rd);
  else {
    ro += dstHit * rd;
    vn = ObjNf (ro);
    if (idObj == idMnt) {
      s = clamp (ro.y / 1.2 + 0.6, 0., 1.);
      vn = VaryNf (10. * ro, vn, 5. - 2. * s);
      col = (0.5 + 0.7 * bgFlm * s) * vec3 (0.2 + 0.1 * (1. - s),
         0.05 + 0.2 * (1. - s), 0.05);
      col = col * (0.1 + 0.1 * max (vn.y, 0.) +
         0.8 * max (dot (vn, sunDir), 0.));


    }
  }
  if (intFlm > 0.) col = mix (col, bgFlm * mix (vec3 (1., 0., 0.1),
     vec3 (1., 1., 0.5), 0.5 * intFlm), 1.2 * intFlm);
  ro = roo - smkPos;


  col = mix (col, col4.rgb, min (col4.a, 1.));
  return pow (clamp (col, 0., 1.), vec3 (0.7));
}

