mat2 r2d (float deg)
{
    float r = radians (deg);
    float c = cos (r);
    float s = sin (r);
    return mat2 (c, s, -s, c);
}

vec3 smin (vec3 d1, vec3 d2, float k)
{
    vec3 h = clamp (.5 + .5*(d2 - d1)/k, .0, 1.);
    return mix (d2, d1, h) - h*k*(1. - h);
}

float vary (float v, float s)
{
    return v*(.5 + .5*cos(s*iTime));
}

// ollj suggested map() variation, which lets you control smooth-minimum distance and
// cube edge-radius with the mouse
float map (vec3 p, out int id)
{
    float t = iTime*.5;
    float s = 3.;
    for(float i = .0; i < 5.; i += 1.) {
        p.xz *= r2d (degrees(t + i));
        p.xy *= r2d (degrees(-t*.4 - i));
        p = smin (p, -p, -.25-3.*iMouse.x/iResolution.x);
        p -= s; s *= .625;
    }
    vec3 size = vec3 (.4);
    float d = length (max (vec3 (.0), abs (p) - size))-iMouse.y/iResolution.y;
    id = 3;
    return d;
}

// my original version of map()
float map2(vec3 p, out int id)
{
	float t = iTime*.5;
	float s = 3.;
	for(float i = .0; i < 5.; i += 1.) {
		p.xz *= r2d (degrees(t + i));
		p.xy *= r2d (degrees(-t*.4 - i));
		p = smin (p, -p, -1.25);
		p -= s;
		s *= .625;
	}
	vec3 size = vec3 (.4);
    float d = length (max (vec3 (.0), abs (p) - size)) - .1;
    id = 3;
    return d;
}

float march (vec3 ro, vec3 rd, out int id, out int iter)
{
	float t = .0;
	float d = .0;
	for (int i = 0;i < 48; ++i) {
		iter = i;
		t = map(ro + d*rd, id);
		if (abs(t) < .01*(1. + .125*t)) break;
		d += t*.95;
	}
	return d;
}

vec3 normal (vec3 p)
{
    int ignored;
    vec2 e = vec2 (.001, .0);
    float d = map (p, ignored);
    return normalize (vec3 (map (p + e.xyy, ignored),
                            map (p + e.yxy, ignored),
                            map (p + e.yyx, ignored)) - d);
}

float shadow (vec3 p, vec3 ldir, vec3 n, float ldist)
{
    int ignored;
    int ignored2;
    float d2w = march (p + .01*n, ldir, ignored, ignored2);
    return ldist < d2w ? 1. : .25;
}

float ao (vec3 p, vec3 n, float d)
{
	int ignored;
	return clamp (map (p + d*n, ignored)/d, .0, 1.);
}

vec3 shade (vec3 rd, vec3 p, vec3 n, vec3 lp, vec3 lc, float li, int id)
{
    vec3 am = vec3(.1);
    float ldist = distance (lp, p);
    float att = 800./(ldist*ldist);
    vec3 ldir = normalize(lp - p);
    vec3 h = normalize(-rd + ldir);
    float s = shadow(p, ldir, n, ldist);
    vec3 mat = vec3(.3);
    float shiny = 80.;
    if (id == 3){ mat = vec3 (1.,.5,.25); shiny = 70.;}
    float sp = pow (max (.0, dot (n, h)), shiny);
    float diff = max (.0, dot (n, ldir));
    float ao = ao (p, n, 1.5);

    return att*s*(am + ao*diff*li*lc*mat + sp*vec3 (1.));
}

vec3 gradient (float value) {
	vec3 color = vec3 (.2);
	if (value > .0 && value < .2) {
		color = vec3 (.3, .4, .5);
	} else if (value >= .2 && value < .4) {
		color = vec3 (.35, .5, .7);
	} else if (value >= .4 && value < .7) {
		color = vec3 (.4, .6, .8);
	} else if (value >= .7 && value < .9) {
		color = vec3 (.5, .7, .85);
	} else if (value >= .9) {
		color = vec3 (.85, .9, .95);
	}
	return color;
}

vec3 shadeToon (vec3 p, vec3 n, vec3 lp, vec3 rd)
{
	vec3 ldir=normalize(lp-p);
	float diff=max(.0,dot(n,ldir));
	vec3 color = gradient (diff);
	vec3 outline = vec3 (.0);

    return color;
}

vec3 camera (vec2 uv, vec3 ro, vec3 aim, float z)
{
    vec3 f = normalize (aim - ro);
    vec3 wu = vec3 (.0, 1., .0);
    vec3 r = normalize (cross (wu, f));
    vec3 u = normalize (cross (f, r));
    vec3 c = ro + f*z;

    return normalize (c + r*uv.x + u*uv.y - ro);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy*2.-1.;
    float aspect = iResolution.x/iResolution.y;
    uv.x *= aspect;

    vec3 ro = vec3 (4., 0.5, 33.);
    vec3 aim = vec3 (.0);
    float zoom = 2.25;
    vec3 rd = camera (uv, ro, aim, zoom);

    int id=0;
	int iter=0;
    float d=march(ro,rd,id,iter);
	float fog=1./(1.+d*d*.05);
    vec3 p=ro+d*rd;
    vec3 n=normal(p);

    // light one
    vec3 lp1=vec3(10.,20.,30.);
    lp1.xz*=r2d(vary(45.,2.));
    vec3 lc1=vec3(.9,.8,.7);
    float li1=24.;

    // light two
	vec3 lp2=vec3(-10.,5.,30.);
    lp2.yz*=r2d(vary(45.,2.));
    vec3 lc2=vec3(.7,.8,.9);
    float li2=20.;

    // light three
	vec3 lp3=vec3(-8.,-20.,30.);
    lp3.xy*=r2d(vary (45.,2.));
    vec3 lc3=vec3(.2,.3,.9);
    float li3=22.;

    vec3 c;
	if (uv.x < uv.y*aspect - .01) {
    	c = shade (rd, p, n, lp1, lc1, li1, id);
    	c += shade (rd, p, n, lp2, lc2, li2, id);
    	c += shade (rd, p, n, lp3, lc3, li3, id);
		c *= fog;
    	c = c/(1. + c);
		c *= 1. - .25*pow (length (uv), 2.);
    	c = pow (c, vec3 (1./2.2));
	} else if (uv.x >= uv.y*aspect - .01 && uv.x < uv.y*aspect + .01) {
		c = vec3 (1.);
	} else {
		float glow = float (iter)/48.;
		c = shadeToon (p, n, lp2, rd);
    }

    fragColor = vec4 (c, 1.);
}

// Created by Robert Śmietana (Logos) - 03.04.2019
// Bielsko-Biała, Poland, UE, Earth, Sol, Milky Way, Local Group, Laniakea :)


vec3 pixelColor(vec2 p)
{

    //--- calculate water (background) color ---//

    vec3 wc = vec3(0.0, 0.2 + 0.4*(0.5 - 0.5*p.y), 0.3 + 0.4*(0.5 - 0.5*p.y));
    wc += vec3(0.08*sin(p.y - p.x));


    //--- get main body of the fish ---//

    if (p.x < 3.968)
    {
        p = vec2(p.x, -p.y) / dot(p, p);
    }


	//--- carve tail of the fish ---//

    else
    {
		p.x -= 3.687;
        if (0.359 - 0.2*cos(5.0*p.y) < dot(p, p)) return wc;
    }


    //--- iterate mandelbrot and return pixel color ---//

	vec2 z = vec2(p);  
	for (int i = 1; i <= 100; i++)
    {  
		z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + p; 

		if (504.0 + 300.0*sin(4.1*iTime) < dot(z, z))
        {
			return z.y < 0.0? vec3(0.0) : vec3(1.0);
		}
	}


    //--- that was water :) ---//

    return wc;

}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    //--- calculate point coordinates ---//

	vec2 c = 2.1*(-2.0*fragCoord.xy/iResolution.xy + 0.9)*vec2(iResolution.x/iResolution.y, 1.0) - vec2(-2.0, 0.0);


    //--- animate fish movement ---//

    c.x += 0.3*sin(0.4*iTime);
    c.y += 0.15 - 0.3*sin(0.43*iTime) + 0.2*cos(c.x);


    //--- add tail swing ---//

    if (0.0 < c.x) c.x += 0.1*c.x*cos(2.0*iTime + sin(0.8*c.x));


    //--- calculate final pixel color ---//

    float a = 3.0;
    float e = 1.0/min(iResolution.x, iResolution.y);    
    vec3 col = vec3(0.0);

    for (float j = -a; j < a; j++)
        for (float i = -a; i < a; i++)
            col += pixelColor(c + 2.1*vec2(i, j) * (e/a)) / (4.0*a*a);

	fragColor = vec4(col, 1.0);

}

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

// Value Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/lsf3WH
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

float fbm(vec2 st)
    {
    float f = noise(st)*1.;st*=2.02;
     f += noise(st)*0.5;st*=2.04;
     f += noise(st)*0.25;st*=2.03;
     f += noise(st)*0.125;st*=2.04;
     f += noise(st)*0.0625;///st*=2.01;

return f;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 st = uv;
    st.x*=iResolution.x/iResolution.y;
     vec3 color = vec3(0.0);

    vec2 pos = vec2(st*10.0);

    color = vec3( noise(pos)*.5+.5 );
	vec3 color1 = vec3(0.022,0.144,0.145);
    vec3 color2 = vec3(0.064,0.008,0.100);

    float sun = 1./pow(length(st-0.5), 1./2.)/4.;
    color = color1*step(0.5, st.x)*(1.0-st.x);
    color += color2*step(0.5, 1.0-st.x)*(st.x);

    color = mix(color1, color2, st.x-0.120);
    color = mix(color, vec3(0.765,0.723,0.661), sun-0.3);


    sun = 1.0-length(vec2(st.x/3., st.y-0.598));
    color1 = vec3(0.525,0.787,0.945);
    color2 = vec3(0.870,0.685,0.660);
    vec3 color3 = vec3(0.990,0.985,0.781);
    color = mix(color1, color2, pow(sun, 3.));//sin(st.y*6.168+-1.316)*0.5+0.5);
    //sun = 1.0-length(vec2(st.x/2., st.y-0.708));
    //color = mix(color, color3, pow(sun, 2.)/1.3);
        color+=pow(st.y, 2.)/8.;
    vec3 color4 = vec3(0.258,0.602,0.820);
    color2 = vec3(0.940,0.231,0.403);
    color2 = mix(color2, color3, st.y+0.10);
    color = mix(color4, color1, st.y)+pow(st.y, 2.)/8.;
    color =mix(color,  color2, pow(sun, 2.200)*1.);
    //color = color*2.0-0.328;
    st.x+=7.696;
    //st.y*=1.816;

    st.x/=2.;
    color *= mix(color, vec3(3.), fbm(st*4.+fbm(st*2.)/2.)*(noise(st*2.)));

    st = fragCoord.xy / iResolution.xy;
    st=st*2.0-1.0;
    st.x*=iResolution.x/iResolution.y;

    st/=1.2;


    color += clamp(vec3(
     1./pow(length(st+vec2(0.010,-0.0)), 2.+sin(atan(st.y,st.x)*50.+3.14159+iTime )/40.  )  *0.06   ), 0.0, 3.7);

    color -= clamp(vec3(
     1./pow(length(st+vec2(0.010,-0.0)), 2.)*0.05   ), .0, 4.0)/(1.+sin(iTime*1.)/10.)+vec3(0.035,0.436,0.440)+sin(iTime*3.)/40.;



	fragColor = vec4(color, 1.0);
}

#define NEAR_CLIPPING_PLANE 0.1
#define FAR_CLIPPING_PLANE 100.0
#define NUMBER_OF_MARCH_STEPS 64
#define EPSILON 0.001
#define DISTANCE_BIAS 0.7

float fmod(float a, float b) { if(a<0.0) { return b - mod(abs(a), b); } return mod(a, b); }

float Scene_Sphere(in vec3 rayPosition, in vec3 position, in float radius)
{
	return length(rayPosition - position) - radius;
}

vec2 Scene(in vec3 rayPosition)
{
    float displacement = sin(5.0 * (rayPosition.x + iTime / 3.0)) * sin(5.0 * rayPosition.y + iTime / 3.0) * sin(5.0 * rayPosition.z) * 0.25;

    vec3 tmpRayPosition = rayPosition;
    // tmpRayPosition.x = fract(rayPosition.x + 0.5) - 0.5;
    // tmpRayPosition.z = fmod(rayPosition.z + 1.0, 2.0) - 1.0;

    // tmpRayPosition.y += sin(rayPosition.x + iTime) * 0.35;
    // tmpRayPosition.y += cos(rayPosition.z + iTime);

    float distance = Scene_Sphere(rayPosition, vec3(cos(iTime * 1.2), sin(iTime * 0.97), 1.0), 0.8);
	float materialID = 1.0;

    // float distance2 = Scene_Sphere(rayPosition, vec3(0, 0.5 * abs(sin(iTime*3.8)) + 1.0, 5.0), 0.5);
	// float materialID2 = 2.0;

    /*
    vec3 spherePosition = rayPosition;
    spherePosition.x = fract(spherePosition.x + 0.5) - 0.5;
    spherePosition.z = fmod(spherePosition.z + 1.0, 2.0) - 1.0;
    spherePosition.y += sin(rayPosition.x + iTime) * 0.35;
    spherePosition.y += cos(rayPosition.z + iTime);

    float distance2 = Scene_Sphere(spherePosition, vec3(0.0, 0.5, 1.0), 0.15);
	float materialID2 = 2.0;


    if(distance2 < distance)
    {
		distance = distance2;
        materialID = materialID2;
    } 
	*/

    return vec2(distance + displacement, materialID);
}

vec2 Raymarch(in vec3 position, in vec3 direction)
{
    float totalDistance = NEAR_CLIPPING_PLANE;
    for(int i = 0 ; i < NUMBER_OF_MARCH_STEPS ; ++i)
    {
        vec2 result = Scene(position + direction * totalDistance);
        if(result.x < EPSILON)
        {
            return vec2(totalDistance, result.y);
        }
        totalDistance += result.x * DISTANCE_BIAS;
        if(totalDistance > FAR_CLIPPING_PLANE)
        {
            break;
        }
    }
    return vec2(FAR_CLIPPING_PLANE, 0.0);
}

vec3 GetNormal(vec3 rayHitPosition, float smoothness)
{	
	vec3 n;
	vec2 dn = vec2(smoothness, 0.0);
	n.x	= Scene(rayHitPosition + dn.xyy).x - Scene(rayHitPosition - dn.xyy).x;
	n.y	= Scene(rayHitPosition + dn.yxy).x - Scene(rayHitPosition - dn.yxy).x;
	n.z	= Scene(rayHitPosition + dn.yyx).x - Scene(rayHitPosition - dn.yyx).x;
	return normalize(n);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 cameraPosition = vec3(0, 1.0, -2.5);
    vec3 direction = normalize(vec3(uv, 2.5));// - cameraPosition);

    vec3 lightDirection = normalize(vec3(0.0, 1.0, -0.5));
    vec3 lightDiffuseColor = vec3(1.4, 1.2, 0.7);
    vec3 lightAmbientColor = vec3(0.2, 0.45, 0.6);

    vec2 result = Raymarch(cameraPosition, direction);

    float fog = pow(1.0 / (1.0 + result.x), 0.45);

    vec3 materialColor = vec3(0.1, 0.1, 0.1);

    if(result.y == 1.0)
    {
        materialColor = vec3(1.0, 0.50, 0.25);
    }
    if(result.y == 2.0)
    {
       	materialColor = vec3(0.7, 0.7, 0.7);
    }

    vec3 intersection = cameraPosition + direction * result.x;
    vec3 normal = GetNormal(intersection, 0.01);
    float diffuse = dot(lightDirection, normal);
    //diffuse = diffuse * 0.5 + 0.5;
    diffuse = max(0.0, diffuse);
    vec3 R = 2.0 * dot(lightDirection, normal) * normal - lightDirection;
    float specular = pow(max(dot(-direction, R), 0.0), 32.0);
    vec3 diffuseLit = materialColor * (diffuse * lightDiffuseColor + lightAmbientColor) + specular;
    fragColor = vec4(diffuseLit, 1.0) * fog;

}
