/*
	Technical details:

	The main volume noise is generated from a deformed periodic grid, which can produce
	a large range of noise-like patterns at very cheap evalutation cost. Allowing for multiple
	fetches of volume gradient computation for improved lighting.

	To further accelerate marching, since the volume is smooth, more than half the the density
	information isn't used to rendering or shading but only as an underlying volume	distance to 
	determine dynamic step size, by carefully selecting an equation	(polynomial for speed) to 
	step as a function of overall density (not necessarialy rendered) the visual results can be 
	the	same as a naive implementation with ~40% increase in rendering performance.

	Since the dynamic marching step size is even less uniform due to steps not being rendered at all
	the fog is evaluated as the difference of the fog integral at each rendered step.

*/

mat2 rot(in float a){float c = cos(a), s = sin(a);return mat2(c,s,-s,c);}
const mat3 m3 = mat3(0.33338, 0.56034, -0.71817, -0.87887, 0.32651, -0.15323, 0.15162, 0.69596, 0.61339)*1.93;
float mag2(vec2 p){return dot(p,p);}
float linstep(in float mn, in float mx, in float x){ return clamp((x - mn)/(mx - mn), 0., 1.); }
float prm1 = 0.;
vec2 bsMo = vec2(0);

vec2 disp(float t){ return vec2(sin(t*0.22)*1., cos(t*0.175)*1.)*2.; }

vec2 map(vec3 p)
{
    vec3 p2 = p;
    p2.xy -= disp(p.z).xy;
    p.xy *= rot(sin(p.z+iTime)*(0.1 + prm1*0.05) + iTime*0.09);
    float cl = mag2(p2.xy);
    float d = 0.;
    p *= .61;
    float z = 1.;
    float trk = 1.;
    float dspAmp = 0.1 + prm1*0.2;
    for(int i = 0; i < 5; i++)
    {
		p += sin(p.zxy*0.75*trk + iTime*trk*.8)*dspAmp;
        d -= abs(dot(cos(p), sin(p.yzx))*z);
        z *= 0.57;
        trk *= 1.4;
        p = p*m3;
    }
    d = abs(d + prm1*3.)+ prm1*.3 - 2.5 + bsMo.y;
    return vec2(d + cl*.2 + 0.25, cl);
}

vec4 render( in vec3 ro, in vec3 rd, float time )
{
	vec4 rez = vec4(0);
    const float ldst = 8.;
	vec3 lpos = vec3(disp(time + ldst)*0.5, time + ldst);
	float t = 1.5;
	float fogT = 0.;
	for(int i=0; i<130; i++)
	{
		if(rez.a > 0.99)break;

		vec3 pos = ro + t*rd;
        vec2 mpv = map(pos);
		float den = clamp(mpv.x-0.3,0.,1.)*1.12;
		float dn = clamp((mpv.x + 2.),0.,3.);

		vec4 col = vec4(0);
        if (mpv.x > 0.6)
        {

            col = vec4(sin(vec3(5.,0.4,0.2) + mpv.y*0.1 +sin(pos.z*0.4)*0.5 + 1.8)*0.5 + 0.5,0.08);
            col *= den*den*den;
			col.rgb *= linstep(4.,-2.5, mpv.x)*2.3;
            float dif =  clamp((den - map(pos+.8).x)/9., 0.001, 1. );
            dif += clamp((den - map(pos+.35).x)/2.5, 0.001, 1. );
            col.xyz *= den*(vec3(0.005,.045,.075) + 1.5*vec3(0.033,0.07,0.03)*dif);
        }

		float fogC = exp(t*0.2 - 2.2);
		col.rgba += vec4(0.06,0.11,0.11, 0.1)*clamp(fogC-fogT, 0., 1.);
		fogT = fogC;
		rez = rez + col*(1. - rez.a);
		t += clamp(0.5 - dn*dn*.05, 0.09, 0.3);
	}
	return clamp(rez, 0.0, 1.0);
}

float getsat(vec3 c)
{
    float mi = min(min(c.x, c.y), c.z);
    float ma = max(max(c.x, c.y), c.z);
    return (ma - mi)/(ma+ 1e-7);
}

//from my "Will it blend" shader (https://www.shadertoy.com/view/lsdGzN)
vec3 iLerp(in vec3 a, in vec3 b, in float x)
{
    vec3 ic = mix(a, b, x) + vec3(1e-6,0.,0.);
    float sd = abs(getsat(ic) - mix(getsat(a), getsat(b), x));
    vec3 dir = normalize(vec3(2.*ic.x - ic.y - ic.z, 2.*ic.y - ic.x - ic.z, 2.*ic.z - ic.y - ic.x));
    float lgt = dot(vec3(1.0), ic);
    float ff = dot(dir, normalize(ic));
    ic += 1.5*dir*sd*ff*lgt;
    return clamp(ic,0.,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 q = fragCoord.xy/iResolution.xy;
    vec2 p = (gl_FragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
    bsMo = (iMouse.xy - 0.5*iResolution.xy)/iResolution.y;

    float time = iTime*3.;
    vec3 ro = vec3(0,0,time);

    ro += vec3(sin(iTime)*0.5,sin(iTime*1.)*0.,0);

    float dspAmp = .85;
    ro.xy += disp(ro.z)*dspAmp;
    float tgtDst = 3.5;

    vec3 target = normalize(ro - vec3(disp(time + tgtDst)*dspAmp, time + tgtDst));
    ro.x -= bsMo.x*2.;
    vec3 rightdir = normalize(cross(target, vec3(0,1,0)));
    vec3 updir = normalize(cross(rightdir, target));
    rightdir = normalize(cross(updir, target));
	vec3 rd=normalize((p.x*rightdir + p.y*updir)*1. - target);
    rd.xy *= rot(-disp(time + 3.5).x*0.2 + bsMo.x);
    prm1 = smoothstep(-0.4, 0.4,sin(iTime*0.3));
	vec4 scn = render(ro, rd, time);

    vec3 col = scn.rgb;
    col = iLerp(col.bgr, col.rgb, clamp(1.-prm1,0.05,1.));

    col = pow(col, vec3(.55,0.65,0.6))*vec3(1.,.97,.9);

    col *= pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.12)*0.7+0.3; //Vign

	fragColor = vec4( col, 1.0 );
}

#define MAX_STEPS (128)
#define SURFACE_DIST (0.001)

int mat=0;

vec2 rotate(vec2 p, float a)
{
   float s=sin(a);
   float c=cos(a);
   mat2 r=mat2(c, -s, s, c);
   return p*r;
}

float sphereDist(vec3 p, vec3 origin, float radius)
{
   return length(p-origin)-radius;
}

float planeDist(vec3 p, float position)
{
   return p.y-position;
}

float sceneDistance(vec3 p)
{
   float s1=sphereDist(p, vec3(0.0, 0.5, 0.0), 0.2);
   if(s1<SURFACE_DIST)
   {
      mat=1;
   }
   float p1=planeDist(p, 0.0);
   if(p1<SURFACE_DIST)
   {
      mat=2;
   }
   p.xz=rotate(p.xz, iTime);
   float s2=sphereDist(p, vec3(1.0, 0.5, 0.0), 0.2);
   if(s2<SURFACE_DIST)
   {
      mat=3;
   }
   return min(p1, min(s1, s2));
}

vec3 normalAt(vec3 p)
{
   float dist=sceneDistance(p);
   vec2 epsilon=vec2(0.001,0.0);
   return normalize(vec3(dist-sceneDistance(p-epsilon.xyy), 
                         dist-sceneDistance(p-epsilon.yxy),
                         dist-sceneDistance(p-epsilon.yyx)));
}

float lightAt(vec3 p)
{
   vec3 lightPosition=vec3(0.0,2.0,0.0);
   vec3 n=normalAt(p);
   vec3 lightDirection=normalize(lightPosition-p);

   float diffuse=clamp(dot(n, lightDirection), 0.0, 1.0);
   return diffuse;
}

vec3 pixelColor(vec2 uv)
{
   vec3 direction=normalize(vec3(uv,1.0));
   vec3 color=vec3(0.0,0.0,0.0);
   float distance=0.0;
   vec3 camera=vec3(0.0,0.5,-1.5);
   float sceneDist=0.0;
   vec3 p;
   int refractCount=0;
   vec3 n;

   for(int i=0;i<MAX_STEPS;i++)
   {
      p=camera+distance*direction;
      sceneDist=sceneDistance(p);

      if(sceneDist<=SURFACE_DIST)
      {
         if(mat==1)
         {
            n=normalAt(p);
            camera=p+SURFACE_DIST*direction*10.0;
            direction=normalize(refract(direction,n,1.0/1.5));

            distance=0.0;
            for(int j=0;j<MAX_STEPS;j++)
            {
               p=camera+distance*direction;
               sceneDist=sceneDistance(p);
               distance+=max(abs(sceneDist),SURFACE_DIST); 
               if(sceneDist>SURFACE_DIST)
               {
                  break;
               }
            }

            camera=p+SURFACE_DIST*direction*10.0;
            distance=0.0;
         }
         else
         {
            break;
         }
      }

      distance+=sceneDist;
   }

   if(sceneDist<=SURFACE_DIST
      && mat!=1)
   {
      color=vec3(lightAt(p));
   }

   if(mat==2)
   {
      color*=vec3(0.0,1.0,0.0);
   }
   else if(mat==3)
   {
      color*=vec3(0.0,0.0,1.0);
   }

   return color;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.x;

    // Output to screen
    fragColor = vec4(pixelColor(uv),1.0);



}

// Here is a method of sampling bilinearly-distributed points.
//
// A uniform distribution can be 're-shaped' into some other distribution using what's
// known as a cumulative distribution function (CDF). This CDF can be created by inverting the integral
// of your distribution function, and that's what I've done here. It turned out to be quite
// straightforward, not requiring higher than 2nd-order polynomials.
//
// For the explanation I'll talk about a 1-dimensional bilinear patch defined with control points
// like this:
//
//	  0  --u-→ 1
//	  | p0   p1
//	  v
//	  ↓ p2   p3
//    1
//
// The bilinear distribution function is F=(p0+(p1-p0)u)(1-v)+(p2+(p3-p2)u)v
//
// For each v there is a linear function c+mu where c=p0+v(p2-p0) and m=(p1-p0)+v((p3-p2)-(p1-p0)),
// which when integrated over [0,1] yields c+m/2
//
// c+m/2 as a function of v is another linear function, which can easily be integrated.
//
// Integrating c+m/2 gives the following quadratic polynomial:
// 
// 		v(v(p2+p3-p0-p1)+2(p0+p1))/4
//
// This function of v can be inverted to obtain a cumulative distribution function.
//
// After a value for v has been found by applying the CDF to a uniformly-chosen v,
// the corresponding linear function of u for that 'slice' of the patch can easily
// be integrated and inverted to produce a CDF for u.
//
//

// Degree-2 polynomial, with c = 0
float quadratic(float x, float b, float a)
{
    return (b + a * x) * x;
}

// Inverse degree-2 polynomial, with c = 0
// Assumes real solution, and returns the 2nd root
float inverseQuadratic(float y, float b, float a)
{
    float d = b * b + 4. * a * y;
    return (sqrt(d) - b) / (2. * a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2. - 1.;

    uv.x *= iResolution.x / iResolution.y;

    fragColor = vec4(0, 0, 0, 1);
    vec2 p = uv.xy / 1.7 + .5;

    fragColor.rgb = vec3(.8);

    if(all(greaterThan(p, vec2(-.05))) && all(lessThan(p, vec2(1.05))))
	    fragColor.rgb = vec3(.05);

    const float pi = 3.14159265358979323;

    if(all(greaterThan(p, vec2(0))) && all(lessThan(p, vec2(1))))
    {

        // Bilinear patch control points
        float p0 = pow(.5 + .5 * cos(iTime + pi * 0.0), 6.);
        float p1 = pow(.5 + .5 * cos(iTime + pi * 0.5), 6.);
        float p2 = pow(.5 + .5 * cos(iTime + pi * 1.0), 6.);
        float p3 = pow(.5 + .5 * cos(iTime + pi * 1.5), 6.);

        // Fv polynomial coefficients
        float b2 = 2. * (p0 + p1) / 4.;
        float c2 = (p2 + p3 - p0 - p1) / 4.;

        // sum is the bilinear patch integrated over the domain
        float sum = quadratic(1., b2, c2);

        const int n = 32;

        fragColor.rgb = vec3(mix(mix(p0, p1, p.x), mix(p2, p3, p.x), p.y));

        for(int i = 0; i < n; ++i)
            for(int j = 0; j < n; ++j)
            {                
                vec4 ns = texelFetch(iChannel0, ivec2(i, j) & 255, 0);

                // Jitter
                float ju = iMouse.z < .5 ? ns.x : .5;
                float jv = iMouse.z < .5 ? ns.y : .5;

                float u = (float(i) + ju) / float(n), v = (float(j) + jv) / float(n);

                // Remap U to the domain of Fv
                v *= sum;

                // Apply Fv
                v = inverseQuadratic(v, b2, c2);

                // U-axis linear function coefficients
                float c2 = mix(p0, p2, v);
                float m2 = mix(p1 - p0, p3 - p2, v) / 2.;

                // Remap U to the domain of Fu
                u *= c2 + m2; // = quadratic(1., c2, m2);

                // Apply Fu
                u = inverseQuadratic(u, c2, m2);

                vec3 sprite = vec3(1. - clamp((distance(p, vec2(u, v)) - .007) / .001, 0., 1.));

                fragColor.rgb = mix(sprite, fragColor.rgb,
                                    clamp((distance(p, vec2(u, v)) - .01) / .001, 0., 1.));
            }
    }

    fragColor.rgb = pow(fragColor.rgb, vec3(1. / 2.2));
}

