const float unskewVal = (3.0-sqrt(3.0))/6.0;
const float skewVal = (sqrt(3.0)-1.0)/2.0;
const float xyDelta = 1.0 - 2.0*unskewVal;

float sNoise(vec2 uv)
{
	vec2 i0 = floor(uv + (uv.x+uv.y) * skewVal);
    vec2 uv0 = i0 - (i0.x+i0.y)*unskewVal;

    vec2 lUv = uv - uv0;

    float wTri = step(lUv.y,lUv.x);
    vec2 vWTri = vec2(wTri, 1.0-wTri);

    vec2 i2 = i0 + 1.0;
    vec2 i1 = i0 + vWTri;

    vec2 uv1 = uv0 + vWTri - unskewVal;
    vec2 uv2 = uv0 + xyDelta;

    vec2 lUv1 = uv - uv1;
    vec2 lUv2 = uv - uv2;

    float p0 = 0.5-dot(lUv,lUv);
    float p1 = 0.5-dot(lUv1,lUv1);
    float p2 = 0.5-dot(lUv2,lUv2);

    p0*=p0;
    p1*=p1;
    p2*=p2;

    float ret = 0.0;

    ret += p0*p0*gradient(uv0, gradients[int(hash(i0)*4.0)], uv);
    ret += p1*p1*gradient(uv1, gradients[int(hash(i1)*4.0)], uv);
    ret += p2*p2*gradient(uv2, gradients[int(hash(i2)*4.0)], uv);

    return 0.5+ret*25.0;
}

