float simplexGrid(vec2 uv, vec3 size)
{
    vec2 u_uv = uv;

	uv = uv + (uv.x+uv.y) * skewVal;
    vec2 uv0 = floor(uv);

    vec2 u_uv0 = uv0 - (uv0.x+uv0.y)*unskewVal;
    u_uv = u_uv - u_uv0;

    return step(uv.x - uv0.x, size.x) 
        + step(uv.y - uv0.y, size.y)
        + step(abs(u_uv.x-u_uv.y),size.z);
}

float simplexVectors(vec2 uv)
{
	vec2 i0 = floor(uv + (uv.x+uv.y) * skewVal);
    vec2 uv0 = i0 - (i0.x+i0.y)*unskewVal;

    vec2 lUv = uv - uv0;

    float wTri = step(lUv.y,lUv.x);
    vec2 vWTri = vec2(wTri, 1.0-wTri);

    vec2 i2 = i0 + 1.0;
    vec2 i1 = i0 + vWTri;

    vec2 uv1 = uv0 + vWTri - unskewVal;
    vec2 uv2 = uv0 + xyDelta;

    vec2 lUv1 = uv - uv1;
    vec2 lUv2 = uv - uv2;

    float p0 = 0.5-dot(lUv,lUv);
    float p1 = 0.5-dot(lUv1,lUv1);
    float p2 = 0.5-dot(lUv2,lUv2);

    float ret = 0.0;

    if(p0 > p1 && p0 > p2)
        ret = vector(uv0, gradients[int(hash(i0)*4.0)], uv, 0.06, 0.03);
    else if(p1 > p0 && p1 > p2)
        ret = vector(uv1, gradients[int(hash(i1)*4.0)], uv, 0.06, 0.03);
    else
        ret = vector(uv2, gradients[int(hash(i2)*4.0)], uv, 0.06, 0.03);

    return ret;
}

float simplexGradients(vec2 uv)
{
	vec2 i0 = floor(uv + (uv.x+uv.y) * skewVal);
    vec2 uv0 = i0 - (i0.x+i0.y)*unskewVal;

    vec2 lUv = uv - uv0;

    float wTri = step(lUv.y,lUv.x);
    vec2 vWTri = vec2(wTri, 1.0-wTri);

    vec2 i2 = i0 + 1.0;
    vec2 i1 = i0 + vWTri;

    vec2 uv1 = uv0 + vWTri - unskewVal;
    vec2 uv2 = uv0 + xyDelta;

    vec2 lUv1 = uv - uv1;
    vec2 lUv2 = uv - uv2;

    float p0 = 0.5-dot(lUv,lUv);
    float p1 = 0.5-dot(lUv1,lUv1);
    float p2 = 0.5-dot(lUv2,lUv2);

    float ret = 0.0;

    if(p0 > p1 && p0 > p2)
        ret = gradient(uv0, gradients[int(hash(i0)*4.0)], uv);
    else if(p1 > p0 && p1 > p2)
        ret = gradient(uv1, gradients[int(hash(i1)*4.0)], uv);
    else
        ret = gradient(uv2, gradients[int(hash(i2)*4.0)], uv);

    return 0.5+ret*0.5;
}

vec3 rainbowNoise(vec2 uv)
{
    vec2 v0 = floor(uv);
    vec2 v2 = v0 + 1.0;
    vec2 v1 = vec2(v2.x, v0.y);
    vec2 v3 = vec2(v0.x, v2.y);

	vec3 g0 = rainbow(gradient(v0, gradients[int(hash(v0)*4.0)], uv)*0.5+0.5);
	vec3 g1 = rainbow(gradient(v1, gradients[int(hash(v1)*4.0)], uv)*0.5+0.5);
    vec3 g2 = rainbow(gradient(v2, gradients[int(hash(v2)*4.0)], uv)*0.5+0.5);
    vec3 g3 = rainbow(gradient(v3, gradients[int(hash(v3)*4.0)], uv)*0.5+0.5);

    vec2 fac = uv-v0;
    fac = vec2(sFac(fac.x),sFac(fac.y));

    vec3 g01 = mix(g0,g1,fac.x);
    vec3 g23 = mix(g3,g2,fac.x);

    vec3 g = mix(g01,g23,fac.y);

    return g;
}

