float distLine(vec3 ro, vec3 rd, vec3 p) {
	return length(cross(p-ro, rd))/length(rd);
}

float drawPoint(vec3 ro, vec3 rd, vec3 p) {
	float d = distLine(ro, rd, p);
    return smoothstep(.06, .05, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(3.*sin(t) + 0.5, 3.*sin(t*0.5) + 0.5, -3.*cos(t)+0.5);

    vec3 lookat = vec3(.5);

    vec3 f = normalize(lookat-ro);
    vec3 r = cross(vec3(0., 1.0, 0.), f);
    vec3 u = cross(f, r);

    float zoom = 1.;
    vec3 c = ro + f*zoom;
    vec3 i = c + uv.x*r + uv.y*u;
    vec3 rd = i-ro;

    float d = 0.;

    d += drawPoint(ro, rd, vec3(0., 0., 0.));
    d += drawPoint(ro, rd, vec3(0., 0., 1.));
    d += drawPoint(ro, rd, vec3(0., 1., 0.));
    d += drawPoint(ro, rd, vec3(0., 1., 1.));
    d += drawPoint(ro, rd, vec3(1., 0., 0.));
    d += drawPoint(ro, rd, vec3(1., 0., 1.));
    d += drawPoint(ro, rd, vec3(1., 1., 0.));
    d += drawPoint(ro, rd, vec3(1., 1., 1.));

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(d, d, d, 1.0);
}

float rnd1(vec2 p)
{
 return fract(sin(dot(p,vec2(13.0,170.0)))*2100.0);
}

vec2 rnd2(vec2 p)
{
 vec2 r=vec2(0.29,0.37);
 return 1.0-2.0*vec2(rnd1(p),rnd1(p*r));
}

float noise(vec2 p)
{
 vec2 i=floor(p);
 vec2 f=fract(p);
 vec2 u=f*f*(3.0-2.0*f);
 return mix(
  mix(
   dot(rnd2(i+vec2(0.0,0.0)),f-vec2(0.0,0.0)),
   dot(rnd2(i+vec2(1.0,0.0)),f-vec2(1.0,0.0)),
   u.x),
  mix(
   dot(rnd2(i+vec2(0.0,1.0)),f-vec2(0.0,1.0)),
   dot(rnd2(i+vec2(1.0,1.0)),f-vec2(1.0,1.0)),
   u.x),
  u.y);
}

float scene(vec3 p)
{
 p=mod(p,5.0)-2.5;
 vec3 sPos=vec3(0.0,0.0,0.0);
 return length(p-sPos)-1.0;
}

vec3 normalAt(vec3 p)
{
 float s=scene(p);
 vec2 e=vec2(0.01,0.0);
 return normalize(vec3(s-scene(p-e.xyy),
                  s-scene(p-e.yxy),
                  s-scene(p-e.yyx)));
}

float diffuse(vec3 p)
{
 vec3 lp=vec3(0.0,3.0,-3.0);
 vec3 ld=normalize(lp-p);
 vec3 n=normalAt(p);
 return dot(ld,n);
}

vec3 pixelColor(vec2 uv)
{
 vec3 c=vec3(0.0,0.0,0.0);
 vec3 o=vec3(0.0,0.0,-5.0);
 vec3 d=vec3(uv,1.0);
 vec3 p;
 float t=0.0;
 for(int i=0;i<100;i++)
 {
  p=o+t*d;
  float s=scene(p);
  if(s<0.01 || t>50.0)
  {
   c=vec3(1.0,0.0,0.0)*diffuse(p);
   break;
  }
  t+=s;
 }
 float td=length(p);
 for(float l=0.0;l>-1.0;l+=0.1)
 {
  if(l>td)
  {
   break;
  }
  p=o+l*d;
  float n=abs(noise(p.xy*0.2+iTime*0.2));
  float f=1.0-exp(-length(p)*n);
  c=mix(c,vec3(0.1,0.1,0.5),f/10.0);
 }
 return c;
}

