  v = ro - p;
  b = dot (rd, v);
  d = b * b + s * s - dot (v, v);
  return (d > 0.) ? - b - sqrt (d) : dstFar;
}

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  q = p;
  q.xy = Rot2D (q.xy, pi / 6.);
  d = PrCylDf (q.xzy, 0.05, 1.18);
  DMINQ (1);
  d = max (min (PrCylAnDf (q, 1.1, 0.05, 0.025), PrCylAnDf (q, 1.16, 0.01, 0.04)), - q.x);
  DMINQ (2);
  q = p;
  q.y -= -1.17;
  d = PrCylDf (q.xzy, 0.7, 0.02);
  DMINQ (3);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0002, -0.0002);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 StarPat (vec3 rd, float scl)
{
  vec3 tm, qn, u;
  vec2 q;
  float f;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = Maxv3 (tm) * rd;
  q = atan (vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.)) / pi;
  f = 0.57 * (Fbm2 (11. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 531.13 * scl * q) +
      Fbm2 (13. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 571.13 * scl * q.yx));
  return 8. * vec3 (1., 1., 0.8) * pow (f, 16.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn, sd;
  float dstBall, dstObj, nDotL, tCyc, t, aRot;
  dstBall = BallHit (ro, rd, vec3 (0.), 1.);
  dstObj = ObjRay (ro, rd);
  if (dstBall < min (dstObj, dstFar)) {
    ro += dstBall * rd;
    vn = normalize (ro);
    nDotL = max (dot (vn, ltDir), 0.);
    sd = vn;
    sd.xy = Rot2D (sd.xy, pi / 6.);
    col = vec3 (0.1, 0.1, 0.15);
    if (abs (sd.y) > 0.997) {
      col = vec3 (0.7, 0.6, 0.1);
      nDotL *= nDotL;
    }
    col = col * (0.2 + 0.8 * nDotL) + 0.05 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
    if (abs (sd.y) < 0.997) {
      tCyc = 10.;
      t = 2. * pi * tCur / tCyc;
      aRot = 0.02 * tCyc * t + 0.5 * (t - sin (t));
      sd.xz = Rot2D (sd.xz, aRot);
      col += 4. * StarPat (sd, 1.) * smoothstep (0.05, 0.4, max (- dot (rd, vn), 0.));
    }
  } else if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 1) {
      col = vec3 (0.7, 0.6, 0.1);
    } else if (idObj == 2) {
      col = vec3 (0.9, 0.8, 0.4) * (1. - 0.5 * step (abs (length (qHit.xy) - 1.1), 0.043) *
         SmoothBump (0.45, 0.55, 0.02, mod (60. * atan (qHit.y, - qHit.x) / pi, 1.)));
    } else if (idObj == 3) {
      col = vec3 (0.6, 0.6, 0.65);
      if (vn.y > 0.99) col *= (1. - 0.1 * smoothstep (0.4, 0.5, Fbm2 (4. * qHit.xz * vec2 (1., 8.)))) *
         (0.7 + 0.3 * smoothstep (0., 0.1, max (abs (qHit.x) - 0.35, abs (qHit.z) - 0.02))) *
         (1. - step (abs (length (qHit.xz) - 0.6), 0.05) *
         0.6 * SmoothBump (0.46, 0.54, 0.02, mod (15. * atan (qHit.z, - qHit.x) / pi + 0.5, 1.)));
    }
    if (idObj > 1) vn = VaryNf (128. * qHit, vn, 1.);
    nDotL = max (dot (vn, ltDir), 0.);
    col = col * (0.2 + 0.8 * nDotL * nDotL) +
       0.2 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
  } else {
    col = StarPat (rd, 4.);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az += 3. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az += 0.03 * pi * tCur;
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., -0.1, -8.);
  dstFar = 50.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), 6.));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define PI2 6.2831852
#define RCOS(v) cos(v)*.5+.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;

    float l = length(uv);
    float lg = smoothstep(.5, .4, l) - .5;

    float t = iTime*2.;
    float ofst = (uv.y*10. + sin(uv.x*10.)*.5) * lg * -1.;

    float r = RCOS(t + ofst);
    float g = RCOS(t+PI2*.33 + ofst);
    float b = RCOS(t+PI2*.66 + ofst);

    fragColor = vec4(vec3(r, g, b) ,1.0);
}

#define ringThickness .05
#define numberOfRings 15.
#define minimumSize 10.
#define maximumSize 0.
#define ringSpeed .1
#define colorChangeSpeed 10.
#define ringIntensity 1.5

vec3 spaceCircles(vec2 uv){
	//Define a mask to contain all the rings
	vec3 mask = vec3(0);

	//Forloop for number of rings
	for (float x = 0.; x < 1.; x += 1./numberOfRings){
		//Define time at offsets, defined by indexer
		float t = (iTime * ringSpeed + x);
		//Color time defined, as the time variable to change ring color, defined by indexer
		float colorT = (iTime * colorChangeSpeed + x);

		//Define size as a value, by linearly scaling between two values
		//using the fraction of the time variable as the input.
		float size = mix(minimumSize, maximumSize, fract(t));
		//Scaling the uv
		vec2 uvScaled = uv * size;
		//Define a fade, to fade in the rings at a certain depth.
		float fade = smoothstep(0.,.05, fract(t));
		//Change color over time
		vec3 col = 0.5 + 0.5 * cos(colorT + uvScaled.xyx *  + vec3(1, 2, 4));

		//Create ring, by calculating the multiplicative inverse distance between
		//the current uv coord and the center of the screen.
		float dis = 1. / distance(uvScaled, vec2(0., 0.));

		//Multiply number to create different colors on the ring
		dis *= 2.;

		//To make sure we only create 1 ring, we cut out all values outside of a certain range.
		//Multiple rings are otherwise created because of the sine / cosine. The 'dist' value gets quite large as it
		//gets closer to the center, since 1 dvivded by a small number creates a large number, which input into one of 
		//these functions (sine or cosine), will return a greater frequency in the fluxuation.
		if (dis < 4.5 && dis > 2.){
			//Outer thickness of the rings
			col *= ringThickness / (sin(dis));
			//Inner thickness of the rings
			col = (col / sin(dis)) * ringThickness;
			//Adds the current ring to the mask, times the fade, to make sure it slowly fades in to the screen.
			//Multiplied is also an intensity value, used to make the intensity of the rings greater or smaller.
			mask += col * ringIntensity * fade;
		}
	}

	return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 mask = vec3(0);
	mask += spaceCircles(uv);
    fragColor = vec4(mask, 1.);
}

