// how deep do the reflections go
const int maxBounces = 2;


// control the tracer precision and efficiency
const float traceStartDistance = .01;
const int traceLoopCount = 200;
const float shadowStartDistance = .1;
const int shadowLoopCount = 50;


// polished copper
//const vec3 blobAlbedo = vec3(0); const vec3 blobSpecColour = vec3(.8,.3,.2); const float blobMaxSpecular = 1.;
// shiny white
const vec3 blobAlbedo = vec3(1); const vec3 blobSpecColour = vec3(.02); const float blobMaxSpecular = 1.;  //.02 = schlick(1.33)
// chocolate
//const vec3 blobAlbedo = vec3(.2,.03,.01); const vec3 blobSpecColour = vec3(.007); const float blobMaxSpecular = .5;



const vec2 quasirand2 = vec2(.754877666247,.569840290998); // from http://extremelearning.com.au/unreasonable-effectiveness-of-quasirandom-sequences/


struct Light
{
    vec3 pos;
    vec3 colour;
};

Light lights[] = Light[]
    (
         Light( vec3(1,2,-3), vec3(1)*8. )
        ,Light( vec3(-1,-.5,-1), vec3(1,.1,0)*1. )
        ,Light( vec3(.5,-.9,-.5), vec3(0,.3,1)*.3 )
	);


float Mat1SDF( vec3 p )
{
    // 4 walls
    float f = -abs(p.x-2.5)-(-4.);
    f = min(f, -abs(p.z+1.)-(-5.) );

    // 4 more walls at 45 degrees for more interesting reflections!
    f = min(f, -abs((p.z+p.x)*.7071+1.)-(-5.) );
    f = min(f, -abs((p.z-+p.x)*.7071+1.5)-(-4.5) );

    return f;
}


float Mat2SDF( vec3 p )
{
    // blob
    vec3 p1 = p - .7*sin(vec3(1,quasirand2)*iTime);
	vec3 p2 = p - .7*sin(vec3(1,quasirand2)*iTime*quasirand2.x);
	vec3 p3 = p - .7*sin(vec3(1,quasirand2)*iTime*quasirand2.y);

    float r = .0;
    float a = length(p )-.8+.4;
    float b = length(p1)-.5+.4;
    float c = length(p2)-.5+.4;
    float d = length(p3)-.5+.4;

    // I based this on a smooth-min function, it didn't work as intended but it's pretty!
    float avg = (a+b+c+d)/4.;
    //a -= avg;
    b -= avg;
    c -= avg;
    d -= avg;
    return (avg - sqrt( a*a+b*b+c*c+d*d+r*r ))*.8;
}

float Mat3SDF( vec3 p )
{
    // floor
    return -abs(p.y-1.)-(-2.);
}

float SDF( vec3 p )
{
    return min(min(Mat1SDF(p),Mat2SDF(p)),Mat3SDF(p));
}


vec3 Trace( in vec3 p, in vec3 ray )
{
    p += ray*traceStartDistance;
    float epsilon = .001;
    float h = 1.;
    for ( int i=0; i < traceLoopCount; i++ )
    {
        if ( h < epsilon )
            break;
        h = SDF(p);
        p += ray*h;
    }
    return p;
}


float Shadow( in vec3 p, in vec3 toLight )
{
    const float softness = 1.;
    toLight = toLight-p;
    float l = length(toLight);
    vec3 ray = toLight/l;
    float epsilon = .001;
    float t = shadowStartDistance;
    float h = 1.;
    float minh = 1e30;
    for ( int i=0; i < shadowLoopCount; i++ )
    {
        if ( h < epsilon || t > l )
            break;
        h = SDF(p+ray*t);
        minh = min(minh,h/max(t*softness,1.));
        t += h;
    }
    return smoothstep(epsilon,.03,minh);
}


vec3 GetNormal( vec3 p )
{
    vec2 d = vec2(-1,1)*.001;
    return normalize(
            SDF(p+d.xxx)*d.xxx +
            SDF(p+d.yyx)*d.yyx +
            SDF(p+d.yxy)*d.yxy +
            SDF(p+d.xyy)*d.xyy
        );
}


void Shading( out vec3 bounce, out vec3 bounceTint, out vec3 diffuse, in vec3 p, in vec3 ray )
{
    vec3 normal = GetNormal(p);

    vec3 light = vec3(0);
    for( int i=0; i < lights.length(); i++ )
    {
        vec3 toLight = lights[i].pos - p;

        float nDotL = dot(normal,toLight);
        if ( nDotL <= 0. ) continue;

        float l = max(nDotL/pow(dot(toLight,toLight),3./2.),0.);

        l *= Shadow(p,lights[i].pos);

        light += lights[i].colour * l;
    }

    // ambient light
    vec3 ao = vec3(.03,.05,.07);
    // sample SDF to approximate occlusion
    ao *= exp2(min(0.,SDF(p+normal*.3)/.3-1.));
    ao *= exp2(min(0.,SDF(p+normal*.15)/.15-1.));
    ao *= exp2(min(0.,SDF(p+normal*.07)/.07-1.));
    light += ao;

    // get material
    vec3 albedo = vec3(.3,.5,.6)*.4;
    vec4 specLevel = vec4(vec3(.9),1);

    // put a pattern on the walls, so they're visible
//    albedo *= 1.-.5*pow(texture(iChannel0,vec2(p.y,p.x+p.z)/2.).rgb,vec3(.2));
    specLevel.rgb = vec3(mix( .8, .95, texture(iChannel0,vec2(p.y,p.x+p.z)/2.).g ));
//    normal = normalize( normal + (texture(iChannel0,vec2(p.y,p.x+p.z)/2.).rgb-.5)*.002 );

    // frame the mirrors - so we can read the scene better
    float frame = .07;
    if ( abs(p.y-1.) > 2.-frame )//|| abs(p.x-2.5)+abs(p.z+1.) > 9.-frame )
    {
        albedo = vec3(.1);
        specLevel = vec4(0);
    }

    float mat1sdf = Mat1SDF(p);
    float mat2sdf = Mat2SDF(p);
    if ( mat2sdf < mat1sdf )
    {
        // main object
        albedo = blobAlbedo;
        specLevel = vec4(blobSpecColour,blobMaxSpecular);
    }

    if ( Mat3SDF(p) < min(mat1sdf,mat2sdf) )
    {
        // floor
        vec2 uv = p.xz;
        uv = uv*cos(.3)+sin(.3)*vec2(-1,1)*uv.yx;
        albedo = mix( vec3(.01), vec3(1), step(.0,(fract(uv.x/1.)-.5)*(fract(uv.y/1.)-.5)) );
        float ior = 1.33;
        float schlick = pow((ior-1.)/(ior+1.),2.);
	    specLevel = vec4(vec3(schlick),1);
    	specLevel *= mix( .2, .7, texture(iChannel0,vec2(p.xz)/2.).g );
    	normal = normalize( normal + (texture(iChannel0,vec2(p.xz)/2.).rgb-.5)*.005 );
    }

    float fresnel = pow(dot(normal,ray)+1.,5.);

    vec3 spec = mix( specLevel.xyz, specLevel.www, fresnel );

    bounceTint = spec;
    diffuse = albedo * (vec3(1)-spec) * light;
    bounce = reflect(ray,normal);
}


void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    vec3 ray = vec3((fragCoord-iResolution.xy*.5)/iResolution.y,2.);
    ray = normalize(ray);

    vec3 camPos = vec3(1,0,-4.5) + vec3(1.5,.5,1)*cos(vec3(1,quasirand2).zyx*iTime*.618);

    vec3 camK = normalize(vec3(0,-.3,0)-camPos);
    vec3 camI = normalize(cross(vec3(0,1,0),camK));
    vec3 camJ = cross(camK,camI);

    ray = ray.x*camI + ray.y*camJ + ray.z*camK;

    vec3 pos = camPos;
    vec3 tint = vec3(1);

    for( int bounce=0; bounce < maxBounces; bounce++ ) // gosh this can handle a lot of bounces!
    {
        pos = Trace( pos, ray );

        vec3 bounceTint, diffuse;
        Shading( ray, bounceTint, diffuse, pos, ray );
        fragColour.rgb += diffuse*tint;
        tint *= bounceTint;
        if ( length(tint) < .01 ) break; // early out if reflections aren't visible
    }


    fragColour.rgb = pow(fragColour.rgb,vec3(1./2.2));

    fragColour.a = 1.;
}

#define SCALE 0.5
#define STEPS 100.

float rand(vec2 p) {
	return fract(sin(dot(p, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noiseTex(vec2 p) {
	vec2 n = floor(p*SCALE);
	vec2 f = fract(p*SCALE);
    f = vec2(smoothstep(0.,1.,f.x),smoothstep(0.,1.,f.y));
	float c1 = rand(n), c2 = rand(n+vec2(1.,0.)), c3 = rand(n+vec2(0.,1.)), c4 = rand(n+vec2(1.,1.));
    return mix(mix(c1,c2,f.x), mix(c3,c4,f.x), f.y);
}

float sdf(vec3 p, vec3 eye) {
    if (length(p - eye) > 30.) return 1000.;
	return p.y - noiseTex(p.xz) + noiseTex(p.xz*10.*vec2(2.5,1.))/100. - noiseTex(p.xz*150.)/350.;
}

vec3 normal(vec3 p, vec3 eye) {
	vec2 e = vec2(0.001, 0.);
	return normalize(vec3(
		sdf(p+e.xyy, eye)-sdf(p-e.xyy, eye),
		sdf(p+e.yxy, eye)-sdf(p-e.yxy, eye),
		sdf(p+e.yyx, eye)-sdf(p-e.yyx, eye)
		));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = ( 2.*fragCoord.xy - iResolution.xy ) /iResolution.y;

	vec2 eyexz = vec2(0.,iTime);
	vec3 eye = vec3(eyexz.x,3. - sdf(vec3(eyexz.x,2.,eyexz.y), eyexz.xxy), eyexz.y);
	vec3 raydir = normalize(vec3(uv.x,uv.y-.5,1.));
	vec3 p = eye;
	float hit = 0.;

	for (float i = 0.; i < STEPS; i++) {
		float d = sdf(p, eye);
		if (d < 0.001) {
			hit = i;
			break;
		}
		p += d * raydir;
	}

	vec3 lightdir = normalize(vec3(-.5,-1.,-1.));
	vec3 color = vec3(0.);
    vec3 dark = vec3(.3,.2,.1);
	if (hit > 0.) {
		color = mix(
            dark,
            vec3(.9,.7,.4),
            clamp(dot(normal(p, eye), -lightdir) - hit/STEPS, 0.,1.));
        color = mix(
            color,
            vec3(1.,0.9,0.5),
            p.y-.3
            );
      	color = mix(
            color,
            dark,
            clamp(length(p-eye)/20., 0., 1.));
    } else if (length(p-eye) > 100.) {
     	color = mix(vec3(.9,.99,.90),vec3(.5,.6,.99),uv.y);
    } else {
        color = dark;
    }

    //color = vec3(noiseTex(uv*10.));

	fragColor = vec4( color, 1.0 );
}

float map(vec3 p) {
	float a = p.z/30.;
	p = vec3(p.x*cos(a)-p.y*sin(a),p.y*cos(a)+p.x*sin(a), p.z);
	p.y += cos(p.z);
	p += cos(p.y*2.)*sin(p.x*2.+p.z)*(.7+sin(p.z/3.)*.4);
	return 2.+3.*abs(sin(iTime)) - length(p.xy);
}

vec3 normal(vec3 p) {
	vec2 e = vec2(0.001, 0);
	return normalize(vec3(
		map(p+e.xyy)-map(p-e.xyy),
		map(p+e.yxy)-map(p-e.yxy),
		map(p+e.yyx)-map(p-e.yyx)
		));
}

vec3 lightdir = normalize(vec3(.5,-.5, 1.1));
float light(vec3 p) {
	return dot(normal(p), -lightdir) * .5 + .5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 pos= (2.*fragCoord.xy - iResolution.xy )/min(iResolution.x, iResolution.y);

	vec3 eye = vec3(0.,0.,iTime*4.);
	vec3 p = eye;
	vec3 raydir = normalize(vec3(pos.x, pos.y, 1.));
	int hit = -1;

	for (int i = 0; i < 200; i++) {
		float d = map(p);
		if (d < 0.001) {
			hit = i;
			break;
		}
		p += d * raydir * .4;
	}

	float color = -.5;
	if (hit > -1) color = light(p) - float(hit)/100.;

	fragColor = vec4(mix(vec3(.5,.2,.4), vec3(.7,.8,.5), color), 1.0 );

}

float plane(vec3 p, float y)
{
 return p.y-y;
}

float sphere(vec3 p,vec3 pos,float r)
{
 return length(pos-p)-r;
}

float scene(vec3 p)
{
 float sphere1=sphere(p,vec3(1.0,1,6),1.0);
 float sphere2=sphere(p,vec3(0.0,sin(iTime)*3.0,10),1.0);
 float plane=plane(p,0.0);
 return min(sphere1,min(sphere2,plane));
}

float raymarch(vec3 origin,vec3 direction)
{
 float traveled=0.0;
 float distance=0.0;
 for(int i=0;i<100;i++)
 {
  vec3 p=origin+traveled*direction;
  distance=scene(p);
  if(distance<=0.01 || traveled>100.0)
  {
   break;
  }
  traveled+=distance;
 }
 return traveled;
}

vec3 normalAt(vec3 p)
{
 vec2 epsilon=vec2(0.01,0);
 float distanceAtP=scene(p);
 return normalize(vec3(distanceAtP-scene(p-epsilon.xyy),
                       distanceAtP-scene(p-epsilon.yxy),
                       distanceAtP-scene(p-epsilon.yyx)));
}

float diffuse(vec3 p)
{
 vec3 sunPos=vec3(sin(iTime),25,cos(iTime)+6.0);
 vec3 normal=normalAt(p);
 vec3 lightDir=normalize(sunPos-p);
 if(raymarch(p+lightDir*0.1,lightDir)<length(sunPos-p))
 {
  return 0.0;
 }
 return clamp(dot(lightDir,normal),0.0,1.0);
}

vec3 applyFog( in vec3  rgb,       // original color of the pixel
               in vec3 p ) // camera to point distance
{
    float distance=length(p);
    //float fogAmount = 1.0 - exp( -distance*(pow(1.0/p.y,2.0)) );
    float fogAmount = (1.0 - exp( p.y-1.5 ))*(p.y<1.5?1.0:0.0);
    vec3  fogColor  = vec3(0.5,0.6,0.7);
    return mix( rgb, fogColor, fogAmount );
}

vec3 applyFog2( in vec3  rgb,      // original color of the pixel
               in vec3 p, // camera to point distance
               in vec3  rayDir,   // camera to point vector
               in vec3  sunDir )  // sun light direction
{
    float distance=length(p);
    float fogAmount = 1.0 - exp( -distance*(pow(1.0/p.y,2.0)) );
    float sunAmount = max( dot( rayDir, sunDir ), 0.0 );
    vec3  fogColor  = mix( vec3(0.5,0.6,0.7), // bluish
                           vec3(1.0,0.9,0.7), // yellowish
                           pow(sunAmount,8.0) );
    return mix( rgb, fogColor, fogAmount );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.x;

