// demo of using an experimental implementation of 3D Worley noise to texture a surface

// modified MATLAB bone colormap

vec3 bone( float t )
{
	 return 0.875 * t + 0.125 * clamp(vec3(4.0, 3.0, 3.0) * t - vec3(3.0, 1.0, 0.0), 0.0, 1.0);
}

// rescaling function

float rescale(float x, vec2 range)
{
	  float a = range.x, b = range.y;
      return (x - a)/(b - a);
}

// simple LCG

#define LCG(k) k = (65 * k) % 1021
#define lr(k) float(k)/1021.

// permutation polynomial

int permp (int i1, int i2)
{
      int t = (i1 + i2) & 255;

      return ((112 * t + 153) * t + 151) & 255;
}

// return the two closest distances for 3D Worley noise
// type controls the type of metric used

vec2 worley(int type, vec3 p)
{
    vec2 dl = vec2(20.0);
    ivec3 iv = ivec3(floor(p));
    vec3 fv = fract(p);

    int j = 0; // initialization for Knuth's "algorithm L"
    ivec3 di = ivec3(1), ki = -di;
    ivec4 fi = ivec4(0, 1, 2, 3);

    // instead of writing a triply nested loop (!!)
    // generate the indices for the neighbors in Gray order (Knuth's "algorithm L")
    // see section 7.2.1.1 of TAOCP, Volume 4A or https://doi.org/10.1145/360336.360343

	for (int k = 0; k < 27; k++) // loop through all neighbors
    { 
		 // seeding
        int s = permp(permp(permp(0, iv.z + ki.z), iv.y + ki.y), iv.x + ki.x); LCG(s);

		 for (int m = 0; m < 2; m++) // two points per cell
             {
                // generate feature points within the cell
                LCG(s); float sz = lr(s);
                LCG(s); float sy = lr(s);
                LCG(s); float sx = lr(s);

                vec3 tp = vec3(ki) + vec3(sx, sy, sz) - fv;
                float c = 0.0;
                if (type == 1) c = dot(tp, tp); // Euclidean metric
                if (type == 2) c = abs(tp.x) + abs(tp.y) + abs(tp.z); // Manhattan metric
                if (type == 3) c = max(abs(tp.x), max(abs(tp.y), abs(tp.z))); // Chebyshev metric

                float m1 = min(c, dl.x); // ranked distances
                dl = vec2(min(m1, dl.y), max(m1, min(max(c, dl.x), dl.y)));
             }

        // updating steps for Knuth's "algorithm L"
        j = fi[0]; fi[0] = 0; ki[2 - j] += di[j];
        if ((ki[2 - j] & 1) == 1) {
            di[j] = -di[j];
            fi[j] = fi[j + 1]; fi[j + 1] = j + 1;
        }
	}

    if (type == 1) dl = sqrt(dl); // don't forget to root at the end for Euclidean distance

    return dl;
}

// raytracing demo code adapted from Inigo Quilez, https://www.shadertoy.com/view/4sfGzS

const mat3 m = mat3( 0.00,  0.80,  0.60,
                    -0.80,  0.36, -0.48,
                    -0.60, -0.48,  0.64 );

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-iResolution.xy + 2.0 * fragCoord.xy) / iResolution.y;

     // camera movement	
	float an = 0.5 * iTime;
	vec3 ro = vec3( 2.5 * cos(an), 1.0, 2.5 * sin(an) );
    vec3 ta = vec3( 0.0, 1.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww, vec3(0.0, 1.0, 0.0) ) );
    vec3 vv = normalize( cross(uu, ww));
	// create view ray
	vec3 rd = normalize( p.x * uu + p.y * vv + 1.5 * ww );

    // sphere center	
	vec3 sc = vec3(0.0, 1.0, 0.0);

    // raytrace
	float tmin = 1.0e3;
	vec3  nor = vec3(0.0);
	float occ = 1.0;
	vec3  pos = vec3(0.0);

	// raytrace-plane
	float h = -ro.y/rd.y;
	if( h > 0.0 ) 
	{ 
		tmin = h; 
		nor = vec3(0.0, 1.0, 0.0); 
		pos = ro + h * rd;
		vec3 di = sc - pos;
		float l = length(di);
		occ = 1.0 - dot(nor, di/l)/(l * l); 
	}

	// raytrace-sphere
	vec3  ce = ro - sc;
	float b = dot( rd, ce );
	float c = dot( ce, ce ) - 1.0;
	h = b * b - c;
	if( h > 0.0 )
	{
		h = -b - sqrt(h);
		if( h < tmin ) 
		{ 
			tmin = h; 
			nor = normalize(ro + h * rd - sc); 
			occ = 0.5 + 0.5 * nor.y;
		}
	}

    // shading/lighting	
	vec3 col = vec3(0.8);
	if( tmin < 100.0 )
	{
	    pos = ro + tmin * rd;
	    float f = 0.0;
        int type = int(0.05 * iTime) % 3 + 1;

		if( p.x < 0.0 )
		{
			vec2 w = worley(type, 5.0 * pos);
            f = length(w.xy)/(w.y + w.x) - w.x;
		}
		else
		{
            vec3 q = 2.0 * pos; // three octaves
            vec2 w = worley(type, q);
            f  = 2.0 * (w.y - w.x); q = m * q * 2.01;
            w = worley(type, q);
            f += 0.5 * (w.y - w.x); q = m * q * 2.02;
            w = worley(type, q);
            f += 0.25 * (w.y - w.x);
		}		

		f = smoothstep( -0.7, 0.7, f );
		f *= occ;
		col = bone(rescale(f, vec2(0.0, 2.8)));
		col = mix( vec3(0.8), col, exp( -0.01 * tmin * tmin ) );
	}

	col = sqrt( col );

	col *= smoothstep( 0.006, 0.008, abs(p.x) );

	fragColor = vec4( col, 1.0 );
}

#define time iTime
#define resolution iResolution
#define texNoise iChannel0
#define texChecker iChannel2
#define texTex1 iChannel1
#define texTex3 iChannel3

mat2 rot(float a) 
{
  float ca=cos(a);
  float sa=sin(a);
  return mat2(ca,sa,-sa,ca);
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}
float sdBox(vec3 p,vec3 s)
{
  vec3 ap=abs(p)-s;
  return length(max(vec3(0.0),ap)) + min (0.0, max(0.0, max(ap.x,max(ap.y,ap.z))));
}
float sdVerticalCapsule( vec3 p, float h, float r )
{
    p.y -= clamp( p.y, 0.0, h );
    return length( p ) - r;
}
float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
    vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
    return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}
float sdTaperedCapsule( vec3 p, float r1, float r2, float h ) 
{ 
  vec2 q = vec2( length(p.xz), p.y ); 
  float b = (r1-r2)/h; float a = sqrt(1.0-b*b); float k = dot(q,vec2(-b,a)); 
  if( k < 0.0 ) return length(q) - r1;
  if( k > a*h ) return length(q-vec2(0.0,h)) - r2;
  return dot(q, vec2(a,b) ) - r1;
}
float sdCappedCone( in vec3 p, in float h, in float r1, in float r2 )
{
    vec2 q = vec2( length(p.xz), p.y );
     vec2 k1 = vec2(r2,h);
    vec2 k2 = vec2(r2-r1,2.0*h);
    vec2 ca = vec2(q.x-min(q.x,(q.y < 0.0)?r1:r2), abs(q.y)-h);
    vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot(k2,k2), 0.0, 1.0 );
    float s = (cb.x < 0.0 && ca.y < 0.0) ? -1.0 : 1.0;
    return s*sqrt( min(dot(ca,ca),dot(cb,cb)) );
}
float rnd(float t) 
{
  return fract(sin(t*745.565)*7842.654);
}

#define matDef 0.0
#define matEcran -1.0 // custom material id must be <0 (tricks for initialize default obj mat  from distance with vec4(dist))
#define matTable -2.0
#define matLight -3.0
#define matBlack -4.0
#define matSol   -5.0
#define matPaper -6.0

vec4 minObj(vec4 obj1, vec4 obj2) { return obj1.x<=obj2.x ? obj1 : obj2; }
vec4 minObj(vec4 obj1, float dist2) { return obj1.x<=dist2 ? obj1 : vec4(dist2); }
vec4 sminObj(vec4 obj1, vec4 obj2, float k ) { float h = clamp( 0.5 + 0.5*(obj2.x-obj1.x)/k, 0.0, 1.0 ); return vec4(mix( obj2.x, obj1.x, h ) - k*h*(1.0-h), minObj(obj1,obj2).yzw); }
vec4 sminObj(vec4 obj1, float dist2, float k ) { float h = clamp( 0.5 + 0.5*(dist2-obj1.x)/k, 0.0, 1.0 ); return vec4(mix( dist2, obj1.x, h ) - k*h*(1.0-h), minObj(obj1,dist2).yzw); }

bool checkBoundingSphere(inout vec4 obj, vec3 p, float h, bool show)
{
  if(show) { obj=minObj(obj,sdSphere(p,h));return true; }
  return sdSphere(p,h)<obj.x;
}
bool checkBoundingBox(inout vec4 obj, vec3 p, vec3 s, bool show)
{
  if(show) { obj=minObj(obj,sdBox(p,s));return true; }
  return sdBox(p,s)<obj.x;
}

vec4 objTable(vec3 p)
{
  vec3 p2 = p;
  p2.x = abs(p2.x);
  p2.z = abs(p2.z);
  vec4 obj= vec4(sdBox(p2-vec3(2,-.8,1),vec3(.06,.8,.06)));     // les pattes de la table
  obj = minObj(obj, vec4(sdBox(p-vec3(0,-1.6,0),vec3(2.2,.05,1.2)), matTable,p.x,p.z)); // le plateau
  return obj;
}

vec4 objPlaystation(vec3 p)
{
  float t=sdBox(p,vec3(0.5,.2,0.35)); // le plateau
  return vec4(t);
}

vec4 objEcran(vec3 p, float h)
{
  vec3 center=vec3(0,.7+h,0);
  vec3 size=vec3(0.75,.5,0.05);
  vec4 obj=vec4(sdBox(p,vec3(0.4,.02,0.2))); // le plateau
  obj = minObj(obj, vec4(sdBox(p+vec3(0,.4,0.1),vec3(0.1,.4,0.02)))); // le pied
  obj = minObj(obj, vec4(sdBox(p+center,size),p.z>0.05?matEcran:matDef,(p+center).x/size.x,(p+center).y/size.y)); // le cadre
  return obj;
}

vec4 objOrdi(vec3 p)
{
  float t = abs(sdBox(p+vec3(0,.6,0.1),vec3(0.25,.6,0.7))); // la tour
  return vec4(t);
}

vec4 objChaisepied(vec3 p)
{
  vec3 p2=p;
  p2.xy*=rot(-.3);
  vec4 obj = vec4(sdBox(p2+vec3(.26,.43,0),vec3(0.36,.03,.03))); // une barre de pied
  p2=p+vec3(0.7,0.1,0);
  p2.yz=p2.yz*rot(3.14/2.0);
  p2.xy*=rot(p.x);
  obj = minObj(obj, vec4(sdRoundedCylinder(p2,.05,.04,.02),matBlack,0,0));
  return obj;
}

vec4 objChaisecoude(vec3 p)
{
  float t = sdBox(p+vec3(0,.15,0),vec3(0.02,.15,.05)); // barre verticale
  t = min(t, sdBox(p+vec3(0,.3,.1),vec3(0.07,.01,.18))); // accoudoir
  return vec4(t);
}

vec4 objChaise(vec3 p)
{
  vec4 obj = vec4(sdVerticalCapsule(p+vec3(0,.9,0),.65,.05)); // la barre verticale
  vec3 p2=p;  
  for(int i=0;i<5;++i)
  {
    obj = minObj(obj,objChaisepied(p2)); // les pieds
    p2.xz*=rot(3.14/2.5);
  }

//  p2.xz*=rot(time);

  obj = minObj(obj, sdRoundedCylinder(p2+vec3(0,1,0),.25,.03,.02));
  obj = minObj(obj, sdBox(p2+vec3(0,.9,0),vec3(.57,.02,.05)));
  obj = minObj(obj, objChaisecoude(p2+vec3(-.55,.9,0)));
  obj = minObj(obj, objChaisecoude(p2+vec3(.55,.9,0)));

  p2=p2+vec3(0,1.5,-.5);
  p2.yz=p2.zy;
  obj = minObj(obj, sdRoundedCylinder(p2,.25,.03,.02));

  return obj;
}

vec4 objLampe(vec3 p)
{
  vec4 obj = vec4(sdSphere( p*vec3(1,1.5,1), .1),matLight,0,0);
  p.y+=.1;
  obj = minObj(obj, vec4(sdCappedCone( p, .1, .1, .15 ),matBlack,0,0));
  p.y+=.1;
  p.xy *= rot(1.0);
  p.y-=.1;
  obj = minObj(obj, vec4(sdVerticalCapsule(p,.6,.03),matBlack,(p.y*2.0-.5),0.0));
  p.x+=.2;
  p.y-=.45;
  p.xy *= rot(-1.0);
  p.y-=.3;
  obj = sminObj(obj, sdVerticalCapsule(p,.6,.03), .1);
  p.y-=.8;
  obj = sminObj(obj, vec4(sdSphere(p,.15)), .1);
  return obj;
}

vec4 objKeyboard(vec3 p)
{
  float t=sdBox(p,vec3(0.65,.05,0.35)); // le contour
  p.z-=.02;
  //float f=max(0.0,texture(texTex1, p.xz*vec2(1.98,.58)).z-.6)+.6;
	float f=max(0.0,max(fract(p.x*15.0),fract(p.z*15.0))-.5);
  t = min(t,  sdBox(p,vec3(0.58,.02,0.26))-f*.09); // les touches
  return vec4(t);
}

vec4 objBureau(vec3 p)
{
  vec3 p2=p;
  p2.xz *= rot(0.5);
  vec3 p3=p;
  p3.xz *= rot(-0.5);

  vec4 obj = objTable(p);
  obj = minObj(obj,objOrdi(p+vec3(-1.6,0,-.3)));
  if(checkBoundingBox(obj,p+vec3(0,2.4,0),vec3(2.2,.8,1.2), false))
  {
    obj = minObj(obj,objPlaystation(p3+vec3(1.1,1.8,1)));
    obj = minObj(obj,objEcran(p3+vec3(1.1,2.04,1),-.1));
    obj = minObj(obj,objEcran(p2+vec3(-1.1,1.68,1),.27));
    obj = minObj(obj,objEcran(p+vec3(0,1.68,.7),.27));
    obj = minObj(obj,objKeyboard(p+vec3(0,1.68,-.7)));
  }
  if(checkBoundingSphere(obj,p-vec3(0,-.9,2.1),1.2, false))
  {
    obj = minObj(obj,objChaise(p+vec3(0,0,-2)));
  }
  return obj;
}  

vec4 objBureaux(vec3 p)
{
  vec3 p2=p;
  p2.xz = abs(p2.xz)-vec2(2.21,1.21);
  return objBureau(p2);
}

vec4 objPlafond(vec3 p)
{
  return vec4(20.0+p.y,matDef,0.0,0.0);
}

vec4 objPaper(vec3 p, float ry, float h)
{
  float width=.25;
  float height=.35;
  p.xz*=rot(-ry);
  return vec4(sdBox(p+vec3(0,h*.5,0),vec3(width,h,height)),matPaper,p.x/width,p.z/height);
}

vec4 objStylo(vec3 p, float ry)
{
  float width=.01;
  float height=.35;
  p.xy*=rot(1.57);
  p.xz*=rot(-ry);
  return vec4(sdVerticalCapsule(p,height,width),matDef,0,0);
}

vec4 objHumanLeg(vec3 p2, float hipsx, float hipsy, float kneex, float anklex)
{
  float hips_knee_len = .9;
  float hips_radius = .25;
  float knee_radius = .15;
  float knee_ankle_len = .9;
  float ankle_radius = .1;
  float foot_radius = .08;
  float ankle_foot_len = .35;
  p2.xz *= rot(hipsy);
  p2.yz *= rot(hipsx); // hips.x
  vec4 obj = vec4(sdTaperedCapsule(p2,hips_radius,knee_radius,hips_knee_len));
  p2.y-=hips_knee_len;
  p2.yz *= rot(kneex); // knee.x
  obj = sminObj(obj,sdTaperedCapsule(p2,knee_radius,ankle_radius,knee_ankle_len), .1);
  p2.y-=knee_ankle_len;
  p2.yz *= rot(1.5+anklex); // ankle.x
  obj = minObj(obj,sdTaperedCapsule((p2-vec3(0,-.03,0)),ankle_radius*1.2,foot_radius,ankle_foot_len));
  return obj;
}

vec4 objHumanArm(vec3 p2, float shoulderx, float shouldery, float shoulderz, float elbowx, float wristx)
{
  float shoulder_elbow_len = .55;
  float shoulder_radius = .15;
  float elbow_radius = .1;
  float elbow_wrist_len = .55;
  float wrist_radius = .07;
  float hand_radius = .08;
  float wrist_hand_len = .2;
  p2.xz *= rot(shouldery);
  p2.xy *= rot(shoulderz);
  p2.yz *= rot(shoulderx);
  vec4 obj = vec4(sdTaperedCapsule(p2,shoulder_radius,elbow_radius,shoulder_elbow_len));
  p2.y-=shoulder_elbow_len;
  p2.yz *= rot(elbowx);
  obj = sminObj(obj,sdTaperedCapsule(p2,elbow_radius,wrist_radius,elbow_wrist_len),.1);
  p2.y-=elbow_wrist_len;
  p2.yz *= rot(wristx);
  obj = minObj(obj,sdTaperedCapsule(p2*vec3(.95,1,1.4),wrist_radius,hand_radius,wrist_hand_len));
  return obj;
}

vec4 objHumanTorso(inout vec3 p2, float hipsx, float spinex)
{
  float hips_spine_len = .1;
  float hips_radius = .45;
  float spine_radius = .4;
  float neck_radius = .45;
  float spine_neck_len = .1;
  p2.yz *= rot(3.14+hipsx); // hips.x
  p2.y -=.15;
  vec4 obj = vec4(sdTaperedCapsule(p2*vec3(1,1,1.4),hips_radius,spine_radius,hips_spine_len)); 
  p2.y-=hips_spine_len;
  p2.y -=.15;
  p2.yz *= rot(spinex); // knee.x
  p2.y -=.15;
  obj = sminObj(obj,sdTaperedCapsule(p2*vec3(1,1,1.3),spine_radius,neck_radius,spine_neck_len),.1);
  p2.y-=spine_neck_len*2.2;
  return obj;
}

vec4 objHumanHead(vec3 p2, float headx, float heady)
{
  p2.yz *= rot(headx); // knee.x
  p2.xz *= rot(heady); // knee.x
  p2.y -=.15;
  vec4 obj = vec4(sdSphere(p2*vec3(1.2,1,1),.25)); 
  p2.y -=.15;
  obj = minObj(obj, vec4(sdRoundedCylinder(p2,.122,.07,.07))); 
  obj = sminObj(obj, vec4(sdRoundedCylinder(p2+vec3(0,.07,-.2),.1,.01,.005)),.1); 
  return obj;
}

vec4 objHuman(vec3 p)
{
  float hips_width = .25;
  float shoulder_width = .4;
  vec4 obj = objHumanLeg(p-vec3( hips_width,-1.6,0.0),1.35,-.3,-1.5,.0);
  obj = sminObj(obj,objHumanLeg(p-vec3(-hips_width,-1.6,0.0),1.4,.3,-1.5,.2+sin(iTime*10.0)*.1),0.1);
  vec3 p2 = p-vec3(0.0,-1.7,0.0);
  obj = sminObj(obj,objHumanTorso(p2,0.0,-.2),.1);
  obj = sminObj(obj,objHumanHead(p2-vec3(0.0,.4,-.05),sin(iTime*10.0)*.1,sin(iTime*1.154)*.5),.1);
  p2.yz *= rot(3.14);
  obj = sminObj(obj,objHumanArm(p2-vec3(-shoulder_width,0.0,0.0),1.2,sin(iTime*.12)*cos(iTime*1.1)*.2,.4,.55,sin(round(iTime)*10.0)*.1),.1);
  obj = sminObj(obj,objHumanArm(p2-vec3( shoulder_width,0.0,0.0),.9,sin(iTime*1.25)*cos(iTime*1.445)*.2,-.4,1.1,sin(iTime*10.0)*.1),.1);
  return obj;
}

vec3 lp = vec3(.6,-2.65,1.8);

vec4 map(vec3 p)
{
  //return minObj(vec4(-p.y,matDef,0,0),objHuman(p));

  vec3 size = vec3(13,1,10);
  vec3 rp = (fract(p/size-.5)-0.5)*size;
  rp.y = p.y;


  vec4 obj = objBureaux(rp);
//obj = minObj(obj,objPlafond(p));
  obj = minObj(obj,vec4(-p.y,matSol,p.x,p.z));  // sol

  if(checkBoundingBox(obj,p+vec3(-2.2,2.3,-1.65),vec3(2.3,.7,.8),false))
  {
    obj = minObj(obj,objLampe(p-lp));
    obj = minObj(obj,objPaper(p-vec3(.6,-1.7,2),.2,.1));
    obj = minObj(obj,objPaper(p-vec3(.6,-1.9,2),.4,.1));
    obj = minObj(obj,objPaper(p-vec3(4.0,-1.7,2),-1.4,.1));
    obj = minObj(obj,objPaper(p-vec3(3.9,-1.9,1.9),-1.6,.1));
    obj = minObj(obj,objPaper(p-vec3(1.5,-1.7,1.25),-1.1,.05));
    obj = minObj(obj,objStylo(p-vec3(.7,-2.1,2.0),0.0));
  }

  p-=size*.5;
  p.xz *=rot(3.14);
  p.x-=4.25;
  p.y+=.1;
  p.z-=1.65;

  if(checkBoundingSphere(obj,p-vec3(0,-1.9,.4),1.6,false))
    obj = minObj(obj,objHuman(p));

  return obj;
}

vec3 plas( vec2 v, float time )
{
  float c = 0.5 + sin( v.x * 10.0 ) + cos( sin( time + v.y ) * 20.0 );
  return vec3( sin(c * 0.2 + cos(time)), c * 0.15, cos( c * 0.1 + time / .4 ) * .25);
}

vec3 colScreenLeft(vec2 uv)
{
  vec2 m;
  m.x = atan(uv.x / uv.y) / 3.14;
  m.y = 1.0 / length(uv) * .2;
  float d = m.y;
  m.x += sin( time ) * 0.1;
  m.y += time * 0.25;
  vec3 t = plas( m * 3.14, time ) / d;
  t = clamp( t, 0.0, 1.0 );
  return t;
}

vec3 colScreenCenter(vec2 uv)
{
  vec2 uvchar = round(vec2(0,round(time*.5))+uv*30.0)/30.0;

  float noisey = texture(texNoise, uvchar.yy).r;
  float noisex1 = texture(texNoise, uvchar.xy).r;
  bool lineon = (fract(uv.y*30.0)>.4);
  float linewidth = rnd(noisey)-1.0;

  vec3 col=vec3(0,.1,0); 
  if(lineon && uv.x<linewidth && uv.x>-.9 && abs(uv.y)<.9) col+=vec3(0,noisex1,0);
  return col;
}

vec3 colScreenRight(vec2 uv)
{
  uv.xy*=1.0+sin(time)*.5;
  uv.x+=time*2.+sin(time*.123);
  uv.y+=sin(uv.x*8.0*.05+time);

  vec3 col=vec3(.5)+sin(vec3(uv.x+uv.y)*vec3(.319,.257,.187))*.5;
  return texture(texChecker, uv).r*col; // + col*(abs(uv.y)>texture(texFFT,(uv.x+1)*.5).r?1.:0.);
}

float colAmbientOcclusion(vec3 p, vec3 n, float dist)
{
  return clamp(map(p+n*dist).x/dist,0.0,1.0);
}
float colSoftShadow(vec3 p, vec3 d, float mint, float maxt, float k)
{
  float shad=1.0;
  float t=.5;
  for(float t=mint;t<maxt;)
  {
    float d=map(p+d*t).x;
    if(abs(d)<.001) return 0.0;
    shad = min(shad,k*d/t);
    t += d;
  }
  return shad;
}

void cam(inout vec3 p)
{
  p.yz *= rot(sin(time*.1)*.1+.5);
  p.xz *= rot(time*.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  // Normalized pixel coordinates (from 0 to 1)
   vec2 xy = fragCoord.xy / iResolution.xy;
	vec2 uv = (-1. + 2.0 * xy) * vec2(iResolution.x/iResolution.y,1.0);


  vec3 s=vec3(-2,0,-8.0+sin(3.14+time-.5));
  vec3 r=normalize(vec3(-uv,2));

  cam(s);
  cam(r);

  vec3 p=s;
  vec4 obj;
  for(int i=0;i<100; ++i)
  {
    obj=map(p);
    if(obj.x<0.001) break;
    p+=obj.x*r;
  }

  vec3 l=normalize(lp-p);
  //l = normalize(vec3(sin(time*1.2)*2,-2,4+sin(time)*2));

  vec2 off=vec2(0.01,0);
  vec3 n=normalize(map(p).x-vec3(map(p-off.xyy).x, map(p-off.yxy).x, map(p-off.yyx).x));
  float shad = 1.0;//

  vec3 lproj=p-lp;
  lproj.y=0.0;
  shad = 1.0/length(lproj)-1.0; // * 
  //shad = colSoftShadow(p+n*.01,l,.1,5,10);

  float ao = colAmbientOcclusion(p,n,max(.4,length(lproj)/10.0))*(sin(time*.2)*.3+.7)*.6+.6;
  vec3 col=vec3(min(1.0,ao+shad));

  if(obj.y==matEcran)
  {
    bool original = p.x>0.0 && p.x<5.0 && p.z>0.0 && p.z<5.0;
    if(original)
    {
         if(p.x<1.45) col=colScreenLeft(obj.zw);
    else if(p.x<2.96) col=colScreenCenter(obj.zw);
    else              col=colScreenRight(obj.zw);
    }
    else             col*=vec3(.2,.2,.2);
  }
  else if(obj.y==matTable)    col *= pow(texture(texTex3,obj.zw*vec2(1.0,2.0)).r,0.2)*vec3(1,1,.95);
  else if(obj.y==matLight)    col  = vec3(1,1,.5);
  else if(obj.y==matBlack)    col *= vec3(obj.z);
  else if(obj.y==matSol)      col *= texture(texNoise,obj.zw*vec2(1.0)).r;
  else if(obj.y==matPaper)    col *= mix(texture(texNoise,p.yy).r*.6+.4, 1., abs(n.y));
  else                        col *= (dot(n,l)*.5+.5);

  fragColor = vec4(col,1);
}

#define PI 3.14159

float sstep(float a, float x) {
	return smoothstep(a*0.98, a*1.02, x);
}

float flower(vec2 p) {
	float a = atan(p.y, p.x);
	float r = length(p);
	float arms = a*(5. + ceil((sin(iTime*3.)+.215)*5.));
	return sstep(.5,r + sin(arms)/5.);
}

vec2 rot(vec2 p, float a) {
	return vec2(p.x*cos(a)-p.y*sin(a),p.y*cos(a)+p.x*sin(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 p = ( 2.*fragCoord.xy - iResolution.xy ) / iResolution.y;
	p = rot(p, iTime/3.);

	vec2 p1 = mod(p*5., 2.)-1.;
	vec3 color = mix(vec3(.5,.2,.0),
			  vec3(.9,.9,.1),
			  flower(p1) - flower(.8*p1));
	color += mix(vec3(.5,.2,.0),
		    vec3(.8,.7,.4),
		    flower(p*.8)) * .9;
	color *= mix(vec3(1.,1.,1.),
		    vec3(.8,.8,.8),
		    flower(p*.4));

	fragColor = vec4( vec3( color ), 1.0 );

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float px = 1.5*(fragCoord.x - 0.5 * iResolution.x) / iResolution.y;
    float py = 1.5*(fragCoord.y - 0.5 * iResolution.y) / iResolution.y;
    float r,g,b;

    px -= 0.75;
	py += 0.33;

	float px2 = px + sin(py * 4.6 + 0.5 * iTime) * 0.2;
	float py2 = py + sin(px * 2.6) * 0.7;

	px2 += sin(py2*px2 * 9.6) * 0.1;
	py2 += sin(px2*px2 * 7.6 + iTime) * 0.2;

	float fx = px2;
	float fy = py2;

	r = sin(10.0f * ((fx*fx)+(fy*fy))) * 0.5 + 0.5;
	g = sin(10.0f * sqrt((fx*fx)+(fy*fy))) * 0.5 + 0.5;
	b = sin(10.0f * pow((fx*fx)+(fy*fy), 1.5)) * 0.5 + 0.5;

	r = (r + b + g) / 3.0 + 0.2f;
	r *= r;
	r *= r;
	g = (b + g) / 2.0;
	b = g;
	g *= 0.9;

	r *= float((int(fragCoord.x) ^ int(fragCoord.y)) & 0xff) / 255.0;
	g += 0.2 * r;
	b += 0.2 * r;

    if ((px2 * 2.0f) - floor(px2 * 2.0f) < 0.5) {
        float t = r;
        r = g;
        g = t;
    }

    // Output to screen
    fragColor = vec4(r,g,b,1.0);
}

