// Demonstrating an experimental 3D Worley noise implementation.
// A little bit of something old, something new, and something borrowed.

// XY range of the display.

#define DISP_SCALE 6.0 

// rescaling functions

float rescale(float x, vec2 range)
{
	  float a = range.x, b = range.y;
      return (x - a)/(b - a);
}

float rescale(float x, vec2 r1, vec2 r2)
{
	  float a = r1.x, b = r1.y;
      float c = r2.x, d = r2.y;
      return c + (d - c) * ((x - a)/(b - a));
}

// custom colormap

vec3 lake( float t )
{
	 return vec3((2.0 * t + 1.0)/3.0, min(4.0 * t, 2.0 * t + 1.0)/3.0, min(0.8 * t + 0.5, 1.0 - 0.1 * t));
}

// simple LCG

#define LCG(k) k = (65 * k) % 1021
#define lr(k) float(k)/1021.

// permutation polynomial

int permp (int i1, int i2)
{
      int t = (i1 + i2) & 255;

      return ((112 * t + 153) * t + 151) & 255;
}

// return the two closest distances for 3D Worley noise
// type controls the type of metric used

vec2 worley(int type, vec3 p)
{
    vec2 dl = vec2(20.0);
    ivec3 iv = ivec3(floor(p));
    vec3 fv = fract(p);

    int j = 0; // initialization for Knuth's "algorithm L"
    ivec3 di = ivec3(1), ki = -di;
    ivec4 fi = ivec4(0, 1, 2, 3);

    // instead of writing a triply nested loop (!!)
    // generate the indices for the neighbors in Gray order (Knuth's "algorithm L")
    // see section 7.2.1.1 of TAOCP, Volume 4A or https://doi.org/10.1145/360336.360343

	for (int k = 0; k < 27; k++) // loop through all neighbors
    { 
		 // seeding
        int s = permp(permp(permp(0, iv.z + ki.z), iv.y + ki.y), iv.x + ki.x); LCG(s);

		 for (int m = 0; m < 2; m++) // two points per cell
             {
                // generate feature points within the cell
                LCG(s); float sz = lr(s);
                LCG(s); float sy = lr(s);
                LCG(s); float sx = lr(s);

                vec3 tp = vec3(ki) + vec3(sx, sy, sz) - fv;
                float c = 0.0;
                if (type == 1) c = dot(tp, tp); // Euclidean metric
                if (type == 2) c = abs(tp.x) + abs(tp.y) + abs(tp.z); // Manhattan metric
                if (type == 3) c = max(abs(tp.x), max(abs(tp.y), abs(tp.z))); // Chebyshev metric

                float m1 = min(c, dl.x); // ranked distances
                dl = vec2(min(m1, dl.y), max(m1, min(max(c, dl.x), dl.y)));
             }

        // updating steps for Knuth's "algorithm L"
        j = fi[0]; fi[0] = 0; ki[2 - j] += di[j];
        if ((ki[2 - j] & 1) == 1) {
            di[j] = -di[j];
            fi[j] = fi[j + 1]; fi[j + 1] = j + 1;
        }
	}

    if (type == 1) dl = sqrt(dl); // don't forget to root at the end for Euclidean distance

    return dl;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
        vec2 aspect = iResolution.xy / iResolution.y;
        vec2 uv = ( fragCoord.xy / iResolution.y ) - 0.5 * aspect;
        uv *= DISP_SCALE;
        uv += iTime;

        vec2 w = worley(int(0.05 * iTime) % 3 + 1, vec3(uv, -0.2 * iTime));

        // split image adapted from Inigo Quilez; https://www.shadertoy.com/view/ll2GD3
        float ry = fragCoord.y / iResolution.y;
        vec3                  col = lake(rescale(w.x, vec2(0.0, 1.0)));
        if ( ry > (1.0/3.0) ) col = lake(rescale(length(w.xy)/(w.y + w.x) - w.x, vec2(0.0, 1.4)));
        if ( ry > (2.0/3.0) ) col = lake(rescale((2.0 * w.y * w.x)/(w.y + w.x) - w.x, vec2(0.0, 0.3)));

        // borders
        col *= smoothstep( 0.5, 0.48, abs(fract(3.0 * ry) - 0.5) );

        fragColor = vec4( vec3(col), 1.0 );
}

// variant of https://shadertoy.com/view/3ts3z2
// variant from https://shadertoy.com/view/3ll3z2

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, 
         U = 2.* u / R.y,
         D = cos(.3*0.-vec2(0,1.57));              // custom dir

    int i =int(U.y/.66);                           // pannel id
    U.y = mod(U.y,.66)-.33;                        // pannel coords

    float t = 10.*iTime,
          kl = 100., kr=.05;                       // wavelenght
    U.x -= t/kl +.5*float(i);
    i = (  int(U.x)+ 2*i ) % 4;
    U.x = fract( U.x ) -.5;
    t = float(i-2)*t;
    float l = length(U), r = .2,
          m = smoothstep(-kr,kr,r-l),              // disk mask
          b = .5-.5*cos(6.28*m),                   // border zone (where effect occurs)
          x = dot(U,D),                            // pos along D
          d = x*x+r*r-l*l, s = -x+sign(x)*sqrt(d), // distance to disk in dir D
        phi =   i>0 ? d > 0. ? kl*s : t            // axial from disk
              : t;

    O = vec4( m + .25*b*sin(phi-t) );              // magic draw
  //O.r += .2+.2*sin(kl*U.x+t);
}

float hash(float seed, float t) {
    vec2 p = vec2(seed, t);
	float h = dot(p,vec2(127.1,311.7));	
    return fract(sin(h)*43758.5453123);
}

float smooth_random(in float seed, in float t) {
    float s1 = floor(t);
    float s2 = s1 + 1.;
    return hash(seed, s1) * (1. - fract(t)) + hash(seed, s2) * fract(t) - 0.5;
}

float rvec2_seed = 1.;
vec2 rvec2(in float x, in float y) {
    rvec2_seed += 0.1;
    return vec2(x + smooth_random(rvec2_seed + 0., iTime * 10.) / 20.,
                y + smooth_random(rvec2_seed + 0.05, iTime * 10.) / 20.);
}


float potential_at(in vec2 coord) {
    coord /= 100.;
    return 1. / length(coord - rvec2(1., -1.)) - 1./length(coord - rvec2(1., 1.)) +
        1. / length(coord - rvec2(-1., 1.)) - 1./length(coord - rvec2(-1., -1.));
    // return 1. / length(coord) + coord.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord - iResolution.xy / 2.;
    float p = potential_at(uv) * 2.;

    //int c = mod(p, 1.) < 0.02 ? 0 : 1;
    float c = sin(3.1415926535 * mod(p, 1.));

    vec3 col;
    if (p < 0.) {
    	col = vec3(1, 0, 0) * c;
    } else {
    	col = vec3(0, .5, 1) * c;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

float plane(vec3 p, float y)
{
 return p.y-y;
}

float sphere(vec3 p,vec3 pos,float r)
{
 return length(pos-p)-r;
