float scene(vec3 p)
{
 float sphere1=sphere(p,vec3(1.0,1,6),1.0);
 float sphere2=sphere(p,vec3(0.0,1.0,10),1.0);
 float sphere3=sphere(p,vec3(-4.0,1.0,20),1.0);
 float plane=plane(p,0.0);
 return min(sphere3,min(sphere1,min(sphere2,plane)));
}

float raymarch(vec3 origin,vec3 direction)
{
 float traveled=0.0;
 float distance=0.0;
 for(int i=0;i<100;i++)
 {
  vec3 p=origin+traveled*direction;
  distance=scene(p);
  if(distance<=0.01 || traveled>100.0)
  {
   break;
  }
  traveled+=distance;
 }
 return traveled;
}

vec3 normalAt(vec3 p)
{
 vec2 epsilon=vec2(0.01,0);
 float distanceAtP=scene(p);
 return normalize(vec3(distanceAtP-scene(p-epsilon.xyy),
                       distanceAtP-scene(p-epsilon.yxy),
                       distanceAtP-scene(p-epsilon.yyx)));
}

float diffuse(vec3 p)
{
 vec3 sunPos=vec3(sin(iTime),5,cos(iTime)+6.0);
 vec3 normal=normalAt(p);
 vec3 lightDir=normalize(sunPos-p);
 if(raymarch(p+lightDir*0.1,lightDir)<length(sunPos-p))
 {
  return 0.0;
 }
 return clamp(dot(lightDir,normal),0.0,1.0);
}

vec3 applyFog( in vec3  rgb,       // original color of the pixel
               in vec3 p ) // camera to point distance
{
    float distance=length(p);
    //float fogAmount = 1.0 - exp( -distance*(pow(1.0/p.y,2.0)) );
    float fogAmount = (1.0 - exp( p.y-1.5 ))*(p.y<1.5?1.0:0.0)*(noise(p.xz*0.2+(vec2(iTime*0.7))));
    vec3  fogColor  = vec3(0.5,0.6,0.7);
    return mix( rgb, fogColor, fogAmount );
}

vec3 applyFog2( in vec3  rgb,      // original color of the pixel
               in vec3 p, // camera to point distance
               in vec3  rayDir,   // camera to point vector
               in vec3  sunDir )  // sun light direction
{
    float distance=length(p);
    float fogAmount = 1.0 - exp( -distance*(pow(1.0/p.y,2.0)) );
    float sunAmount = max( dot( rayDir, sunDir ), 0.0 );
    vec3  fogColor  = mix( vec3(0.5,0.6,0.7), // bluish
                           vec3(1.0,0.9,0.7), // yellowish
                           pow(sunAmount,8.0) );
    return mix( rgb, fogColor, fogAmount );
}

