float distLine( vec2 p, vec2 a, vec2 b){
	vec2 pa = p - a;
    vec2 ba = b - a;
    float t = clamp(dot(pa, ba)/dot(ba, ba), 0.0, 1.0);
    return length(pa - ba*t);
}

float noiseFloat(vec2 p){
    p = fract(p * vec2(213.53, 970.19));
    p = p + dot(p, p+548.23);
    return fract(p.x * p.y);
}

vec2 noiseVector(vec2 p){
	float n = noiseFloat(p);
    return vec2(n, noiseFloat(p+n));
}


vec2 GetPos(vec2 id, vec2 offsets){
	vec2 n = noiseVector(id + offsets) * iTime;
    return sin(n) * 0.4 + offsets;
}

float Line(vec2 p, vec2 a, vec2 b){
	float d = distLine(p, a, b);
    float m = smoothstep(0.03, 0.002, d);
    m = m * smoothstep(1.5, 0.1, length(a-b));
    return m;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord -1.0*iResolution.xy)/iResolution.y;

    float m = 0.0;
    uv = uv * 10.0;
    vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);

    vec2 p[9];

    int i = 0;
    for(float y = -1.0; y<=1.0; y++){
    	for(float x = -1.0; x<=1.0; x++){
            p[i++] = GetPos(id, vec2(x, y));
        }
    }

    for(int i=0; i<9; i++){
    	m = m + Line(gv, p[4], p[i]);

        vec2 j = (p[i] - gv) * 28.0;
        float sparkle = 1.0/dot(j, j);

        m = m + sparkle * (sin((iTime+p[i].x)*3.0)*0.5 + 0.5);
    }

    m = m + Line(gv, p[1], p[3]);
    m = m + Line(gv, p[1], p[5]);
    m = m + Line(gv, p[7], p[3]);
    m = m + Line(gv, p[7], p[5]);

    vec3 col = vec3(0.0, m, 0.0);    

    // Output to screen
    fragColor = vec4(col,1.0);
}

// forked from https://www.shadertoy.com/view/Wtf3Wj

#define MAX_STEPS 200
#define MAX_DIST 250.
#define SURF_DIST .01


float sphereDist(vec3 p) {
    float sphere = length(p) - 1.;
	return sphere;
}


//get the distance to the closest
//point on the closest object
float sceneDist(vec3 p) {
	float planeDist = p.y;
    vec3 spherePos = vec3(0,1,8);
    float sphereRadius = 1.;

    float objDist = sphereDist(p-spherePos);

    float dist = min(planeDist, objDist);

    return dist;
}


float rayMarch(vec3 rayOrig, vec3 rayDir) {
	float dist = 0.;

    for (int i = 0; i < MAX_STEPS; ++i) {
		vec3 p = rayOrig + dist * rayDir;
		float sceneDist = sceneDist(p);

        dist += sceneDist;

        if (dist > MAX_DIST || sceneDist < SURF_DIST) {
        	break;
        }
	}

    return dist;
}


//get the normal of a point in 3d space,
//only valid if the point is on the surface
//of an object
vec3 getNormal(vec3 p) {
	float d = sceneDist(p);

    //just a helper variable
    vec2 e = vec2(.01, 0);

    //shift the point by a small
    //amount along each axis
    vec3 n = d - vec3(
        sceneDist(p - e.xyy),
        sceneDist(p - e.yxy),
        sceneDist(p - e.yyx)
    );

    return normalize(n);
}


//get the strength of the light
//at a 3d point
float getLight(vec3 p) {
	vec3 lightPos = vec3(-2, 5, 8);

    //move the light in a circle
    lightPos.xz += vec2(cos(iTime), sin(iTime)) * 10.;

 	vec3 normal = getNormal(p);
    vec3 toLight = normalize(lightPos - p);

    float angle = dot(normal, toLight);
    angle = clamp(angle, 0., 1.);

    vec3 rayOrig = p+normal*.02;
    vec3 rayDir = toLight;
    float dist = 0.;
    float maxDist = length(p-lightPos)+0.1;

    // contains last 3 distances
	vec3 history = vec3(0.);

    const float maxDistToObject = 0.3;

    // minimal distance to object for penumbra calculation
	float md = 1.0e+10;

    //add shadows
    for (int i = 0; i < MAX_STEPS; ++i) {
		vec3 p = rayOrig + dist * rayDir;

        history.yz = history.xy;
		history.x = sceneDist(p);
        dist += history.x;

        // detect V pattern
        if (history.y < maxDistToObject && history.x >= history.y && history.z > history.y)
            md = min(md, history.y);

        if (dist < 0.0 || dist > maxDist) {
        	break;
        }

        if (history.x < SURF_DIST) {
            md = 0.0;
            break;
        }
	}

    angle = angle * smoothstep(0.0, maxDistToObject, md);
    return angle;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    //ray origin is the camera position
    vec3 rayOrig = vec3(0, 5, -12);
    vec3 rayDir = vec3(uv.x, uv.y, 1.0);

    float dist = rayMarch(rayOrig, rayDir);

    //get the point that the ray hit
    //to use for lighting
    vec3 rayHitPos = rayOrig + rayDir * dist;
    float diff = getLight(rayHitPos);

    //grayscale colour based on lighting value
    vec3 col = vec3(diff);

    fragColor = vec4(col,1.0);
}

