void BuildCamera(vec3 pos, vec3 dir, vec3 up, float hfov, float aspect){
    camera.pos = pos;
    camera.dir = normalize(dir);
    camera.up = normalize(up);
    camera.right = cross(camera.dir, camera.up);
    camera.up = cross(camera.right, camera.dir);

    vec3 center = camera.pos + camera.dir;
    float h = tan(hfov/180.0*PI) * 2.0;
    float w = h * aspect;

    camera.horizontal = w * camera.right;
    camera.vertical = h * camera.up;
    camera.ll_corner = center - 0.5 * camera.horizontal - 0.5 * camera.vertical;
}

Ray BuildRay(vec2 fragCoord){
    vec2 uv = fragCoord / vec2(iResolution.x, iResolution.y);
    vec3 position = camera.pos;
    return Ray(position, camera.ll_corner + uv.x*camera.horizontal + uv.y*camera.vertical - position);
}

